export const qbMerchMultipleChoice = [
  {"FABM1-MerchBusActgMCQ-001": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cash Discount", "type": "Multiple Choice", "competency": "Understand", "question": "What does the credit term '2/10, n/30' mean?", "options": ["20% discount if paid within 10 days, net due in 30 days", "2% discount if paid within 10 days, net due in 30 days", "2% interest charged if not paid within 10 days", "Net due in 10 days, 2% penalty after 30 days"], "answer": 1, "explanation": "'2/10, n/30' means the buyer can take a 2% cash discount if the invoice is paid within 10 days; otherwise, the full (net) amount is due within 30 days."}},
  {"FABM1-MerchBusActgMCQ-002": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cash Discount", "type": "Multiple Choice", "competency": "Apply", "question": "A company purchases goods for ₱5,000 on terms 1/10, n/30. If payment is made within the discount period, how much cash is paid?", "options": ["₱5,000", "₱4,500", "₱4,950", "₱4,900"], "answer": 2, "explanation": "The discount is 1% of ₱5,000, which is ₱50. The cash payment is ₱5,000 - ₱50 = ₱4,950."}},
  {"FABM1-MerchBusActgMCQ-003": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight In", "type": "Multiple Choice", "competency": "Understand", "question": "If goods are shipped 'FOB Shipping Point', who is responsible for paying the freight costs?", "options": ["The seller", "The buyer", "The freight carrier", "Split equally between buyer and seller"], "answer": 1, "explanation": "FOB Shipping Point means title passes to the buyer at the shipping point. Therefore, the buyer owns the goods in transit and pays the freight costs."}},
  {"FABM1-MerchBusActgMCQ-004": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight In", "type": "Multiple Choice", "competency": "Remember", "question": "In a periodic inventory system, which account is used to record transportation costs paid by the buyer for incoming goods?", "options": ["Delivery Expense", "Freight Out", "Freight In", "Merchandise Inventory"], "answer": 2, "explanation": "Under the periodic system, transportation costs for purchased goods are debited to a specific 'Freight In' account, which is later added to net purchases to calculate the cost of goods purchased."}},
  {"FABM1-MerchBusActgMCQ-005": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Out", "type": "Multiple Choice", "competency": "Understand", "question": "If goods are sold 'FOB Destination', how is the freight cost classified by the seller?", "options": ["As part of Cost of Goods Sold", "As an operating expense (selling expense)", "As a reduction in Sales Revenue", "As an asset"], "answer": 1, "explanation": "When the seller pays freight (FOB Destination), it is a cost of delivering goods to the customer. This is recorded as Freight-Out (or Delivery Expense), which is an operating/selling expense."}},
  {"FABM1-MerchBusActgMCQ-006": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Out", "type": "Multiple Choice", "competency": "Apply", "question": "Seller S ships goods to Buyer B, terms FOB Destination. The freight bill is ₱200. Which entry should Seller S make?", "options": ["Debit Freight In ₱200; Credit Cash ₱200", "Debit Freight Out ₱200; Credit Cash ₱200", "Debit Inventory ₱200; Credit Cash ₱200", "Debit Accounts Receivable ₱200; Credit Cash ₱200"], "answer": 1, "explanation": "FOB Destination means the seller bears the cost. The expense is recorded as Freight Out (or Delivery Expense)."}},
  {"FABM1-MerchBusActgMCQ-007": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following primarily distinguishes a merchandising business from a service business?", "options": ["It generates revenue by providing professional advice.", "It buys and sells goods without altering their physical form.", "It converts raw materials into finished products.", "It has no operating expenses."], "answer": 1, "explanation": "A merchandising business earns revenue by buying and selling goods (merchandise) in the same physical form, unlike service businesses (services) or manufacturing businesses (conversion of materials)."}},
  {"FABM1-MerchBusActgMCQ-008": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Understand", "question": "In the operating cycle of a merchandising firm, what is the next step after purchasing inventory?", "options": ["Collecting cash from customers", "Selling the inventory", "Calculating net income", "Paying the supplier"], "answer": 1, "explanation": "The typical operating cycle involves purchasing inventory, selling that inventory to customers, and then collecting the cash."}},
  {"FABM1-MerchBusActgMCQ-009": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory System", "type": "Multiple Choice", "competency": "Understand", "question": "How is the Ending Inventory recorded in the books under a periodic inventory system?", "options": ["It is updated continuously after every sale.", "It is recorded via a closing entry after a physical count.", "It is estimated based on sales data.", "It is ignored until the next year."], "answer": 1, "explanation": "In the periodic system, the Inventory account balance remains unchanged during the period. The Ending Inventory is determined by a physical count and recorded through a closing (or adjusting) entry at the end of the period."}},
  {"FABM1-MerchBusActgMCQ-010": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory System", "type": "Multiple Choice", "competency": "Understand", "question": "What is the primary advantage of the perpetual inventory system?", "options": ["It requires less record-keeping.", "It provides continuous, real-time balances of inventory levels.", "It eliminates the need for an annual physical count.", "It is cheaper to implement."], "answer": 1, "explanation": "The perpetual system updates inventory records for every purchase and sale, providing real-time data on stock levels and COGS, though an annual physical count is still required for verification."}},
  {"FABM1-MerchBusActgMCQ-011": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory System", "type": "Multiple Choice", "competency": "Apply", "question": "Under the perpetual inventory system, the entry to record the purchase of merchandise on account includes a debit to:", "options": ["Purchases", "Merchandise Inventory", "Accounts Payable", "Cost of Goods Sold"], "answer": 1, "explanation": "In a perpetual system, purchases of merchandise are debited directly to the 'Merchandise Inventory' asset account, not a 'Purchases' account."}},
  {"FABM1-MerchBusActgMCQ-012": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory System", "type": "Multiple Choice", "competency": "Remember", "question": "When a sale is made under the perpetual inventory system, how many journal entries (or parts) are typically required?", "options": ["One: To record the revenue.", "One: To record the reduction of inventory.", "Two: One for revenue and one for the cost of goods sold.", "Three: Revenue, Cost, and Cash."], "answer": 2, "explanation": "A perpetual system requires two entries at the time of sale: (1) Record the sale (Debit Cash/AR, Credit Sales) and (2) Record the cost (Debit COGS, Credit Merchandise Inventory)."}},
  {"FABM1-MerchBusActgMCQ-013": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "Under a periodic inventory system, what is the effect of a purchase return on the accounting equation?", "options": ["Assets increase, Liabilities increase", "Assets decrease, Liabilities decrease", "Assets decrease, Owner's Equity increases", "No effect"], "answer": 1, "explanation": "Returning goods reduces the liability (Accounts Payable) owed to the supplier. In a periodic system, it is recorded in 'Purchase Returns and Allowances' (a contra-expense), effectively reducing the net cost of purchases, but the immediate balance sheet effect is lowering the payable and the net asset value."}},
  {"FABM1-MerchBusActgMCQ-014": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Remember", "question": "The 'Purchase Returns and Allowances' account is classified as what type of account?", "options": ["Asset", "Liability", "Contra-revenue", "Contra-expense (Contra-purchase)"], "answer": 3, "explanation": "Purchase Returns and Allowances has a normal credit balance and is deducted from Purchases (an expense/cost account) to arrive at Net Purchases."}},
  {"FABM1-MerchBusActgMCQ-015": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Apply", "question": "A customer returns defective goods sold for ₱500 on account. What is the journal entry for the seller (ignoring cost of goods sold)?", "options": ["Debit Sales ₱500; Credit Accounts Receivable ₱500", "Debit Sales Returns and Allowances ₱500; Credit Accounts Receivable ₱500", "Debit Accounts Receivable ₱500; Credit Sales Returns and Allowances ₱500", "Debit Sales Returns and Allowances ₱500; Credit Cash ₱500"], "answer": 1, "explanation": "The seller debits the contra-revenue account 'Sales Returns and Allowances' and credits 'Accounts Receivable' to reduce the amount owed by the customer."}},
  {"FABM1-MerchBusActgMCQ-016": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Understand", "question": "Why do companies use a separate 'Sales Returns and Allowances' account instead of debiting 'Sales' directly?", "options": ["It is required by tax laws.", "To allow management to monitor the volume of returns and dissatisfaction.", "Because 'Sales' is an asset account.", "It increases Net Income."], "answer": 1, "explanation": "Maintaining a separate account allows management to track the magnitude of returns, which can indicate issues with product quality or shipping accuracy."}},
  {"FABM1-MerchBusActgMCQ-017": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trade Discount", "type": "Multiple Choice", "competency": "Apply", "question": "A supplier offers a trade discount of 20% on an item with a list price of ₱1,000. What amount is recorded in the buyer's purchases account?", "options": ["₱1,000", "₱800", "₱200", "₱1,200"], "answer": 1, "explanation": "Trade discounts are deducted from the list price to determine the invoice price (₱1,000 - 20% = ₱800). Purchases are recorded at the invoice price; trade discounts are not recorded separately in the accounts."}},
  {"FABM1-MerchBusActgMCQ-018": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trade Discount", "type": "Multiple Choice", "competency": "Remember", "question": "Why are trade discounts generally used by sellers?", "options": ["To encourage early payment of debts", "To reduce the cost of goods sold", "To encourage bulk buying or adjusting prices without changing catalogs", "To account for damaged goods"], "answer": 2, "explanation": "Trade discounts are used to encourage large volume purchases (bulk buying) or to offer different pricing to different classes of customers without reprinting catalogs."}},
  {"FABM1-MerchBusActgMCQ-019": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Operating Cycle", "type": "Multiple Choice", "competency": "Remember", "question": "Which of the following best describes the operating cycle of a merchandising company?", "options": ["Cash > Perform Services > Accounts Receivable > Cash", "Cash > Buy Inventory > Sell Inventory > Accounts Receivable > Cash", "Buy Inventory > Cash > Sell Inventory > Accounts Receivable", "Cash > Accounts Receivable > Sell Inventory > Buy Inventory"], "answer": 1, "explanation": "The merchandising operating cycle involves using cash to buy inventory, selling that inventory, and collecting cash from customers."}},
  {"FABM1-MerchBusActgMCQ-020": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Apply", "question": "If Sales are ₱100,000, Sales Returns are ₱5,000, and Cost of Goods Sold is ₱60,000, what is the Gross Profit?", "options": ["₱35,000", "₱40,000", "₱95,000", "₱30,000"], "answer": 0, "explanation": "Net Sales = ₱100,000 - ₱5,000 = ₱95,000. Gross Profit = ₱95,000 - ₱60,000 = ₱35,000."}},
  {"FABM1-MerchBusActgMCQ-021": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "If a supplier offers 'FOB Destination, Freight Prepaid', who pays the carrier?", "options": ["The Buyer", "The Seller", "The Customer's Customer", "Split equally"], "answer": 1, "explanation": "FOB Destination means seller is responsible, and 'Freight Prepaid' means the seller actually pays the cash to the carrier."}},
  {"FABM1-MerchBusActgMCQ-022": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Account Classification", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following accounts is increased by a credit?", "options": ["Sales Discounts", "Merchandise Inventory", "Purchase Returns and Allowances", "Freight-In"], "answer": 2, "explanation": "Purchase Returns and Allowances is a contra-expense account that reduces Purchases, so it has a normal credit balance."}},
  {"FABM1-MerchBusActgMCQ-023": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Apply", "question": "A retailer buys a product for ₱40 and sells it for ₱100. If the operating expenses are 20% of sales, what is the Net Income?", "options": ["₱40", "₱60", "₱20", "₱80"], "answer": 0, "explanation": "Sales ₱100 - COGS ₱40 = Gross Profit ₱60. Operating Exp = 20% of ₱100 = ₱20. Net Income = ₱60 - ₱20 = ₱40."}},
  {"FABM1-MerchBusActgMCQ-024": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "When does title pass to the buyer if the terms are 'FOB Shipping Point'?", "options": ["When the carrier accepts the goods from the seller", "When the goods arrive at the buyer's warehouse", "When the invoice is paid", "When the purchase order is signed"], "answer": 0, "explanation": "FOB Shipping Point means the buyer becomes the owner as soon as the goods are loaded onto the transport vehicle."}},
  {"FABM1-MerchBusActgMCQ-025": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company uses the Periodic system, which adjusting entry is required at year-end regarding inventory?", "options": ["Debit Inventory (End), Credit Inventory (Beg)", "Debit Inventory (End), Credit Income Summary; Debit Income Summary, Credit Inventory (Beg)", "Debit COGS, Credit Inventory", "No entry is needed"], "answer": 1, "explanation": "In periodic systems, the beginning inventory is removed (credited) and the ending inventory is established (debited) through closing/adjusting entries."}},
  {"FABM1-MerchBusActgMCQ-026": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Calculate Cost of Goods Available for Sale: Beg Inv 200u @ ₱10; Purchase 300u @ ₱15; Purchase Returns 50u @ ₱15.", "options": ["₱5,750", "₱6,500", "₱5,000", "₱4,250"], "answer": 0, "explanation": "Beg Inv ₱2,000. Net Purch = (300*15) - (50*15) = 4500 - 750 = ₱3,750. GAS = ₱2,000 + ₱3,750 = ₱5,750."}},
  {"FABM1-MerchBusActgMCQ-027": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "What is the primary advantage of the Perpetual Inventory System?", "options": ["It is easier to use manually.", "It provides real-time information on inventory levels and shrinkage.", "It requires no journal entries for sales.", "It eliminates the need for any physical counts ever."], "answer": 1, "explanation": "Its main benefit is the continuous tracking of stock, allowing for better management and immediate detection of discrepancies."}},
  {"FABM1-MerchBusActgMCQ-028": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trade Discount", "type": "Multiple Choice", "competency": "Understand", "question": "Which term describes a reduction in the selling price granted to a customer who buys a large quantity of goods?", "options": ["Sales Discount", "Quantity (Trade) Discount", "Purchase Return", "Freight Allowance"], "answer": 1, "explanation": "Trade or quantity discounts are given to encourage bulk buying and are deducted before the transaction is recorded."}},
  {"FABM1-MerchBusActgMCQ-029": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Apply", "question": "Perpetual System: Purchased ₱2,000 merchandise on account. Returned ₱400. Paid balance within discount period (2%). Entry to record payment includes:", "options": ["Credit Cash ₱1,600", "Credit Merchandise Inventory ₱32", "Debit Accounts Payable ₱2,000", "Debit Purchases ₱1,600"], "answer": 1, "explanation": "Payable is ₱1,600. Discount is ₱1,600 * 2% = ₱32. In perpetual, discounts are credited to Merchandise Inventory."}},
  {"FABM1-MerchBusActgMCQ-030": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Under the Gross Method of recording purchases, purchase discounts are recorded:", "options": ["At the time of purchase", "Only when payment is made within the discount period", "At the end of the year", "Never"], "answer": 1, "explanation": "The purchase is recorded at full price; the discount is recognized only if and when it is actually taken."}},
  {"FABM1-MerchBusActgMCQ-031": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Understand", "question": "Under the FIFO method, which costs are assigned to ending inventory?", "options": ["The oldest costs", "The average costs", "The most recent costs", "The lowest costs"], "answer": 2, "explanation": "FIFO assumes the first items in are the first sold, leaving the most recent purchases in ending inventory."}},
  {"FABM1-MerchBusActgMCQ-032": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Understand", "question": "Sales Returns and Allowances is deducted from:", "options": ["Cost of Goods Sold", "Gross Profit", "Total Sales Revenue", "Operating Expenses"], "answer": 2, "explanation": "It is a contra-revenue account deducted from Gross Sales to arrive at Net Sales."}},
  {"FABM1-MerchBusActgMCQ-033": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Errors", "type": "Multiple Choice", "competency": "Analyze", "question": "If ending inventory is understated, what is the effect on the current year's Net Income?", "options": ["Net Income is understated", "Net Income is overstated", "No effect", "Assets are overstated"], "answer": 0, "explanation": "Understated End Inv -> Overstated COGS -> Understated Net Income."}},
  {"FABM1-MerchBusActgMCQ-034": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average", "type": "Multiple Choice", "competency": "Apply", "question": "Moving Average: Inv 10u @ ₱10. Purchase 10u @ ₱14. What is the new unit cost?", "options": ["₱12.00", "₱10.00", "₱14.00", "₱11.50"], "answer": 0, "explanation": "Total Cost ₱100 + ₱140 = ₱240. Total Units 20. ₱240 / 20 = ₱12.00."}},
  {"FABM1-MerchBusActgMCQ-035": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Selling Expenses", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is an example of a selling expense?", "options": ["Office supplies expense", "Store rent expense", "Interest expense", "Legal fees"], "answer": 1, "explanation": "Store rent is directly related to the sales function (the showroom), making it a selling expense."}},
  {"FABM1-MerchBusActgMCQ-036": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "In a periodic system, the 'Purchase Returns and Allowances' account is closed to:", "options": ["Retained Earnings", "Cost of Goods Sold", "Income Summary (Credit side)", "Income Summary (Debit side)"], "answer": 2, "explanation": "It has a normal credit balance, so it is debited to close, and Income Summary is credited (effectively reducing the cost of purchases)."}},
  {"FABM1-MerchBusActgMCQ-037": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Apply", "question": "Sales ₱80,000; COGS ₱50,000; Sales Returns ₱5,000; Sales Discounts ₱2,000. Calculate Gross Profit.", "options": ["₱23,000", "₱30,000", "₱28,000", "₱25,000"], "answer": 0, "explanation": "Net Sales = 80k - 5k - 2k = 73k. Gross Profit = 73k - 50k = ₱23,000."}},
  {"FABM1-MerchBusActgMCQ-038": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Internal Control", "type": "Multiple Choice", "competency": "Understand", "question": "Which document is used by the receiving department to check the quantity and condition of goods received?", "options": ["Purchase Order (Blind Copy)", "Sales Invoice", "Check Voucher", "Credit Memo"], "answer": 0, "explanation": "A blind purchase order (without quantities) forces the receiving clerk to actually count the goods."}},
  {"FABM1-MerchBusActgMCQ-039": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Account Classification", "type": "Multiple Choice", "competency": "Analyze", "question": "Why is 'Freight-In' part of COGS while 'Freight-Out' is an operating expense?", "options": ["Freight-In adds value to the inventory; Freight-Out is a service to the customer.", "Freight-In is tax deductible; Freight-Out is not.", "Freight-Out is always smaller.", "They should both be in COGS."], "answer": 0, "explanation": "Freight-In is a cost of acquisition (product cost). Freight-Out is a cost of distribution (period cost)."}},
  {"FABM1-MerchBusActgMCQ-040": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Ratios", "type": "Multiple Choice", "competency": "Apply", "question": "Inventory turnover is 10 times. Average inventory is ₱20,000. What is the Cost of Goods Sold?", "options": ["₱200,000", "₱2,000", "₱100,000", "₱20,000"], "answer": 0, "explanation": "Turnover = COGS / Avg Inv. 10 = COGS / 20,000. COGS = ₱200,000."}},
  {"FABM1-MerchBusActgMCQ-041": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "LIFO", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory method assumes the last goods purchased are the first ones sold?", "options": ["LIFO", "FIFO", "Average Cost", "Specific Identification"], "answer": 0, "explanation": "LIFO stands for Last-In, First-Out."}},
  {"FABM1-MerchBusActgMCQ-042": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Understand", "question": "In a period of rising prices, which inventory method results in the highest net income?", "options": ["FIFO", "Weighted Average", "Moving Average", "Specific Identification"], "answer": 0, "explanation": "FIFO matches older, lower costs against current revenues, resulting in higher gross profit and net income."}},
  {"FABM1-MerchBusActgMCQ-043": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "Credit terms 'n/30' indicate:", "options": ["A 30% discount is available", "The net amount is due in 30 days with no discount", "New customers have 30 days to pay", "Interest is charged after 30 days"], "answer": 1, "explanation": "'n' stands for net. It means the full invoice amount is due within 30 days."}},
  {"FABM1-MerchBusActgMCQ-044": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Apply", "question": "Company buys goods for ₱500, FOB Shipping Point, Freight Collect ₱50. How much cash does the buyer pay to the carrier?", "options": ["₱0", "₱50", "₱550", "₱500"], "answer": 1, "explanation": "'Freight Collect' means the buyer pays the carrier upon arrival. The buyer pays ₱50."}},
  {"FABM1-MerchBusActgMCQ-045": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "Net Income from Operations excludes which of the following?", "options": ["Cost of Goods Sold", "Sales Salaries", "Gain on Sale of Equipment", "Advertising Expense"], "answer": 2, "explanation": "Gains/Losses on asset sales are 'Other Income/Expense', not part of operating income."}},
  {"FABM1-MerchBusActgMCQ-046": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Costing Methods", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company uses FIFO and prices are falling, how will COGS compare to LIFO?", "options": ["FIFO COGS will be higher", "FIFO COGS will be lower", "They will be the same", "Cannot be determined"], "answer": 0, "explanation": "In deflation (falling prices), FIFO sells the older (higher cost) goods first, resulting in higher COGS."}},
  {"FABM1-MerchBusActgMCQ-047": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average", "type": "Multiple Choice", "competency": "Apply", "question": "Weighted Average: 10 units @ ₱10, 10 units @ ₱20. Sold 5 units. What is the value of Ending Inventory?", "options": ["₱225", "₱200", "₱250", "₱150"], "answer": 0, "explanation": "Avg Cost = ₱15. Remaining units = 15. Value = 15 * ₱15 = ₱225."}},
  {"FABM1-MerchBusActgMCQ-048": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "Which account is NOT used in a Perpetual Inventory System?", "options": ["Cost of Goods Sold", "Merchandise Inventory", "Sales", "Purchases"], "answer": 3, "explanation": "The 'Purchases' account is replaced by direct debits to 'Merchandise Inventory' in a perpetual system."}},
  {"FABM1-MerchBusActgMCQ-049": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Allowances", "type": "Multiple Choice", "competency": "Understand", "question": "A sales allowance differs from a sales return because:", "options": ["The customer keeps the goods in an allowance", "The customer returns the goods in an allowance", "Allowances are for cash sales only", "Allowances are never recorded"], "answer": 0, "explanation": "In an allowance, the customer is given a price reduction for defective goods but retains possession of them."}},
  {"FABM1-MerchBusActgMCQ-050": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Apply", "question": "Invoice date Nov 1. Terms 2/10, n/30. When is the last day to pay and receive the discount?", "options": ["Nov 10", "Nov 11", "Nov 30", "Nov 15"], "answer": 1, "explanation": "10 days from Nov 1 is Nov 11."}},
  {"FABM1-MerchBusActgMCQ-051": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Balance Sheet", "type": "Multiple Choice", "competency": "Understand", "question": "In a Classified Balance Sheet, Merchandise Inventory is listed immediately after:", "options": ["Cash", "Accounts Receivable", "Prepaid Insurance", "Property, Plant, and Equipment"], "answer": 1, "explanation": "Current assets are listed by liquidity: Cash, AR, then Inventory."}},
  {"FABM1-MerchBusActgMCQ-052": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Apply", "question": "Net Sales ₱200,000. Beg Inv ₱20,000. End Inv ₱30,000. Purch ₱150,000. Calculate Gross Profit.", "options": ["₱60,000", "₱50,000", "₱40,000", "₱70,000"], "answer": 0, "explanation": "COGS = 20 + 150 - 30 = 140. GP = 200 - 140 = ₱60,000."}},
  {"FABM1-MerchBusActgMCQ-053": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average", "type": "Multiple Choice", "competency": "Apply", "question": "Calculate the Weighted Average Unit Cost: Beginning Inv: 10 units @ ₱10; Purchase: 10 units @ ₱12.", "options": ["₱10.00", "₱11.00", "₱12.00", "₱11.50"], "answer": 1, "explanation": "Total Cost = (10*₱10) + (10*₱12) = ₱220. Total Units = 20. Average = ₱220 / 20 = ₱11.00."}},
  {"FABM1-MerchBusActgMCQ-054": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "If a seller pays the freight costs on behalf of a buyer (FOB Shipping Point), the seller records this as:", "options": ["Freight-Out Expense", "A receivable from the buyer", "Cost of Goods Sold", "A reduction in Sales"], "answer": 1, "explanation": "Since it's FOB Shipping Point, the buyer owes the freight. If the seller prepays it, they add it to the Accounts Receivable."}},
  {"FABM1-MerchBusActgMCQ-055": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Control", "type": "Multiple Choice", "competency": "Analyze", "question": "Which system provides better control over inventory theft?", "options": ["Periodic", "Perpetual", "Cash Basis", "Single Entry"], "answer": 1, "explanation": "Perpetual allows comparison between what the computer says should be there vs. what is actually there."}},
  {"FABM1-MerchBusActgMCQ-056": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Costs", "type": "Multiple Choice", "competency": "Understand", "question": "Inventory costs include all of the following EXCEPT:", "options": ["Invoice price less discounts", "Freight-In", "Storage costs during production", "Interest on loan to buy inventory"], "answer": 3, "explanation": "Interest is a financing cost, not a product cost (unless it's a qualifying asset under specific standards, but generally no for merchandise)."}},
  {"FABM1-MerchBusActgMCQ-057": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Understand", "question": "The account 'Sales Discounts' is closed at the end of the period by:", "options": ["Debiting Income Summary", "Crediting Income Summary", "Debiting Sales", "Crediting Cash"], "answer": 1, "explanation": "Sales Discounts has a normal debit balance. To close it, you credit Sales Discounts and debit Income Summary."}},
  {"FABM1-MerchBusActgMCQ-058": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Apply", "question": "FIFO (Periodic): Beg Inv 10u @ ₱10. Purch 10u @ ₱12. Purch 10u @ ₱14. End Inv 12 units. Value?", "options": ["₱164", "₱168", "₱120", "₱144"], "answer": 0, "explanation": "End Inv comes from newest layers: 10u @ ₱14 (₱140) + 2u @ ₱12 (₱24) = ₱164."}},
  {"FABM1-MerchBusActgMCQ-059": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Consignment", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following creates a 'Consigned Goods' relationship?", "options": ["Seller ships goods to buyer FOB Destination", "Manufacturer sends goods to a retailer to sell on their behalf", "Retailer buys goods on credit", "Customer returns goods for a refund"], "answer": 1, "explanation": "Consignment is when an agent (retailer) sells goods owned by the principal (manufacturer)."}},
  {"FABM1-MerchBusActgMCQ-060": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "FOB Destination, Freight Collect. Who pays the carrier and who bears the expense?", "options": ["Buyer pays, Buyer bears expense", "Buyer pays, Seller bears expense", "Seller pays, Seller bears expense", "Seller pays, Buyer bears expense"], "answer": 1, "explanation": "Freight Collect = Buyer pays cash. FOB Destination = Seller's expense. Buyer pays and deducts it from the payment to Seller."}},
  {"FABM1-MerchBusActgMCQ-061": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Apply", "question": "Perpetual Inventory balance is ₱50,000. Physical count shows ₱48,000. The adjusting entry is:", "options": ["Debit COGS ₱2,000; Credit Inventory ₱2,000", "Debit Inventory ₱2,000; Credit COGS ₱2,000", "Debit Loss ₱2,000; Credit Sales ₱2,000", "Debit Inventory ₱2,000; Credit Gain ₱2,000"], "answer": 0, "explanation": "Inventory must be reduced (credited) to match the count. The loss is debited to COGS."}},
  {"FABM1-MerchBusActgMCQ-062": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Administrative Expenses", "type": "Multiple Choice", "competency": "Understand", "question": "Administrative expenses include:", "options": ["Sales commissions", "Delivery vehicle depreciation", "Executive salaries", "Store advertising"], "answer": 2, "explanation": "Executive salaries are general overhead, not directly tied to the selling activity."}},
  {"FABM1-MerchBusActgMCQ-063": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory costing method requires re-calculating the average cost after every purchase?", "options": ["Weighted Average (Periodic)", "Moving Average (Perpetual)", "FIFO", "Specific Identification"], "answer": 1, "explanation": "The Moving Average method, used in perpetual systems, recalculates the unit cost after every purchase."}},
  {"FABM1-MerchBusActgMCQ-064": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Purchases ₱60,000; Purchase Returns ₱5,000; Purchase Discounts ₱1,000; Freight-In ₱4,000. What is Cost of Goods Purchased?", "options": ["₱58,000", "₱60,000", "₱59,000", "₱54,000"], "answer": 0, "explanation": "60k - 5k - 1k + 4k = ₱58,000."}},
  {"FABM1-MerchBusActgMCQ-065": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Costing Methods", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory method is most susceptible to profit manipulation by management?", "options": ["FIFO", "Specific Identification", "Weighted Average", "Perpetual"], "answer": 1, "explanation": "Managers can 'cherry-pick' which specific units to sell to hit a desired COGS/Profit target."}},
  {"FABM1-MerchBusActgMCQ-066": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Account Classification", "type": "Multiple Choice", "competency": "Understand", "question": "The entry to record Cost of Goods Sold includes a debit to:", "options": ["Merchandise Inventory", "Sales", "Cost of Goods Sold", "Accounts Receivable"], "answer": 2, "explanation": "COGS is an expense, so it is debited."}},
  {"FABM1-MerchBusActgMCQ-067": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "LIFO", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company uses LIFO and prices are rising, ending inventory on the balance sheet will be:", "options": ["Higher than FIFO", "Lower than FIFO", "Same as FIFO", "Equal to market value"], "answer": 1, "explanation": "LIFO leaves the oldest (cheaper) costs in inventory, resulting in a lower asset value compared to FIFO."}},
  {"FABM1-MerchBusActgMCQ-068": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "What is the meaning of '2/10, n/30' if the invoice is ₱1,000 dated Jan 1?", "options": ["Pay ₱980 by Jan 11 or ₱1,000 by Jan 31", "Pay ₱20 by Jan 10", "Pay ₱1,000 by Jan 10", "Pay ₱1,020 by Jan 31"], "answer": 0, "explanation": "2% discount (₱20) if paid by Jan 11. Full amount (₱1000) due by Jan 31 (30 days)."}},
  {"FABM1-MerchBusActgMCQ-069": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Apply", "question": "Company returned ₱500 of goods purchased on credit. Periodic System entry?", "options": ["Debit Accounts Payable ₱500, Credit Inventory ₱500", "Debit Accounts Payable ₱500, Credit Purchase Returns ₱500", "Debit Cash ₱500, Credit Purchases ₱500", "Debit Purchase Returns ₱500, Credit AP ₱500"], "answer": 1, "explanation": "Periodic uses the specific 'Purchase Returns' account."}},
  {"FABM1-MerchBusActgMCQ-070": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "Net Sales - Cost of Goods Sold = ?", "options": ["Net Income", "Operating Income", "Gross Margin", "EBITDA"], "answer": 2, "explanation": "Gross Margin is another term for Gross Profit."}},
  {"FABM1-MerchBusActgMCQ-071": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Inv ₱5k. Purch ₱20k. End Inv ₱8k. Sales ₱30k. Operating Exp ₱5k. Net Income?", "options": ["₱13,000", "₱8,000", "₱17,000", "₱12,000"], "answer": 1, "explanation": "COGS = 5+20-8 = 17k. GP = 30-17 = 13k. Net Income = 13k - 5k = ₱8,000."}},
  {"FABM1-MerchBusActgMCQ-072": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is NOT a characteristic of the Periodic Inventory System?", "options": ["Cost of Goods Sold is calculated at period end", "Inventory account is not updated during the period", "Detailed records of inventory on hand are maintained continuously", "Purchases account is used"], "answer": 2, "explanation": "Continuous records are the definition of a Perpetual system, not Periodic."}},
  {"FABM1-MerchBusActgMCQ-073": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "Freight-In is reported on the Income Statement (Periodic) in which section?", "options": ["Operating Expenses", "Cost of Goods Sold calculation", "Other Revenues and Gains", "Deduction from Sales"], "answer": 1, "explanation": "It is added to Purchases to determine Cost of Goods Purchased."}},
  {"FABM1-MerchBusActgMCQ-074": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "What is the formula for Net Sales?", "options": ["Sales - Cost of Goods Sold", "Sales - Sales Returns & Allowances - Sales Discounts", "Gross Profit - Operating Expenses", "Sales + Freight Out"], "answer": 1, "explanation": "Net Sales is calculated by deducting Sales Returns, Allowances, and Discounts from Gross Sales."}},
  {"FABM1-MerchBusActgMCQ-075": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Apply", "question": "Sold goods for ₱1,000 cash. Cost was ₱600. Return rate is estimated at 0%. Perpetual entry to record cost?", "options": ["Debit COGS ₱600, Credit Sales ₱600", "Debit COGS ₱600, Credit Inventory ₱600", "Debit Inventory ₱600, Credit COGS ₱600", "Debit Sales ₱1,000, Credit Inventory ₱1,000"], "answer": 1, "explanation": "The cost side of the entry debits the expense (COGS) and credits the asset (Inventory)."}},
  {"FABM1-MerchBusActgMCQ-076": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "When a buyer returns damaged goods under the periodic system, which account is credited?", "options": ["Merchandise Inventory", "Purchase Returns and Allowances", "Accounts Payable", "Sales Returns"], "answer": 1, "explanation": "In a periodic system, the return is recorded in the contra-cost account 'Purchase Returns and Allowances'."}},
  {"FABM1-MerchBusActgMCQ-077": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "When a buyer returns damaged goods under the perpetual system, which account is credited?", "options": ["Purchase Returns and Allowances", "Cost of Goods Sold", "Merchandise Inventory", "Sales Revenue"], "answer": 2, "explanation": "In a perpetual system, returns of purchases directly reduce the Merchandise Inventory account."}},
  {"FABM1-MerchBusActgMCQ-078": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Costs", "type": "Multiple Choice", "competency": "Analyze", "question": "Why is 'Freight-In' added to the cost of purchases?", "options": ["It is an administrative expense.", "It is a selling expense.", "It is a necessary cost to get the inventory ready for sale.", "It increases the sales price."], "answer": 2, "explanation": "Costs incurred to bring inventory to the location and condition for sale are capitalized as product costs."}},
  {"FABM1-MerchBusActgMCQ-079": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Apply", "question": "Goods costing ₱1,000 are sold for ₱1,500 on account. In a perpetual system, what is the journal entry for the cost part?", "options": ["Debit Accounts Receivable ₱1,500; Credit Sales ₱1,500", "Debit Cost of Goods Sold ₱1,000; Credit Merchandise Inventory ₱1,000", "Debit Purchases ₱1,000; Credit Inventory ₱1,000", "Debit Inventory ₱1,000; Credit Cost of Goods Sold ₱1,000"], "answer": 1, "explanation": "Perpetual systems require a second entry at the time of sale to record the expense (COGS) and reduce inventory."}},
  {"FABM1-MerchBusActgMCQ-080": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "In a perpetual inventory system, which account is debited when goods are purchased for resale?", "options": ["Purchases", "Cost of Goods Sold", "Merchandise Inventory", "Supplies"], "answer": 2, "explanation": "Under the perpetual system, purchases of merchandise are recorded directly to the Merchandise Inventory asset account."}},
  {"FABM1-MerchBusActgMCQ-081": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Beginning Inventory ₱20,000; Net Purchases ₱50,000; Ending Inventory ₱10,000. What is Cost of Goods Sold?", "options": ["₱60,000", "₱70,000", "₱40,000", "₱80,000"], "answer": 0, "explanation": "COGS = Beginning Inventory + Net Purchases - Ending Inventory. 20k + 50k - 10k = 60k."}},
  {"FABM1-MerchBusActgMCQ-082": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Purchase Discounts are credited to which account in a Perpetual Inventory System?", "options": ["Purchase Discounts", "Merchandise Inventory", "Sales Discounts", "Cash"], "answer": 1, "explanation": "In a perpetual system, a discount on purchase reduces the cost basis of the inventory directly."}},
  {"FABM1-MerchBusActgMCQ-083": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounting Principles", "type": "Multiple Choice", "competency": "Analyze", "question": "Which accounting principle requires the inventory method used to be consistent from year to year?", "options": ["Materiality", "Consistency Principle", "Full Disclosure", "Conservatism"], "answer": 1, "explanation": "The Consistency Principle states that a company should use the same accounting methods period to period for comparability."}},
  {"FABM1-MerchBusActgMCQ-084": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "The inventory system that provides a continuous record of inventory balances is:", "options": ["Periodic System", "Perpetual System", "Physical System", "Accrual System"], "answer": 1, "explanation": "Perpetual systems update records immediately after every purchase or sale."}},
  {"FABM1-MerchBusActgMCQ-085": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Company purchased ₱2,000 of goods, terms 1/15, n/60. Freight was ₱100 FOB Shipping Point (paid by buyer). What is the total cost of inventory if paid within discount period?", "options": ["₱2,080", "₱2,100", "₱1,980", "₱2,000"], "answer": 0, "explanation": "Discount applies to merchandise only: ₱2,000 * 0.99 = ₱1,980. Add Freight: ₱1,980 + ₱100 = ₱2,080."}},
  {"FABM1-MerchBusActgMCQ-086": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Understand", "question": "Cost of Goods Available for Sale is equal to:", "options": ["Beginning Inventory + Net Purchases", "Beginning Inventory + Cost of Goods Sold", "Ending Inventory + Sales", "Net Purchases - Ending Inventory"], "answer": 0, "explanation": "It represents the total pool of goods that could potentially be sold: what you started with plus what you bought."}},
  {"FABM1-MerchBusActgMCQ-087": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "In the Periodic system, the 'Cost of Goods Sold' is determined:", "options": ["At the time of each sale", "At the end of the accounting period", "At the beginning of the period", "When cash is collected"], "answer": 1, "explanation": "In periodic systems, COGS is a residual calculation performed after the physical count at period end."}},
  {"FABM1-MerchBusActgMCQ-088": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average", "type": "Multiple Choice", "competency": "Apply", "question": "Under Weighted Average (Periodic), calculate Ending Inventory: Begin 100u @ ₱10; Purchase 100u @ ₱20. Sold 150u.", "options": ["₱750", "₱1,000", "₱500", "₱1,500"], "answer": 0, "explanation": "Total Cost = ₱3,000. Total Units = 200. Avg Cost = ₱15. End Inv = 50 units * ₱15 = ₱750."}},
  {"FABM1-MerchBusActgMCQ-089": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Errors", "type": "Multiple Choice", "competency": "Analyze", "question": "If ending inventory is overstated, what is the effect on Cost of Goods Sold and Net Income?", "options": ["COGS overstated, Net Income understated", "COGS understated, Net Income overstated", "COGS overstated, Net Income overstated", "No effect"], "answer": 1, "explanation": "COGS = Available - End Inv. If End Inv is too high, the deduction is too large, making COGS too low, which increases Net Income."}},
  {"FABM1-MerchBusActgMCQ-090": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "In a periodic inventory system, which account is debited when goods are purchased for resale?", "options": ["Merchandise Inventory", "Purchases", "Accounts Payable", "Cost of Goods Sold"], "answer": 1, "explanation": "Under the periodic system, purchases are recorded in a temporary account called 'Purchases'."}},
  {"FABM1-MerchBusActgMCQ-091": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Contra Accounts", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is NOT a contra account?", "options": ["Sales Returns and Allowances", "Sales Discounts", "Purchase Returns and Allowances", "Freight-Out"], "answer": 3, "explanation": "Freight-Out is a regular expense account. The others are contra accounts to Sales or Purchases."}},
  {"FABM1-MerchBusActgMCQ-092": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "The difference between Sales Revenue and Cost of Goods Sold is called:", "options": ["Net Income", "Gross Profit", "Operating Income", "Retained Earnings"], "answer": 1, "explanation": "Gross Profit (or Gross Margin) is the direct profit from selling inventory before operating expenses."}},
  {"FABM1-MerchBusActgMCQ-093": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Apply", "question": "Invoice price is ₱500. Terms 2/10, n/30. Payment is made after 20 days. How much is paid?", "options": ["₱490", "₱500", "₱510", "₱400"], "answer": 1, "explanation": "Payment was made after the 10-day discount period, so the full gross amount (₱500) is due."}},
  {"FABM1-MerchBusActgMCQ-094": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory method parallels the actual physical flow of merchandise for most businesses (e.g., grocery stores)?", "options": ["FIFO", "LIFO", "Weighted Average", "Moving Average"], "answer": 0, "explanation": "Most businesses sell the oldest goods first to prevent spoilage/obsolescence, matching the FIFO assumption."}},
  {"FABM1-MerchBusActgMCQ-095": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trade vs Cash Discount", "type": "Multiple Choice", "competency": "Analyze", "question": "What is the primary difference between a trade discount and a cash discount?", "options": ["Trade discounts are for prompt payment; cash discounts are for quantity.", "Trade discounts are recorded in accounts; cash discounts are not.", "Trade discounts reduce list price to invoice price; cash discounts are for prompt payment.", "There is no difference."], "answer": 2, "explanation": "Trade discounts determine the actual selling price (invoice price). Cash discounts incentivize early payment of that invoice."}},
  {"FABM1-MerchBusActgMCQ-096": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "In a periodic system, the physical inventory count is needed to determine:", "options": ["Sales Revenue", "Cash on hand", "Cost of Goods Sold and Ending Inventory", "Accounts Receivable"], "answer": 2, "explanation": "Because the system doesn't track inventory flow, a physical count is required to value Ending Inventory and derive COGS."}},
  {"FABM1-MerchBusActgMCQ-097": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Apply", "question": "Gross Profit is ₱40,000. Operating Expenses are ₱15,000. Net Sales are ₱100,000. What is the Net Income?", "options": ["₱25,000", "₱60,000", "₱85,000", "₱55,000"], "answer": 0, "explanation": "Net Income = Gross Profit - Operating Expenses. ₱40,000 - ₱15,000 = ₱25,000."}},
  {"FABM1-MerchBusActgMCQ-098": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Specific Identification", "type": "Multiple Choice", "competency": "Understand", "question": "The 'Specific Identification' method is most appropriate for:", "options": ["Low-value, high-volume items like nails", "Unique, high-value items like luxury cars", "Liquids like gasoline", "Identical produce items"], "answer": 1, "explanation": "It tracks the specific cost of individual items, which is feasible only for unique, expensive goods."}},
  {"FABM1-MerchBusActgMCQ-099": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Calculate Net Purchases: Purchases ₱50,000, Purchase Returns ₱2,000, Purchase Discounts ₱1,000, Freight-In ₱3,000.", "options": ["₱50,000", "₱47,000", "₱51,000", "₱52,000"], "answer": 0, "explanation": "Net Purch = Purchases - Returns - Discounts + Freight-In. ₱50k - ₱2k - ₱1k + ₱3k = ₱50,000."}},
  {"FABM1-MerchBusActgMCQ-100": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Balance Sheet", "type": "Multiple Choice", "competency": "Understand", "question": "Merchandise Inventory is reported on the Balance Sheet as a:", "options": ["Current Liability", "Current Asset", "Long-term Asset", "Owner's Equity"], "answer": 1, "explanation": "Inventory is a liquid asset expected to be sold within the operating cycle, hence Current Asset."}},
  {"FABM1-MerchBusActgMCQ-101": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trade Discount", "type": "Multiple Choice", "competency": "Apply", "question": "A seller offers a trade discount of 20% on an item with a list price of ₱1,000. What amount is recorded in the purchases journal?", "options": ["₱1,000", "₱200", "₱800", "₱1,200"], "answer": 2, "explanation": "Trade discounts are deducted before recording the transaction. ₱1,000 - (₱1,000 * 20%) = ₱800."}},
  {"FABM1-MerchBusActgMCQ-102": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average", "type": "Multiple Choice", "competency": "Apply", "question": "Moving Average (Perpetual): 10 units @ ₱10. Buy 10 units @ ₱20. Sell 5 units. What is the cost of the sale?", "options": ["₱50", "₱75", "₱100", "₱60"], "answer": 1, "explanation": "New Avg Cost = (10*₱10 + 10*₱20) / 20 = ₱15/unit. Cost of sale = 5 units * ₱15 = ₱75."}},
  {"FABM1-MerchBusActgMCQ-103": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Understand", "question": "Which account is closed to Income Summary for a merchandising business but not a service business?", "options": ["Salaries Expense", "Cost of Goods Sold", "Depreciation Expense", "Rent Expense"], "answer": 1, "explanation": "Cost of Goods Sold is an expense unique to businesses that sell inventory."}},
  {"FABM1-MerchBusActgMCQ-104": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company uses FOB Destination, at what point does the buyer record the inventory?", "options": ["When goods leave the seller's warehouse", "When goods arrive at the buyer's warehouse", "When the order is placed", "When payment is made"], "answer": 1, "explanation": "Title transfers upon arrival (destination), so the buyer records the asset only then."}},
  {"FABM1-MerchBusActgMCQ-105": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "Under the Periodic system, freight-in is recorded in:", "options": ["Merchandise Inventory account", "A separate 'Freight-In' account", "Cost of Goods Sold account", "Operating Expenses"], "answer": 1, "explanation": "Periodic systems use temporary accounts, so a specific Freight-In account is used."}},
  {"FABM1-MerchBusActgMCQ-106": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "A ₱1,000 sale is made with terms 2/10, n/30. The customer returns ₱200 of goods, then pays the balance within the discount period. Amount received?", "options": ["₱780", "₱800", "₱784", "₱980"], "answer": 2, "explanation": "Remaining balance ₱800. Discount 2% of ₱800 = ₱16. Cash received = ₱800 - ₱16 = ₱784."}},
  {"FABM1-MerchBusActgMCQ-107": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "In a perpetual inventory system, the inventory account balance should match:", "options": ["The amount of purchases for the year", "The value of inventory currently on hand", "The cost of goods sold", "The gross profit"], "answer": 1, "explanation": "Since it is updated continuously, the ledger balance should equal the actual value of goods on hand (barring theft/shrinkage)."}},
  {"FABM1-MerchBusActgMCQ-108": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "The entry to record a sale on credit in a perpetual system includes a credit to:", "options": ["Accounts Receivable and Cost of Goods Sold", "Sales Revenue and Merchandise Inventory", "Sales Revenue and Cost of Goods Sold", "Inventory and Accounts Receivable"], "answer": 1, "explanation": "The sales entry credits Sales Revenue (to increase revenue) and credits Merchandise Inventory (to reduce asset)."}},
  {"FABM1-MerchBusActgMCQ-109": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "If Beginning Inventory is ₱5,000 and Ending Inventory is ₱7,000, and COGS is ₱20,000, what were the Purchases?", "options": ["₱22,000", "₱18,000", "₱25,000", "₱12,000"], "answer": 0, "explanation": "COGS = Beg + Purch - End. ₱20k = ₱5k + Purch - ₱7k. ₱20k = Purch - ₱2k. Purch = ₱22,000."}},
  {"FABM1-MerchBusActgMCQ-110": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "Shrinkage (theft/spoilage) is recorded in a perpetual system by:", "options": ["Debiting COGS, Crediting Inventory", "Debiting Inventory, Crediting COGS", "Debiting Loss on Theft, Crediting Sales", "No entry is made"], "answer": 0, "explanation": "To adjust the book balance to the physical count, inventory is credited, and the cost is usually expensed to COGS."}},
  {"FABM1-MerchBusActgMCQ-111": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Flow Assumptions", "type": "Multiple Choice", "competency": "Understand", "question": "Which cost flow assumption produces the same results for both periodic and perpetual inventory systems?", "options": ["FIFO", "Moving Average", "Weighted Average", "LIFO"], "answer": 0, "explanation": "FIFO always uses the oldest costs first, regardless of whether the calculation is done continuously or at the end."}},
  {"FABM1-MerchBusActgMCQ-112": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "What does the credit term '2/10, n/30' mean?", "options": ["2% interest is charged if not paid in 10 days", "10% discount if paid in 2 days", "2% discount if paid within 10 days, otherwise net amount due in 30 days", "Net amount due in 10 days, with 2% interest after 30 days"], "answer": 2, "explanation": "It means a 2% cash discount is available if payment is made within 10 days; otherwise, the full amount is due in 30 days."}},
  {"FABM1-MerchBusActgMCQ-113": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Business Documents", "type": "Multiple Choice", "competency": "Analyze", "question": "A credit memorandum issued by the seller to the buyer indicates:", "options": ["The buyer has to pay more money", "The seller has reduced the amount the buyer owes (Sales Return)", "The goods have been shipped", "A discount was lost"], "answer": 1, "explanation": "A credit memo reduces the Accounts Receivable balance on the seller's books, acknowledging a return or allowance."}},
  {"FABM1-MerchBusActgMCQ-114": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Costs", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following items is included in the cost of merchandise inventory?", "options": ["Advertising costs", "Freight-out", "Import duties and tariffs", "Sales commissions"], "answer": 2, "explanation": "Costs directly attributable to acquiring the inventory, such as import duties, are capitalized."}},
  {"FABM1-MerchBusActgMCQ-115": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "A company sells goods for ₱10,000 with terms 3/10, n/30. If the customer pays on day 9, how much is the Sales Discount?", "options": ["₱0", "₱200", "₱300", "₱100"], "answer": 2, "explanation": "₱10,000 * 0.03 = ₱300 discount."}},
  {"FABM1-MerchBusActgMCQ-116": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "In a periodic inventory system, the 'Merchandise Inventory' account balance usually remains unchanged throughout the period until:", "options": ["A purchase is made", "A sale is made", "The closing entries are recorded", "Freight is paid"], "answer": 2, "explanation": "In a periodic system, the inventory account reflects the beginning balance until it is adjusted to the ending balance during closing."}},
  {"FABM1-MerchBusActgMCQ-117": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Errors", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company understates its ending inventory, how does this affect the Cost of Goods Sold (COGS) for the *next* period?", "options": ["COGS will be overstated", "COGS will be understated", "No effect on next period", "Sales will be overstated"], "answer": 1, "explanation": "Ending inventory of period 1 becomes Beginning inventory of period 2. If Beginning inventory is understated, COGS available is lower, making COGS understated."}},
  {"FABM1-MerchBusActgMCQ-118": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trade/Cash Discount Math", "type": "Multiple Choice", "competency": "Apply", "question": "List price is ₱5,000. Trade discount is 10%. Cash terms 2/10, n/30. What is the amount to be paid within the discount period?", "options": ["₱4,500", "₱4,400", "₱4,410", "₱4,900"], "answer": 2, "explanation": "Invoice Price = ₱5,000 - ₱500 = ₱4,500. Cash Discount = ₱4,500 * 2% = ₱90. Paid = ₱4,500 - ₱90 = ₱4,410."}},
  {"FABM1-MerchBusActgMCQ-119": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "Under the perpetual system, a shortage discovered during a physical inventory count results in a debit to:", "options": ["Merchandise Inventory", "Cost of Goods Sold", "Sales Revenue", "Inventory Shortage Expense"], "answer": 1, "explanation": "Inventory shrinkage is typically expensed directly to Cost of Goods Sold unless the amount is material."}},
  {"FABM1-MerchBusActgMCQ-120": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "LIFO", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory method generally results in the lowest income tax expense during periods of inflation?", "options": ["FIFO", "LIFO", "Weighted Average", "Specific Identification"], "answer": 1, "explanation": "LIFO matches the most recent (higher) costs against revenue, reducing taxable income."}},
  {"FABM1-MerchBusActgMCQ-121": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Ratios", "type": "Multiple Choice", "competency": "Apply", "question": "Sales ₱200,000; Sales Returns ₱10,000; Sales Discounts ₱5,000; COGS ₱120,000. Calculate Gross Profit Rate.", "options": ["40%", "35.1%", "32.5%", "60%"], "answer": 1, "explanation": "Net Sales = 200k - 10k - 5k = 185k. Gross Profit = 185k - 120k = 65k. Rate = 65k / 185k = ~35.1%."}},
  {"FABM1-MerchBusActgMCQ-122": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounting Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "In the accounting cycle for a merchandising business, which step is unique compared to a service business?", "options": ["Journalizing transactions", "Posting to ledger", "Physical inventory count (for Periodic)", "Preparing trial balance"], "answer": 2, "explanation": "Service businesses do not sell physical goods, so they do not require physical inventory counts for COGS calculation."}},
  {"FABM1-MerchBusActgMCQ-123": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cash Discount", "type": "Multiple Choice", "competency": "Apply", "question": "Company A purchased goods for ₱5,000 terms 2/10, n/30. They returned ₱500 worth of damaged goods. If they pay within the discount period, how much cash is paid?", "options": ["₱4,500", "₱4,410", "₱4,400", "₱4,900"], "answer": 1, "explanation": "Adjusted balance: ₱5,000 - ₱500 = ₱4,500. Discount: ₱4,500 * 0.02 = ₱90. Cash paid: ₱4,500 - ₱90 = ₱4,410."}},
  {"FABM1-MerchBusActgMCQ-124": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average", "type": "Multiple Choice", "competency": "Apply", "question": "Weighted Average (Periodic): Begin Inv 10u @ ₱5; Purch 15u @ ₱7; Purch 25u @ ₱8. Ending Inv is 20 units. Value of Ending Inv?", "options": ["₱140", "₱142", "₱150", "₱160"], "answer": 1, "explanation": "Total Cost = (10*5)+(15*7)+(25*8) = 50+105+200=355. Total units = 50. Avg = 7.1. End Inv = 20 * 7.1 = ₱142."}},
  {"FABM1-MerchBusActgMCQ-125": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Account Classification", "type": "Multiple Choice", "competency": "Understand", "question": "The normal balance of the 'Sales Returns and Allowances' account is:", "options": ["Credit", "Debit", "Zero", "Dependent on the transaction"], "answer": 1, "explanation": "As a contra-revenue account, it has a normal debit balance, opposite to Sales Revenue."}},
  {"FABM1-MerchBusActgMCQ-126": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Analyze", "question": "Why would a company choose FIFO over Weighted Average during a period of rising prices?", "options": ["To report lower net income", "To report higher assets (inventory value)", "To pay less taxes", "To simplify calculations"], "answer": 1, "explanation": "FIFO leaves the most recent, higher costs in inventory, increasing the asset value on the balance sheet."}},
  {"FABM1-MerchBusActgMCQ-127": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Business Documents", "type": "Multiple Choice", "competency": "Understand", "question": "What document triggers the recording of a purchase in the accounting system?", "options": ["Purchase Requisition", "Purchase Order", "Purchase Invoice", "Receiving Report"], "answer": 2, "explanation": "The Purchase Invoice (bill) is the source document that provides the final cost and terms for recording."}},
  {"FABM1-MerchBusActgMCQ-128": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Apply", "question": "Company A sells to Company B, FOB Destination. The truck crashes, destroying the goods. Who bears the loss?", "options": ["Company A (Seller)", "Company B (Buyer)", "The driver", "No one"], "answer": 0, "explanation": "FOB Destination means title/risk transfers only upon delivery. Since they didn't arrive, the seller bears the loss."}},
  {"FABM1-MerchBusActgMCQ-129": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Account Classification", "type": "Multiple Choice", "competency": "Understand", "question": "Cost of Goods Sold is classified as:", "options": ["An asset", "A liability", "An expense", "Revenue"], "answer": 2, "explanation": "It represents the expense of the inventory sold to generate revenue."}},
  {"FABM1-MerchBusActgMCQ-130": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cash Discount", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase: ₱1,000. Terms 2/10, n/30. Returned ₱200. Freight-In (paid cash) ₱50. Paid within discount period. Total cash outflow?", "options": ["₱834", "₱850", "₱830", "₱840"], "answer": 0, "explanation": "Net Payable: ₱1,000 - ₱200 = ₱800. Discount: ₱800 * 2% = ₱16. Payment: ₱784. Add Freight: ₱784 + ₱50 = ₱834."}},
  {"FABM1-MerchBusActgMCQ-131": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory system requires two journal entries for every sale?", "options": ["Periodic", "Perpetual", "Hybrid", "Cash Basis"], "answer": 1, "explanation": "Perpetual requires one entry for revenue (Cash/AR & Sales) and one for cost (COGS & Inventory)."}},
  {"FABM1-MerchBusActgMCQ-132": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "In a Multi-Step Income Statement, 'Income from Operations' is calculated by subtracting what from Gross Profit?", "options": ["Income Taxes", "Operating Expenses", "Interest Expense", "Cost of Goods Sold"], "answer": 1, "explanation": "Operating Income = Gross Profit - Operating Expenses (Selling & Administrative)."}},
  {"FABM1-MerchBusActgMCQ-133": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Sales ₱500,000; Gross Profit ₱200,000. What is the Cost of Goods Sold?", "options": ["₱700,000", "₱300,000", "₱200,000", "₱250,000"], "answer": 1, "explanation": "Sales - COGS = Gross Profit. 500k - COGS = 200k. COGS = 300k."}},
  {"FABM1-MerchBusActgMCQ-134": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Account Classification", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following accounts is a contra-revenue account?", "options": ["Sales Returns and Allowances", "Cost of Goods Sold", "Purchase Discounts", "Merchandise Inventory"], "answer": 0, "explanation": "Sales Returns and Allowances is deducted from Sales Revenue, making it a contra-revenue account."}},
  {"FABM1-MerchBusActgMCQ-135": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Business Documents", "type": "Multiple Choice", "competency": "Understand", "question": "A 'debit memorandum' issued by a buyer to a seller indicates:", "options": ["The buyer is paying more", "The buyer has reduced the Accounts Payable balance (Purchase Return)", "The goods have arrived", "A refund has been received"], "answer": 1, "explanation": "It informs the seller that the buyer has debited (reduced) the seller's Account Payable due to a return."}},
  {"FABM1-MerchBusActgMCQ-136": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving vs Weighted Average", "type": "Multiple Choice", "competency": "Analyze", "question": "How does the 'Moving Average' method differ from 'Weighted Average'?", "options": ["Moving Average is for periodic systems.", "Weighted Average calculates cost after every sale.", "Moving Average calculates a new unit cost after every purchase.", "There is no difference."], "answer": 2, "explanation": "Moving average is the term used for the average cost method in a perpetual system, re-averaging after every purchase."}},
  {"FABM1-MerchBusActgMCQ-137": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Out", "type": "Multiple Choice", "competency": "Understand", "question": "Freight-Out expenses are typically categorized as:", "options": ["Selling Expenses", "General and Administrative Expenses", "Cost of Goods Sold", "Other Comprehensive Income"], "answer": 0, "explanation": "Since it is a cost to deliver goods to customers, it is considered a selling expense."}},
  {"FABM1-MerchBusActgMCQ-138": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Beginning Inventory ₱10,000. Purchases ₱40,000. Ending Inventory ₱5,000. Sales ₱60,000. What is the Goods Available for Sale?", "options": ["₱50,000", "₱45,000", "₱35,000", "₱55,000"], "answer": 0, "explanation": "GAS = Beg Inventory + Purchases. ₱10,000 + ₱40,000 = ₱50,000."}},
  {"FABM1-MerchBusActgMCQ-139": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "When using the Periodic System, which account is used to record the cost of merchandise bought?", "options": ["Merchandise Inventory", "Cost of Goods Sold", "Purchases", "Accounts Payable"], "answer": 2, "explanation": "The 'Purchases' account is a temporary account used exclusively in the periodic system."}},
  {"FABM1-MerchBusActgMCQ-140": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Operating Expenses", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is NOT considered an operating expense?", "options": ["Rent Expense", "Salaries Expense", "Interest Expense", "Depreciation Expense"], "answer": 2, "explanation": "Interest Expense is a financing cost, usually listed under 'Other Expenses and Losses', not operating."}},
  {"FABM1-MerchBusActgMCQ-141": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounting Equation", "type": "Multiple Choice", "competency": "Apply", "question": "Perpetual system: Sold goods costing ₱600 for ₱900. What is the effect on the accounting equation?", "options": ["Assets increase by ₱900.", "Assets increase by ₱300; Equity increases by ₱300.", "Assets increase by ₱900; Equity increases by ₱600.", "Liabilities decrease by ₱300."], "answer": 1, "explanation": "Cash/AR increases ₱900, Inventory decreases ₱600 (Net Asset increase ₱300). Revenue ₱900, Expense ₱600 (Net Equity increase ₱300)."}},
  {"FABM1-MerchBusActgMCQ-142": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Net Sales Formula", "type": "Multiple Choice", "competency": "Understand", "question": "What is the result of 'Sales - Sales Returns and Allowances - Sales Discounts'?", "options": ["Gross Profit", "Net Sales", "Operating Income", "Net Income"], "answer": 1, "explanation": "This is the standard formula for Net Sales."}},
  {"FABM1-MerchBusActgMCQ-143": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory costing method assumes that the goods sold are a mix of all goods available for sale?", "options": ["FIFO", "Specific Identification", "Average Cost", "LIFO"], "answer": 2, "explanation": "The average cost method blends costs, assuming a random mixture of units sold."}},
  {"FABM1-MerchBusActgMCQ-144": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "If a company has Net Sales of ₱100,000 and a Gross Profit Rate of 40%, what is the Cost of Goods Sold?", "options": ["₱40,000", "₱60,000", "₱100,000", "₱140,000"], "answer": 1, "explanation": "If Gross Profit is 40%, COGS is 60%. ₱100,000 * 0.60 = ₱60,000."}},
  {"FABM1-MerchBusActgMCQ-145": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "Who pays the freight costs when the terms are FOB Shipping Point?", "options": ["The seller", "The buyer", "Split equally", "The freight company"], "answer": 1, "explanation": "FOB Shipping Point means title passes at shipping point; therefore, the buyer owns the goods in transit and pays freight."}},
  {"FABM1-MerchBusActgMCQ-146": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Specific Identification", "type": "Multiple Choice", "competency": "Analyze", "question": "Why is the Specific Identification method rarely used for large volumes of inexpensive items?", "options": ["It is illegal for cheap items.", "It is too costly and time-consuming to track each unit.", "It results in lower taxes.", "It is not accurate."], "answer": 1, "explanation": "The administrative burden of tracking every single low-value item (like a box of screws) outweighs the benefit."}},
  {"FABM1-MerchBusActgMCQ-147": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "Purchase Returns and Allowances is a contra account to:", "options": ["Sales", "Accounts Payable", "Purchases", "Capital"], "answer": 2, "explanation": "It reduces the gross Purchases account to arrive at Net Purchases."}},
  {"FABM1-MerchBusActgMCQ-148": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Apply", "question": "Company purchased 100 units at ₱10. Later purchased 100 units at ₱12. Sold 150 units using FIFO. What is the Cost of Goods Sold?", "options": ["₱1,600", "₱1,700", "₱1,500", "₱1,800"], "answer": 0, "explanation": "First 100 @ ₱10 = ₱1,000. Next 50 @ ₱12 = ₱600. Total COGS = ₱1,600."}},
  {"FABM1-MerchBusActgMCQ-149": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Understand", "question": "In a perpetual system, the entry to record the payment of freight on goods sold (FOB Destination) is:", "options": ["Debit Inventory, Credit Cash", "Debit Freight-In, Credit Cash", "Debit Freight-Out, Credit Cash", "Debit Cost of Goods Sold, Credit Cash"], "answer": 2, "explanation": "Freight on sales (delivery expense) is an operating expense called Freight-Out."}},
  {"FABM1-MerchBusActgMCQ-150": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Errors", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following creates a discrepancy between the book balance of inventory and the physical count?", "options": ["Sales returns", "Purchase discounts", "Theft or spoilage", "Freight charges"], "answer": 2, "explanation": "Theft (shrinkage) removes physical items without a corresponding accounting entry, creating a discrepancy."}},
  {"FABM1-MerchBusActgMCQ-151": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Ratios", "type": "Multiple Choice", "competency": "Apply", "question": "Beginning Inv ₱30,000. Purchases ₱70,000. Ending Inv ₱20,000. What is the Inventory Turnover Ratio if COGS is used?", "options": ["3.2 times", "4.0 times", "2.8 times", "8.0 times"], "answer": 0, "explanation": "COGS = 30+70-20 = 80k. Avg Inv = (30+20)/2 = 25k. Turnover = 80k/25k = 3.2."}},
  {"FABM1-MerchBusActgMCQ-152": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "2/10, EOM means:", "options": ["2% discount if paid within 10 days of invoice date", "2% discount if paid by the 10th of the following month", "2% discount if paid within 10 days of End of Month", "Net due 10 days after End of Month"], "answer": 1, "explanation": "EOM stands for End of Month. 2/10 EOM usually means 2% discount if paid by the 10th day of the month following the sale."}},
  {"FABM1-MerchBusActgMCQ-153": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company switches from Periodic to Perpetual, which account will see significantly more activity?", "options": ["Purchases", "Merchandise Inventory", "Sales Returns", "Freight-In"], "answer": 1, "explanation": "In Periodic, Inventory is touched only at closing. In Perpetual, it is touched with every buy/sell."}},
  {"FABM1-MerchBusActgMCQ-154": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory", "type": "Multiple Choice", "competency": "Apply", "question": "A buyer returns goods purchased for cash (₱500). The journal entry for the buyer (Perpetual) is:", "options": ["Debit Cash, Credit Purchases", "Debit Cash, Credit Merchandise Inventory", "Debit Accounts Payable, Credit Inventory", "Debit Sales Returns, Credit Cash"], "answer": 1, "explanation": "Cash is received back (Debit), and the asset Inventory is reduced (Credit)."}},
  {"FABM1-MerchBusActgMCQ-155": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "Who owns the goods while they are in transit under FOB Shipping Point?", "options": ["Seller", "Buyer", "Common Carrier", "Bank"], "answer": 1, "explanation": "Title passes to the buyer the moment the goods leave the shipping point."}},
  {"FABM1-MerchBusActgMCQ-156": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Out", "type": "Multiple Choice", "competency": "Understand", "question": "How is Freight-Out classified in the income statement?", "options": ["Part of Cost of Goods Sold", "Deduction from Sales", "Operating Expense", "Other Income"], "answer": 2, "explanation": "Freight-Out is a delivery expense incurred by the seller and is classified as an operating expense."}},
  {"FABM1-MerchBusActgMCQ-157": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is NOT part of the computation of Net Purchases?", "options": ["Purchases", "Purchase Returns", "Freight-Out", "Freight-In"], "answer": 2, "explanation": "Freight-Out is a selling expense, not a cost of acquiring inventory."}},
  {"FABM1-MerchBusActgMCQ-158": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average", "type": "Multiple Choice", "competency": "Apply", "question": "Moving Average (Perpetual): 50 units @ ₱10. Purchase 50 units @ ₱12. Sell 80 units. What is the value of Ending Inventory?", "options": ["₱200", "₱240", "₱220", "₱250"], "answer": 2, "explanation": "Avg Cost = (500+600)/100 = ₱11. Remaining units = 20. End Inv = 20 * ₱11 = ₱220."}},
  {"FABM1-MerchBusActgMCQ-159": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Understand", "question": "Which account is debited when a customer returns goods for credit (on account)?", "options": ["Accounts Receivable", "Cash", "Sales Returns and Allowances", "Sales Revenue"], "answer": 2, "explanation": "Sales Returns is debited to reduce Net Sales; Accounts Receivable is credited."}},
  {"FABM1-MerchBusActgMCQ-160": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Specific Identification", "type": "Multiple Choice", "competency": "Analyze", "question": "Which inventory method allows a manager to manipulate income by choosing which specific item to deliver?", "options": ["FIFO", "LIFO", "Average Cost", "Specific Identification"], "answer": 3, "explanation": "Specific Identification allows choosing a high-cost or low-cost unit to fulfill an order, affecting COGS."}},
  {"FABM1-MerchBusActgMCQ-161": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Operating Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "The operating cycle of a merchandiser is typically:", "options": ["Shorter than a service business", "Longer than a service business", "The same as a service business", "Zero"], "answer": 1, "explanation": "It is longer because it involves buying inventory and holding it before selling and collecting cash."}},
  {"FABM1-MerchBusActgMCQ-162": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Apply", "question": "A discount of 1/10, n/30 is offered. If the buyer does NOT take the discount, the effective annual interest rate they are paying is roughly:", "options": ["1%", "18%", "36%", "12%"], "answer": 1, "explanation": "They pay 1% to borrow money for 20 days (30-10). 360/20 = 18 periods. 1% * 18 = 18%."}},
  {"FABM1-MerchBusActgMCQ-163": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Consignment", "type": "Multiple Choice", "competency": "Understand", "question": "Goods held on consignment are included in the inventory of:", "options": ["The Consignee (holder)", "The Consignor (owner)", "Both parties", "Neither party"], "answer": 1, "explanation": "Consigned goods remain the property of the Consignor until sold."}},
  {"FABM1-MerchBusActgMCQ-164": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Sales Discounts Forfeited (if using the Net Method) is classified as:", "options": ["Revenue", "Other Income/Expense", "Cost of Goods Sold", "Liability"], "answer": 1, "explanation": "It is essentially interest income earned by the seller for extending credit beyond the discount period."}},
  {"FABM1-MerchBusActgMCQ-165": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Calculate Goods Available for Sale: Beg Inv 50u @ ₱10; Purch 50u @ ₱12; Purch Return 10u @ ₱12.", "options": ["₱1,100", "₱980", "₱1,000", "₱1,200"], "answer": 1, "explanation": "Beg Inv ₱500. Net Purch = (50*₱12) - (10*₱12) = ₱600 - ₱120 = ₱480. GAS = ₱500 + ₱480 = ₱980."}},
  {"FABM1-MerchBusActgMCQ-166": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Understand", "question": "Which account is used to close the beginning inventory balance in a Periodic system?", "options": ["Income Summary (Debit)", "Income Summary (Credit)", "Purchases", "Retained Earnings"], "answer": 0, "explanation": "Beginning Inventory is an expense/cost of the period, so it is debited to Income Summary to remove it from the books."}},
  {"FABM1-MerchBusActgMCQ-167": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Costing Methods", "type": "Multiple Choice", "competency": "Understand", "question": "Consistency in accounting principle means a company should:", "options": ["Use the same inventory method from year to year", "Always use FIFO", "Switch methods to maximize income", "Use different methods for different products"], "answer": 0, "explanation": "Consistency requires using the same accounting methods over time to ensure financial statements are comparable."}},
  {"FABM1-MerchBusActgMCQ-168": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Understand", "question": "FIFO stands for:", "options": ["Fast In, Fast Out", "First In, First Out", "Fixed In, Fixed Out", "Final In, First Out"], "answer": 1, "explanation": "FIFO is the acronym for the First-In, First-Out inventory costing method."}},
  {"FABM1-MerchBusActgMCQ-169": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Understand", "question": "FIFO stands for:", "options": ["Fast In, Fast Out", "First In, First Out", "Fixed In, Fixed Out", "Final In, First Out"], "answer": 1, "explanation": "FIFO is the acronym for the First-In, First-Out inventory costing method."}},
  {"FABM1-MerchBusActgMCQ-170": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Analyze", "question": "Under FIFO, the ending inventory value approximates:", "options": ["Current replacement cost", "Historical cost from years ago", "Average cost of the period", "Lowest possible cost"], "answer": 0, "explanation": "Since FIFO leaves the most recently purchased units in ending inventory, the value is close to current market prices."}},
  {"FABM1-MerchBusActgMCQ-171": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Understand", "question": "FIFO assumes that costs flow through the inventory account in:", "options": ["The same order they were incurred", "The reverse order they were incurred", "A random order", "An average order"], "answer": 0, "explanation": "FIFO matches the physical flow of goods where the first items bought are the first items sold."}},
  {"FABM1-MerchBusActgMCQ-172": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "FIFO Costing", "type": "Multiple Choice", "competency": "Understand", "question": "FIFO assumes that costs flow through the inventory account in:", "options": ["The same order they were incurred", "The reverse order they were incurred", "A random order", "An average order"], "answer": 0, "explanation": "FIFO matches the physical flow of goods where the first items bought are the first items sold."}},
  {"FABM1-MerchBusActgMCQ-173": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight In", "type": "Multiple Choice", "competency": "Understand", "question": "Freight costs paid by the buyer for transportation of goods purchased are classified as:", "options": ["Freight Out", "Freight In", "Delivery Expense", "Selling Expense"], "answer": 1, "explanation": "Freight In represents the transportation cost of incoming inventory paid by the buyer."}},
  {"FABM1-MerchBusActgMCQ-174": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight In", "type": "Multiple Choice", "competency": "Analyze", "question": "Under a periodic system, Freight In is:", "options": ["Subtracted from Purchases", "Added to Sales", "Added to Purchases to determine Cost of Goods Purchased", " Treated as an operating expense"], "answer": 2, "explanation": "Freight In is a product cost and is added to Net Purchases to calculate the total Cost of Goods Purchased."}},
  {"FABM1-MerchBusActgMCQ-175": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight In", "type": "Multiple Choice", "competency": "Analyze", "question": "If terms are FOB Shipping Point, who owns the goods while they are in transit?", "options": ["Seller", "Buyer", "Freight Carrier", "No one"], "answer": 1, "explanation": "Ownership passes to the buyer at the shipping point, so the buyer owns the goods in transit."}},
  {"FABM1-MerchBusActgMCQ-176": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight In", "type": "Multiple Choice", "competency": "Analyze", "question": "Freight In increases which of the following?", "options": ["Operating Expenses", "Cost of Goods Sold", "Sales Revenue", "Purchase Discounts"], "answer": 1, "explanation": "Freight In increases the total cost of purchases, which eventually flows into Cost of Goods Sold."}},
  {"FABM1-MerchBusActgMCQ-177": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Out", "type": "Multiple Choice", "competency": "Remember", "question": "Freight Out is reported on the income statement as:", "options": ["Part of Cost of Goods Sold", "A deduction from Sales", "An Operating Expense", "A contra-asset"], "answer": 2, "explanation": "Freight Out is the cost of delivering goods to customers and is classified as a selling (operating) expense."}},
  {"FABM1-MerchBusActgMCQ-178": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Out", "type": "Multiple Choice", "competency": "Analyze", "question": "If terms are FOB Destination, who is responsible for the freight costs?", "options": ["Buyer", "Seller", "Customer", "Split evenly"], "answer": 1, "explanation": "FOB Destination means the seller is responsible for delivering the goods to the buyer's location."}},
  {"FABM1-MerchBusActgMCQ-179": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Out", "type": "Multiple Choice", "competency": "Analyze", "question": "Which of the following decreases Net Income but not Gross Profit?", "options": ["Cost of Goods Sold", "Sales Returns", "Freight Out", "Freight In"], "answer": 2, "explanation": "Freight Out is an operating expense subtracted from Gross Profit to get Net Income. The others affect Gross Profit."}},
  {"FABM1-MerchBusActgMCQ-180": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is considered an operating expense for a merchandising business?", "options": ["Cost of Goods Sold", "Sales Returns and Allowances", "Freight Out", "Purchase Discounts"], "answer": 2, "explanation": "Freight Out is a delivery expense incurred by the seller and is classified as an operating (selling) expense."}},
  {"FABM1-MerchBusActgMCQ-181": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Analyze", "question": "If Sales are ₱500,000, Sales Returns are ₱20,000, and Cost of Goods Sold is ₱300,000, what is the Gross Profit?", "options": ["₱180,000", "₱200,000", "₱220,000", "₱480,000"], "answer": 0, "explanation": "Net Sales (₱500,000 - ₱20,000) = ₱480,000. Gross Profit = Net Sales - COGS (₱480,000 - ₱300,000) = ₱180,000."}},
  {"FABM1-MerchBusActgMCQ-182": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Remember", "question": "The inventory system that provides a continuous record of inventory on hand is called:", "options": ["Periodic Inventory System", "Perpetual Inventory System", "Physical Inventory System", "Double-Entry System"], "answer": 1, "explanation": "A perpetual inventory system updates inventory records continuously after every purchase and sale."}},
  {"FABM1-MerchBusActgMCQ-183": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is NOT part of the cost of inventory?", "options": ["Purchase price", "Freight In", "Storage costs necessary for production", "Freight Out"], "answer": 3, "explanation": "Freight Out is a selling expense, not a cost to acquire inventory."}},
  {"FABM1-MerchBusActgMCQ-184": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Analyze", "question": "Which of the following would NOT affect Cost of Goods Purchased?", "options": ["Freight In", "Purchase Returns", "Freight Out", "Purchase Discounts"], "answer": 2, "explanation": "Freight Out is a selling expense and does not affect the cost of acquiring goods."}},
  {"FABM1-MerchBusActgMCQ-185": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Understand", "question": "Trade discounts are:", "options": ["Recorded in the journal", "Used to calculate the invoice price but not recorded", "Given for early payment", "Always 2%"], "answer": 1, "explanation": "Trade discounts are deductions from the list price to determine the actual selling price and are not recorded in the books."}},
  {"FABM1-MerchBusActgMCQ-186": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Analyze", "question": "Gross Profit Rate is calculated as:", "options": ["Gross Profit / Net Sales", "Net Income / Net Sales", "Gross Profit / Cost of Goods Sold", "Net Sales / Gross Profit"], "answer": 0, "explanation": "The Gross Profit Rate expresses gross profit as a percentage of Net Sales."}},
  {"FABM1-MerchBusActgMCQ-187": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Understand", "question": "The income statement of a merchandising business is typically prepared using a:", "options": ["Single-step format", "Multi-step format", "Classified format", "Account form"], "answer": 1, "explanation": "A multi-step income statement highlights Gross Profit and distinguishes between operating and non-operating activities, which is standard for merchandisers."}},
  {"FABM1-MerchBusActgMCQ-188": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Merchandising Operations", "type": "Multiple Choice", "competency": "Remember", "question": "Operating expenses are usually divided into which two categories?", "options": ["Selling expenses and Cost of Goods Sold", "Selling expenses and Administrative expenses", "Administrative expenses and Financial expenses", "Variable and Fixed expenses"], "answer": 1, "explanation": "On a multi-step income statement, operating expenses are typically classified as Selling expenses and Administrative expenses."}},
  {"FABM1-MerchBusActgMCQ-189": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average Costing", "type": "Multiple Choice", "competency": "Understand", "question": "The Moving Average method is used with which inventory system?", "options": ["Periodic Inventory System", "Perpetual Inventory System", "Physical Count System", "Retail Inventory System"], "answer": 1, "explanation": "The Moving Average method requires recalculating the average cost after every purchase, which is a feature of the perpetual inventory system."}},
  {"FABM1-MerchBusActgMCQ-190": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average Costing", "type": "Multiple Choice", "competency": "Analyze", "question": "Under the Moving Average method, when a sale occurs, the cost of goods sold is based on:", "options": ["The oldest unit cost", "The most recent purchase cost", "The average cost at the time of sale", "The average cost at the end of the month"], "answer": 2, "explanation": "COGS is calculated using the current moving average unit cost established after the last purchase."}},
  {"FABM1-MerchBusActgMCQ-191": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average Costing", "type": "Multiple Choice", "competency": "Understand", "question": "Which system is absolutely required to use the Moving Average costing method?", "options": ["Periodic Inventory System", "Perpetual Inventory System", "Hybrid System", "Accrual System"], "answer": 1, "explanation": "Moving Average relies on a continuous (perpetual) update of the average cost after every purchase transaction."}},
  {"FABM1-MerchBusActgMCQ-192": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average Costing", "type": "Multiple Choice", "competency": "Understand", "question": "Which system is absolutely required to use the Moving Average costing method?", "options": ["Periodic Inventory System", "Perpetual Inventory System", "Hybrid System", "Accrual System"], "answer": 1, "explanation": "Moving Average relies on a continuous (perpetual) update of the average cost after every purchase transaction."}},
  {"FABM1-MerchBusActgMCQ-193": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory System", "type": "Multiple Choice", "competency": "Understand", "question": "Under the periodic inventory system, the 'Merchandise Inventory' account balance remains unchanged until:", "options": ["A sale is made", "A purchase is made", "The end of the accounting period", "A return is processed"], "answer": 2, "explanation": "In a periodic system, the inventory account is not updated during the period; it is adjusted only at the end of the period based on a physical count."}},
  {"FABM1-MerchBusActgMCQ-194": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory System", "type": "Multiple Choice", "competency": "Analyze", "question": "Which account is debited when goods are purchased on account under a periodic inventory system?", "options": ["Merchandise Inventory", "Cost of Goods Sold", "Purchases", "Accounts Payable"], "answer": 2, "explanation": "The 'Purchases' account is used to record the acquisition of inventory in a periodic system."}},
  {"FABM1-MerchBusActgMCQ-195": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory System", "type": "Multiple Choice", "competency": "Analyze", "question": "In a periodic system, the Cost of Goods Sold is determined by:", "options": ["Looking at the general ledger account", "A calculation: Goods Available for Sale - Ending Inventory", "A calculation: Sales - Net Income", "The sum of all purchase invoices"], "answer": 1, "explanation": "COGS is a derived figure in the periodic system: Beginning Inventory + Net Purchases - Ending Inventory."}},
  {"FABM1-MerchBusActgMCQ-196": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory System", "type": "Multiple Choice", "competency": "Understand", "question": "Which account is generally NOT found in the ledger of a company using a Perpetual Inventory System?", "options": ["Cost of Goods Sold", "Merchandise Inventory", "Sales", "Purchases"], "answer": 3, "explanation": "The 'Purchases' account is specific to the periodic system; perpetual systems use 'Merchandise Inventory' instead."}},
  {"FABM1-MerchBusActgMCQ-197": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Inventory System", "type": "Multiple Choice", "competency": "Analyze", "question": "Calculate Ending Inventory: Goods Available for Sale = 50,000; COGS = 35,000.", "options": ["₱15,000", "₱85,000", "₱50,000", "₱35,000"], "answer": 0, "explanation": "Ending Inventory = Goods Available for Sale - Cost of Goods Sold. ₱50,000 - ₱35,000 = ₱15,000."}},
  {"FABM1-MerchBusActgMCQ-198": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory System", "type": "Multiple Choice", "competency": "Understand", "question": "In a perpetual inventory system, the entry to record the cost of merchandise sold includes a debit to:", "options": ["Sales", "Merchandise Inventory", "Cost of Goods Sold", "Purchases"], "answer": 2, "explanation": "When goods are sold in a perpetual system, Cost of Goods Sold is debited and Merchandise Inventory is credited."}},
  {"FABM1-MerchBusActgMCQ-199": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory System", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company uses a perpetual inventory system, what entry is made for a ₱500 purchase return on credit?", "options": ["Debit Accounts Payable, Credit Purchase Returns", "Debit Accounts Payable, Credit Merchandise Inventory", "Debit Cash, Credit Purchases", "Debit Sales Returns, Credit Accounts Receivable"], "answer": 1, "explanation": "Under perpetual inventory, purchase returns reduce the inventory balance directly, so Merchandise Inventory is credited."}},
  {"FABM1-MerchBusActgMCQ-200": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory System", "type": "Multiple Choice", "competency": "Understand", "question": "What account is used to record inventory shrinkage in a perpetual system?", "options": ["Shrinkage Expense", "Cost of Goods Sold", "Loss on Inventory", "Purchase Returns"], "answer": 1, "explanation": "Inventory shrinkage is typically adjusted by debiting Cost of Goods Sold and crediting Merchandise Inventory."}},
  {"FABM1-MerchBusActgMCQ-201": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Inventory System", "type": "Multiple Choice", "competency": "Understand", "question": "A physical count of inventory is taken in a perpetual system primarily to:", "options": ["Calculate Cost of Goods Sold", "Determine the Ending Inventory balance for the first time", "Verify the accuracy of the perpetual records", "Prepare the tax return"], "answer": 2, "explanation": "Even with perpetual records, a physical count is necessary to verify accuracy and identify shrinkage or errors."}},
  {"FABM1-MerchBusActgMCQ-202": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Analyze", "question": "A buyer returns defective goods costing ₱1,000 to the seller. In a periodic system, the buyer would credit:", "options": ["Merchandise Inventory", "Accounts Payable", "Purchase Returns and Allowances", "Cash"], "answer": 2, "explanation": "In a periodic system, returns are recorded in the specific contra-purchase account 'Purchase Returns and Allowances'."}},
  {"FABM1-MerchBusActgMCQ-203": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "Purchase Returns and Allowances has a normal balance of:", "options": ["Debit", "Credit", "Zero", "Variable"], "answer": 1, "explanation": "It is a contra-expense (contra-purchase) account, so it has a normal credit balance."}},
  {"FABM1-MerchBusActgMCQ-204": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Analyze", "question": "A purchase return for credit in a perpetual system is recorded by debiting Accounts Payable and crediting:", "options": ["Purchase Returns", "Cash", "Merchandise Inventory", "Cost of Goods Sold"], "answer": 2, "explanation": "In a perpetual system, returns reduce the asset 'Merchandise Inventory' directly."}},
  {"FABM1-MerchBusActgMCQ-205": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchases Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "A discount offered to induce prompt payment of an invoice is called a:", "options": ["Trade discount", "Quantity discount", "Purchase discount", "Sales allowance"], "answer": 2, "explanation": "A purchase discount (cash discount) is offered to encourage early payment."}},
  {"FABM1-MerchBusActgMCQ-206": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchases Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "Terms 2/10, n/30 mean a 2% discount is available if payment is made within:", "options": ["2 days", "10 days", "30 days", "The discount period is not specified"], "answer": 1, "explanation": "The '2/10' portion specifies a 2% discount if paid within 10 days."}},
  {"FABM1-MerchBusActgMCQ-207": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchases Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company fails to take a purchase discount, the full payment essentially includes:", "options": ["A penalty fee", "Interest for the use of money", "A reduction in COGS", "An increase in equity"], "answer": 1, "explanation": "The lost discount is viewed as a finance charge or interest paid for delaying payment."}},
  {"FABM1-MerchBusActgMCQ-208": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchases Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "If an invoice for ₱5,000 with terms 1/15, n/30 is paid within the discount period, the amount of the discount is:", "options": ["₱50", "₱100", "₱500", "₱10"], "answer": 0, "explanation": "₱5,000 * 1% = ₱50."}},
  {"FABM1-MerchBusActgMCQ-209": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Sales Discounts is a contra account to:", "options": ["Purchases", "Sales Revenue", "Accounts Receivable", "Cost of Goods Sold"], "answer": 1, "explanation": "Sales Discounts is deducted from Sales Revenue to determine Net Sales."}},
  {"FABM1-MerchBusActgMCQ-210": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "If a customer pays a ₱1,000 invoice within the 2% discount period, the entry to record the receipt of cash would include a debit to Cash for:", "options": ["₱1,000", "₱980", "₱1,020", "₱20"], "answer": 1, "explanation": "₱1,000 - (₱1,000 * 2%) = ₱980."}},
  {"FABM1-MerchBusActgMCQ-211": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "Credit terms 'n/eom' mean:", "options": ["Net due 30 days after invoice date", "Net due at the end of the month", "Net due 10 days after end of month", "No discount available"], "answer": 1, "explanation": "'n/eom' stands for net due at the end of the month."}},
  {"FABM1-MerchBusActgMCQ-212": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Understand", "question": "When a customer returns goods for credit, the seller usually issues a:", "options": ["Debit Memorandum", "Credit Memorandum", "Purchase Order", "Sales Invoice"], "answer": 1, "explanation": "A Credit Memorandum indicates that the seller has credited (reduced) the customer's Accounts Receivable balance."}},
  {"FABM1-MerchBusActgMCQ-213": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Understand", "question": "Sales Returns and Allowances usually has a normal balance of:", "options": ["Debit", "Credit", "Zero", "It depends"], "answer": 0, "explanation": "It is a contra-revenue account, so its normal balance is a Debit (opposite of Sales)."}},
  {"FABM1-MerchBusActgMCQ-214": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Analyze", "question": "If a customer returns goods for a cash refund, the seller debits Sales Returns and Allowances and credits:", "options": ["Accounts Receivable", "Cash", "Sales", "Inventory"], "answer": 1, "explanation": "Since it is a cash refund, the Cash account is credited (reduced)."}},
  {"FABM1-MerchBusActgMCQ-215": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average Costing", "type": "Multiple Choice", "competency": "Analyze", "question": "How is the weighted average unit cost calculated?", "options": ["Total Cost of Goods Sold / Total Units Sold", "Cost of Goods Available for Sale / Total Units Available for Sale", "(Beginning Cost + Ending Cost) / 2", "Total Sales / Total Units Sold"], "answer": 1, "explanation": "The weighted average unit cost is the total cost of goods available for sale divided by the total number of units available for sale."}},
  {"FABM1-MerchBusActgMCQ-216": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average Costing", "type": "Multiple Choice", "competency": "Understand", "question": "The Weighted Average method is most appropriate for businesses that sell:", "options": ["Unique, high-value items", "Large quantities of similar items", "Perishable goods only", "Custom-made products"], "answer": 1, "explanation": "Weighted Average is ideal for homogeneous products (like grains or fuel) where individual units are indistinguishable."}},
  {"FABM1-MerchBusActgMCQ-217": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average Costing", "type": "Multiple Choice", "competency": "Analyze", "question": "In the weighted average method (periodic), the unit cost is determined:", "options": ["After each purchase", "After each sale", "At the end of the period", "At the beginning of the period"], "answer": 2, "explanation": "In a periodic system, the weighted average cost is computed once at the end of the period."}},
  {"FABM1-MerchBusActgMCQ-217": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Weighted Average Costing", "type": "Multiple Choice", "competency": "Analyze", "question": "In the weighted average method (periodic), the unit cost is determined:", "options": ["After each purchase", "After each sale", "At the end of the period", "At the beginning of the period"], "answer": 2, "explanation": "In a periodic system, the weighted average cost is computed once at the end of the period."}},
  {"FABM1-MerchBusActgMCQ-218": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Errors", "type": "Multiple Choice", "competency": "Analyze", "question": "If the ending inventory of the current year is overstated, what is the effect on the net income of the *following* year?", "options": ["Overstated", "Understated", "No effect", "Depends on tax rate"], "answer": 1, "explanation": "Overstated ending inventory becomes overstated beginning inventory next year, increasing COGS and understating Net Income."}},
  {"FABM1-MerchBusActgMCQ-219": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Analyze", "question": "Which system would a high-volume retailer of low-cost items (like a grocery store) prefer for theft detection purposes?", "options": ["Periodic System", "Perpetual System", "Manual System", "Single-Entry System"], "answer": 1, "explanation": "Perpetual systems track what *should* be on hand, allowing immediate detection of discrepancies (theft) when compared to physical counts."}},
  {"FABM1-MerchBusActgMCQ-220": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Flow Assumptions", "type": "Multiple Choice", "competency": "Analyze", "question": "During a period of rising prices (inflation), why might a company choose LIFO over FIFO for tax purposes?", "options": ["To report higher assets", "To increase net income", "To reduce taxable income", "To simplify record keeping"], "answer": 2, "explanation": "LIFO matches higher recent costs against revenue, lowering taxable income and thus tax liability during inflation."}},
  {"FABM1-MerchBusActgMCQ-221": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Terms", "type": "Multiple Choice", "competency": "Analyze", "question": "Why is 'Freight-In' considered a product cost while 'Freight-Out' is a period cost?", "options": ["Freight-In adds value to the inventory asset; Freight-Out is a selling expense", "Freight-In is tax deductible; Freight-Out is not", "Freight-Out is always smaller", "They should both be product costs"], "answer": 0, "explanation": "Freight-In is necessary to get the asset ready for sale (capitalized), while Freight-Out is a cost of selling/distribution (expensed)."}},
  {"FABM1-MerchBusActgMCQ-222": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "If a customer routinely misses the discount period (e.g., 2/10, n/30), what does this suggest about their financial management?", "options": ["They are effectively borrowing money at a very high interest rate", "They are saving money by paying later", "They are managing cash flow efficiently", "They prefer paying full price"], "answer": 0, "explanation": "Missing a 2% discount to pay 20 days later is equivalent to an annual interest rate of approx 36%, suggesting poor cash management."}},
  {"FABM1-MerchBusActgMCQ-223": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Operating Cycle", "type": "Multiple Choice", "competency": "Analyze", "question": "How does the operating cycle of a merchandising business differ from that of a service business?", "options": ["It is shorter because goods sell fast", "It includes an inventory holding period", "It does not involve Accounts Receivable", "It requires less capital"], "answer": 1, "explanation": "Merchandisers must buy inventory and hold it until sale, adding an extra stage (Inventory period) that service businesses lack."}},
  {"FABM1-MerchBusActgMCQ-224": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Gross Profit Analysis", "type": "Multiple Choice", "competency": "Analyze", "question": "If Sales increase by 10% and Cost of Goods Sold increases by 15%, what happens to the Gross Profit margin?", "options": ["It increases", "It decreases", "It remains the same", "It doubles"], "answer": 1, "explanation": "Since the cost of the product is rising faster than the selling price/volume, the profit margin per unit (or total) will shrink."}},
  {"FABM1-MerchBusActgMCQ-225": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the journal entry to record a purchase of merchandise for $5,000 on account, terms 2/10, n/30, under the periodic system.", "options": ["Debit Purchases $5,000; Credit Accounts Payable $5,000", "Debit Inventory $5,000; Credit Accounts Payable $5,000", "Debit Purchases $5,000; Credit Cash $5,000", "Debit Accounts Payable $5,000; Credit Purchases $5,000"], "answer": 0, "explanation": "In a periodic system, merchandise bought on credit is recorded by debiting Purchases and crediting Accounts Payable."}},
 {"FABM1-MerchBusActgMCQ-226": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the journal entry to record the return of $500 worth of damaged goods to a supplier under the perpetual system.", "options": ["Debit Accounts Payable $500; Credit Purchase Returns $500", "Debit Accounts Payable $500; Credit Merchandise Inventory $500", "Debit Cash $500; Credit Inventory $500", "Debit Purchase Returns $500; Credit Inventory $500"], "answer": 1, "explanation": "In a perpetual system, returns reduce the Inventory account directly; Accounts Payable is debited to reduce the liability."}},
 {"FABM1-MerchBusActgMCQ-227": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the compound entry to record the receipt of payment from a customer for a $1,000 invoice (terms 2/10, n/30) within the discount period.", "options": ["Debit Cash $1,000; Credit Accounts Receivable $1,000", "Debit Cash $980; Debit Sales Discounts $20; Credit Accounts Receivable $1,000", "Debit Cash $980; Credit Accounts Receivable $980", "Debit Cash $980; Debit Sales Returns $20; Credit Accounts Receivable $1,000"], "answer": 1, "explanation": "You record the cash received ($980), the expense of the discount ($20), and remove the full receivable ($1,000)."}},
 {"FABM1-MerchBusActgMCQ-228": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record the payment of freight costs of $100 on goods sold FOB Destination.", "options": ["Debit Freight-In $100; Credit Cash $100", "Debit Merchandise Inventory $100; Credit Cash $100", "Debit Freight-Out $100; Credit Cash $100", "Debit Sales $100; Credit Cash $100"], "answer": 2, "explanation": "FOB Destination means the seller pays freight. This is a delivery expense, recorded as 'Freight-Out'."}},
 {"FABM1-MerchBusActgMCQ-229": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entries required to record a sale of $2,000 (cost $1,200) on account under the perpetual system.", "options": ["One entry: Debit AR $2,000; Credit Sales $2,000", "One entry: Debit AR $2,000; Credit Inventory $2,000", "Two entries: (1) Debit AR $2,000, Credit Sales $2,000; (2) Debit COGS $1,200, Credit Inventory $1,200", "Two entries: (1) Debit Cash $2,000, Credit Sales $2,000; (2) Debit Inventory $1,200, Credit COGS $1,200"], "answer": 2, "explanation": "Perpetual systems require recording the revenue (Sales) and the cost (COGS) simultaneously."}},
 {"FABM1-MerchBusActgMCQ-230": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General Ledger", "type": "Multiple Choice", "competency": "Create", "question": "When posting a journal entry that includes a debit to Accounts Payable of $400 and a credit to Cash of $392 and Inventory of $8, how would you update the Accounts Payable ledger?", "options": ["Enter $400 in the credit column", "Enter $400 in the debit column and reduce the balance", "Enter $392 in the debit column", "Enter $8 in the credit column"], "answer": 1, "explanation": "A debit to Accounts Payable reduces the liability, so you enter $400 in the debit column and calculate the new lower balance."}},
 {"FABM1-MerchBusActgMCQ-231": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trial Balance", "type": "Multiple Choice", "competency": "Create", "question": "If you are preparing a trial balance and the total debits are $50,000 and total credits are $49,500, what is your immediate next step?", "options": ["Proceed to financial statements", "Force the totals to match", "Investigate the $500 discrepancy", "Create an adjusting entry for $500"], "answer": 2, "explanation": "A trial balance must balance. Any discrepancy indicates an error that must be found (investigated) before proceeding."}},
 {"FABM1-MerchBusActgMCQ-232": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Worksheet", "type": "Multiple Choice", "competency": "Create", "question": "In preparing a worksheet for a periodic inventory system, where do you extend the Beginning Inventory amount from the Trial Balance columns?", "options": ["To the Adjusted Trial Balance Debit column only", "To the Income Statement Debit column", "To the Income Statement Credit column", "To the Balance Sheet Debit column"], "answer": 1, "explanation": "Beginning Inventory is part of the Cost of Goods Available for Sale (an expense equivalent), so it is extended to the Income Statement Debit column."}},
 {"FABM1-MerchBusActgMCQ-233": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Worksheet", "type": "Multiple Choice", "competency": "Create", "question": "In a worksheet, you calculate a Net Loss. To balance the Income Statement columns, where do you enter the Net Loss amount?", "options": ["Debit column of Income Statement", "Credit column of Income Statement", "Credit column of Balance Sheet", "Debit column of Adjustments"], "answer": 1, "explanation": "A Net Loss means Debits (Expenses) > Credits (Revenue). To balance, you add the loss to the Credit column."}},
 {"FABM1-MerchBusActgMCQ-234": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "When creating a Multi-Step Income Statement, how do you determine 'Net Sales'?", "options": ["Gross Sales + Freight Out", "Gross Sales - Cost of Goods Sold", "Gross Sales - Sales Returns & Allowances - Sales Discounts", "Gross Sales - Operating Expenses"], "answer": 2, "explanation": "Net Sales is created by deducting contra-revenue accounts (Returns, Allowances, Discounts) from Gross Sales."}},
 {"FABM1-MerchBusActgMCQ-235": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "You are preparing the Cost of Goods Sold section. Which items do you sum to calculate 'Cost of Goods Available for Sale'?", "options": ["Beginning Inventory and Net Sales", "Beginning Inventory and Cost of Goods Purchased", "Ending Inventory and Cost of Goods Purchased", "Net Purchases and Freight-Out"], "answer": 1, "explanation": "GAS is the sum of what you started with (Beg Inventory) and what you bought (Net Purchases + Freight In)."}},
 {"FABM1-MerchBusActgMCQ-236": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Adjusting Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the adjusting entry to recognize $500 of store supplies used during the period.", "options": ["Debit Supplies Expense $500; Credit Store Supplies $500", "Debit Store Supplies $500; Credit Supplies Expense $500", "Debit COGS $500; Credit Store Supplies $500", "Debit Retained Earnings $500; Credit Store Supplies $500"], "answer": 0, "explanation": "To recognize usage, you debit the expense account and credit the asset account."}},
 {"FABM1-MerchBusActgMCQ-237": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Adjusting Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the adjusting entry to record shrinkage of $200 found during a physical count (Perpetual System).", "options": ["Debit Inventory $200; Credit COGS $200", "Debit COGS $200; Credit Inventory $200", "Debit Loss on Theft $200; Credit Sales $200", "No entry needed"], "answer": 1, "explanation": "Shrinkage reduces the asset (Credit Inventory) and is typically expensed to Cost of Goods Sold (Debit COGS)."}},
 {"FABM1-MerchBusActgMCQ-238": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to close the Sales Revenue account with a balance of $50,000.", "options": ["Debit Income Summary $50,000; Credit Sales $50,000", "Debit Sales $50,000; Credit Income Summary $50,000", "Debit Sales $50,000; Credit Capital $50,000", "Debit Cash $50,000; Credit Sales $50,000"], "answer": 1, "explanation": "Sales has a credit balance. To close it (make it zero), you must debit Sales and credit Income Summary."}},
 {"FABM1-MerchBusActgMCQ-239": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to close the 'Sales Returns and Allowances' account (balance $2,000).", "options": ["Debit Sales Returns $2,000; Credit Income Summary $2,000", "Debit Income Summary $2,000; Credit Sales Returns $2,000", "Debit Sales Returns $2,000; Credit Sales $2,000", "Debit Capital $2,000; Credit Sales Returns $2,000"], "answer": 1, "explanation": "Sales Returns is a debit balance account. To close it, you credit Sales Returns and debit Income Summary."}},
 {"FABM1-MerchBusActgMCQ-240": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "In a periodic system, how do you create the entry to establish the Ending Inventory of $15,000 during the closing process?", "options": ["Debit Income Summary $15,000; Credit Inventory $15,000", "Debit Inventory $15,000; Credit Income Summary $15,000", "Debit Inventory $15,000; Credit Purchases $15,000", "Debit COGS $15,000; Credit Inventory $15,000"], "answer": 1, "explanation": "Ending Inventory is set up by debiting the asset account (Inventory) and crediting Income Summary."}},
 {"FABM1-MerchBusActgMCQ-241": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to close a Net Income of $10,000 to the Owner's Capital account.", "options": ["Debit Capital $10,000; Credit Income Summary $10,000", "Debit Income Summary $10,000; Credit Owner's Capital $10,000", "Debit Sales $10,000; Credit Capital $10,000", "Debit Income Summary $10,000; Credit Cash $10,000"], "answer": 1, "explanation": "Net Income results in a credit balance in Income Summary. To close it, debit Income Summary and credit Capital."}},
 {"FABM1-MerchBusActgMCQ-242": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Post-Closing Trial Balance", "type": "Multiple Choice", "competency": "Create", "question": "Which of the following accounts would you include when creating a Post-Closing Trial Balance?", "options": ["Sales Revenue", "Cost of Goods Sold", "Merchandise Inventory", "Sales Discounts"], "answer": 2, "explanation": "Only permanent (Balance Sheet) accounts appear on the Post-Closing Trial Balance. Inventory is an asset."}},
 {"FABM1-MerchBusActgMCQ-243": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Reversing Entries", "type": "Multiple Choice", "competency": "Create", "question": "You made an adjusting entry for accrued salaries: Debit Salaries Expense, Credit Salaries Payable. Create the reversing entry.", "options": ["Debit Salaries Expense; Credit Salaries Payable", "Debit Salaries Payable; Credit Salaries Expense", "Debit Salaries Payable; Credit Cash", "Debit Cash; Credit Salaries Expense"], "answer": 1, "explanation": "A reversing entry is the exact opposite of the adjusting entry: Debit the liability (Payable) and credit the expense."}},
 {"FABM1-MerchBusActgMCQ-244": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record a purchase allowance of $200 granted by a supplier (Periodic System).", "options": ["Debit Accounts Payable $200; Credit Merchandise Inventory $200", "Debit Accounts Payable $200; Credit Purchase Returns and Allowances $200", "Debit Cash $200; Credit Purchases $200", "Debit Purchase Allowances $200; Credit Accounts Payable $200"], "answer": 1, "explanation": "In periodic, you credit the specific contra-account 'Purchase Returns and Allowances' and debit AP."}},
 {"FABM1-MerchBusActgMCQ-245": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the journal entry for the payment of an invoice of $3,000, terms 1/10, n/30, after the discount period has expired.", "options": ["Debit Accounts Payable $3,000; Credit Cash $3,000", "Debit Accounts Payable $3,000; Credit Cash $2,970; Credit Discounts $30", "Debit Purchases $3,000; Credit Cash $3,000", "Debit Accounts Payable $2,970; Credit Cash $2,970"], "answer": 0, "explanation": "Since the discount period passed, the full amount is paid. Debit AP, Credit Cash for the full $3,000."}},
 {"FABM1-MerchBusActgMCQ-246": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "In creating the Equity Statement, how do you handle Owner's Drawings?", "options": ["Add to Beginning Capital", "Subtract from Beginning Capital (after adding Net Income)", "Include as an expense on Income Statement", "Subtract from Total Assets"], "answer": 1, "explanation": "Drawings reduce owner's equity. They are subtracted from the sum of Beginning Capital and Net Income."}},
 {"FABM1-MerchBusActgMCQ-247": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "When preparing a Balance Sheet, how do you report 'Allowance for Doubtful Accounts'?", "options": ["As a liability", "As a deduction from Sales", "As a deduction from Accounts Receivable", "As an operating expense"], "answer": 2, "explanation": "It is a contra-asset account presented as a deduction from Accounts Receivable to show Net Realizable Value."}},
 {"FABM1-MerchBusActgMCQ-248": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Worksheet", "type": "Multiple Choice", "competency": "Create", "question": "On the worksheet, where do you extend the 'Sales Discounts' balance?", "options": ["Income Statement Credit column", "Income Statement Debit column", "Balance Sheet Debit column", "Balance Sheet Credit column"], "answer": 1, "explanation": "Sales Discounts is a contra-revenue account (debit balance), so it acts like an expense in the Income Statement Debit column."}},
 {"FABM1-MerchBusActgMCQ-249": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record a sale of $500 paid immediately by check (Perpetual System, Cost $300).", "options": ["Debit Cash $500; Credit Sales $500", "Debit Cash $500; Credit Sales $500 AND Debit COGS $300; Credit Inventory $300", "Debit AR $500; Credit Sales $500", "Debit Cash $500; Credit Inventory $300; Credit Gain $200"], "answer": 1, "explanation": "A cash sale in perpetual requires two entries: one for revenue (Cash/Sales) and one for cost (COGS/Inventory)."}},
 {"FABM1-MerchBusActgMCQ-250": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Adjusting Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the adjusting entry for Unearned Revenue that has now been earned ($1,000).", "options": ["Debit Sales Revenue $1,000; Credit Unearned Revenue $1,000", "Debit Unearned Revenue $1,000; Credit Sales Revenue $1,000", "Debit Cash $1,000; Credit Sales Revenue $1,000", "Debit Unearned Revenue $1,000; Credit Cash $1,000"], "answer": 1, "explanation": "As the liability (Unearned Revenue) decreases, you debit it. As revenue is earned, you credit Sales Revenue."}},
 {"FABM1-MerchBusActgMCQ-251": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to close the 'Purchases' account (Periodic System, balance $40,000).", "options": ["Debit Purchases $40,000; Credit Income Summary $40,000", "Debit Income Summary $40,000; Credit Purchases $40,000", "Debit Inventory $40,000; Credit Purchases $40,000", "Debit Purchases $40,000; Credit Capital $40,000"], "answer": 1, "explanation": "Purchases is a debit account (cost). To close it, credit Purchases and debit Income Summary."}},
 {"FABM1-MerchBusActgMCQ-252": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Post-Closing Trial Balance", "type": "Multiple Choice", "competency": "Create", "question": "If you mistakenly include 'Depreciation Expense' on the Post-Closing Trial Balance, what does this imply?", "options": ["The trial balance is correct", "You failed to post the adjusting entries", "You failed to journalize or post the closing entries for expenses", "The asset was sold"], "answer": 2, "explanation": "Expenses are temporary accounts. If they appear post-closing, it means they were not closed properly."}},
 {"FABM1-MerchBusActgMCQ-253": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record the purchase of equipment for use in the business (not for resale) on credit.", "options": ["Debit Purchases; Credit Accounts Payable", "Debit Equipment; Credit Accounts Payable", "Debit Inventory; Credit Accounts Payable", "Debit Equipment; Credit Cash"], "answer": 1, "explanation": "Only goods for resale are debited to Purchases/Inventory. Long-term assets are debited to their specific asset account."}},
 {"FABM1-MerchBusActgMCQ-254": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "When creating the Income Statement, 'Freight-Out' is listed under:", "options": ["Cost of Goods Sold", "Other Income", "Operating Expenses", "Sales Deductions"], "answer": 2, "explanation": "Freight-Out is a selling expense, part of Operating Expenses."}},
 {"FABM1-MerchBusActgMCQ-255": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to close the Owner's Drawing account (balance $5,000).", "options": ["Debit Capital $5,000; Credit Drawing $5,000", "Debit Drawing $5,000; Credit Capital $5,000", "Debit Income Summary $5,000; Credit Drawing $5,000", "Debit Drawing $5,000; Credit Income Summary $5,000"], "answer": 0, "explanation": "Drawings are closed directly to Capital. Debit Capital (to reduce it) and credit Drawing (to zero it)."}},
 {"FABM1-MerchBusActgMCQ-256": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry for a credit memorandum received from the bank for interest earned ($50).", "options": ["Debit Cash $50; Credit Interest Revenue $50", "Debit Interest Expense $50; Credit Cash $50", "Debit Bank Charges $50; Credit Cash $50", "Debit Accounts Receivable $50; Credit Interest Revenue $50"], "answer": 0, "explanation": "The bank collected money/added interest for you. Debit Cash (increase), Credit Interest Revenue."}},
 {"FABM1-MerchBusActgMCQ-257": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Reversing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Which of the following adjusting entries should generally be reversed?", "options": ["Depreciation Expense", "Bad Debts Expense", "Accrued Interest Expense", "Prepaid Insurance (recorded as Asset initially)"], "answer": 2, "explanation": "Reversing entries are optional but most common for Accruals (Accrued Revenues/Expenses) to simplify the next period's payments."}},
 {"FABM1-MerchBusActgMCQ-258": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trial Balance", "type": "Multiple Choice", "competency": "Create", "question": "When creating an Adjusted Trial Balance, how do you determine the balance of 'Prepaid Rent'?", "options": ["Use the unadjusted balance", "Unadjusted balance minus the adjustment for rent expired", "Unadjusted balance plus rent expense", "The amount paid in cash"], "answer": 1, "explanation": "The adjusted balance is the original amount minus the portion that was used up (expensed) during the period."}},
 {"FABM1-MerchBusActgMCQ-259": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record the purchase of merchandise paying $1,000 cash and $4,000 on credit (Periodic).", "options": ["Debit Purchases $5,000; Credit Cash $1,000; Credit AP $4,000", "Debit Inventory $5,000; Credit Cash $1,000; Credit AP $4,000", "Debit Purchases $4,000; Credit AP $4,000", "Debit AP $4,000; Credit Purchases $5,000"], "answer": 0, "explanation": "Debit the total cost to Purchases. Credit Cash for the amount paid and Credit AP for the balance."}},
 {"FABM1-MerchBusActgMCQ-260": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "In preparing a 'Classified' Balance Sheet, where would you place 'Mortgage Payable due in 5 years'?", "options": ["Current Liabilities", "Long-Term Liabilities", "Owner's Equity", "Current Assets"], "answer": 1, "explanation": "Obligations due after more than one year are classified as Long-Term Liabilities."}},
 {"FABM1-MerchBusActgMCQ-261": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to replenish the Petty Cash fund when receipts show $40 for supplies and $10 for postage.", "options": ["Debit Petty Cash $50; Credit Cash $50", "Debit Supplies $40; Debit Postage $10; Credit Cash $50", "Debit Supplies $40; Debit Postage $10; Credit Petty Cash $50", "Debit Cash $50; Credit Income $50"], "answer": 1, "explanation": "Replenishment records the expenses. You debit the expenses and credit the main Cash account (sending a check to the fund)."}},
 {"FABM1-MerchBusActgMCQ-262": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General Ledger", "type": "Multiple Choice", "competency": "Create", "question": "If you post a credit of $500 to the 'Cash' ledger account which had a debit balance of $2,000, what is the new balance?", "options": ["$2,500 Debit", "$1,500 Debit", "$1,500 Credit", "$2,500 Credit"], "answer": 1, "explanation": "A credit to a normal debit balance account reduces it. $2,000 - $500 = $1,500 Debit."}},
 {"FABM1-MerchBusActgMCQ-263": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to close 'Freight-In' (Periodic System, balance $1,000).", "options": ["Debit Freight-In $1,000; Credit Income Summary $1,000", "Debit Income Summary $1,000; Credit Freight-In $1,000", "Debit COGS $1,000; Credit Freight-In $1,000", "Debit Capital $1,000; Credit Freight-In $1,000"], "answer": 1, "explanation": "Freight-In is a temporary cost account with a debit balance. Close it by crediting Freight-In and debiting Income Summary."}},
 {"FABM1-MerchBusActgMCQ-264": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry when a customer pays their account within the discount period, but you (the seller) forgot to record the discount originally.", "options": ["Debit Cash; Credit AR", "Debit Cash; Debit Sales Discounts; Credit AR", "Debit Cash; Credit Sales", "Debit AR; Credit Cash"], "answer": 1, "explanation": "Even if forgotten, the transaction must reflect reality: Cash received is net, AR credited is gross, difference is Sales Discount."}},
 {"FABM1-MerchBusActgMCQ-265": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Worksheet", "type": "Multiple Choice", "competency": "Create", "question": "In the Balance Sheet columns of the worksheet, the Debit column total is $100,000 and the Credit column total is $80,000. What does the difference represent?", "options": ["Net Loss", "Net Income", "Owner's Drawings", "Error in calculation"], "answer": 1, "explanation": "In Balance Sheet columns, Debits > Credits indicates Net Income (Assets increased more than Liab+Equity before closing profit)."}},
 {"FABM1-MerchBusActgMCQ-266": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to correct an error: A $500 purchase of supplies was incorrectly debited to Equipment.", "options": ["Debit Supplies $500; Credit Equipment $500", "Debit Equipment $500; Credit Supplies $500", "Debit Supplies $500; Credit Cash $500", "Debit Supplies Expense $500; Credit Equipment $500"], "answer": 0, "explanation": "To correct, you must debit the correct account (Supplies) and credit the incorrect account (Equipment) to remove the amount."}},
 {"FABM1-MerchBusActgMCQ-267": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "How do you present 'Inventory' on a Common-Size Balance Sheet?", "options": ["As a dollar amount", "As a percentage of Total Assets", "As a percentage of Net Sales", "As a percentage of Gross Profit"], "answer": 1, "explanation": "Common-Size Balance Sheets express every line item as a percentage of Total Assets."}},
 {"FABM1-MerchBusActgMCQ-268": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Adjusting Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the adjusting entry for depreciation of store equipment ($1,000).", "options": ["Debit Equipment $1,000; Credit Depreciation Expense $1,000", "Debit Depreciation Expense $1,000; Credit Equipment $1,000", "Debit Depreciation Expense $1,000; Credit Accumulated Depreciation $1,000", "Debit Accumulated Depreciation $1,000; Credit Equipment $1,000"], "answer": 2, "explanation": "Depreciation is expensed and the reduction in asset value is recorded in the contra-asset 'Accumulated Depreciation'."}},
 {"FABM1-MerchBusActgMCQ-269": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record a sales return where the customer is given a credit for future purchases (Periodic).", "options": ["Debit Sales Returns; Credit Cash", "Debit Sales Returns; Credit Accounts Receivable", "Debit Accounts Receivable; Credit Sales Returns", "Debit Sales; Credit Accounts Receivable"], "answer": 1, "explanation": "Since no cash is returned, you reduce the customer's balance by crediting Accounts Receivable."}},
 {"FABM1-MerchBusActgMCQ-270": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "When closing the Income Summary account with a debit balance (Net Loss), what is the entry?", "options": ["Debit Capital; Credit Income Summary", "Debit Income Summary; Credit Capital", "Debit Sales; Credit Income Summary", "Debit Cash; Credit Income Summary"], "answer": 0, "explanation": "A debit balance in Income Summary represents a loss. To close it, credit Income Summary and debit Capital (reducing equity)."}},
 {"FABM1-MerchBusActgMCQ-271": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record the purchase of 100 units at $10 each, trade discount 10%. Terms n/30. (Periodic)", "options": ["Debit Purchases $1,000; Credit AP $1,000", "Debit Purchases $900; Credit AP $900", "Debit Purchases $900; Debit Discount $100; Credit AP $1,000", "Debit Inventory $900; Credit AP $900"], "answer": 1, "explanation": "Trade discounts are deducted immediately. The transaction is recorded at the invoice price ($1,000 - 10% = $900)."}},
 {"FABM1-MerchBusActgMCQ-272": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Reversing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Why would you create a reversing entry for Accrued Wages?", "options": ["To correct an error in the adjustment", "To avoid double counting the expense in the next period's payroll entry", "To increase Net Income", "To close the wages account"], "answer": 1, "explanation": "Reversing allows the bookkeeper to just record the full payroll payment in the new period without worrying about the prior accrual split."}},
 {"FABM1-MerchBusActgMCQ-273": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Create", "question": "In creating the Statement of Cash Flows (Direct Method), how do you report 'Cash paid to suppliers'?", "options": ["As an Investing Activity", "As a Financing Activity", "As an Operating Activity", "It is not reported"], "answer": 2, "explanation": "Payments to suppliers for inventory are part of the day-to-day Operating Activities."}},
 {"FABM1-MerchBusActgMCQ-274": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Journalizing Transactions", "type": "Multiple Choice", "competency": "Create", "question": "Create the entry to record a freight allowance given to a customer (we credit their account for shipping they paid).", "options": ["Debit Freight-Out; Credit Accounts Receivable", "Debit Sales Returns; Credit Accounts Receivable", "Debit Sales; Credit Cash", "Debit Delivery Expense; Credit Cash"], "answer": 0, "explanation": "This recognizes the delivery expense (Freight-Out) and reduces the amount the customer owes us (Credit AR)."}},
 {"FABM1-MerchBusActgMCQ-275": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Nature of Business", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following creates the primary difference between a service business and a merchandising business?", "options": ["The presence of Merchandise Inventory", "The use of double-entry bookkeeping", "The need for a cash account", "The accounting period used"], "answer": 0, "explanation": "A merchandising business buys and sells physical goods (inventory), whereas a service business provides intangible services."}},
 {"FABM1-MerchBusActgMCQ-276": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "What does 'Cost of Goods Sold' represent on the Income Statement?", "options": ["The total cost of all inventory purchased during the year", "The cost of the specific inventory items that were sold to customers", "The selling price of the goods sold", "The cost of inventory remaining at the end of the year"], "answer": 1, "explanation": "COGS matches the expense of the inventory against the revenue it generated; it is the cost of only those items that were actually sold."}},
 {"FABM1-MerchBusActgMCQ-277": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Terms", "type": "Multiple Choice", "competency": "Understand", "question": "Why is 'Freight-In' added to the cost of purchases while 'Freight-Out' is treated as an operating expense?", "options": ["Freight-In is paid to bring goods in for sale (Asset cost); Freight-Out is a cost to deliver goods (Selling expense)", "Freight-In is always a larger amount", "Freight-In is paid by the buyer; Freight-Out is paid by the supplier", "There is no difference; it is a choice of management"], "answer": 0, "explanation": "Costs to prepare an asset for sale (getting it to the store) are capitalized (Freight-In). Costs to sell/deliver it are expenses (Freight-Out)."}},
 {"FABM1-MerchBusActgMCQ-278": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Contra Accounts", "type": "Multiple Choice", "competency": "Understand", "question": "Why are 'Sales Discounts' and 'Sales Returns' classified as contra-revenue accounts?", "options": ["Because they are expenses that increase Net Income", "Because they have a credit balance", "Because they are deducted from Gross Sales to calculate Net Sales", "Because they are liabilities"], "answer": 2, "explanation": "They reduce the total sales figure to show the actual net revenue earned, so they are 'contra' (opposite) to the Sales revenue account."}},
 {"FABM1-MerchBusActgMCQ-279": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Operating Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "Which best describes the operating cycle of a merchandiser?", "options": ["Cash -> Purchase of Equipment -> Depreciation -> Cash", "Cash -> Purchase of Inventory -> Sale of Inventory -> Collection of Cash", "Sale of Inventory -> Purchase of Inventory -> Cash", "Cash -> Accounts Payable -> Accounts Receivable"], "answer": 1, "explanation": "The cycle tracks cash flowing into inventory, which flows into sales/receivables, and finally back into cash."}},
 {"FABM1-MerchBusActgMCQ-280": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Understand", "question": "What is the main purpose of a physical inventory count in a Perpetual Inventory System?", "options": ["To calculate Cost of Goods Sold for the first time", "To determine the amount of inventory shrinkage or theft", "To determine the Gross Profit", "It is not required in a perpetual system"], "answer": 1, "explanation": "Since the perpetual system already tracks what *should* be there, the count is used to identify discrepancies (shrinkage) between records and reality."}},
 {"FABM1-MerchBusActgMCQ-281": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Trade Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "A product has a list price of $1,000. The manufacturer offers a trade discount series of 20/10. What is the invoice price?", "options": ["$700", "$720", "$800", "$1,000"], "answer": 1, "explanation": "First discount: $1,000 * 20% = $200 (Bal $800). Second discount: $800 * 10% = $80. Invoice Price = $800 - $80 = $720."}},
 {"FABM1-MerchBusActgMCQ-282": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cash Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase on Mar 1 for $2,000, terms 2/10, n/30. Return $500 on Mar 3. Payment made on Mar 9. How much cash is paid?", "options": ["$1,470", "$1,500", "$1,960", "$1,455"], "answer": 0, "explanation": "Net Payable = $2,000 - $500 = $1,500. Discount = $1,500 * 2% = $30. Cash Paid = $1,500 - $30 = $1,470."}},
 {"FABM1-MerchBusActgMCQ-283": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Net Sales Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Sales $50,000; Sales Returns $2,000; Sales Discounts $500; Freight-Out $1,000. Calculate Net Sales.", "options": ["$47,500", "$46,500", "$48,000", "$47,000"], "answer": 0, "explanation": "Net Sales = Sales - Returns - Discounts. $50,000 - $2,000 - $500 = $47,500. Freight-Out is an operating expense, not deducted from Sales."}},
 {"FABM1-MerchBusActgMCQ-284": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost of Goods Sold", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Inv $10,000; Net Purchases $40,000; End Inv $8,000. Calculate Cost of Goods Sold.", "options": ["$42,000", "$38,000", "$50,000", "$58,000"], "answer": 0, "explanation": "COGS = (Beg Inv + Net Purchases) - End Inv. ($10k + $40k) - $8k = $42,000."}},
 {"FABM1-MerchBusActgMCQ-285": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Gross Profit", "type": "Multiple Choice", "competency": "Apply", "question": "If Net Sales are $100,000 and the Gross Profit Rate is 35%, what is the Cost of Goods Sold?", "options": ["$35,000", "$65,000", "$135,000", "$60,000"], "answer": 1, "explanation": "If Gross Profit is 35%, then COGS is 65% of Net Sales. $100,000 * 0.65 = $65,000."}},
 {"FABM1-MerchBusActgMCQ-286": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Net Purchases", "type": "Multiple Choice", "competency": "Apply", "question": "Purchases $20,000; Freight-In $1,000; Purchase Returns $2,000; Purchase Discounts $500. Calculate Net Purchases.", "options": ["$18,500", "$19,500", "$21,000", "$23,000"], "answer": 0, "explanation": "Net Purchases = Purchases - Returns - Discounts. $20k - $2k - $0.5k = $17.5k. Note: Some formulas include Freight-In here for 'Net Cost of Purchases', but strictly 'Net Purchases' usually excludes freight. If asking for Cost of Goods Purchased, answer would be $18.5k. Assuming standard formula for GAS: $18,500 is the Net Cost of Purchases."}},
 {"FABM1-MerchBusActgMCQ-287": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Costing (FIFO)", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Inv: 10 units @ $10. Purchase: 10 units @ $12. Sold 15 units. Under FIFO, what is the Cost of Goods Sold?", "options": ["$160", "$170", "$150", "$180"], "answer": 0, "explanation": "FIFO sells oldest first. 10 units @ $10 ($100) + 5 units @ $12 ($60) = $160."}},
 {"FABM1-MerchBusActgMCQ-288": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Costing (Weighted Avg)", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Inv: 100 units @ $10. Purchase: 100 units @ $14. What is the weighted average unit cost?", "options": ["$12.00", "$10.00", "$14.00", "$12.50"], "answer": 0, "explanation": "Total Cost $1000 + $1400 = $2400. Total Units 200. Avg = $2400 / 200 = $12.00."}},
 {"FABM1-MerchBusActgMCQ-289": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Operating Income", "type": "Multiple Choice", "competency": "Apply", "question": "Gross Profit $50,000. Salaries Expense $10,000; Rent Expense $5,000; Interest Expense $1,000. Calculate Operating Income.", "options": ["$34,000", "$35,000", "$40,000", "$44,000"], "answer": 1, "explanation": "Operating Income = Gross Profit - Operating Expenses (Salaries + Rent). $50k - $15k = $35,000. Interest is non-operating."}},
 {"FABM1-MerchBusActgMCQ-290": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Net Income", "type": "Multiple Choice", "competency": "Apply", "question": "Net Sales $200,000; COGS $120,000; Operating Exp $50,000; Other Income $5,000. Calculate Net Income.", "options": ["$30,000", "$35,000", "$25,000", "$80,000"], "answer": 1, "explanation": "Gross Profit = $80k. Operating Income = $80k - $50k = $30k. Net Income = $30k + $5k = $35,000."}},
 {"FABM1-MerchBusActgMCQ-291": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Costs", "type": "Multiple Choice", "competency": "Apply", "question": "Buyer purchases goods for $500 FOB Shipping Point. Freight cost is $50. How much does the Buyer debit to Inventory (Perpetual)?", "options": ["$500", "$550", "$450", "$50"], "answer": 1, "explanation": "FOB Shipping Point freight is a product cost. Buyer debits Inventory for purchase price ($500) + freight ($50) = $550."}},
 {"FABM1-MerchBusActgMCQ-292": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Apply", "question": "Purchased $1,000 on account. Returned $200. Paid remaining balance with a 2% discount. What is the final cash payment?", "options": ["$780", "$784", "$800", "$980"], "answer": 1, "explanation": "Balance due $800. Discount = $800 * 2% = $16. Cash Payment = $800 - $16 = $784."}},
 {"FABM1-MerchBusActgMCQ-293": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Estimation", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Inv $50,000; Purchases $100,000; Sales $150,000. Historical Gross Profit rate is 40%. Estimate Ending Inventory.", "options": ["$90,000", "$60,000", "$40,000", "$10,000"], "answer": 1, "explanation": "GAS = $150k. Est COGS = Sales ($150k) * 60% = $90k. Est End Inv = GAS ($150k) - Est COGS ($90k) = $60,000."}},
 {"FABM1-MerchBusActgMCQ-294": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Periodic Closing", "type": "Multiple Choice", "competency": "Apply", "question": "Periodic System: Merchandise Inventory (Beginning) is $20,000. Ending is $25,000. What is the net impact on Income Summary from closing these two?", "options": ["Debit $5,000", "Credit $5,000", "Debit $45,000", "Credit $45,000"], "answer": 1, "explanation": "Close Beg (Debit Inc Summ $20k). Setup End (Credit Inc Summ $25k). Net impact is Credit $5,000."}},
 {"FABM1-MerchBusActgMCQ-295": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Adjusting Entries", "type": "Multiple Choice", "competency": "Apply", "question": "Unearned Sales Revenue account has a balance of $5,000. Analysis shows $3,000 has been earned. Apply the adjustment.", "options": ["Debit Unearned Revenue $3,000; Credit Sales $3,000", "Debit Sales $3,000; Credit Unearned Revenue $3,000", "Debit Unearned Revenue $2,000; Credit Sales $2,000", "Debit Cash $3,000; Credit Sales $3,000"], "answer": 0, "explanation": "Decrease liability (Debit Unearned $3k) and increase revenue (Credit Sales $3k) for the earned portion."}},
 {"FABM1-MerchBusActgMCQ-296": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Moving Average", "type": "Multiple Choice", "competency": "Apply", "question": "Perpetual: 10 units @ $10. Buy 10 units @ $14. Sell 10 units. Buy 10 units @ $16. What is the new moving average cost?", "options": ["$13.33", "$14.00", "$15.00", "$12.00"], "answer": 1, "explanation": "1) Avg = $12. Sell 10, remaining 10 @ $12. 2) Buy 10 @ $16. Total Cost $120+$160=$280. Total units 20. New Avg = $14."}},
 {"FABM1-MerchBusActgMCQ-297": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Transportation Costs", "type": "Multiple Choice", "competency": "Apply", "question": "Sold goods $1,000 FOB Destination. Freight $100. Apply the correct journal entry for the seller regarding freight.", "options": ["Debit Freight-Out $100; Credit Cash $100", "Debit Sales $100; Credit Cash $100", "Debit AR $100; Credit Cash $100", "Debit COGS $100; Credit Cash $100"], "answer": 0, "explanation": "FOB Destination is seller's expense. Debit Freight-Out (Expense), Credit Cash."}},
 {"FABM1-MerchBusActgMCQ-298": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Owner's Equity", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Capital $50,000; Net Income $20,000; Drawings $5,000. Apply the formula to find End Capital.", "options": ["$65,000", "$70,000", "$75,000", "$45,000"], "answer": 0, "explanation": "End Capital = Beg + Net Income - Drawings. $50k + $20k - $5k = $65,000."}},
 {"FABM1-MerchBusActgMCQ-299": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost of Goods Purchased", "type": "Multiple Choice", "competency": "Apply", "question": "Purchases $50,000; Returns $2,000; Discounts $1,000; Freight-In $3,000. Calculate Cost of Goods Purchased.", "options": ["$50,000", "$47,000", "$51,000", "$52,000"], "answer": 0, "explanation": "Net Purch = 50-2-1 = 47k. Cost of Goods Purchased = Net Purch (47k) + Freight-In (3k) = $50,000."}},
 {"FABM1-MerchBusActgMCQ-300": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Working Capital", "type": "Multiple Choice", "competency": "Apply", "question": "Current Assets $100,000; Current Liabilities $40,000; Inventory $30,000. Calculate Working Capital.", "options": ["$60,000", "$30,000", "$70,000", "$90,000"], "answer": 0, "explanation": "Working Capital = Current Assets - Current Liabilities. $100k - $40k = $60,000."}},
 {"FABM1-MerchBusActgMCQ-301": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Bank Reconciliation", "type": "Multiple Choice", "competency": "Apply", "question": "Bank statement balance $5,000. Deposit in transit $1,000. Outstanding checks $500. Calculate adjusted bank balance.", "options": ["$5,500", "$4,500", "$6,000", "$5,000"], "answer": 0, "explanation": "Adjusted = Bank Balance + Deposits in Transit - Outstanding Checks. $5,000 + $1,000 - $500 = $5,500."}},
 {"FABM1-MerchBusActgMCQ-302": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Turnover", "type": "Multiple Choice", "competency": "Apply", "question": "COGS $200,000. Avg Inventory $50,000. Calculate Inventory Turnover.", "options": ["4 times", "5 times", "2.5 times", "10 times"], "answer": 0, "explanation": "Turnover = COGS / Avg Inv. $200,000 / $50,000 = 4."}},
 {"FABM1-MerchBusActgMCQ-303": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Goods Available for Sale", "type": "Multiple Choice", "competency": "Apply", "question": "End Inv $20,000; COGS $80,000. Apply the concept to find Cost of Goods Available for Sale.", "options": ["$60,000", "$100,000", "$80,000", "$120,000"], "answer": 1, "explanation": "GAS = COGS + End Inv. $80,000 + $20,000 = $100,000."}},
 {"FABM1-MerchBusActgMCQ-304": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Perpetual Sales Entry", "type": "Multiple Choice", "competency": "Apply", "question": "Sold goods for $1,000 on account (Cost $600). Apply the correct journal entries.", "options": ["Debit AR 1000/Credit Sales 1000 ONLY", "Debit AR 1000/Credit Sales 1000 AND Debit COGS 600/Credit Inv 600", "Debit Cash 1000/Credit Sales 1000", "Debit COGS 600/Credit Inv 600 ONLY"], "answer": 1, "explanation": "Perpetual requires recording both the revenue ($1,000) and the expense/asset reduction ($600)."}},
 {"FABM1-MerchBusActgMCQ-305": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Nature of Business", "type": "Multiple Choice", "competency": "Remember", "question": "What type of business buys raw materials and converts them into finished products?", "options": ["Service", "Merchandising", "Manufacturing", "Non-profit"], "answer": 2, "explanation": "Manufacturing businesses convert raw materials; merchandisers buy and sell finished goods."}},
 {"FABM1-MerchBusActgMCQ-306": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Nature of Business", "type": "Multiple Choice", "competency": "Remember", "question": "A business that buys goods from a manufacturer and sells them to retailers is called a:", "options": ["Retailer", "Wholesaler", "Service Provider", "Consumer"], "answer": 1, "explanation": "Wholesalers act as intermediaries between manufacturers and retailers."}},
 {"FABM1-MerchBusActgMCQ-307": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Nature of Business", "type": "Multiple Choice", "competency": "Remember", "question": "A business that sells goods directly to the final consumer is called a:", "options": ["Wholesaler", "Retailer", "Manufacturer", "Broker"], "answer": 1, "explanation": "Retailers sell directly to the end-user (consumer)."}},
 {"FABM1-MerchBusActgMCQ-308": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "The account used to record the cost of merchandise sold to customers is:", "options": ["Sales Expense", "Cost of Goods Sold", "Purchases", "Merchandise Expense"], "answer": 1, "explanation": "Cost of Goods Sold (COGS) is the standard account name for the expense of inventory sold."}},
 {"FABM1-MerchBusActgMCQ-309": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "The normal balance of the Sales Revenue account is:", "options": ["Debit", "Credit", "Zero", "Variable"], "answer": 1, "explanation": "Revenue accounts normally have a credit balance."}},
 {"FABM1-MerchBusActgMCQ-310": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "The normal balance of the Merchandise Inventory account is:", "options": ["Debit", "Credit", "Zero", "Variable"], "answer": 0, "explanation": "Merchandise Inventory is an asset, so it has a normal debit balance."}},
 {"FABM1-MerchBusActgMCQ-311": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Which of the following is a contra-revenue account?", "options": ["Sales Discounts", "Cost of Goods Sold", "Freight-Out", "Advertising Expense"], "answer": 0, "explanation": "Sales Discounts is deducted from Sales Revenue, making it a contra-revenue account."}},
 {"FABM1-MerchBusActgMCQ-312": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Remember", "question": "Which inventory system updates the inventory account after every purchase and sale?", "options": ["Periodic", "Perpetual", "Hybrid", "Accrual"], "answer": 1, "explanation": "Perpetual systems maintain a continuous (perpetual) record of balances."}},
 {"FABM1-MerchBusActgMCQ-313": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Remember", "question": "Which inventory system relies on a physical count at the end of the period to determine Cost of Goods Sold?", "options": ["Perpetual", "Periodic", "Automated", "Just-In-Time"], "answer": 1, "explanation": "Periodic systems do not track COGS during the period; it is calculated after the count."}},
 {"FABM1-MerchBusActgMCQ-314": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Discounts", "type": "Multiple Choice", "competency": "Remember", "question": "A discount offered to encourage prompt payment of an account is called a:", "options": ["Trade Discount", "Cash Discount", "Quantity Discount", "Seasonal Discount"], "answer": 1, "explanation": "Cash discounts (e.g., 2/10, n/30) incentivize early payment."}},
 {"FABM1-MerchBusActgMCQ-315": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Discounts", "type": "Multiple Choice", "competency": "Remember", "question": "A discount deducted from the list price to determine the invoice price is called a:", "options": ["Cash Discount", "Trade Discount", "Sales Discount", "Purchase Discount"], "answer": 1, "explanation": "Trade discounts are used to set the price for a specific class of buyer (e.g., wholesaler vs. retailer)."}},
 {"FABM1-MerchBusActgMCQ-316": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Terms", "type": "Multiple Choice", "competency": "Remember", "question": "FOB stands for:", "options": ["Freight On Board", "Free On Board", "Freight Over Board", "Free Over Board"], "answer": 1, "explanation": "FOB is the standard abbreviation for 'Free On Board'."}},
 {"FABM1-MerchBusActgMCQ-317": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Terms", "type": "Multiple Choice", "competency": "Remember", "question": "Which term implies that the buyer pays the freight costs?", "options": ["FOB Destination", "FOB Shipping Point", "CIF", "Freight Prepaid"], "answer": 1, "explanation": "FOB Shipping Point means the buyer takes ownership at the shipping point and pays freight."}},
 {"FABM1-MerchBusActgMCQ-318": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Freight Terms", "type": "Multiple Choice", "competency": "Remember", "question": "Which term implies that ownership of goods remains with the seller until they reach the buyer?", "options": ["FOB Shipping Point", "FOB Destination", "COD", "Ex-Works"], "answer": 1, "explanation": "FOB Destination means title passes only when goods arrive at the destination."}},
 {"FABM1-MerchBusActgMCQ-319": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Documents", "type": "Multiple Choice", "competency": "Remember", "question": "The source document that evidences a sale of merchandise is the:", "options": ["Sales Invoice", "Purchase Order", "Receiving Report", "Check"], "answer": 0, "explanation": "The Sales Invoice is the primary document recording a sales transaction."}},
 {"FABM1-MerchBusActgMCQ-320": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Documents", "type": "Multiple Choice", "competency": "Remember", "question": "A document issued by a seller to a buyer to reduce the amount owed is called a:", "options": ["Debit Memo", "Credit Memo", "Invoice", "Check"], "answer": 1, "explanation": "A Credit Memo credits (reduces) the Accounts Receivable balance."}},
 {"FABM1-MerchBusActgMCQ-321": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Costing Methods", "type": "Multiple Choice", "competency": "Remember", "question": "FIFO stands for:", "options": ["Fast In, Fast Out", "First In, First Out", "First In, First Over", "Final In, First Out"], "answer": 1, "explanation": "FIFO is the acronym for First-In, First-Out."}},
 {"FABM1-MerchBusActgMCQ-322": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Costing Methods", "type": "Multiple Choice", "competency": "Remember", "question": "LIFO stands for:", "options": ["Last In, First Out", "Late In, First Out", "Last In, Final Out", "Least In, First Out"], "answer": 0, "explanation": "LIFO is the acronym for Last-In, First-Out."}},
 {"FABM1-MerchBusActgMCQ-323": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Costing Methods", "type": "Multiple Choice", "competency": "Remember", "question": "Which costing method assumes that the specific items sold can be identified?", "options": ["FIFO", "LIFO", "Weighted Average", "Specific Identification"], "answer": 3, "explanation": "Specific Identification tracks the actual cost of each individual item sold."}},
 {"FABM1-MerchBusActgMCQ-324": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Remember", "question": "Net Sales minus Cost of Goods Sold equals:", "options": ["Net Income", "Gross Profit", "Operating Income", "Retained Earnings"], "answer": 1, "explanation": "This is the fundamental formula for Gross Profit."}},
 {"FABM1-MerchBusActgMCQ-325": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Remember", "question": "Operating Expenses are typically divided into two categories: Selling Expenses and:", "options": ["Buying Expenses", "Administrative Expenses", "Cost of Goods Sold", "Financial Expenses"], "answer": 1, "explanation": "The standard classification is Selling Expenses and General & Administrative Expenses."}},
 {"FABM1-MerchBusActgMCQ-326": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Freight-In is classified as a(n):", "options": ["Operating Expense", "Contra-Revenue", "Addition to Purchases (Cost)", "Contra-Asset"], "answer": 2, "explanation": "Freight-In is added to the cost of purchases to determine the total cost of acquiring inventory."}},
 {"FABM1-MerchBusActgMCQ-327": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Freight-Out is classified as a(n):", "options": ["Operating Expense", "Addition to Cost of Goods Sold", "Contra-Sales", "Asset"], "answer": 0, "explanation": "Freight-Out (delivery expense) is a selling expense, part of operating expenses."}},
 {"FABM1-MerchBusActgMCQ-328": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Remember", "question": "In the credit term '2/10, n/30', the 'n' stands for:", "options": ["Now", "Net", "New", "Next"], "answer": 1, "explanation": "'n' stands for the Net amount (the full invoice total). "}},
 {"FABM1-MerchBusActgMCQ-329": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Remember", "question": "EOM stands for:", "options": ["End of Month", "Early of Month", "End of Market", "Every Other Month"], "answer": 0, "explanation": "EOM is the standard abbreviation for End of Month."}},
 {"FABM1-MerchBusActgMCQ-330": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Purchase Returns and Allowances is a contra account to:", "options": ["Sales", "Accounts Payable", "Purchases", "Cash"], "answer": 2, "explanation": "It reduces the Purchases account balance."}},
 {"FABM1-MerchBusActgMCQ-331": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Which account is used only in a Periodic Inventory System?", "options": ["Merchandise Inventory", "Sales", "Purchases", "Accounts Receivable"], "answer": 2, "explanation": "The 'Purchases' account is specific to the Periodic system; Perpetual uses 'Inventory' for buying goods."}},
 {"FABM1-MerchBusActgMCQ-332": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Remember", "question": "On the Balance Sheet, Merchandise Inventory is reported as a:", "options": ["Current Liability", "Current Asset", "Non-Current Asset", "Revenue"], "answer": 1, "explanation": "Inventory is a Current Asset because it is expected to be sold within the operating cycle."}},
 {"FABM1-MerchBusActgMCQ-333": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General", "type": "Multiple Choice", "competency": "Remember", "question": "The loss of inventory due to theft, spoilage, or breakage is called:", "options": ["Depreciation", "Amortization", "Shrinkage", "Obsolescence"], "answer": 2, "explanation": "Inventory shrinkage refers to the difference between recorded inventory and actual physical inventory."}},
 {"FABM1-MerchBusActgMCQ-334": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Remember", "question": "Which income statement format separates Operating Revenues and Expenses from Non-Operating items?", "options": ["Single-Step", "Multi-Step", "Classified", "Account Form"], "answer": 1, "explanation": "The Multi-Step Income Statement calculates Gross Profit and Operating Income separately."}},
 {"FABM1-MerchBusActgMCQ-335": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General", "type": "Multiple Choice", "competency": "Remember", "question": "The time it takes to buy inventory, sell it, and collect the cash is called the:", "options": ["Accounting Period", "Operating Cycle", "Fiscal Year", "Reporting Cycle"], "answer": 1, "explanation": "The Operating Cycle is the lifespan of cash turning into goods and back into cash."}},
 {"FABM1-MerchBusActgMCQ-336": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Remember", "question": "Gross Profit minus Operating Expenses equals:", "options": ["Net Income", "Net Sales", "Income from Operations", "Cost of Goods Sold"], "answer": 2, "explanation": "Income from Operations (or Operating Income) is the profit from core business activities."}},
 {"FABM1-MerchBusActgMCQ-337": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Sales Discounts Forfeited is treated as:", "options": ["Operating Expense", "Other Income", "Cost of Goods Sold", "Liability"], "answer": 1, "explanation": "It is income earned from the customer's failure to take a discount (typically interest-like income)."}},
 {"FABM1-MerchBusActgMCQ-338": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Accumulated Depreciation is a contra account to:", "options": ["Expenses", "Liabilities", "Assets", "Equity"], "answer": 2, "explanation": "It reduces the book value of Property, Plant, and Equipment assets."}},
 {"FABM1-MerchBusActgMCQ-339": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Documents", "type": "Multiple Choice", "competency": "Remember", "question": "A formal request to a supplier to buy goods is called a:", "options": ["Purchase Invoice", "Purchase Order", "Sales Order", "Receipt"], "answer": 1, "explanation": "The Purchase Order (PO) is the buyer's official offer to the seller."}},
 {"FABM1-MerchBusActgMCQ-340": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Documents", "type": "Multiple Choice", "competency": "Remember", "question": "A document listing the items included in a shipment is called a:", "options": ["Check", "Packing Slip", "Ledger", "Journal"], "answer": 1, "explanation": "A Packing Slip accompanies the goods to verify contents."}},
 {"FABM1-MerchBusActgMCQ-341": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Ledgers", "type": "Multiple Choice", "competency": "Remember", "question": "A separate ledger containing individual accounts for each customer is called the:", "options": ["General Ledger", "Accounts Receivable Subsidiary Ledger", "Purchases Journal", "Sales Journal"], "answer": 1, "explanation": "The Subsidiary Ledger breaks down the control account (AR) by individual customer."}},
 {"FABM1-MerchBusActgMCQ-342": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Ledgers", "type": "Multiple Choice", "competency": "Remember", "question": "The account in the General Ledger that summarizes the subsidiary ledger is called a:", "options": ["Summary Account", "Control Account", "Master Account", "Detail Account"], "answer": 1, "explanation": "Control Accounts (e.g., Accounts Receivable) show the total balance of the subsidiary ledger."}},
 {"FABM1-MerchBusActgMCQ-343": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Unearned Revenue is classified as a(n):", "options": ["Asset", "Liability", "Revenue", "Expense"], "answer": 1, "explanation": "Unearned Revenue represents an obligation to deliver goods/services in the future, so it is a liability."}},
 {"FABM1-MerchBusActgMCQ-344": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "Prepaid Insurance is classified as a(n):", "options": ["Asset", "Liability", "Expense", "Revenue"], "answer": 0, "explanation": "Prepaid expenses are assets because they provide future economic benefit."}},
 {"FABM1-MerchBusActgMCQ-345": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Remember", "question": "Which account is NOT closed at the end of the accounting period?", "options": ["Sales Revenue", "Cost of Goods Sold", "Merchandise Inventory", "Sales Discounts"], "answer": 2, "explanation": "Merchandise Inventory is a permanent (real) asset account and carries its balance forward."}},
 {"FABM1-MerchBusActgMCQ-346": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Remember", "question": "Temporary accounts are closed to which summary account?", "options": ["Retained Earnings", "Income Summary", "Cash", "Capital Stock"], "answer": 1, "explanation": "Income Summary is the holding tank for revenues and expenses during the closing process."}},
 {"FABM1-MerchBusActgMCQ-347": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Costing Methods", "type": "Multiple Choice", "competency": "Remember", "question": "The Moving Average method is associated with which inventory system?", "options": ["Periodic", "Perpetual", "Both", "Neither"], "answer": 1, "explanation": "Moving Average requires recalculating cost after every purchase, which is a feature of Perpetual systems."}},
 {"FABM1-MerchBusActgMCQ-348": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General", "type": "Multiple Choice", "competency": "Remember", "question": "Which principle requires that inventory be reported at the Lower of Cost or Net Realizable Value?", "options": ["Consistency", "Conservatism", "Matching", "Full Disclosure"], "answer": 1, "explanation": "Conservatism dictates that when in doubt, choose the method that is least likely to overstate assets/income."}},
 {"FABM1-MerchBusActgMCQ-349": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Statements", "type": "Multiple Choice", "competency": "Remember", "question": "The formula for Cost of Goods Available for Sale is:", "options": ["Beginning Inventory + Net Purchases", "Sales - Gross Profit", "Ending Inventory + COGS", "Purchases - Returns"], "answer": 0, "explanation": "It is the sum of beginning stock plus what was added (net purchases) during the period."}},
 {"FABM1-MerchBusActgMCQ-350": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Remember", "question": "In the term '1/15, n/60', the number '15' represents:", "options": ["The discount percentage", "The number of days in the discount period", "The number of days until full payment is due", "The penalty rate"], "answer": 1, "explanation": "The second number indicates the duration (in days) the discount is available."}},
 {"FABM1-MerchBusActgMCQ-351": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General", "type": "Multiple Choice", "competency": "Remember", "question": "Which of the following is considered a 'Period Cost'?", "options": ["Freight-In", "Advertising Expense", "Purchase Price of Inventory", "Import Duties"], "answer": 1, "explanation": "Period costs (like advertising) are expensed in the period incurred, unlike product costs which are capitalized."}},
 {"FABM1-MerchBusActgMCQ-352": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts", "type": "Multiple Choice", "competency": "Remember", "question": "What is the normal balance of Purchase Discounts?", "options": ["Debit", "Credit", "Zero", "Variable"], "answer": 1, "explanation": "It is a contra-expense (contra-purchase) account, so it has a Credit balance."}},
 {"FABM1-MerchBusActgMCQ-353": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General", "type": "Multiple Choice", "competency": "Remember", "question": "GAAP stands for:", "options": ["Generally Accepted Accounting Principles", "Global Accounting and Auditing Procedures", "General Association of Accounting Practitioners", "Governmental Audit and Accounting Policies"], "answer": 0, "explanation": "GAAP stands for Generally Accepted Accounting Principles."}},
 {"FABM1-MerchBusActgMCQ-354": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "General", "type": "Multiple Choice", "competency": "Remember", "question": "The entity concept states that:", "options": ["The business is separate from its owners", "The business will continue indefinitely", "Money is the common unit of measure", "Revenue is recorded when earned"], "answer": 0, "explanation": "The Entity Concept separates the financial affairs of the business from the personal affairs of the owner."}},
 {"FABM1-MerchBusActgMCQ-355": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Ethical Sourcing", "type": "Multiple Choice", "competency": "Evaluate", "question": "A merchandising company can source cheaper goods from a supplier known for poor labor conditions or pay more for ethically sourced goods. From an Ignatian perspective of 'Solidarity', what is the most discerning choice?", "options": ["Choose the cheaper supplier to maximize shareholder profit immediately", "Choose the ethical supplier to uphold human dignity, even if margins decrease", "Mix the orders to balance profit and public image", "Ignore the supplier's practices as it is outside the company's control"], "answer": 1, "explanation": "Solidarity involves standing with the marginalized. Prioritizing human dignity over profit reflects ethical discernment and social responsibility."}},
 {"FABM1-MerchBusActgMCQ-356": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Management", "type": "Multiple Choice", "competency": "Evaluate", "question": "A grocery store ends the day with significant perishable inventory. Weighing the value of 'Stewardship', what is the most appropriate action?", "options": ["Throw it away to clear space for fresh stock", "Sell it as 'fresh' the next day to avoid loss", "Donate it to a local food bank before it spoils", "Keep it on the shelf until a customer complains"], "answer": 2, "explanation": "Stewardship involves the responsible use of resources. Donating food minimizes waste and serves the community, aligning with the common good."}},
 {"FABM1-MerchBusActgMCQ-357": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Pricing Strategy", "type": "Multiple Choice", "competency": "Evaluate", "question": "During a natural disaster, demand for bottled water spikes. A store owner considers tripling the price. Evaluating this through the lens of 'Common Good', how should you judge this potential action?", "options": ["It is good business; supply and demand dictate price", "It is unethical exploitation of vulnerable people (price gouging)", "It is necessary to prevent stocking out", "It is acceptable if competitors are doing it"], "answer": 1, "explanation": "The Common Good requires accessing essential needs. Exploiting a crisis for excess profit violates the ethical obligation to the community."}},
 {"FABM1-MerchBusActgMCQ-358": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Internal Controls", "type": "Multiple Choice", "competency": "Evaluate", "question": "An employee is caught stealing inventory (shrinkage) due to severe financial hardship. In applying 'Cura Personalis' (Care for the whole person), how should management weigh their response?", "options": ["Immediate termination and criminal prosecution without dialogue", "Ignore the theft to be nice", "Address the theft firmly while seeking to understand and assist with the root cause", "Deduct the loss from the employee's wages without telling them"], "answer": 2, "explanation": "Cura Personalis balances justice with compassion. It requires addressing the wrong (theft) while caring for the individual's circumstances."}},
 {"FABM1-MerchBusActgMCQ-359": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Reporting", "type": "Multiple Choice", "competency": "Evaluate", "question": "A manager suggests 'smoothing' earnings by hiding current profits in a 'reserve' account to cover future losses. Evaluate the ethical implication of this action.", "options": ["It is prudent management to stabilize income", "It is a smart way to keep stock prices steady", "It is deceptive and violates the principle of Truthfulness in reporting", "It is acceptable if the auditors don't find it"], "answer": 2, "explanation": "Accounting relies on transparency and truth. Hiding profits, even for 'stability', misleads stakeholders and lacks integrity."}},
 {"FABM1-MerchBusActgMCQ-360": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Purchase Discounts", "type": "Multiple Choice", "competency": "Evaluate", "question": "Your company missed the discount period by 2 days but the accountant suggests taking the 2% discount anyway, assuming the supplier won't notice. Weigh the ethical quality of this suggestion.", "options": ["It is dishonest and effectively theft of the supplier's revenue", "It is a standard business practice to save money", "It is the supplier's fault if they don't catch it", "It is acceptable because 2 days is insignificant"], "answer": 0, "explanation": "Taking an unearned discount is dishonest. Integrity (honesty in small things) is central to ethical business character."}},
 {"FABM1-MerchBusActgMCQ-361": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Supplier Relations", "type": "Multiple Choice", "competency": "Evaluate", "question": "A large retailer demands extended payment terms (90 days) from small local suppliers, causing them cash flow issues. Evaluate this use of market power.", "options": ["It is smart leverage to improve the retailer's working capital", "It is an unjust exploitation of the powerful against the weak", "It is a necessary strategy to compete globally", "It is fair because the suppliers agreed to it (under duress)"], "answer": 1, "explanation": "While legal, using power to crush the liquidity of small partners violates the principle of fairness and Justice."}},
 {"FABM1-MerchBusActgMCQ-362": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Evaluate", "question": "A customer returns a product that was clearly damaged by their own misuse, but claims it was defective. The store policy allows manager discretion. What describes a 'Magis' (Doing more/better) response?", "options": ["Refuse the return immediately to save costs", "Accept the return to build a long-term relationship, while educating the customer gently", "Argue with the customer to prove them wrong", "Accept the return but ban the customer from the store"], "answer": 1, "explanation": "Magis seeks the 'greater' good. Building trust and showing generosity, even when not strictly required, serves a higher value than immediate cost saving."}},
 {"FABM1-MerchBusActgMCQ-363": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Valuation", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company holds inventory that is now obsolete and unsellable. The manager refuses to write it down because it will lower his bonus. Evaluate this decision.", "options": ["Understandable self-interest", "A breach of fiduciary duty and lack of detachment from personal gain", "Acceptable if he plans to sell it next year", "Smart accounting to protect the stock price"], "answer": 1, "explanation": "Ignatian detachment implies not being enslaved by personal gain (bonus). The manager is placing self-interest above the truth of the financial position."}},
 {"FABM1-MerchBusActgMCQ-364": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cost Management", "type": "Multiple Choice", "competency": "Evaluate", "question": "To reduce Operating Expenses, a company plans to cut employee health benefits while maintaining high executive bonuses. Judging this by the standard of 'Social Justice', what is the conclusion?", "options": ["It is a necessary business decision to stay profitable", "It creates an unjust disparity and fails to protect the vulnerable", "It is fair because executives work harder", "It is acceptable if legal"], "answer": 1, "explanation": "Social Justice demands equitable distribution of burdens and rewards. Cutting essential benefits for the many to reward the few is unjust."}},
 {"FABM1-MerchBusActgMCQ-365": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Product Safety", "type": "Multiple Choice", "competency": "Evaluate", "question": "A toy retailer discovers a product has a minor safety defect. A recall would be expensive. Discern the ethical path.", "options": ["Wait for customer complaints before acting", "Calculate the cost of lawsuits vs. recall and choose the cheaper option", "Initiate an immediate recall to protect children, regardless of cost", "Discount the product to sell it quickly"], "answer": 2, "explanation": "The value of human life and safety supersedes financial loss. A discerning leader prioritizes the well-being of the consumer."}},
 {"FABM1-MerchBusActgMCQ-366": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Environmental Impact", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company can save on Freight-In costs by using a shipping method with high carbon emissions. Weighing 'Care for our Common Home' (Laudato Si'), what is the implication?", "options": ["Cost savings justify the emissions", "The environmental damage is an 'externality' not on the balance sheet", "The company should seek a balance, paying more for greener transport if possible", "Environmental issues are not an accounting concern"], "answer": 2, "explanation": "Care for our Common Home requires businesses to factor environmental impact into financial decisions, even if it increases costs."}},
 {"FABM1-MerchBusActgMCQ-367": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Marketing/Sales", "type": "Multiple Choice", "competency": "Evaluate", "question": "A sales team inflates revenue numbers at year-end by shipping goods to customers who didn't order them (channel stuffing). Evaluate this practice.", "options": ["Creative marketing strategy", "Fraudulent manipulation of financial truth", "Proactive inventory management", "Acceptable if the customers return them later"], "answer": 1, "explanation": "This is deceptive and creates a false picture of success. It violates the commitment to truth and integrity."}},
 {"FABM1-MerchBusActgMCQ-368": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Technology", "type": "Multiple Choice", "competency": "Evaluate", "question": "Implementing a new Perpetual Inventory System will make 5 warehouse clerks redundant. How should management discern this transition?", "options": ["Fire them immediately to realize savings", "Hide the plan until the last minute", "Retrain the clerks for new roles or offer generous severance (Cura Personalis)", "Blame the technology for the job losses"], "answer": 2, "explanation": "Efficiency should not trample human dignity. Management has a duty to care for the displaced workers through retraining or support."}},
 {"FABM1-MerchBusActgMCQ-369": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Taxation", "type": "Multiple Choice", "competency": "Evaluate", "question": "A multinational merchandiser uses loopholes to pay zero tax in the country where it operates. Evaluate this in terms of 'Distributive Justice'.", "options": ["It is smart tax planning and maximizes shareholder value", "It deprives the local community of resources for infrastructure and services", "It is the government's fault for having loopholes", "It is ethically neutral"], "answer": 1, "explanation": "Distributive justice requires contributing a fair share to the society that enables the business to exist. Aggressive avoidance harms the community."}},
 {"FABM1-MerchBusActgMCQ-370": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Quality Control", "type": "Multiple Choice", "competency": "Evaluate", "question": "A supplier offers 'Grade A' rice that is actually mixed with 'Grade B'. Selling it as 'Grade A' increases profit margins. What virtue is being compromised?", "options": ["Prudence", "Honesty/Integrity", "Fortitude", "Hope"], "answer": 1, "explanation": "Misrepresenting the quality of goods is a direct violation of honesty and breaches the trust of the customer."}},
 {"FABM1-MerchBusActgMCQ-371": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Competition", "type": "Multiple Choice", "competency": "Evaluate", "question": "A large chain lowers prices below cost (predatory pricing) specifically to drive a local mom-and-pop store out of business. Evaluate the morality of this strategy.", "options": ["It is the survival of the fittest", "It is a standard barrier to entry", "It is an unjust use of power to destroy a neighbor's livelihood", "It benefits the consumer in the short run, so it's good"], "answer": 2, "explanation": "Deliberately seeking to destroy a competitor's livelihood through financial force lacks charity and fairness."}},
 {"FABM1-MerchBusActgMCQ-372": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accounts Payable", "type": "Multiple Choice", "competency": "Evaluate", "question": "You discover a double payment to a supplier. They haven't noticed. Discern the right course of action.", "options": ["Keep the money, it's their mistake", "Wait to see if they ask for it", "Notify them immediately and return the funds", "Credit it to 'Miscellaneous Income'"], "answer": 2, "explanation": "Honesty requires returning what does not belong to you, regardless of whether the other party noticed."}},
 {"FABM1-MerchBusActgMCQ-373": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Payroll", "type": "Multiple Choice", "competency": "Evaluate", "question": "To avoid paying overtime rates required by law, a manager asks staff to 'clock out' but keep working. Evaluate this request.", "options": ["It shows dedication to the company", "It is wage theft and exploitation of subordinates", "It is a necessary sacrifice for the budget", "It is acceptable if the employees agree"], "answer": 1, "explanation": "This is exploitation. A leader should protect the rights of workers, not pressure them into uncompensated labor."}},
 {"FABM1-MerchBusActgMCQ-374": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Customer Data", "type": "Multiple Choice", "competency": "Evaluate", "question": "A retailer collects customer purchase history. A third party offers to buy this data. Weighing the 'Right to Privacy', what is the ethical concern?", "options": ["No concern, data is an asset", "Selling personal data without explicit consent treats people as commodities", "It increases revenue, which is good", "If the data is anonymized, there are zero ethical issues"], "answer": 1, "explanation": "Treating customers merely as data points to be sold commodifies the person and violates their privacy and trust."}},
 {"FABM1-MerchBusActgMCQ-375": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Selection", "type": "Multiple Choice", "competency": "Evaluate", "question": "A bookstore decides not to stock magazines that promote violence or degradation, even though they sell well. What value is guiding this decision?", "options": ["Censorship", "Profit maximization", "Moral Responsibility/Formation of Culture", "Poor business sense"], "answer": 2, "explanation": "Business has a role in shaping culture. Rejecting profitable but harmful items reflects moral responsibility."}},
 {"FABM1-MerchBusActgMCQ-376": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Financial Analysis", "type": "Multiple Choice", "competency": "Evaluate", "question": "When analyzing a company's success, an investor looks only at Net Income. From an Ignatian 'Integral Human Development' perspective, what is missing?", "options": ["Nothing, Net Income is the only metric", "The impact on employees, community, and environment (Triple Bottom Line)", "The stock price", "The tax rate"], "answer": 1, "explanation": "Integral development looks at the whole picture—economic, social, and spiritual well-being, not just financial profit."}},
 {"FABM1-MerchBusActgMCQ-377": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Shrinkage", "type": "Multiple Choice", "competency": "Evaluate", "question": "High shrinkage rates are detected in a store. Management installs intrusive surveillance cameras in bathrooms. Evaluate this measure.", "options": ["Effective loss prevention", "A violation of human dignity and privacy", "Necessary to catch thieves", "Standard industry practice"], "answer": 1, "explanation": "Security measures must respect the inherent dignity and privacy of individuals. Invasive surveillance crosses that line."}},
 {"FABM1-MerchBusActgMCQ-378": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Auditing", "type": "Multiple Choice", "competency": "Evaluate", "question": "An internal auditor is asked by a friend (the manager) to overlook a missing receipt. Discern the conflict.", "options": ["Friendship is more important than rules", "Professional Integrity requires reporting the discrepancy impartially", "It is a small amount, so it doesn't matter", "Magis means helping a friend"], "answer": 1, "explanation": "Integrity and professional duty must take precedence over personal loyalty to ensure truthfulness in reporting."}},
 {"FABM1-MerchBusActgMCQ-379": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Credit Policy", "type": "Multiple Choice", "competency": "Evaluate", "question": "A store offers 'Zero Interest' credit but hides high penalties in the fine print, targeting low-income buyers. Evaluate the morality of this offer.", "options": ["Smart marketing to a niche segment", "Predatory lending that exploits the lack of financial literacy", "Helping the poor access goods", "It is legal, so it is ethical"], "answer": 1, "explanation": "Deliberately confusing or exploiting vulnerable customers with hidden terms is predatory and unjust."}},
 {"FABM1-MerchBusActgMCQ-380": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Costing", "type": "Multiple Choice", "competency": "Evaluate", "question": "Management switches from FIFO to LIFO solely to pay less taxes during inflation, while telling shareholders the company is growing. Is there an ethical tension?", "options": ["No, tax avoidance is a duty", "Yes, there is a tension between tax minimization and transparent reporting of asset value", "No, accounting methods are arbitrary", "Yes, but LIFO is illegal"], "answer": 1, "explanation": "While legal, the tension lies in the motivation. Is the goal true representation of the business, or just manipulating the bottom line?"}},
 {"FABM1-MerchBusActgMCQ-381": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Returns Policy", "type": "Multiple Choice", "competency": "Evaluate", "question": "A strict 'No Returns' policy protects profit but leaves customers stuck with defective goods. How does this policy rate on 'Service'?", "options": ["It is excellent service to shareholders", "It fails to serve the needs of the neighbor (customer) and erodes trust", "It encourages customers to be careful", "It is a neutral policy"], "answer": 1, "explanation": "Business is a service. Policies that burden the customer unfairly fail the call to serve the neighbor."}},
 {"FABM1-MerchBusActgMCQ-382": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Whistleblowing", "type": "Multiple Choice", "competency": "Evaluate", "question": "A junior accountant notices the CFO inflating inventory values. They are afraid of losing their job. What does the virtue of 'Fortitude' (Courage) demand?", "options": ["Ignoring it to stay safe", "Reporting the fraud despite the personal risk", "Quitting quietly", "Joining in to get a raise"], "answer": 1, "explanation": "Fortitude is the courage to do the right thing despite fear or consequences. Reporting fraud protects the community."}},
 {"FABM1-MerchBusActgMCQ-383": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Supply Chain", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company discovers its supplier uses child labor. Switching suppliers will cost 10% more. Discern the decision.", "options": ["Keep the supplier but ask them to hide it", "Switch immediately; complicity in child labor is unacceptable", "Wait until the contract expires", "Pass the cost to the customer and blame the supplier"], "answer": 1, "explanation": "Moral complicity in evil (child labor) must be rejected immediately, even at a financial cost."}},
 {"FABM1-MerchBusActgMCQ-384": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Advertising", "type": "Multiple Choice", "competency": "Evaluate", "question": "Marketing expenses include an ad campaign that creates false needs and anxieties in teenagers to sell clothes. Evaluate this approach.", "options": ["Effective demand generation", "Manipulative and harmful to the mental health of the youth", "Standard industry practice", "Good for the economy"], "answer": 1, "explanation": "Marketing should inform, not manipulate. Exploiting insecurities for profit harms the psychological well-being of the person."}},
 {"FABM1-MerchBusActgMCQ-385": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cash Management", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company delays paying its utility bills until the last possible second to maximize interest income. While legal, how does this reflect on 'Stewardship of Relationships'?", "options": ["It is smart cash management", "It strains relationships and treats partners merely as creditors", "It shows dominance", "It is irrelevant"], "answer": 1, "explanation": "Relationships require mutual respect. Consistently delaying payment exploits the partner's patience for minor gain."}},
 {"FABM1-MerchBusActgMCQ-386": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sustainability", "type": "Multiple Choice", "competency": "Evaluate", "question": "A clothing retailer engages in 'Fast Fashion', encouraging consumers to discard clothes after a few wears. Evaluate this regarding 'Sustainability'.", "options": ["It keeps the economy moving", "It promotes a 'throwaway culture' that degrades the earth", "It helps the poor buy cheap clothes", "It increases inventory turnover"], "answer": 1, "explanation": "The 'throwaway culture' (referenced by Pope Francis) disrespects creation and resources. Promoting waste for profit is unsustainable."}},
 {"FABM1-MerchBusActgMCQ-387": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Expense Allocation", "type": "Multiple Choice", "competency": "Evaluate", "question": "An owner charges personal family vacations to the business as 'Travel Expense'. Evaluate this action.", "options": ["A nice perk of ownership", "Tax fraud and a violation of the Entity Concept", "Acceptable if the business is profitable", "Good for employee morale"], "answer": 1, "explanation": "This violates the separation of business and personal life (Entity Concept) and is stealing from the taxable entity/other stakeholders."}},
 {"FABM1-MerchBusActgMCQ-388": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Investments", "type": "Multiple Choice", "competency": "Evaluate", "question": "A merchandising firm has excess cash. They invest it in a company that produces landmines. Discern this investment choice.", "options": ["Money is neutral; highest return is best", "Investing involves moral cooperation; one should not support destructive industries", "It is the finance department's problem", "It diversifies the portfolio"], "answer": 1, "explanation": "Investment is a moral act. Supporting industries that harm life contradicts the pursuit of peace and the common good."}},
 {"FABM1-MerchBusActgMCQ-389": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Transparency", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company uses complex jargon in its financial footnotes to obscure a large liability. What principle is violated?", "options": ["Complexity", "Transparency/Truthfulness", "Consistency", "Conservatism"], "answer": 1, "explanation": "Information should be clear and understandable. Obscuring the truth violates the duty of transparency to stakeholders."}},
 {"FABM1-MerchBusActgMCQ-390": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Leadership", "type": "Multiple Choice", "competency": "Evaluate", "question": "A CEO takes a pay cut to avoid laying off warehouse staff during a downturn. This is an example of:", "options": ["Poor financial planning", "Servant Leadership", "Weakness", "Inefficiency"], "answer": 1, "explanation": "Servant leadership prioritizes the needs of the team. Sacrificing personal gain for the good of the group is a high ethical standard."}},
 {"FABM1-MerchBusActgMCQ-391": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Community Relations", "type": "Multiple Choice", "competency": "Evaluate", "question": "A store opens in a community but hires no locals and gives nothing back to local causes. Evaluate its presence.", "options": ["It is an extractive institution, taking without contributing", "It provides goods, which is enough", "It is smart to bring in outside experts", "It is neutral"], "answer": 0, "explanation": "A business should be a neighbor. Extracting profit without contributing to the local social fabric is parasitic."}},
 {"FABM1-MerchBusActgMCQ-392": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Sales Tactics", "type": "Multiple Choice", "competency": "Evaluate", "question": "Sales staff are pressured to sell extended warranties to elderly customers who don't understand them. Evaluate this sales tactic.", "options": ["High-pressure sales are standard", "It is exploitation of the vulnerable", "It increases high-margin revenue", "Caveat Emptor (Buyer Beware)"], "answer": 1, "explanation": "Exploiting a customer's lack of understanding or vulnerability is unjust and unethical."}},
 {"FABM1-MerchBusActgMCQ-393": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Inventory Waste", "type": "Multiple Choice", "competency": "Evaluate", "question": "A fashion brand burns unsold inventory to maintain 'brand exclusivity' and scarcity. Weigh this against the value of 'Solidarity'.", "options": ["Protects brand equity", "An immense waste of resources that could clothe the needy", "Smart supply management", "Tax deductible loss"], "answer": 1, "explanation": "Destroying usable goods while others go without is a grave offense against solidarity and stewardship."}},
 {"FABM1-MerchBusActgMCQ-394": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Fair Trade", "type": "Multiple Choice", "competency": "Evaluate", "question": "Buying 'Fair Trade' coffee for the office costs more but ensures farmers get a living wage. Discern the value.", "options": ["Waste of shareholder money", "An expression of justice and recognition of the worker's dignity", "Marketing gimmick", "Unnecessary expense"], "answer": 1, "explanation": "Fair Trade aligns commerce with justice, ensuring that the primary producer is treated with dignity."}},
 {"FABM1-MerchBusActgMCQ-395": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Conflict of Interest", "type": "Multiple Choice", "competency": "Evaluate", "question": "The purchasing manager buys supplies from a company owned by his brother at a slightly higher price. Evaluate this.", "options": ["Supporting family is good", "A Conflict of Interest that harms the company", "Acceptable if the supplies are good", "Standard networking"], "answer": 1, "explanation": "This places personal interest (family) above the duty to the company, creating a conflict of interest."}},
 {"FABM1-MerchBusActgMCQ-396": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Gender Equity", "type": "Multiple Choice", "competency": "Evaluate", "question": "A store charges more for women's personal care products than identical men's products (Pink Tax). Evaluate this pricing.", "options": ["Smart segmentation", "Discriminatory and unjust", "Market-driven pricing", "Women are willing to pay more"], "answer": 1, "explanation": "Pricing based solely on gender for identical goods is discriminatory and violates the principle of equality."}},
 {"FABM1-MerchBusActgMCQ-397": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Gratitude", "type": "Multiple Choice", "competency": "Evaluate", "question": "At year-end, the owner takes all the credit for the profits. What Ignatian attitude is lacking?", "options": ["Ambition", "Gratitude and recognition of others' contributions", "Strategy", "Focus"], "answer": 1, "explanation": "Gratitude acknowledges that success is a collective effort. Failing to recognize the team is arrogance."}},
 {"FABM1-MerchBusActgMCQ-398": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Work-Life Balance", "type": "Multiple Choice", "competency": "Evaluate", "question": "During inventory counts, staff are required to work 16-hour shifts for a week without rest. Discern this management style.", "options": ["It shows commitment", "It treats people like machines, ignoring their physical and family needs", "It gets the job done fast", "It saves hiring temps"], "answer": 1, "explanation": "People are not machines. Excessive work demands violate the dignity of the worker and their need for rest."}},
 {"FABM1-MerchBusActgMCQ-399": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Accessibility", "type": "Multiple Choice", "competency": "Evaluate", "question": "A store layout is cluttered and inaccessible to people with disabilities. Management says fixing it is 'too expensive'. Evaluate this.", "options": ["Cost-benefit analysis says no", "Excludes members of the community and fails in inclusivity", "It's their problem", "They can shop online"], "answer": 1, "explanation": "Inclusivity requires making reasonable accommodations so all members of the community can participate."}},
 {"FABM1-MerchBusActgMCQ-400": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Cultural Sensitivity", "type": "Multiple Choice", "competency": "Evaluate", "question": "A merchandising campaign uses sacred religious symbols of a minority group as fashion accessories. Discern the appropriateness.", "options": ["It's trendy and profitable", "It is cultural appropriation and disrespectful", "It honors them", "No one cares"], "answer": 1, "explanation": "Respect for others includes respecting their sacred symbols. Commodifying them for fashion is disrespectful."}},
 {"FABM1-MerchBusActgMCQ-401": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Discernment", "type": "Multiple Choice", "competency": "Evaluate", "question": "Before making a major strategic shift, the leadership team takes a day of silence to reflect on the impact. This is an example of:", "options": ["Wasting time", "Ignatian Discernment", "Indecision", "Lack of strategy"], "answer": 1, "explanation": "Taking time to reflect deeply on the implications of a decision is the core of discernment."}},
 {"FABM1-MerchBusActgMCQ-402": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Magis", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company meets all legal requirements for pollution but chooses to install expensive filters to eliminate it entirely. This reflects:", "options": ["Poor financial sense", "The spirit of Magis (the more/better)", "Legal compliance", "Fear of fines"], "answer": 1, "explanation": "Magis means going beyond the minimum requirement (compliance) to do what is best/greater for the good."}},
 {"FABM1-MerchBusActgMCQ-403": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Short-termism", "type": "Multiple Choice", "competency": "Evaluate", "question": "Cutting maintenance budgets to boost this quarter's earnings, risking future breakdowns. Evaluate this timeline.", "options": ["Smart quarterly management", "Short-termism that compromises long-term stewardship", "Efficiency", "Asset management"], "answer": 1, "explanation": "Stewardship requires looking at the long term. Sacrificing the future for immediate appearance is irresponsible."}},
 {"FABM1-MerchBusActgMCQ-404": {"subject": "FABM1", "topic": "Accounting for Merchandising Business", "subtopic": "Final Purpose", "type": "Multiple Choice", "competency": "Evaluate", "question": "Ultimately, from an Ignatian viewpoint, the profit generated by a merchandising business is:", "options": ["The final goal and end in itself", "A means to support life, serve the community, and glorify God", "A score in a game", "Ownership's private property only"], "answer": 1, "explanation": "In the Ignatian view, all created things (including profit) are means to a higher end: service, love, and the greater glory of God."}},
 {"FABM1-MerchBusActgMCQ-405": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Business Activities", "type": "Multiple Choice", "competency": "Understand", "question": "Which distinct feature primarily differentiates the operating cycle of a merchandising business from that of a service business?", "options": ["The payment of salaries to employees", "The collection of cash from customers", "The purchase and holding of merchandise inventory", "The incurring of administrative expenses"], "answer": 2, "explanation": "The critical difference is the 'Inventory' phase. Service businesses sell time/skills immediately, whereas merchandisers must acquire and hold physical goods before revenue can be generated."}},
{"FABM1-MerchBusActgMCQ-406": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Supply Chain", "type": "Multiple Choice", "competency": "Understand", "question": "In the standard supply chain, a business that buys in large quantities from manufacturers and sells in smaller quantities to retailers is known as a:", "options": ["Broker", "Consumer", "Wholesaler", "Service Provider"], "answer": 2, "explanation": "Wholesalers act as intermediaries who break bulk, buying massive amounts from producers and distributing them to retailers who then sell to the public."}},
{"FABM1-MerchBusActgMCQ-407": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Operating Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "Why is the operating cycle of a merchandising business typically longer than that of a service business?", "options": ["Merchandisers have more complex tax filings", "Merchandisers must first convert cash into inventory, then inventory into receivables, before collecting cash", "Merchandisers offer longer credit terms", "Merchandisers are generally less efficient"], "answer": 1, "explanation": "The cycle has an extra step: Cash -> Inventory -> Receivables -> Cash. Service businesses skip the inventory step (Cash -> Service/Receivables -> Cash)."}},
{"FABM1-MerchBusActgMCQ-408": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Source Documents", "type": "Multiple Choice", "competency": "Understand", "question": "Which source document specifically serves as the 'bill' sent by the seller to the buyer, detailing the description, quantity, and price of goods sold?", "options": ["Official Receipt", "Sales Invoice", "Receiving Report", "Purchase Requisition"], "answer": 1, "explanation": "The Sales Invoice is the demand for payment and the primary evidence of the sale transaction on credit."}},
{"FABM1-MerchBusActgMCQ-409": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Trade Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "A 'Trade Discount' is often listed in catalogs but is not recorded in the accounting books. What is its primary purpose?", "options": ["To account for damaged goods", "To adjust the listed price to the actual selling price for different classes of buyers", "To encourage early payment of credit purchases", "To reduce the tax liability of the seller"], "answer": 1, "explanation": "Trade discounts allow sellers to change prices for bulk buyers or specific customers without reprinting catalogs. They establish the actual transaction price."}},
{"FABM1-MerchBusActgMCQ-410": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "Interpret the credit term '2/10, n/30'. What does the 'n/30' signify?", "options": ["The buyer enters into a new contract after 30 days", "The buyer can return the goods within 30 days", "No interest is charged for the first 30 days", "The net (full) amount of the invoice is due within 30 days if the discount is not taken"], "answer": 3, "explanation": "'n/30' means 'net 30'. If the buyer chooses not to pay early for the discount, the full invoice amount is mandatory by the 30th day."}},
{"FABM1-MerchBusActgMCQ-411": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "Under 'FOB Shipping Point', at what point does the ownership (title) of the goods transfer from the seller to the buyer?", "options": ["When the goods reach the buyer's warehouse", "When the seller places the goods with the common carrier", "When the customer pays for the goods", "When the goods are inspected by the buyer"], "answer": 1, "explanation": "FOB (Free on Board) Shipping Point means the buyer owns the goods as soon as they are shipped. The buyer bears the freight cost and risk of loss during transit."}},
{"FABM1-MerchBusActgMCQ-412": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Shipping Terms", "type": "Multiple Choice", "competency": "Understand", "question": "If goods are sold 'FOB Destination, Freight Prepaid', who is responsible for the freight charges and who owns the goods during transit?", "options": ["Seller pays, Seller owns", "Buyer pays, Buyer owns", "Buyer pays, Seller owns", "Seller pays, Buyer owns"], "answer": 0, "explanation": "FOB Destination means title passes on arrival (Seller owns in transit). Freight Prepaid means the seller paid the carrier. Thus, Seller pays and Seller owns during transit."}},
{"FABM1-MerchBusActgMCQ-413": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "In the operations of a merchandising business, 'Gross Profit' is calculated as:", "options": ["Net Sales - Operating Expenses", "Net Sales - Cost of Goods Sold", "Sales - Sales Returns and Allowances", "Net Income + Operating Expenses"], "answer": 1, "explanation": "Gross Profit represents the markup earned on the inventory itself, before deducting operating expenses (Rent, Salaries, Utilities)."}},
{"FABM1-MerchBusActgMCQ-414": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Understand", "question": "Which inventory system requires a physical count of goods on hand at the end of the period to determine the Cost of Goods Sold?", "options": ["Perpetual Inventory System", "Just-in-Time System", "Periodic Inventory System", "Automated Point of Sale System"], "answer": 2, "explanation": "The Periodic system does not track inventory levels in real-time. It relies on the physical count at the end of the period to figure out how much inventory was sold."}},
{"FABM1-MerchBusActgMCQ-415": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Internal Control", "type": "Multiple Choice", "competency": "Understand", "question": "From an internal control perspective, what is the primary advantage of the Perpetual Inventory System?", "options": ["It allows for the identification of inventory shrinkage by comparing records to physical counts", "It is cheaper to implement", "It requires less data entry", "It eliminates the need for source documents"], "answer": 0, "explanation": "Because the Perpetual system keeps a running 'book' balance, any difference between the book balance and the physical count reveals theft, breakage, or error (shrinkage)."}},
{"FABM1-MerchBusActgMCQ-416": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Commercial Terms", "type": "Multiple Choice", "competency": "Understand", "question": "What is the function of a 'Credit Memorandum' when issued by a seller to a customer?", "options": ["To acknowledge that the customer returned goods and their account balance has been reduced", "To demand immediate payment", "To increase the customer's debt due to interest", "To approve a new credit limit"], "answer": 0, "explanation": "A Credit Memo informs the customer that the seller has 'credited' (reduced) their Accounts Receivable balance, usually due to a return or allowance."}},
{"FABM1-MerchBusActgMCQ-417": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Revenue Recognition", "type": "Multiple Choice", "competency": "Understand", "question": "A customer returns defective merchandise to the seller. How does this transaction affect the seller's 'Net Sales'?", "options": ["It increases Net Sales", "It decreases Net Sales", "It has no effect on Net Sales", "It is recorded as an operating expense"], "answer": 1, "explanation": "Sales Returns are a contra-revenue account. When deducted from Gross Sales, they reduce the Net Sales figure."}},
{"FABM1-MerchBusActgMCQ-418": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Purchase Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Why do suppliers offer 'Cash Discounts' (e.g., 2/10, n/30) to merchandising businesses?", "options": ["To increase the total profit margin per unit", "To avoid shipping costs", "To clear out old inventory", "To accelerate cash flow and reduce the risk of bad debts"], "answer": 3, "explanation": "The primary motive is liquidity. Getting cash sooner (even at a small discount) is valuable for operations and reduces the chance the buyer will default later."}},
{"FABM1-MerchBusActgMCQ-419": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Freight Costs", "type": "Multiple Choice", "competency": "Understand", "question": "In the context of merchandising operations, 'Freight-In' is considered part of the cost of acquiring inventory. Why is 'Freight-Out' treated differently?", "options": ["Freight-Out is paid by the government", "Freight-Out is recoverable from the manufacturer", "Freight-Out is a selling expense because it is a cost to deliver goods to the customer", "Freight-Out is always immaterial in amount"], "answer": 2, "explanation": "Freight-In is a cost to get the goods *ready* for sale (Asset cost). Freight-Out is a cost to *make* the sale (Expense)."}},
{"FABM1-MerchBusActgMCQ-420": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Valuation", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following best describes 'Merchandise Inventory' on the Statement of Financial Position?", "options": ["A Liability until it is sold", "A Non-Current Asset held for investment", "An Intangible Asset", "A Current Asset held for sale in the ordinary course of business"], "answer": 3, "explanation": "Merchandise Inventory is the defining Current Asset of a merchandiser—goods held specifically for resale within the normal operating cycle."}},
{"FABM1-MerchBusActgMCQ-421": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Business Forms", "type": "Multiple Choice", "competency": "Understand", "question": "A department store is an example of a merchandising business. Which of the following is NOT a merchandising business?", "options": ["A grocery store selling food", "A hardware store selling tools", "A pharmacy selling medicine", "A consultancy firm providing legal advice"], "answer": 3, "explanation": "A consultancy firm sells advice (service), not physical goods. The others buy and resell tangible items."}},
{"FABM1-MerchBusActgMCQ-422": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Cost of Goods Sold", "type": "Multiple Choice", "competency": "Understand", "question": "Conceptually, what does 'Cost of Goods Sold' represent?", "options": ["The total selling price of all goods sold", "The cost of goods remaining in the warehouse", "The administrative costs of selling goods", "The cost incurred to purchase the goods that were actually sold during the period"], "answer": 3, "explanation": "COGS is an expense representing the original acquisition cost of the inventory that has left the business to generate revenue."}},
{"FABM1-MerchBusActgMCQ-423": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Receiving Procedures", "type": "Multiple Choice", "competency": "Understand", "question": "What is the purpose of a 'Receiving Report' in the operations of a merchandising business?", "options": ["To order goods from a supplier", "To bill the customer", "To document the quantity and condition of goods when they arrive from a supplier", "To record the payment of cash"], "answer": 2, "explanation": "The Receiving Report is an internal document used to verify that the goods ordered (Purchase Order) match what was actually delivered, before payment is authorized."}},
{"FABM1-MerchBusActgMCQ-424": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Profitability", "type": "Multiple Choice", "competency": "Understand", "question": "If a merchandising business marks up its goods by 50% on cost, but expenses rise significantly, it is possible to have:", "options": ["A low Gross Profit and high Net Income", "Negative Sales", "A high Gross Profit but a low or negative Net Income", "Zero Cost of Goods Sold"], "answer": 2, "explanation": "Gross Profit depends on the markup (Sales - COGS). Net Income depends on operating expenses. You can have a healthy markup but still lose money if rent/salaries are too high."}},
{"FABM1-MerchBusActgMCQ-425": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "A 'Purchase Allowance' differs from a 'Purchase Return' in that:", "options": ["In an allowance, the goods are destroyed", "In an allowance, the buyer pays more", "In an allowance, the goods are kept by the buyer but a price reduction is granted", "In an allowance, the seller refuses to acknowledge the defect"], "answer": 2, "explanation": "A return involves physical movement back to the seller. An allowance is a financial adjustment where the buyer agrees to keep slightly defective goods for a lower price."}},
{"FABM1-MerchBusActgMCQ-426": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Sales", "type": "Multiple Choice", "competency": "Understand", "question": "What is 'Net Sales'?", "options": ["Gross Sales minus Cost of Goods Sold", "Gross Sales minus Sales Returns, Allowances, and Discounts", "Gross Sales plus Freight-Out", "Cash Sales minus Credit Sales"], "answer": 1, "explanation": "Net Sales is the actual revenue earned. It is calculated by taking total (Gross) Sales and subtracting the contra-revenue accounts (returns, allowances, discounts). "}},
{"FABM1-MerchBusActgMCQ-427": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "FOB Terms", "type": "Multiple Choice", "competency": "Understand", "question": "Why is the distinction between FOB Shipping Point and FOB Destination important for end-of-year inventory counts?", "options": ["It determines whether goods in transit should be included in the buyer's or seller's inventory count", "It determines who pays the driver", "It determines the currency of payment", "It determines the quality of the goods"], "answer": 0, "explanation": "Goods in transit must be counted by the legal owner. If FOB Shipping Point, the buyer counts them (even if not yet received). If Destination, the seller counts them."}},
{"FABM1-MerchBusActgMCQ-428": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Operating Expenses", "type": "Multiple Choice", "competency": "Understand", "question": "In a merchandising income statement, 'Selling Expenses' typically include:", "options": ["Cost of goods sold", "Advertising, sales commissions, and store supplies", "Office salaries and depreciation of office equipment", "Interest expense and taxes"], "answer": 1, "explanation": "Selling expenses are those directly tied to the effort of making sales and distributing the product. Office/Admin expenses are for general support."}},
{"FABM1-MerchBusActgMCQ-429": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Flow", "type": "Multiple Choice", "competency": "Understand", "question": "In the flow of costs for a merchandising business, 'Goods Available for Sale' is composed of:", "options": ["Sales - Cost of Goods Sold", "Net Purchases - Purchase Returns", "Beginning Inventory + Net Cost of Purchases", "Ending Inventory + Cost of Goods Sold"], "answer": 2, "explanation": "The 'pool' of goods a company *can* sell is what they started with (Beginning Inventory) plus what they bought during the period (Net Purchases)."}},
{"FABM1-MerchBusActgMCQ-430": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Purchases - Trade Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "A retailer purchases goods with a list price of ₱50,000, receiving a trade discount of 20% and credit terms of 2/10, n/30. Analyze the initial journal entry to record this purchase.", "options": ["Debit Purchases ₱50,000; Credit Accounts Payable ₱40,000; Credit Purchase Discounts ₱10,000", "Debit Purchases ₱40,000; Credit Accounts Payable ₱40,000", "Debit Purchases ₱50,000; Credit Accounts Payable ₱50,000", "Debit Purchases ₱39,200; Credit Accounts Payable ₱39,200"], "answer": 1, "explanation": "Trade discounts are deducted immediately to determine the invoice price and are never recorded in the accounts. The entry uses the net amount (₱50,000 - 20% = ₱40,000)."}},
{"FABM1-MerchBusActgMCQ-431": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Shipping Point", "type": "Multiple Choice", "competency": "Analyze", "question": "Seller S ships goods to Buyer B, terms FOB Shipping Point, Freight Prepaid. S pays the ₱500 shipping. How should Buyer B analyze and record this transaction?", "options": ["No entry is required for the freight by the buyer", "Debit Freight-In ₱500; Credit Cash ₱500", "Debit Delivery Expense ₱500; Credit Accounts Payable ₱500", "Debit Freight-In (or Inventory) ₱500; Credit Accounts Payable ₱500"], "answer": 3, "explanation": "FOB Shipping Point means the buyer bears the cost. Since the seller prepaid it as a convenience, the buyer owes the seller for the freight, increasing Accounts Payable."}},
{"FABM1-MerchBusActgMCQ-432": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Returns (Perpetual)", "type": "Multiple Choice", "competency": "Analyze", "question": "Under the Perpetual Inventory System, analyze the entries required when a customer returns non-defective goods that can be resold.", "options": ["One entry to debit Inventory and credit Cost of Goods Sold", "Two entries: One to debit Sales Returns and one to debit Sales Discounts", "Two entries: One to update the Revenue/Receivable and one to reinstate the Inventory/COGS", "One entry to debit Sales Returns and credit Accounts Receivable"], "answer": 2, "explanation": "The perpetual system tracks inventory real-time. A return requires reversing the sales revenue (Dr Sales Returns, Cr AR) AND putting the asset back on the books (Dr Inventory, Cr COGS)."}},
{"FABM1-MerchBusActgMCQ-433": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Payment with Returns", "type": "Multiple Choice", "competency": "Analyze", "question": "Company X bought ₱10,000 of goods (terms 2/10, n/30). They returned ₱2,000 worth of goods on Day 3. If they pay the balance on Day 9, analyze the amount of cash required.", "options": ["₱8,000", "₱7,800", "₱7,840", "₱9,800"], "answer": 2, "explanation": "The discount applies only to the outstanding balance. Adjusted balance = ₱10,000 - ₱2,000 = ₱8,000. Discount = 2% of ₱8,000 = ₱160. Cash needed = ₱8,000 - ₱160 = ₱7,840."}},
{"FABM1-MerchBusActgMCQ-434": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Net Price Method", "type": "Multiple Choice", "competency": "Analyze", "question": "A company uses the Net Price Method. They purchase goods for ₱1,000, terms 2/10, n/30. They fail to pay within 10 days and pay on day 30. Analyze the entry on day 30.", "options": ["Debit Accounts Payable ₱1,000; Credit Purchase Discounts ₱20; Credit Cash ₱980", "Debit Accounts Payable ₱980; Debit Interest Expense ₱20; Credit Cash ₱1,000", "Debit Accounts Payable ₱980; Debit Purchase Discounts Lost ₱20; Credit Cash ₱1,000", "Debit Accounts Payable ₱1,000; Credit Cash ₱1,000"], "answer": 2, "explanation": "Under the Net Method, the liability was recorded at ₱980. Paying ₱1,000 later means the ₱20 difference is a financial penalty, recorded as 'Purchase Discounts Lost'."}},
{"FABM1-MerchBusActgMCQ-435": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Inventory Adjustments", "type": "Multiple Choice", "competency": "Analyze", "question": "At year-end, the perpetual inventory records show a balance of ₱50,000, but the physical count shows ₱48,500. Analyze the necessary adjusting entry.", "options": ["Debit Inventory ₱1,500; Credit Cost of Goods Sold ₱1,500", "Debit Miscellaneous Expense ₱1,500; Credit Inventory ₱1,500", "Debit Cost of Goods Sold ₱1,500; Credit Inventory ₱1,500", "Debit Loss on Inventory ₱1,500; Credit Purchases ₱1,500"], "answer": 2, "explanation": "Inventory shrinkage is an expense usually absorbed into Cost of Goods Sold (unless material). The asset account must be credited to match the physical count."}},
{"FABM1-MerchBusActgMCQ-436": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Destination", "type": "Multiple Choice", "competency": "Analyze", "question": "Analyze the effect of 'FOB Destination, Freight Collect' on the Seller's books.", "options": ["Debit Accounts Receivable; Credit Cash", "Debit Freight-Out; Credit Accounts Receivable (or Cash)", "Debit Freight-Out; Credit Accounts Payable (to buyer)", "Debit Freight-Out; Credit Cash"], "answer": 2, "explanation": "FOB Destination means Seller bears the cost. 'Freight Collect' means Buyer paid the carrier upon arrival. The Seller must record the expense (Freight-Out) and owe the Buyer (Credit AP or reduce AR). "}},
{"FABM1-MerchBusActgMCQ-437": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Closing Entries (Periodic)", "type": "Multiple Choice", "competency": "Analyze", "question": "In a Periodic System, analyze the closing entry regarding Beginning Inventory.", "options": ["Debit Inventory (Beginning); Credit Income Summary", "Debit Income Summary; Credit Inventory (Beginning)", "No entry needed; it rolls over", "Debit Cost of Goods Sold; Credit Inventory (Beginning)"], "answer": 1, "explanation": "In a periodic system closing entry, Beginning Inventory is a cost that must be removed (Credited) and transferred to Income Summary (Debited) to calculate COGS."}},
{"FABM1-MerchBusActgMCQ-438": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "VAT/Sales Tax", "type": "Multiple Choice", "competency": "Analyze", "question": "A merchant sells goods for ₱1,120 inclusive of 12% VAT. Analyze the credit entry to Sales Revenue.", "options": ["Credit Sales ₱1,120", "Credit Sales ₱1,000", "Credit Sales ₱1,000; Credit Output Tax ₱120", "Credit Sales ₱1,254.40"], "answer": 2, "explanation": "Revenue is the amount earned exclusive of tax. ₱1,120 / 1.12 = ₱1,000 Sales Revenue. The ₱120 is a liability (Output Tax Payable). Option 2 shows the full credit side."}},
{"FABM1-MerchBusActgMCQ-439": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "A customer pays a ₱5,000 invoice within the 2% discount period. Analyze the impact on the 'Sales Discounts' account and its classification.", "options": ["Credit Sales Discounts ₱100; It is a Liability account", "Debit Sales Discounts ₱100; It is an Expense account", "Credit Sales Discounts ₱100; It is a Revenue account", "Debit Sales Discounts ₱100; It is a Contra-Revenue account"], "answer": 3, "explanation": "Sales Discounts reduces the revenue earned. It is a contra-revenue account, which normally has a debit balance (opposite of Sales). "}},
{"FABM1-MerchBusActgMCQ-440": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Periodic System Purchases", "type": "Multiple Choice", "competency": "Analyze", "question": "Under the Periodic System, a return of defective goods to a supplier is recorded by:", "options": ["Debiting Accounts Payable and crediting Inventory", "Debiting Cash and crediting Purchases", "Debiting Purchase Returns and Allowances and crediting Accounts Payable", "Debiting Accounts Payable and crediting Purchase Returns and Allowances"], "answer": 3, "explanation": "The Periodic system does not touch the 'Inventory' account during the period. Returns are tracked in a specific contra-cost account: 'Purchase Returns and Allowances'."}},
{"FABM1-MerchBusActgMCQ-441": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Credit Card Sales", "type": "Multiple Choice", "competency": "Analyze", "question": "A business accepts credit cards. Sales are ₱1,000, and the bank charges a 3% fee. The cash is deposited immediately. Analyze the journal entry.", "options": ["Debit Cash ₱970; Debit Sales Discounts ₱30; Credit Sales ₱1,000", "Debit Cash ₱1,000; Credit Sales ₱1,000", "Debit Accounts Receivable ₱1,000; Credit Sales ₱1,000", "Debit Cash ₱970; Debit Credit Card Expense ₱30; Credit Sales ₱1,000"], "answer": 3, "explanation": "The bank fee is an operating expense (Credit Card Expense). The business only receives the net cash (₱970) but must recognize the full revenue (₱1,000). "}},
{"FABM1-MerchBusActgMCQ-442": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Gross Profit Analysis", "type": "Multiple Choice", "competency": "Analyze", "question": "If a company erroneously records a 'Freight-In' payment as 'Freight-Out', analyze the effect on Gross Profit.", "options": ["Net Income will be overstated", "Gross Profit will be understated", "Gross Profit will be overstated", "Gross Profit will be unaffected"], "answer": 2, "explanation": "Freight-In reduces Gross Profit (part of COGS). Freight-Out is an operating expense (below Gross Profit). By moving a cost from COGS to Op Expense, Gross Profit is artificially overstated."}},
{"FABM1-MerchBusActgMCQ-443": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Cost of Goods Sold", "type": "Multiple Choice", "competency": "Analyze", "question": "Analyze the calculation of Cost of Goods Sold in a periodic system if: Beg Inv = ₱10, Net Purchases = ₱100, End Inv = ₱20.", "options": ["₱110", "₱80", "₱90", "₱130"], "answer": 2, "explanation": "Goods Available for Sale = ₱10 + ₱100 = ₱110. If ₱20 is left, then ₱90 must have been sold. (₱110 - ₱20 = ₱90)."}},
{"FABM1-MerchBusActgMCQ-444": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Account Normal Balances", "type": "Multiple Choice", "competency": "Analyze", "question": "Which of the following accounts typically has a Credit balance?", "options": ["Sales Returns and Allowances", "Purchase Discounts", "Freight-In", "Merchandise Inventory"], "answer": 1, "explanation": "Purchase Discounts is a contra-cost account (reduces Purchases). Since Purchases is a Debit, Purchase Discounts is a Credit. The others are Debits."}},
{"FABM1-MerchBusActgMCQ-445": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "FOB Shipping Point in Transit", "type": "Multiple Choice", "competency": "Analyze", "question": "At year-end, goods purchased FOB Shipping Point are still in transit. If the buyer fails to record this purchase, analyze the effect on the financial statements.", "options": ["No effect since goods haven't arrived", "Assets understated, Liabilities understated", "Net Income overstated", "Assets overstated, Liabilities overstated"], "answer": 1, "explanation": "FOB Shipping Point means the buyer owns them in transit. Failure to record means Inventory (Asset) is missing and Accounts Payable (Liability) is missing. Both are understated."}},
{"FABM1-MerchBusActgMCQ-446": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Complex Transaction", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold goods for ₱1,000 cash. The cost of the goods was ₱600. The customer immediately returned goods with a sales price of ₱100 and a cost of ₱60. Analyze the net impact on Gross Profit.", "options": ["₱40 decrease", "₱400 increase", "₱300 increase", "₱360 increase"], "answer": 3, "explanation": "Initial GP = ₱1,000 - ₱600 = ₱400. Return impact = reduces Sales by ₱100 and reduces COGS by ₱60 (Net ₱40 reduction in GP). Final GP = ₱400 - ₱40 = ₱360."}},
{"FABM1-MerchBusActgMCQ-447": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Settlement of Accounts", "type": "Multiple Choice", "competency": "Analyze", "question": "A buyer owes ₱10,000 (Gross). Terms 2/10, n/30. They return ₱1,000 of goods before payment. If they pay within the discount period, what is the entry to Cash?", "options": ["Credit Cash ₱8,820", "Credit Cash ₱9,800", "Credit Cash ₱9,000", "Credit Cash ₱8,800"], "answer": 0, "explanation": "Debt is ₱9,000. Discount is 2% of ₱9,000 (₱180). Cash paid = ₱9,000 - ₱180 = ₱8,820."}},
{"FABM1-MerchBusActgMCQ-448": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Allowances", "type": "Multiple Choice", "competency": "Analyze", "question": "A customer claims goods are slightly scratched. The seller issues a Credit Memo for ₱200. The cost of the goods is not affected. Analyze the Seller's entry.", "options": ["Debit Sales Returns and Allowances ₱200; Credit Cash ₱200", "Debit Sales ₱200; Credit Accounts Receivable ₱200", "Debit Sales Returns and Allowances ₱200; Credit Accounts Receivable ₱200", "Debit Accounts Receivable ₱200; Credit Sales ₱200"], "answer": 2, "explanation": "This is an allowance (price reduction), not a return of goods. Debit the contra-revenue account and reduce the receivable. No inventory adjustment is needed."}},
{"FABM1-MerchBusActgMCQ-449": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Perpetual Inventory Shortage", "type": "Multiple Choice", "competency": "Analyze", "question": "Why does the Perpetual System require an adjusting entry for inventory count differences, whereas the Periodic System does not?", "options": ["Perpetual systems maintain a book balance that must be reconciled to reality; Periodic systems derive the balance from the count itself", "Periodic systems are more accurate", "Periodic systems use scanners", "Perpetual systems do not track theft"], "answer": 0, "explanation": "In Periodic, 'Ending Inventory' is a plugged figure from the count, so shrinkage is automatically (and silently) buried in COGS. Perpetual requires an explicit entry to correct the book record."}},
{"FABM1-MerchBusActgMCQ-450": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Operating Cycle Analysis", "type": "Multiple Choice", "competency": "Analyze", "question": "Which transaction creates a 'Receivable' in the merchandising operating cycle?", "options": ["Payment of freight costs", "Collection of cash from customers", "Purchase of inventory on credit", "Sale of inventory on credit"], "answer": 3, "explanation": "The sale of inventory on credit is the step that converts Inventory (Asset) into Accounts Receivable (Asset), to be later converted to Cash."}},
{"FABM1-MerchBusActgMCQ-451": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Advance Payments", "type": "Multiple Choice", "competency": "Analyze", "question": "A retailer pays ₱5,000 in advance for goods to be delivered next month. Analyze the debit side of this entry.", "options": ["Debit Purchases", "Debit Accounts Payable", "Debit Inventory", "Debit Advances to Suppliers (Asset)"], "answer": 3, "explanation": "Since title hasn't passed and goods aren't received, it is not yet Inventory or Purchases. It is a prehistoric asset (receivable/prepayment) often called 'Advances to Suppliers'."}},
{"FABM1-MerchBusActgMCQ-452": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Purchase Returns (Credit)", "type": "Multiple Choice", "competency": "Analyze", "question": "Company A returns goods to Supplier B (originally purchased on credit). Analyze the effect on Company A's accounting equation.", "options": ["Assets Increase, Owner's Equity Increases", "Assets Decrease, Expenses Increase", "Assets Decrease, Liabilities Decrease", "Liabilities Decrease, Owner's Equity Increases"], "answer": 2, "explanation": "Returning goods reduces Inventory (Asset Decrease) and reduces the amount owed to the supplier (Liability Decrease)."}},
{"FABM1-MerchBusActgMCQ-453": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Cost Principle", "type": "Multiple Choice", "competency": "Analyze", "question": "A company purchases inventory for ₱500, paying ₱50 for freight-in. They later sell it for ₱800. Analyze the expense recorded at the time of sale (Perpetual).", "options": ["₱550", "₱800", "₱500", "₱450"], "answer": 0, "explanation": "The cost of the inventory includes the purchase price plus freight-in (₱500 + ₱50 = ₱550). When sold, this total cost moves to COGS."}},
{"FABM1-MerchBusActgMCQ-454": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Discount Period Analysis", "type": "Multiple Choice", "competency": "Analyze", "question": "An invoice dated Dec 2 has terms 2/10, EOM. What is the last date to pay to receive the discount?", "options": ["Dec 31", "Dec 12", "Jan 10", "Jan 1"], "answer": 2, "explanation": "EOM means 'End of Month'. 2/10 EOM usually means the discount period is the first 10 days of the *following* month. The EOM counts as the starting block."}},
{"FABM1-MerchBusAcgMCQ-455": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Worksheet - Inventory Extension", "type": "Multiple Choice", "competency": "Create", "question": "In a periodic inventory system worksheet, you are extending the account 'Merchandise Inventory, Beginning' with a debit balance of ₱50,000. Which column set does this amount specifically go to?", "options": ["Balance Sheet Debit", "Income Statement Debit", "Income Statement Credit", "Balance Sheet Credit"], "answer": 1, "explanation": "Beginning Inventory is a component of Cost of Goods Sold. In the periodic system, it is treated as a cost to be closed out, so it is extended to the Income Statement Debit column."}},
{"FABM1-MerchBusAcgMCQ-456": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Cost of Goods Sold", "type": "Multiple Choice", "competency": "Create", "question": "Calculate Cost of Goods Sold given: Beg. Inventory ₱20,000; Purchases ₱150,000; Purchase Returns ₱5,000; Freight-In ₱2,000; End. Inventory ₱30,000.", "options": ["₱143,000", "₱167,000", "₱137,000", "₱147,000"], "answer": 2, "explanation": "COGS = (Beg Inv + Purchases + Freight In - Purchase Returns) - End Inv. (20k + 150k + 2k - 5k) - 30k = ₱137,000."}},
{"FABM1-MerchBusAcgMCQ-457": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Gross Profit Calculation", "type": "Multiple Choice", "competency": "Create", "question": "A company reports Net Sales of ₱500,000 and a Gross Profit rate of 40%. Determining the Cost of Goods Sold to be reported on the Income Statement.", "options": ["₱350,000", "₱200,000", "₱150,000", "₱300,000"], "answer": 3, "explanation": "If Gross Profit is 40%, then Cost of Goods Sold is 60%. ₱500,000 * 60% = ₱300,000."}},
{"FABM1-MerchBusAcgMCQ-458": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Worksheet - Balancing", "type": "Multiple Choice", "competency": "Create", "question": "In the Income Statement columns of the worksheet, the total Debits are ₱850,000 and total Credits are ₱920,000. Create the entry needed to balance the worksheet columns.", "options": ["Debit Balance Sheet ₱70,000; Credit Income Statement ₱70,000 (Loss)", "Debit Income Statement ₱70,000; Credit Balance Sheet ₱70,000", "Debit Income Statement ₱70,000; Credit Capital ₱70,000", "Credit Income Statement ₱70,000; Debit Balance Sheet ₱70,000"], "answer": 1, "explanation": "Credits (Revenue) exceed Debits (Expenses) by ₱70,000, indicating Net Income. To balance, you Debit the Income Statement column and Credit the Balance Sheet column (increasing Equity)."}},
{"FABM1-MerchBusAcgMCQ-459": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Net Sales Calculation", "type": "Multiple Choice", "competency": "Create", "question": "Calculate Net Sales: Sales ₱800,000; Sales Returns ₱10,000; Sales Discounts ₱5,000; Cost of Goods Sold ₱400,000.", "options": ["₱795,000", "₱785,000", "₱400,000", "₱385,000"], "answer": 1, "explanation": "Net Sales = Gross Sales - Sales Returns - Sales Discounts. ₱800,000 - ₱10,000 - ₱5,000 = ₱785,000. COGS is irrelevant for this specific line item."}},
{"FABM1-MerchBusAcgMCQ-460": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Operating Expenses", "type": "Multiple Choice", "competency": "Create", "question": "From the following accounts, calculate Total Operating Expenses: Sales Salaries ₱50,000; Office Supplies Expense ₱10,000; Freight-Out ₱5,000; Freight-In ₱8,000; Interest Expense ₱2,000.", "options": ["₱65,000", "₱73,000", "₱75,000", "₱67,000"], "answer": 0, "explanation": "Operating Expenses = Sales Salaries (50k) + Office Supplies Exp (10k) + Freight-Out (5k) = ₱65,000. Freight-In is part of COGS. Interest Expense is a Non-Operating item."}},
{"FABM1-MerchBusAcgMCQ-461": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Ending Inventory Entry", "type": "Multiple Choice", "competency": "Create", "question": "In a periodic inventory worksheet, you must enter the Ending Inventory of ₱45,000. Which pair of columns will this amount appear in?", "options": ["Income Statement Credit and Balance Sheet Debit", "Income Statement Debit and Balance Sheet Credit", "Adjusted Trial Balance Credit and Balance Sheet Debit", "Trial Balance Debit and Adjusted Trial Balance Debit"], "answer": 0, "explanation": "Ending Inventory is subtracted from COGS (Credit in Income Statement column) and is an Asset (Debit in Balance Sheet column)."}},
{"FABM1-MerchBusAcgMCQ-462": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Net Income", "type": "Multiple Choice", "competency": "Create", "question": "Given: Gross Profit ₱150,000; Total Operating Expenses ₱90,000; Interest Revenue ₱5,000; Interest Expense ₱2,000. Calculate Net Income.", "options": ["₱65,000", "₱58,000", "₱63,000", "₱60,000"], "answer": 2, "explanation": "Operating Income = 150k - 90k = 60k. Net Income = Operating Income + Other Income (5k) - Other Expenses (2k) = ₱63,000."}},
{"FABM1-MerchBusAcgMCQ-463": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Goods Available for Sale", "type": "Multiple Choice", "competency": "Create", "question": "Calculate Cost of Goods Available for Sale: Purchases ₱200,000; Purchase Returns ₱10,000; Purchase Discounts ₱4,000; Freight-In ₱6,000; Beg. Inv. ₱30,000.", "options": ["₱212,000", "₱222,000", "₱216,000", "₱192,000"], "answer": 1, "explanation": "Net Purchases = 200k - 10k - 4k = 186k. Net Cost of Purchases = 186k + 6k (Freight) = 192k. Goods Available = Beg Inv (30k) + Net Cost of Purchases (192k) = ₱222,000."}},
{"FABM1-MerchBusAcgMCQ-464": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Statement of Changes in Equity", "type": "Multiple Choice", "competency": "Create", "question": "Calculate the Ending Capital: Beginning Capital ₱500,000; Net Loss ₱50,000; Additional Investment ₱20,000; Drawings ₱30,000.", "options": ["₱500,000", "₱540,000", "₱490,000", "₱440,000"], "answer": 3, "explanation": "Ending Capital = Beg Capital + Investments - Net Loss - Drawings. 500k + 20k - 50k - 30k = ₱440,000."}},
{"FABM1-MerchBusAcgMCQ-465": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Current Assets", "type": "Multiple Choice", "competency": "Create", "question": "Prepare the Current Assets section. Which total is correct? Cash ₱50,000; AR ₱40,000; Mdse Inventory ₱100,000; Store Equipment ₱150,000; Prepaid Rent ₱10,000.", "options": ["₱200,000", "₱350,000", "₱190,000", "₱210,000"], "answer": 0, "explanation": "Current Assets = Cash (50k) + AR (40k) + Inventory (100k) + Prepaid Rent (10k) = ₱200,000. Equipment is Non-Current."}},
{"FABM1-MerchBusAcgMCQ-466": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Closing Entries", "type": "Multiple Choice", "competency": "Create", "question": "Create the compound closing entry for the credit-balance temporary accounts. Sales ₱500k; Purchase Returns ₱10k; Purchase Discounts ₱5k; Interest Income ₱2k.", "options": ["Debit Income Summary ₱500k; Credit Sales ₱500k", "Debit Income Summary ₱517,000; Credit All Accounts", "Debit Sales ₱500k; Credit Income Summary ₱500k", "Debit Sales ₱500k, Debit Purch Returns ₱10k, Debit Purch Disc ₱5k, Debit Int Income ₱2k; Credit Income Summary ₱517,000"], "answer": 3, "explanation": "To close credit-balance accounts (Revenue & Contra-Cost), you Debit them and Credit Income Summary for the total."}},
{"FABM1-MerchBusAcgMCQ-467": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Net Purchases", "type": "Multiple Choice", "competency": "Create", "question": "Determine Net Purchases: Purchases ₱120,000; Freight-In ₱5,000; Purchase Returns ₱8,000; Purchase Discounts ₱2,000.", "options": ["₱105,000", "₱115,000", "₱110,000", "₱125,000"], "answer": 2, "explanation": "Net Purchases = Purchases - Returns - Discounts. 120k - 8k - 2k = ₱110,000. Note: Freight-In is added later to get 'Net Cost of Purchases', but 'Net Purchases' strictly refers to the invoice price adjustment."}},
{"FABM1-MerchBusAcgMCQ-468": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Income Statement Format", "type": "Multiple Choice", "competency": "Create", "question": "In a Multi-Step Income Statement, what figure is derived immediately after deducting Operating Expenses from Gross Profit?", "options": ["Earnings Before Tax", "Operating Income / Income from Operations", "Comprehensive Income", "Net Income"], "answer": 1, "explanation": "Gross Profit - Operating Expenses = Operating Income. Non-operating items (Interest, Gains/Losses) are handled after this subtotal."}},
{"FABM1-MerchBusAcgMCQ-469": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Worksheet - Unearned Revenue", "type": "Multiple Choice", "competency": "Create", "question": "The Adjusted Trial Balance shows Unearned Sales Revenue of ₱10,000. In which column of the worksheet should this be extended?", "options": ["Balance Sheet Debit", "Income Statement Credit", "Income Statement Debit", "Balance Sheet Credit"], "answer": 3, "explanation": "Unearned Revenue is a Liability. Liabilities are extended to the Balance Sheet Credit column."}},
{"FABM1-MerchBusAcgMCQ-470": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Closing Income Summary", "type": "Multiple Choice", "competency": "Create", "question": "Income Summary has a Credit balance of ₱50,000 and a Debit balance of ₱30,000 after posting revenues and expenses. Create the entry to close Income Summary to Capital.", "options": ["Debit Capital ₱20,000; Credit Income Summary ₱20,000", "Debit Income Summary ₱50,000; Credit Capital ₱50,000", "Debit Capital ₱30,000; Credit Income Summary ₱30,000", "Debit Income Summary ₱20,000; Credit Capital ₱20,000"], "answer": 3, "explanation": "Net Income is 20k (50k Credit - 30k Debit). To close the 20k Credit balance in Income Summary, you Debit Income Summary and Credit Capital (transferring profit)."}},
{"FABM1-MerchBusAcgMCQ-471": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Net Sales Ratio", "type": "Multiple Choice", "competency": "Create", "question": "If Cost of Goods Sold is ₱600,000 and the Gross Profit Rate is 25%, calculate the Net Sales amount.", "options": ["₱750,000", "₱450,000", "₱800,000", "₱2,400,000"], "answer": 2, "explanation": "If GP rate is 25%, COGS ratio is 75%. Net Sales = COGS / 0.75. ₱600,000 / 0.75 = ₱800,000."}},
{"FABM1-MerchBusAcgMCQ-472": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Purchase Discounts Lost", "type": "Multiple Choice", "competency": "Create", "question": "Using the Net Method, a company records 'Purchase Discounts Lost' of ₱500. Where is this account presented in the Income Statement?", "options": ["Operating Expenses", "It is not reported", "Other Expenses / Finance Costs", "Cost of Goods Sold"], "answer": 2, "explanation": "Purchase Discounts Lost is a finance charge (penalty for not paying early), classified under Other Expenses or Finance Costs, not Operating Expenses."}},
{"FABM1-MerchBusAcgMCQ-473": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Worksheet - Drawings", "type": "Multiple Choice", "competency": "Create", "question": "Where is the Owner's Drawing account extended to in the worksheet?", "options": ["Income Statement Debit", "Income Statement Credit", "Balance Sheet Debit", "Balance Sheet Credit"], "answer": 2, "explanation": "Drawings are a contra-equity account (reduce capital). Since Capital is a Credit, Drawings are a Debit. They appear in the Balance Sheet Debit column."}},
{"FABM1-MerchBusAcgMCQ-474": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Inventory Shortage", "type": "Multiple Choice", "competency": "Create", "question": "Perpetual records show Inventory of ₱40,000. Physical count shows ₱38,000. Calculate the impact on Net Income when this adjustment is recorded.", "options": ["Net Income increases by ₱2,000", "Net Income decreases by ₱38,000", "Net Income decreases by ₱2,000", "No effect on Net Income"], "answer": 2, "explanation": "The ₱2,000 shortage is recorded as an increase in COGS (or loss), which increases expenses and therefore decreases Net Income."}},
{"FABM1-MerchBusAcgMCQ-475": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Operating Income", "type": "Multiple Choice", "competency": "Create", "question": "Calculate Operating Income: Sales ₱100k; COGS ₱60k; Selling Exp ₱15k; Admin Exp ₱10k; Loss on Sale of Asset ₱5k.", "options": ["₱15,000", "₱10,000", "₱25,000", "₱40,000"], "answer": 0, "explanation": "Gross Profit = 40k. Operating Expenses = 15k + 10k = 25k. Operating Income = 40k - 25k = ₱15,000. Loss on asset is Non-Operating."}},
{"FABM1-MerchBusAcgMCQ-476": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Gross Profit with Returns", "type": "Multiple Choice", "competency": "Create", "question": "Company A sold ₱50,000 of goods (Cost ₱30,000). Customer returned ₱5,000 of goods (Cost ₱3,000). Calculate the final Gross Profit on this transaction set.", "options": ["₱20,000", "₱18,000", "₱22,000", "₱17,000"], "answer": 1, "explanation": "Net Sales = 50k - 5k = 45k. Net COGS = 30k - 3k = 27k. Gross Profit = 45k - 27k = ₱18,000."}},
{"FABM1-MerchBusAcgMCQ-477": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Statement of Financial Position", "type": "Multiple Choice", "competency": "Create", "question": "Which of the following accounts would NOT be included in the calculation of Total Liabilities on the Statement of Financial Position?", "options": ["Unearned Rent Revenue", "Accounts Payable", "Accrued Salaries", "Allowance for Bad Debts"], "answer": 3, "explanation": "Allowance for Bad Debts is a Contra-Asset (deducted from Accounts Receivable), not a Liability."}},
{"FABM1-MerchBusAcgMCQ-478": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Freight Allocation", "type": "Multiple Choice", "competency": "Create", "question": "In preparing the Income Statement, where do you report 'Freight-Out' of ₱12,000?", "options": ["Deduct from Sales", "Selling Expenses", "Add to Cost of Goods Sold", "Administrative Expenses"], "answer": 1, "explanation": "Freight-Out is a delivery expense incurred to sell the product, thus it is a Selling Expense."}},
{"FABM1-MerchBusAcgMCQ-479": {"subject": "FABM1", "topic": "Worksheet and Financial Statements of Merchandising Business", "subtopic": "Comprehensive Income", "type": "Multiple Choice", "competency": "Create", "question": "If Net Income is ₱100,000 and the company has a 'Unrealized Gain on Available-for-Sale Securities' of ₱10,000, calculate Comprehensive Income.", "options": ["₱10,000", "₱90,000", "₱100,000", "₱110,000"], "answer": 3, "explanation": "Comprehensive Income = Net Income + Other Comprehensive Income. 100k + 10k = ₱110,000."}},
{"FABM1-MerchBusAcgMCQ-480": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Apply the closing entry for a company with Sales of ₱500,000, Sales Returns of ₱10,000, and Interest Income of ₱5,000.", "options": ["Debit Sales ₱500,000; Credit Income Summary ₱500,000", "Debit Sales ₱490,000; Credit Income Summary ₱490,000", "Debit Sales ₱500,000, Debit Interest Income ₱5,000; Credit Income Summary ₱505,000", "Debit Income Summary ₱505,000; Credit Sales ₱500,000, Credit Interest Income ₱5,000"], "answer": 2, "explanation": "Revenues (and Other Income) have credit balances. To close them, you must Debit the specific revenue accounts and Credit Income Summary for the total."}},
{"FABM1-MerchBusAcgMCQ-481": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Cost Accounts (Periodic)", "type": "Multiple Choice", "competency": "Apply", "question": "Using the Periodic Inventory System, apply the entry to close the Beginning Inventory of ₱40,000.", "options": ["No entry is needed; it remains on the books", "Debit Merchandise Inventory ₱40,000; Credit Income Summary ₱40,000", "Debit Capital ₱40,000; Credit Merchandise Inventory ₱40,000", "Debit Income Summary ₱40,000; Credit Merchandise Inventory ₱40,000"], "answer": 3, "explanation": "In the Periodic system, Beginning Inventory is considered a cost of the period that must be removed. It is credited to remove the balance, and Income Summary is debited."}},
{"FABM1-MerchBusAcgMCQ-482": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Establishing Ending Inventory", "type": "Multiple Choice", "competency": "Apply", "question": "Using the Periodic Inventory System, apply the entry to record the Ending Inventory of ₱65,000 during the closing process.", "options": ["Debit Merchandise Inventory ₱65,000; Credit Capital ₱65,000", "Debit Merchandise Inventory ₱65,000; Credit Income Summary ₱65,000", "Debit Sales ₱65,000; Credit Merchandise Inventory ₱65,000", "Debit Income Summary ₱65,000; Credit Merchandise Inventory ₱65,000"], "answer": 1, "explanation": "Ending Inventory is an asset that must be set up on the books for the next period. You Debit Inventory (Asset) and Credit Income Summary (as a deduction from Cost of Goods Available)."}},
{"FABM1-MerchBusAcgMCQ-483": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Contra-Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Discounts has a debit balance of ₱3,500. Apply the correct closing entry.", "options": ["Debit Income Summary ₱3,500; Credit Sales Discounts ₱3,500", "Debit Sales Discounts ₱3,500; Credit Income Summary ₱3,500", "Debit Sales ₱3,500; Credit Sales Discounts ₱3,500", "Debit Capital ₱3,500; Credit Sales Discounts ₱3,500"], "answer": 0, "explanation": "Contra-revenue accounts like Sales Discounts have Debit balances. To close them, you must Credit the account and Debit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-484": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Net Loss", "type": "Multiple Choice", "competency": "Apply", "question": "After posting revenues and expenses, the Income Summary account has a Debit balance of ₱25,000. Apply the entry to close this balance.", "options": ["Debit Owner's Drawings ₱25,000; Credit Income Summary ₱25,000", "Debit Sales ₱25,000; Credit Income Summary ₱25,000", "Debit Income Summary ₱25,000; Credit Owner's Capital ₱25,000", "Debit Owner's Capital ₱25,000; Credit Income Summary ₱25,000"], "answer": 3, "explanation": "A Debit balance in Income Summary indicates a Net Loss (Expenses > Revenues). To close it, you Credit Income Summary and Debit Capital (reducing equity)."}},
{"FABM1-MerchBusAcgMCQ-485": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Drawings", "type": "Multiple Choice", "competency": "Apply", "question": "The owner withdrew ₱15,000 during the year. Apply the closing entry for the drawing account.", "options": ["Debit Expenses ₱15,000; Credit Owner's Drawing ₱15,000", "Debit Income Summary ₱15,000; Credit Owner's Drawing ₱15,000", "Debit Owner's Drawing ₱15,000; Credit Owner's Capital ₱15,000", "Debit Owner's Capital ₱15,000; Credit Owner's Drawing ₱15,000"], "answer": 3, "explanation": "Drawings are not expenses and do not go to Income Summary. They are closed directly to Capital. Debit Capital (to reduce it) and Credit Drawings (to zero it out)."}},
{"FABM1-MerchBusAcgMCQ-486": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Entries (Accruals)", "type": "Multiple Choice", "competency": "Apply", "question": "On Dec 31, you recorded Accrued Salaries of ₱4,000. Apply the reversing entry on Jan 1.", "options": ["Debit Salaries Payable ₱4,000; Credit Salaries Expense ₱4,000", "No reversing entry is required", "Debit Salaries Expense ₱4,000; Credit Salaries Payable ₱4,000", "Debit Salaries Payable ₱4,000; Credit Cash ₱4,000"], "answer": 0, "explanation": "The adjusting entry was Dr Salary Exp, Cr Salary Payable. The reversing entry is the exact opposite: Dr Salary Payable, Cr Salary Expense. This simplifies the payment entry later."}},
{"FABM1-MerchBusAcgMCQ-487": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 9: Post-Closing Trial Balance", "type": "Multiple Choice", "competency": "Apply", "question": "Which of the following accounts would appear on the Post-Closing Trial Balance?", "options": ["Sales Revenue", "Accumulated Depreciation", "Cost of Goods Sold", "Depreciation Expense"], "answer": 1, "explanation": "Only 'Real' or 'Permanent' accounts (Assets, Liabilities, Equity) appear on the Post-Closing Trial Balance. Nominal accounts (Rev, Exp) are closed."}},
{"FABM1-MerchBusAcgMCQ-488": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Entry (Shrinkage)", "type": "Multiple Choice", "competency": "Apply", "question": "Perpetual records show Inventory of ₱100,000. Physical count is ₱98,000. Apply the adjusting entry.", "options": ["Debit Sales ₱2,000; Credit Inventory ₱2,000", "Debit Cost of Goods Sold ₱2,000; Credit Inventory ₱2,000", "Debit Loss on Shrinkage ₱2,000; Credit Purchases ₱2,000", "Debit Inventory ₱2,000; Credit Cost of Goods Sold ₱2,000"], "answer": 1, "explanation": "To bring the book balance down to the physical count, you Credit Inventory. The debit usually goes to COGS (or a loss account). Dr COGS ₱2,000, Cr Inventory ₱2,000."}},
{"FABM1-MerchBusAcgMCQ-489": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Entries (Deferrals)", "type": "Multiple Choice", "competency": "Apply", "question": "A company uses the 'Expense Method' for supplies. On Dec 31, they adjusted the Unused Supplies to an asset. Apply the reversing entry.", "options": ["Debit Supplies Expense; Credit Cash", "Debit Unused Supplies; Credit Supplies Expense", "No reversing entry allowed", "Debit Supplies Expense; Credit Unused Supplies"], "answer": 3, "explanation": "If the Expense Method was used, the adjustment created an Asset (Dr Asset, Cr Expense). The reversal removes the Asset and puts it back into Expense (Dr Expense, Cr Asset) for the new year."}},
{"FABM1-MerchBusAcgMCQ-490": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchasing Accounts", "type": "Multiple Choice", "competency": "Apply", "question": "Apply the closing entry for Purchases (₱200,000) and Freight-In (₱5,000).", "options": ["Debit Cost of Goods Sold ₱205,000; Credit Cash ₱205,000", "Debit Purchases ₱200,000; Debit Freight-In ₱5,000; Credit Income Summary ₱205,000", "Debit Capital ₱205,000; Credit Purchases ₱205,000", "Debit Income Summary ₱205,000; Credit Purchases ₱200,000; Credit Freight-In ₱5,000"], "answer": 3, "explanation": "Purchases and Freight-In have Debit balances. To close them, you must Credit the accounts and Debit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-491": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchase Returns", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase Returns and Allowances has a Credit balance of ₱8,000. Apply the closing entry.", "options": ["Debit Capital ₱8,000; Credit Purchase Returns ₱8,000", "Debit Purchase Returns ₱8,000; Credit Income Summary ₱8,000", "Debit Purchase Returns ₱8,000; Credit Purchases ₱8,000", "Debit Income Summary ₱8,000; Credit Purchase Returns ₱8,000"], "answer": 1, "explanation": "It is a contra-cost account with a Credit balance. To close it, you Debit the account and Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-492": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Entry (Bad Debts)", "type": "Multiple Choice", "competency": "Apply", "question": "Accounts Receivable is ₱100,000. The company estimates 5% will be uncollectible. The Allowance account has a credit balance of ₱1,000. Apply the adjustment.", "options": ["Debit Allowance for Bad Debts ₱1,000; Credit Bad Debts Expense ₱1,000", "Debit Bad Debts Expense ₱4,000; Credit Allowance for Bad Debts ₱4,000", "Debit Bad Debts Expense ₱5,000; Credit Accounts Receivable ₱5,000", "Debit Bad Debts Expense ₱5,000; Credit Allowance for Bad Debts ₱5,000"], "answer": 1, "explanation": "Required Allowance = 100k * 5% = ₱5,000. Existing Credit = ₱1,000. Adjustment needed = ₱5,000 - ₱1,000 = ₱4,000."}},
{"FABM1-MerchBusAcgMCQ-493": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 9: Trial Balance Verification", "type": "Multiple Choice", "competency": "Apply", "question": "If the Post-Closing Trial Balance credits exceed the debits by ₱500, what is the most likely error?", "options": ["Drawings were not closed", "Net Income was closed twice", "A ₱500 Asset account was omitted", "A ₱250 Expense was recorded as a Credit"], "answer": 2, "explanation": "Assets are Debits. If a Debit account is missing, the Debits will be lower than Credits. If a ₱500 asset is omitted, Credits will exceed Debits by exactly ₱500."}},
{"FABM1-MerchBusAcgMCQ-494": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Compound Closing", "type": "Multiple Choice", "competency": "Apply", "question": "Calculate the final Net Income to be closed to Capital: Sales ₱100k; Purch ₱60k; End Inv ₱20k; Beg Inv ₱10k; Op Exp ₱30k.", "options": ["₱20,000", "₱30,000", "₱40,000", "₱10,000"], "answer": 0, "explanation": "COGS = Beg(10) + Purch(60) - End(20) = 50k. Gross Profit = Sales(100) - COGS(50) = 50k. Net Income = GP(50) - Op Exp(30) = ₱20,000."}},
{"FABM1-MerchBusAcgMCQ-495": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Accrued Income", "type": "Multiple Choice", "competency": "Apply", "question": "You adjusted for Accrued Interest Income of ₱1,200 (Dr Interest Receivable, Cr Interest Income). Apply the reversal.", "options": ["Debit Cash ₱1,200; Credit Interest Receivable ₱1,200", "Debit Interest Income ₱1,200; Credit Cash ₱1,200", "Debit Interest Income ₱1,200; Credit Interest Receivable ₱1,200", "Debit Interest Receivable ₱1,200; Credit Interest Income ₱1,200"], "answer": 2, "explanation": "Reversal swaps the adjustment. Dr Interest Income (Nominal), Cr Interest Receivable (Real). This allows the full receipt next period to be credited to Revenue."}},
{"FABM1-MerchBusAcgMCQ-496": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Entry (Unearned Revenue)", "type": "Multiple Choice", "competency": "Apply", "question": "Received ₱12,000 for a 1-year service contract on Nov 1, credited to 'Unearned Revenue'. Apply the adjustment on Dec 31.", "options": ["Debit Unearned Revenue ₱2,000; Credit Service Revenue ₱2,000", "Debit Unearned Revenue ₱10,000; Credit Service Revenue ₱10,000", "Debit Service Revenue ₱2,000; Credit Unearned Revenue ₱2,000", "Debit Cash ₱2,000; Credit Service Revenue ₱2,000"], "answer": 0, "explanation": "Liability Method used. 2 months earned (Nov/Dec). ₱12,000 * 2/12 = ₱2,000. Debit Liability (decrease), Credit Revenue (increase). "}},
{"FABM1-MerchBusAcgMCQ-497": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Effect on Equity", "type": "Multiple Choice", "competency": "Apply", "question": "Before closing, Capital is ₱50,000. Net Income is ₱20,000. Drawings are ₱5,000. Apply the closing procedures to determine the ending Capital balance.", "options": ["₱70,000", "₱75,000", "₱65,000", "₱45,000"], "answer": 2, "explanation": "Beg Capital ₱50,000 + Net Income ₱20,000 - Drawings ₱5,000 = Ending Capital ₱65,000."}},
{"FABM1-MerchBusAcgMCQ-498": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 9: Retained Earnings (Corp vs Prop)", "type": "Multiple Choice", "competency": "Apply", "question": "If this were a corporation instead of a proprietorship, to which account would the Income Summary be closed?", "options": ["Common Stock", "Retained Earnings", "Dividends", "Share Capital"], "answer": 1, "explanation": "In a corporation, Net Income is closed to Retained Earnings, not to Share Capital."}},
{"FABM1-MerchBusAcgMCQ-499": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Operating Expenses", "type": "Multiple Choice", "competency": "Apply", "question": "Apply the single compound entry to close: Salaries Exp ₱10,000; Rent Exp ₱5,000; Utilities Exp ₱2,000.", "options": ["Debit Capital ₱17,000; Credit Expenses ₱17,000", "Debit Salaries Exp ₱10k, Rent Exp ₱5k, Utilities Exp ₱2k; Credit Income Summary ₱17,000", "Debit Income Summary ₱17,000; Credit Salaries Exp ₱10k, Rent Exp ₱5k, Utilities Exp ₱2k", "Debit Expenses ₱17,000; Credit Cash ₱17,000"], "answer": 2, "explanation": "Expenses have Debit balances. To close, Credit the individual expenses and Debit Income Summary for the total."}},
{"FABM1-MerchBusAcgMCQ-500": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Opening Balances", "type": "Multiple Choice", "competency": "Apply", "question": "When opening the new books for the next year (Step 10), what determines the opening balances of the Asset, Liability, and Capital accounts?", "options": ["They are estimated based on budgets", "They are equal to the balances in the Post-Closing Trial Balance of the previous year", "They are equal to the Income Statement totals", "They start at zero"], "answer": 1, "explanation": "Real accounts carry forward. The ending balance of Year 1 becomes the beginning balance of Year 2."}},
{"FABM1-MerchBusAcgMCQ-501": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Entry (Depreciation)", "type": "Multiple Choice", "competency": "Apply", "question": "Equipment costs ₱120,000, salvage value ₱20,000, useful life 10 years. Apply the annual depreciation adjustment.", "options": ["Debit Depreciation Exp ₱12,000; Credit Accumulated Depreciation ₱12,000", "Debit Depreciation Exp ₱10,000; Credit Equipment ₱10,000", "Debit Equipment ₱10,000; Credit Cash ₱10,000", "Debit Depreciation Exp ₱10,000; Credit Accumulated Depreciation ₱10,000"], "answer": 3, "explanation": "(Cost 120k - Salvage 20k) / 10 years = ₱10,000 per year. Entry increases Expense and increases Accumulated Depreciation."}},
{"FABM1-MerchBusAcgMCQ-502": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Income Summary Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Income Summary has total Debits of ₱150,000 (Expenses) and total Credits of ₱180,000 (Revenues). Apply the final closing step for this account.", "options": ["Debit Capital ₱30,000; Credit Income Summary ₱30,000", "Debit Sales ₱30,000; Credit Capital ₱30,000", "Debit Income Summary ₱30,000; Credit Capital ₱30,000", "Debit Income Summary ₱180,000; Credit Capital ₱180,000"], "answer": 2, "explanation": "Credit side (Rev) is higher by ₱30,000. This is Net Income. To close the credit balance, Debit Income Summary and Credit Capital."}},
{"FABM1-MerchBusAcgMCQ-503": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Sequence", "type": "Multiple Choice", "competency": "Apply", "question": "Which is the correct sequence of closing entries?", "options": ["1. Close Drawings, 2. Close Revenues, 3. Close Expenses, 4. Close Capital", "1. Close Revenues, 2. Close Expenses, 3. Close Income Summary, 4. Close Drawings", "1. Close Expenses, 2. Close Revenues, 3. Close Drawings, 4. Close Income Summary", "1. Close Income Summary, 2. Close Revenues, 3. Close Expenses, 4. Close Drawings"], "answer": 1, "explanation": "Standard sequence: Revenues to IncSum -> Expenses to IncSum -> IncSum (Net Income/Loss) to Capital -> Drawings to Capital."}},
{"FABM1-MerchBusAcgMCQ-504": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Prepaid Expense (Asset Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Prepaid Rent was debited for ₱60,000 (12 months) on Aug 1. It is now Dec 31. Apply the adjusting entry.", "options": ["Debit Rent Expense ₱25,000; Credit Prepaid Rent ₱25,000", "Debit Prepaid Rent ₱25,000; Credit Rent Expense ₱25,000", "Debit Rent Expense ₱35,000; Credit Prepaid Rent ₱35,000", "Debit Rent Expense ₱20,000; Credit Prepaid Rent ₱20,000"], "answer": 0, "explanation": "Asset Method used. 5 months passed (Aug-Dec). ₱60,000 * 5/12 = ₱25,000 used. Debit Expense, Credit Asset."}},
{"FABM1-MerchBusAcgMCQ-505": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Segregation of Duties", "type": "Multiple Choice", "competency": "Evaluate", "question": "A merchandising company assigns the same employee to order merchandise, inspect the delivery, and authorize payment. Evaluate the risk associated with this assignment.", "options": ["No risk as long as the external auditor checks the records annually", "Low risk because one person handles the entire transaction efficiency", "Moderate risk, acceptable if the employee is trusted", "High risk of theft or kickbacks because of the lack of segregation of duties"], "answer": 3, "explanation": "Segregation of duties requires separating authorization, custody, and recording. One person doing all three allows them to create and conceal fraudulent transactions."}},
{"FABM1-MerchBusAcgMCQ-506": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Cash Receipts", "type": "Multiple Choice", "competency": "Evaluate", "question": "A cashier at a retail store has access to the cash register tape and the daily cash receipts. At the end of the day, she prepares the deposit slip and updates the accounting records. Evaluate this procedure.", "options": ["Effective; it saves time and labor costs", "Ineffective; the cashier can steal cash and manipulate the records to hide the theft", "Effective; it ensures the records match the cash on hand", "Ineffective; the deposit slip should be prepared by the bank"], "answer": 1, "explanation": "The cashier has custody of the asset (cash) and access to recording. They could steal cash and alter the records (lapping or underreporting sales) to cover it up."}},
{"FABM1-MerchBusAcgMCQ-507": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Voucher System", "type": "Multiple Choice", "competency": "Evaluate", "question": "A manager approves a payment of ₱50,000 for goods without seeing a Receiving Report, relying only on the Supplier's Invoice. Evaluate the validity of this disbursement.", "options": ["Valid, assuming the supplier is a long-term partner", "Invalid, because there is no proof that the goods were actually received", "Valid, because the invoice is a demand for payment", "Invalid, because the amount is too high for a single signature"], "answer": 1, "explanation": "The 'Three-Way Match' (Purchase Order, Receiving Report, Invoice) is critical. Paying without a Receiving Report risks paying for goods that were never delivered or were defective."}},
{"FABM1-MerchBusAcgMCQ-508": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Bank Reconciliation", "type": "Multiple Choice", "competency": "Evaluate", "question": "The bookkeeper prepares the bank reconciliation and also writes checks. Evaluate the potential for fraud.", "options": ["Low; the bank statement serves as an independent check", "Moderate; acceptable if the owner signs the checks", "High; the bookkeeper can forge a check and manipulate the reconciliation to hide it", "Low; pre-numbered checks prevent this risk"], "answer": 2, "explanation": "If the person who writes checks also reconciles the bank account, they can manipulate the list of outstanding checks to hide fraudulent withdrawals."}},
{"FABM1-MerchBusAcgMCQ-509": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Inventory Controls", "type": "Multiple Choice", "competency": "Evaluate", "question": "A warehouse manager suggests eliminating the annual physical inventory count because the Perpetual Inventory System is computerized and accurate. Evaluate this proposal.", "options": ["Accept; modern systems are error-proof", "Reject; the government requires monthly counts", "Accept; it saves significant labor costs", "Reject; physical counts are necessary to detect shrinkage, theft, and spoilage not captured by the system"], "answer": 3, "explanation": "Computers only record what they are told. They cannot detect theft, breakage, or unrecorded movements. Physical counts are essential to verify the existence of the asset."}},
{"FABM1-MerchBusAcgMCQ-510": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Petty Cash", "type": "Multiple Choice", "competency": "Evaluate", "question": "The petty cash custodian has personal financial difficulties and the fund is rarely replenished or counted by a surprise auditor. Evaluate the control environment.", "options": ["Weak, creating a high risk of misappropriation or 'borrowing' from the fund", "Strong, because the fund is small", "Neutral, personal issues do not affect work", "Strong, as long as receipts are eventually produced"], "answer": 0, "explanation": "Weak monitoring (no surprise counts) combined with motive (financial pressure) creates a high fraud risk triangle scenario."}},
{"FABM1-MerchBusAcgMCQ-511": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Evaluate", "question": "Sales clerks are allowed to process cash refunds for returns without a manager's approval. Evaluate the risk.", "options": ["Low; it improves customer service speed", "Moderate; acceptable for amounts under ₱1,000", "Zero; the cash register tracks all transactions", "High; clerks could process fake refunds and pocket the cash"], "answer": 3, "explanation": "Unsupervised refunds are a common theft method. A clerk can process a refund for a non-existent return and take the cash equivalent."}},
{"FABM1-MerchBusAcgMCQ-512": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Purchasing Ethics", "type": "Multiple Choice", "competency": "Evaluate", "question": "A purchasing agent consistently awards contracts to a supplier who offers them personal vacations, even though the supplier's prices are 10% higher. Evaluate this situation.", "options": ["It is good business relationship building", "It is acceptable if the supplier delivers on time", "It is a conflict of interest and kickback fraud costing the company money", "It is a marketing expense for the supplier"], "answer": 2, "explanation": "Accepting personal gifts in exchange for unfavorable contracts violates purchasing ethics and internal controls, directly harming the company's profitability."}},
{"FABM1-MerchBusAcgMCQ-513": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Receivables Aging", "type": "Multiple Choice", "competency": "Evaluate", "question": "Management ignores the aging of accounts receivable and continues to sell on credit to customers with balances overdue by 120 days. Evaluate the impact on liquidity.", "options": ["Neutral; as long as they eventually pay", "Positive; it builds customer loyalty", "Positive; it increases sales revenue", "Negative; it increases the risk of bad debts and ties up cash flow"], "answer": 3, "explanation": "Ignoring credit limits and aging reports leads to uncollectible accounts. Sales are useless if cash is not collected; it drains working capital."}},
{"FABM1-MerchBusAcgMCQ-514": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Check Signing", "type": "Multiple Choice", "competency": "Evaluate", "question": "To save time, the CEO pre-signs a stack of blank checks for the accountant to use when the CEO is traveling. Evaluate this practice.", "options": ["Catastrophic control failure; it bypasses the authorization safeguard entirely", "Acceptable for emergency situations only", "Efficient; it ensures vendors are paid on time", "Acceptable if the checks are kept in a locked drawer"], "answer": 0, "explanation": "Pre-signing blank checks removes the control of authorization. The holder of the checks can write them to anyone for any amount, virtually guaranteeing theft."}},
{"FABM1-MerchBusAcgMCQ-515": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Information Systems", "type": "Multiple Choice", "competency": "Evaluate", "question": "Employees share a single password to access the accounting system to avoid forgetting it. Evaluate the implication for the audit trail.", "options": ["It simplifies access management", "It reduces IT support costs", "It destroys the audit trail; accountability cannot be assigned to a specific individual", "It has no effect if the employees are honest"], "answer": 2, "explanation": "Unique user IDs are required to track who did what. Shared passwords make it impossible to determine which employee entered a fraudulent or erroneous transaction."}},
{"FABM1-MerchBusAcgMCQ-516": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Cost-Benefit", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company considers installing a security system costing ₱500,000 per year to prevent inventory theft estimated at ₱50,000 per year. Evaluate the feasibility.", "options": ["Implement; it sends a message to employees", "Do not implement; security systems are ineffective", "Implement; theft must be stopped at all costs", "Do not implement; the cost of the control exceeds the benefit"], "answer": 3, "explanation": "Internal controls are subject to cost-benefit analysis. Spending ₱500k to save ₱50k is irrational financial management."}},
{"FABM1-MerchBusAcgMCQ-517": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Collusion", "type": "Multiple Choice", "competency": "Evaluate", "question": "Internal controls are designed to separate duties, but the warehouse manager and the truck driver agree to steal goods and split the proceeds. Evaluate the limitation of internal control here.", "options": ["Management override", "Cost-benefit constraint", "Human error", "Collusion"], "answer": 3, "explanation": "Collusion occurs when two or more people work together to circumvent segregation of duties, which is a rare but inherent limitation of internal control systems."}},
{"FABM1-MerchBusAcgMCQ-518": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Bonding Employees", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company requires employees who handle cash to be bonded (insured). Evaluate the purpose of this policy.", "options": ["To increase the employee's salary", "To recover losses from the insurance company if theft occurs and to discourage theft via background checks", "To prevent the employees from quitting", "To reduce the company's tax liability"], "answer": 1, "explanation": "Fidelity bonds insure the company against theft. The insurance company also performs background checks, which acts as a deterrent."}},
{"FABM1-MerchBusAcgMCQ-519": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Document Renumbering", "type": "Multiple Choice", "competency": "Evaluate", "question": "Sales invoices are not pre-numbered. Evaluate the risk regarding revenue recognition.", "options": ["High risk; customers will be confused", "Low risk; the date is sufficient", "No risk; numbers are just labels", "High risk; completeness cannot be verified, allowing sales to be unrecorded (skimmed)"], "answer": 3, "explanation": "Pre-numbered documents allow the company to account for the sequence. If a number is missing, it is investigated. Without numbers, an employee can destroy an invoice and pocket the cash without detection."}},
{"FABM1-MerchBusAcgMCQ-520": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Rotation of Duties", "type": "Multiple Choice", "competency": "Evaluate", "question": "A company mandates that all accounting employees take a continuous 2-week vacation annually, during which another employee covers their tasks. Evaluate this policy.", "options": ["Good; it acts as a deterrent to fraud because the substitute might discover irregularities", "Bad; it costs money to train substitutes", "Bad; it disrupts workflow", "Good; it improves employee morale only"], "answer": 0, "explanation": "Mandatory vacations/job rotation are fraud deterrents. A fraudster usually needs to be present daily to keep the scheme covering up. A substitute would likely notice the discrepancies."}},
{"FABM1-MerchBusAcgMCQ-521": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Payment Authorization", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Accounts Payable clerk is allowed to add new vendors to the master file and also process payments. Evaluate the risk.", "options": ["Efficient; streamlines vendor onboarding", "Moderate risk; acceptable for small amounts", "Low risk; vendors are vetted by banks", "High risk; the clerk can create a shell company (fake vendor) and pay themselves"], "answer": 3, "explanation": "This allows the creation of 'Ghost Vendors'. Segregation of duties demands that the person maintaining the Master Vendor File is different from the person processing payments."}},
{"FABM1-MerchBusAcgMCQ-522": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Independent Verification", "type": "Multiple Choice", "competency": "Evaluate", "question": "A supervisor reviews the daily cash register tape against the actual cash in the drawer. Evaluate the type of control activity.", "options": ["Human resource control", "Physical control", "Independent internal verification", "Segregation of duties"], "answer": 2, "explanation": "Independent verification involves an employee who is not responsible for the information checking the accuracy of the data recorded by another employee."}},
{"FABM1-MerchBusAcgMCQ-523": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Physical Controls", "type": "Multiple Choice", "competency": "Evaluate", "question": "High-value electronics are stored on open shelves near the exit. Evaluate the physical control environment.", "options": ["Adequate if there are cameras", "Adequate; customers can see the products", "Inadequate; they should be in the back room", "Inadequate; high risk of shoplifting require locked cabinets or sensors"], "answer": 3, "explanation": "Physical controls must match the asset's risk. High-value, portable items require barriers (locked cases) or electronic tags, not just cameras."}},
{"FABM1-MerchBusAcgMCQ-524": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Management Override", "type": "Multiple Choice", "competency": "Evaluate", "question": "The CFO orders the accountant to record revenue for goods not yet shipped to meet quarterly targets. Evaluate this scenario.", "options": ["Acceptable if the goods ship next week", "Standard practice for aggressive growth", "Management override of internal control leading to fraudulent financial reporting", "A minor error in timing"], "answer": 2, "explanation": "Management override is a top-level failure where controls are ignored by authority figures. This is financial statement fraud (Bill and Hold scheme)."}},
{"FABM1-MerchBusAcgMCQ-525": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Credit Memos", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Sales Manager issues credit memos for sales returns and allowances. The accounting department records them based solely on the Manager's email. Evaluate the documentation.", "options": ["Sufficient; emails are legal documents", "Insufficient; the customer must sign the email", "Insufficient; a standard form and evidence of return (Receiving Report) should accompany the request", "Sufficient; the Manager has authority"], "answer": 2, "explanation": "Credit memos reduce Receivables (an asset). To prevent the manager from writing off friends' debts, the accounting dept needs objective evidence (Receiving Report of returned goods) before recording."}},
{"FABM1-MerchBusAcgMCQ-526": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Payroll - Ghost Employees", "type": "Multiple Choice", "competency": "Evaluate", "question": "The payroll supervisor distributes the paychecks to employees. He also authorizes the hours worked. Evaluate the risk.", "options": ["High; the supervisor can authorize pay for a terminated or fake employee and keep the check", "Moderate; acceptable in small teams", "Low; direct deposit eliminates this risk", "Low; the supervisor knows the employees best"], "answer": 0, "explanation": "If the supervisor authorizes hours AND distributes checks, they can keep a 'Ghost Employee' on the payroll and pocket the unclaimed check. Pay distribution should be done by an independent party."}},
{"FABM1-MerchBusAcgMCQ-527": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Check Endorsement", "type": "Multiple Choice", "competency": "Evaluate", "question": "Incoming checks are not stamped 'For Deposit Only' immediately upon opening the mail. Evaluate the risk.", "options": ["Risk that the check could be stolen and endorsed to a third party or cashed", "No risk; banks check IDs", "No risk; checks can only be cashed by the company", "Risk only if the check is for a large amount"], "answer": 0, "explanation": "Restrictive endorsement ('For Deposit Only') prevents a stolen check from being cashed or deposited into a thief's personal account. It should be applied immediately."}},
{"FABM1-MerchBusAcgMCQ-528": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Control Environment", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company owner openly uses company funds for personal luxury cars and gambling debts. Evaluate the effect on the overall internal control system.", "options": ["It improves morale as employees see the owner's success", "It only affects the equity account", "It degrades the Control Environment, signaling to employees that dishonesty is acceptable", "No effect; it's the owner's money"], "answer": 2, "explanation": "'Tone at the Top' is the foundation of internal control. If leadership is unethical, the entire control structure usually collapses as employees mimic the behavior."}},
{"FABM1-MerchBusAcgMCQ-529": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Audit Committee", "type": "Multiple Choice", "competency": "Evaluate", "question": "The internal auditor reports directly to the CFO. Evaluate the reporting structure.", "options": ["Optimal; the CFO understands the numbers", "Flawed; they should report to the CEO", "Optimal; it streamlines communication", "Flawed; the auditor cannot objectively audit the CFO's work; they should report to the Audit Committee"], "answer": 3, "explanation": "To be independent, Internal Audit must report to the Board of Directors (Audit Committee), not to the management (CFO/CEO) they are supposed to be auditing."}},
{"FABM1-MerchBusAcgMCQ-530": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Chart of Accounts", "type": "Multiple Choice", "competency": "Understand", "question": "Which account title is found in the ledger of a merchandising business but typically not in that of a service business?", "options": ["Depreciation Expense", "Merchandise Inventory", "Accounts Receivable", "Salaries Expense"], "answer": 1, "explanation": "Merchandise Inventory is unique to businesses that buy and sell tangible goods. Service businesses do not hold inventory for resale."}},
{"FABM1-MerchBusAcgMCQ-531": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Commercial Terms", "type": "Multiple Choice", "competency": "Understand", "question": "What is the primary difference between a 'List Price' and an 'Invoice Price'?", "options": ["Invoice price is the price after trade discounts are deducted", "List price includes the shipping costs", "List price is always lower than invoice price", "Invoice price includes the cash discount already deducted"], "answer": 0, "explanation": "List price is the catalog price. Trade discounts are deducted from the list price to arrive at the Invoice Price (the actual amount billed)."}},
{"FABM1-MerchBusAcgMCQ-532": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Source Documents", "type": "Multiple Choice", "competency": "Understand", "question": "Which document is issued by a buyer to inform a seller that the buyer's account has been debited (reduced) due to a return of goods?", "options": ["Credit Memorandum", "Sales Invoice", "Debit Memorandum", "Purchase Order"], "answer": 2, "explanation": "A Debit Memorandum is sent by the buyer to the seller to indicate that the seller's Account Payable balance has been debited (reduced) on the buyer's books."}},
{"FABM1-MerchBusAcgMCQ-533": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Understand", "question": "In a Periodic Inventory System, when are the 'Cost of Goods Sold' and 'Ending Inventory' accounts updated?", "options": ["After every sale", "At the end of the accounting period", "Weekly", "At the beginning of the month"], "answer": 1, "explanation": "The Periodic system does not track cost of goods sold during the year. These figures are calculated and updated only at the end of the period via closing entries."}},
{"FABM1-MerchBusAcgMCQ-534": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "FOB Terms", "type": "Multiple Choice", "competency": "Understand", "question": "What does the term 'Freight Collect' signify regarding the payment of shipping costs?", "options": ["The buyer pays the carrier upon delivery", "The seller has already paid the carrier", "The carrier ships the goods for free", "The freight cost is deducted from the invoice"], "answer": 0, "explanation": "'Freight Collect' means the carrier collects the shipping charges from the buyer at the time of delivery."}},
{"FABM1-MerchBusAcgMCQ-535": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Operating Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "Which step in the merchandising operating cycle converts 'Accounts Receivable' into 'Cash'?", "options": ["Sale of merchandise", "Collection", "Purchase of inventory", "Returns"], "answer": 1, "explanation": "The Collection phase is the final step of the cycle where the business collects payment from customers, converting receivables back into cash."}},
{"FABM1-MerchBusAcgMCQ-536": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Revenue Accounts", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following accounts normally has a Debit balance?", "options": ["Sales Revenue", "Sales Returns and Allowances", "Purchase Returns and Allowances", "Accounts Payable"], "answer": 1, "explanation": "Sales Returns and Allowances is a contra-revenue account. Since Sales has a Credit balance, its contra account must have a Debit balance."}},
{"FABM1-MerchBusAcgMCQ-537": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Cost Components", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following costs is NOT included in the Cost of Goods Available for Sale?", "options": ["Freight-Out", "Net Cost of Purchases", "Beginning Inventory", "Freight-In"], "answer": 0, "explanation": "Freight-Out is a selling expense. It is a cost to deliver goods to customers, not a cost to acquire goods available for sale."}},
{"FABM1-MerchBusAcgMCQ-538": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Source Documents", "type": "Multiple Choice", "competency": "Understand", "question": "What is the primary purpose of a 'Purchase Order'?", "options": ["To acknowledge receipt of goods", "To authorize the supplier to ship goods at a specified price and quantity", "To request a catalog from a supplier", "To bill the customer for goods sold"], "answer": 1, "explanation": "A Purchase Order (PO) is a formal document sent by a buyer to a seller, authorizing the purchase and shipment of specific goods."}},
{"FABM1-MerchBusAcgMCQ-539": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Profitability", "type": "Multiple Choice", "competency": "Understand", "question": "Which financial metric measures the percentage of each peso of sales that remains after deducting the Cost of Goods Sold?", "options": ["Return on Assets", "Net Profit Margin", "Gross Profit Rate", "Current Ratio"], "answer": 2, "explanation": "The Gross Profit Rate (Gross Profit / Net Sales) indicates the margin earned on the merchandise itself before operating expenses."}},
{"FABM1-MerchBusAcgMCQ-540": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Classification", "type": "Multiple Choice", "competency": "Understand", "question": "Why is 'Merchandise Inventory' classified as a Current Asset?", "options": ["It is expected to be sold within one year or the operating cycle, whichever is longer", "It is a tangible asset used in operations for more than one year", "It has no physical substance", "It is owed to suppliers"], "answer": 0, "explanation": "Current assets are resources expected to be realized in cash, sold, or consumed within the normal operating cycle or one year."}},
{"FABM1-MerchBusAcgMCQ-541": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "In the credit term '1/15, n/60', what does the '15' represent?", "options": ["The discount percentage", "The number of days in the discount period", "The number of days until the full invoice is due", "The monthly interest rate"], "answer": 1, "explanation": "The '15' indicates the Discount Period. If the buyer pays within 15 days, they receive the discount."}},
{"FABM1-MerchBusAcgMCQ-542": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Periodic System", "type": "Multiple Choice", "competency": "Understand", "question": "What is a major disadvantage of the Periodic Inventory System compared to the Perpetual System?", "options": ["It requires expensive technology", "It is too complex for small businesses", "It does not provide immediate information on inventory quantity or shrinkage", "It cannot calculate Cost of Goods Sold"], "answer": 2, "explanation": "Because inventory is only updated at the end of the period, the Periodic system leaves management 'blind' regarding current stock levels and theft during the period."}},
{"FABM1-MerchBusAcgMCQ-543": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Purchase Accounts", "type": "Multiple Choice", "competency": "Understand", "question": "Which account is a 'Contra-Cost' account that reduces the total Purchases figure?", "options": ["Freight-In", "Purchase Discounts", "Sales Discounts", "Accounts Payable"], "answer": 1, "explanation": "Purchase Discounts have a Credit balance and are deducted from Purchases (Debit) to determine Net Purchases."}},
{"FABM1-MerchBusAcgMCQ-544": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "FOB Concepts", "type": "Multiple Choice", "competency": "Understand", "question": "If goods are shipped 'FOB Destination', when does the seller recognize the revenue from the sale?", "options": ["When the goods are ordered", "When the goods are delivered and accepted by the buyer", "When the goods leave the seller's warehouse", "When the sales invoice is printed"], "answer": 1, "explanation": "Revenue is recognized when the performance obligation is satisfied. Under FOB Destination, the obligation is met when goods reach the buyer."}},
{"FABM1-MerchBusAcgMCQ-545": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Business Activities", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is an example of a 'Retailer'?", "options": ["A factory that turns steel into cars", "A lawyer providing legal defense", "A supermarket selling groceries to families", "A company buying corn to make cereal"], "answer": 2, "explanation": "A retailer buys finished goods and sells them to the final consumer (families). Factories are manufacturers; lawyers are service providers."}},
{"FABM1-MerchBusAcgMCQ-546": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Freight Accounting", "type": "Multiple Choice", "competency": "Understand", "question": "Conceptually, why is 'Freight-In' added to the cost of purchases rather than expensed immediately?", "options": ["It is a cost necessary to bring the inventory to its salable condition and location", "It is a selling activity", "It is required by tax laws only", "It makes the profit look higher"], "answer": 0, "explanation": "The cost principle states that the cost of an asset includes all expenditures necessary to acquire it and get it ready for use/sale. Shipping is essential to acquisition."}},
{"FABM1-MerchBusAcgMCQ-547": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Net Income", "type": "Multiple Choice", "competency": "Understand", "question": "Which equation correctly represents the calculation of Net Income for a merchandiser?", "options": ["Gross Profit - Operating Expenses", "Sales - Cost of Goods Sold", "Beginning Inventory + Purchases - Ending Inventory", "Sales - Operating Expenses"], "answer": 0, "explanation": "Net Income is the bottom line. It is found by taking Gross Profit (which is Sales minus COGS) and subtracting Operating Expenses."}},
{"FABM1-MerchBusAcgMCQ-548": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Shortage", "type": "Multiple Choice", "competency": "Understand", "question": "What is 'Inventory Shrinkage'?", "options": ["The physical deterioration of goods due to age", "The difference between the recorded inventory amount and the actual physical count due to theft or error", "The discount given to customers", "The return of goods to suppliers"], "answer": 1, "explanation": "Shrinkage refers to the loss of inventory that occurs between the time of purchase and the time of sale, usually discovered during a physical count."}},
{"FABM1-MerchBusAcgMCQ-549": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "In a Multi-Step Income Statement, 'Income from Operations' excludes which of the following?", "options": ["Interest Expense", "Rent Expense", "Sales Salaries", "Depreciation Expense"], "answer": 0, "explanation": "Interest Expense is a financing cost, not an operating cost. It is listed under 'Other Revenues and Gains/Expenses and Losses' below Operating Income."}},
{"FABM1-MerchBusAcgMCQ-550": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Perpetual System", "type": "Multiple Choice", "competency": "Understand", "question": "Why does the Perpetual System typically result in better stock management than the Periodic System?", "options": ["It provides real-time data on stock levels, allowing for timely reordering", "It is mandated by law for all businesses", "It assumes no theft ever occurs", "It allows the company to avoid paying freight charges"], "answer": 0, "explanation": "Real-time tracking means the business knows exactly what it has at any moment, preventing stockouts (running out) or overstocking."}},
{"FABM1-MerchBusAcgMCQ-551": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Accounting Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "Which step in the accounting cycle is unique to the closing process of a Periodic Inventory System?", "options": ["Closing the Dividends account", "Removing the Beginning Inventory balance and recording the Ending Inventory balance", "Closing Sales Revenue to Income Summary", "Closing Expenses to Income Summary"], "answer": 1, "explanation": "In a Periodic system, Inventory is not updated during the year. The closing process is the specific moment where Beg. Inventory (Cost) is removed and End. Inventory (Asset) is entered."}},
{"FABM1-MerchBusAcgMCQ-552": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "What is the main difference between a 'Sales Discount' and a 'Sales Return'?", "options": ["A discount is for early payment; a return is for bringing back goods", "A discount is an expense; a return is a revenue", "A discount increases sales; a return decreases sales", "There is no difference"], "answer": 0, "explanation": "Both reduce Net Sales, but the trigger differs. Discounts encourage speed of payment. Returns correct for unwanted/defective goods."}},
{"FABM1-MerchBusAcgMCQ-553": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "What is the 'Credit Period'?", "options": ["The time allowed for the buyer to pay the full invoice amount", "The time allowed to take a discount", "The time it takes to ship the goods", "The time the bank takes to clear a check"], "answer": 0, "explanation": "The Credit Period (e.g., n/30) is the total timeframe given to the buyer to settle their debt before it becomes past due."}},
{"FABM1-MerchBusAcgMCQ-554": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Ledgers", "type": "Multiple Choice", "competency": "Understand", "question": "What is the purpose of the 'Accounts Payable Subsidiary Ledger'?", "options": ["To calculate the total expenses of the company", "To track the specific amount owed to each individual supplier", "To record cash sales", "To replace the General Ledger"], "answer": 1, "explanation": "The General Ledger shows the total debt. The Subsidiary Ledger breaks that total down by vendor so the company knows exactly whom to pay and how much."}},
{"FABM1-MerchBusActgMCQ-555": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "VAT on Sales", "type": "Multiple Choice", "competency": "Analyze", "question": "A merchant sells goods for ₱11,200 cash, inclusive of 12% VAT. Analyze the journal entry to record the revenue.", "options": ["Debit Cash ₱11,200; Credit Sales ₱11,200", "Debit Cash ₱11,200; Credit Sales ₱10,000; Credit Output Tax ₱1,200", "Debit Cash ₱10,000; Credit Sales ₱10,000", "Debit Cash ₱11,200; Credit Sales ₱12,544"], "answer": 1, "explanation": "The cash received includes the tax. Sales Revenue = ₱11,200 / 1.12 = ₱10,000. The difference (₱1,200) is Output Tax Payable."}},
{"FABM1-MerchBusActgMCQ-556": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Shipping Point, Prepaid", "type": "Multiple Choice", "competency": "Analyze", "question": "Buyer B purchases goods from Seller S, terms FOB Shipping Point, Freight Prepaid. S pays the shipping cost of ₱1,000. Analyze the entry on Buyer B's books regarding the freight.", "options": ["Debit Freight-In ₱1,000; Credit Cash ₱1,000", "Debit Freight-In ₱1,000; Credit Accounts Payable ₱1,000", "No entry required", "Debit Delivery Expense ₱1,000; Credit Cash ₱1,000"], "answer": 1, "explanation": "FOB Shipping Point means Buyer pays. Since Seller prepaid it, Buyer owes Seller. Debit Freight-In (Asset/Cost) and Credit Accounts Payable."}},
{"FABM1-MerchBusActgMCQ-557": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Returns", "type": "Multiple Choice", "competency": "Analyze", "question": "A customer returns goods sold on credit for ₱5,000 (Cost ₱3,000). Using the Perpetual System, analyze the seller's entries.", "options": ["Debit Sales Returns ₱5,000; Credit AR ₱5,000", "Debit Sales Returns ₱5,000, Credit AR ₱5,000; AND Debit Inventory ₱3,000, Credit COGS ₱3,000", "Debit Inventory ₱5,000; Credit Sales ₱5,000", "Debit Sales Returns ₱5,000, Credit Cash ₱5,000"], "answer": 1, "explanation": "Two entries are needed: one to reverse the revenue (at selling price) and one to restore the inventory (at cost)."}},
{"FABM1-MerchBusActgMCQ-558": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Purchase Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "Purchased goods for ₱100,000, terms 2/10, n/30. Returned ₱10,000 worth of goods on Day 2. Paid the balance on Day 9. Analyze the cash payment.", "options": ["₱88,000", "₱88,200", "₱90,000", "₱98,000"], "answer": 1, "explanation": "Balance = ₱100k - ₱10k = ₱90k. Discount = 2% of ₱90k = ₱1,800. Cash Paid = ₱90k - ₱1,800 = ₱88,200."}},
{"FABM1-MerchBusActgMCQ-559": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Periodic Inventory Purchases", "type": "Multiple Choice", "competency": "Analyze", "question": "Under the Periodic Inventory System, analyze the entry to record the purchase of merchandise on account for ₱20,000.", "options": ["Debit Merchandise Inventory ₱20,000; Credit Accounts Payable ₱20,000", "Debit Purchases ₱20,000; Credit Accounts Payable ₱20,000", "Debit Cost of Goods Sold ₱20,000; Credit Accounts Payable ₱20,000", "Debit Accounts Payable ₱20,000; Credit Purchases ₱20,000"], "answer": 1, "explanation": "In a Periodic system, goods bought are recorded in the 'Purchases' account (an expense-like account), not directly to the Asset 'Inventory'."}},
{"FABM1-MerchBusActgMCQ-560": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales with Downpayment", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold goods for ₱50,000. Received a 20% downpayment, with the balance on credit. Analyze the debit side of the entry.", "options": ["Debit Cash ₱10,000; Debit Accounts Receivable ₱40,000", "Debit Cash ₱50,000", "Debit Accounts Receivable ₱50,000", "Debit Cash ₱10,000; Debit Sales ₱40,000"], "answer": 0, "explanation": "Downpayment = ₱10k (Cash). Balance = ₱40k (AR). Both are assets increasing, so both are Debited."}},
{"FABM1-MerchBusActgMCQ-561": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Net Price Method (Payment)", "type": "Multiple Choice", "competency": "Analyze", "question": "Using the Net Price Method, a purchase of ₱10,000 (terms 2/10, n/30) was recorded at ₱9,800. If paid after the discount period, analyze the entry.", "options": ["Debit AP ₱10,000; Credit Cash ₱10,000", "Debit AP ₱9,800; Debit Purchase Discounts Lost ₱200; Credit Cash ₱10,000", "Debit AP ₱9,800; Debit Interest Expense ₱200; Credit Cash ₱10,000", "Debit AP ₱10,000; Credit Purchase Discounts ₱200; Credit Cash ₱9,800"], "answer": 1, "explanation": "The liability is on the books at ₱9,800. Cash out is ₱10,000. The difference is the penalty for missing the discount, recorded as 'Purchase Discounts Lost'."}},
{"FABM1-MerchBusActgMCQ-562": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "VAT Input Tax", "type": "Multiple Choice", "competency": "Analyze", "question": "Purchased inventory for ₱22,400 inclusive of 12% VAT. Analyze the debit entry.", "options": ["Debit Purchases ₱22,400", "Debit Purchases ₱20,000; Debit Input Tax ₱2,400", "Debit Purchases ₱20,000; Debit Output Tax ₱2,400", "Debit Purchases ₱22,400; Credit Cash ₱22,400"], "answer": 1, "explanation": "Purchases are recorded net of tax (₱22,400 / 1.12 = ₱20,000). The tax paid is a receivable from the government called 'Input Tax' (₱2,400)."}},
{"FABM1-MerchBusActgMCQ-563": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Credit Memo Effect", "type": "Multiple Choice", "competency": "Analyze", "question": "A buyer receives a Credit Memo for ₱1,500 due to damaged goods. Analyze the effect on the Buyer's accounting equation.", "options": ["Assets Increase, Liabilities Increase", "Assets Decrease, Liabilities Decrease", "Assets Decrease, Expenses Increase", "Liabilities Decrease, Owner's Equity Increases"], "answer": 3, "explanation": "For the buyer, a Credit Memo means a reduction in debt. Dr Accounts Payable (Liab Decrease) and Cr Purchase Returns (Cost Decrease/Equity Increase)."}},
{"FABM1-MerchBusActgMCQ-564": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Destination", "type": "Multiple Choice", "competency": "Analyze", "question": "Seller S ships goods FOB Destination. The freight bill is ₱500. Analyze the Seller's entry.", "options": ["Debit Freight-Out ₱500; Credit Cash ₱500", "Debit Freight-In ₱500; Credit Cash ₱500", "Debit Accounts Receivable ₱500; Credit Cash ₱500", "No entry required"], "answer": 0, "explanation": "FOB Destination means the seller bears the cost. It is an operating expense called Freight-Out (or Delivery Expense)."}},
{"FABM1-MerchBusActgMCQ-565": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Cash Short/Over", "type": "Multiple Choice", "competency": "Analyze", "question": "The cash register tape shows sales of ₱5,000, but the cash drawer contains only ₱4,950. Analyze the entry to record sales.", "options": ["Debit Cash ₱5,000; Credit Sales ₱5,000", "Debit Cash ₱4,950; Debit Cash Short and Over ₱50; Credit Sales ₱5,000", "Debit Cash ₱4,950; Credit Sales ₱4,950", "Debit Cash ₱4,950; Debit Loss on Sales ₱50; Credit Sales ₱5,000"], "answer": 1, "explanation": "You can only debit the cash you actually have (₱4,950). You must credit the full sales revenue (₱5,000). The difference is debited to 'Cash Short and Over'."}},
{"FABM1-MerchBusActgMCQ-566": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Compound Discount", "type": "Multiple Choice", "competency": "Analyze", "question": "List price ₱20,000, Trade Discount 10%, Terms 2/10, n/30. Analyze the amount to be debited to Accounts Payable if paid within 10 days.", "options": ["₱20,000", "₱17,640", "₱18,000", "₱19,600"], "answer": 2, "explanation": "The AP is recorded at the invoice price (List - Trade Discount). ₱20,000 - 10% = ₱18,000. When paying, you debit AP for the full ₱18,000 to clear the debt."}},
{"FABM1-MerchBusActgMCQ-567": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Perpetual Inventory Sale", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold goods for ₱8,000 (Cost ₱6,000). Analyze the impact on Assets under the Perpetual System.", "options": ["Assets increase by ₱8,000", "Assets increase by ₱2,000", "Assets decrease by ₱6,000", "Assets increase by ₱14,000"], "answer": 1, "explanation": "Assets increase by Cash/AR (+₱8,000) and decrease by Inventory (-₱6,000). Net impact is +₱2,000 (which equals the Gross Profit)."}},
{"FABM1-MerchBusActgMCQ-568": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Shipping Point, Collect", "type": "Multiple Choice", "competency": "Analyze", "question": "Goods shipped FOB Shipping Point, Freight Collect. Freight is ₱400. Analyze Buyer's entry.", "options": ["Debit Freight-In ₱400; Credit Accounts Payable ₱400", "Debit Freight-In ₱400; Credit Cash ₱400", "Debit Accounts Payable ₱400; Credit Cash ₱400", "No entry required"], "answer": 1, "explanation": "Buyer bears the cost (FOB SP) and Buyer pays the carrier directly (Collect). Debit Freight-In, Credit Cash."}},
{"FABM1-MerchBusActgMCQ-569": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Promissory Note", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold merchandise for ₱50,000, receiving a 30-day, 6% promissory note. Analyze the debit entry.", "options": ["Debit Accounts Receivable ₱50,000", "Debit Notes Receivable ₱50,000", "Debit Cash ₱50,000", "Debit Notes Payable ₱50,000"], "answer": 1, "explanation": "A formal written promise to pay converts the asset from Accounts Receivable to Notes Receivable."}},
{"FABM1-MerchBusActgMCQ-570": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Credit Card Fee", "type": "Multiple Choice", "competency": "Analyze", "question": "Credit card sales of ₱10,000 are deposited with a 4% bank fee deducted immediately. Analyze the Revenue recorded.", "options": ["₱9,600", "₱10,000", "₱10,400", "₱400"], "answer": 1, "explanation": "Revenue is always recorded at the gross amount (₱10,000). The fee is recorded as an expense, but does not reduce the 'Sales' account credit."}},
{"FABM1-MerchBusActgMCQ-571": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Collection with Discount", "type": "Multiple Choice", "competency": "Analyze", "question": "Collected ₱4,900 from a customer in full settlement of a ₱5,000 account within the discount period. Analyze the entry to record the discount.", "options": ["Debit Sales Discounts ₱100", "Credit Sales Discounts ₱100", "Debit Purchase Discounts ₱100", "Credit Purchase Discounts ₱100"], "answer": 0, "explanation": "Seller gives the discount. It reduces revenue, so it is a Debit to 'Sales Discounts' (Contra-Revenue)."}},
{"FABM1-MerchBusActgMCQ-572": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Asset Classification", "type": "Multiple Choice", "competency": "Analyze", "question": "A computer store buys a computer for use in its accounting office. Analyze the correct debit account.", "options": ["Merchandise Inventory", "Purchases", "Office Equipment", "Supplies Expense"], "answer": 2, "explanation": "Since the computer is for internal use, not resale, it is a Non-Current Asset (Office Equipment), not Inventory."}},
{"FABM1-MerchBusActgMCQ-573": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Partial Payment", "type": "Multiple Choice", "competency": "Analyze", "question": "Customer owes ₱10,000. Terms 2/10, n/30. They pay ₱5,000 cash within the discount period (partial payment). Analyze the reduction in Accounts Receivable.", "options": ["₱5,000", "₱5,102", "₱4,900", "₱5,100"], "answer": 1, "explanation": "The cash paid represents 98% of the credit given. Credit to AR = ₱5,000 / 0.98 = ₱5,102. (₱5,102 debt is wiped out by paying ₱5,000)."}},
{"FABM1-MerchBusActgMCQ-574": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Purchase Returns (Net Method)", "type": "Multiple Choice", "competency": "Analyze", "question": "Using the Net Method, goods purchased for ₱9,800 (Gross ₱10,000) are returned. Analyze the credit to Purchase Returns.", "options": ["₱10,000", "₱9,800", "₱200", "₱10,200"], "answer": 1, "explanation": "Under the Net Method, purchases and returns are recorded at the net (discounted) price. If the whole lot is returned, credit the amount originally debited (₱9,800)."}},
{"FABM1-MerchBusActgMCQ-575": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Settlement with Freight", "type": "Multiple Choice", "competency": "Analyze", "question": "Buyer owes ₱10,000 for goods. Terms 2/10, n/30. Seller prepaid ₱500 freight (FOB Shipping Point) and added it to the invoice. Total Invoice ₱10,500. Analyze cash needed to pay in full within discount period.", "options": ["₱10,500", "₱10,290", "₱10,300", "₱10,400"], "answer": 2, "explanation": "Discount applies to goods only (₱10,000), not freight. Discount = ₱200. Pay Goods (₱9,800) + Freight (₱500) = ₱10,300."}},
{"FABM1-MerchBusActgMCQ-576": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Cost of Goods Sold Calculation", "type": "Multiple Choice", "competency": "Analyze", "question": "Beginning Inventory ₱50k, Purchases ₱200k, Freight-In ₱10k, Returns ₱20k, Ending Inventory ₱60k. Analyze COGS.", "options": ["₱180,000", "₱190,000", "₱240,000", "₱170,000"], "answer": 0, "explanation": "COGS = (50 + 200 + 10 - 20) - 60 = 240 - 60 = ₱180,000."}},
{"FABM1-MerchBusActgMCQ-577": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Error Analysis", "type": "Multiple Choice", "competency": "Analyze", "question": "A purchase of ₱5,000 on credit was erroneously recorded as a debit to Supplies and credit to Cash. Analyze the correcting entry.", "options": ["Debit Purchases ₱5,000; Credit Supplies ₱5,000", "Debit Purchases ₱5,000, Debit Cash ₱5,000; Credit Supplies ₱5,000, Credit Accounts Payable ₱5,000", "Debit Accounts Payable ₱5,000; Credit Cash ₱5,000", "Debit Purchases ₱5,000; Credit Cash ₱5,000"], "answer": 1, "explanation": "Reverse the wrong entry (Dr Cash, Cr Supplies) and record the right one (Dr Purchases, Cr AP). Combined: Dr Purch, Dr Cash / Cr Supplies, Cr AP."}},
{"FABM1-MerchBusActgMCQ-578": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Tax Remittance", "type": "Multiple Choice", "competency": "Analyze", "question": "A company has Output Tax ₱50,000 and Input Tax ₱30,000. Analyze the entry to record the VAT payable remittance to the BIR.", "options": ["Debit Output Tax ₱50,000; Credit Input Tax ₱30,000; Credit Cash ₱20,000", "Debit VAT Payable ₱20,000; Credit Cash ₱20,000", "Debit Output Tax ₱50,000; Credit Cash ₱50,000", "Debit Cash ₱20,000; Credit VAT Payable ₱20,000"], "answer": 1, "explanation": "The net liability is ₱20k. The standard payment entry debits the Liability (VAT Payable) and Credits Cash."}},
{"FABM1-MerchBusActgMCQ-579": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Perpetual Inventory (Freight)", "type": "Multiple Choice", "competency": "Analyze", "question": "Under Perpetual Inventory, buyer pays ₱500 freight on FOB Shipping Point purchase. Analyze the Debit account.", "options": ["Freight-In", "Merchandise Inventory", "Delivery Expense", "Purchases"], "answer": 1, "explanation": "In a Perpetual system, all costs to acquire inventory (including freight-in) are debited directly to the 'Merchandise Inventory' asset account, not a separate freight account."}},  
{"FABM1-MerchBusAcgMCQ-580": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Contra-Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Returns and Allowances has a debit balance of ₱12,000. Apply the closing entry.", "options": ["Debit Sales Returns ₱12,000; Credit Income Summary ₱12,000", "Debit Sales ₱12,000; Credit Sales Returns ₱12,000", "Debit Income Summary ₱12,000; Credit Sales Returns ₱12,000", "Debit Capital ₱12,000; Credit Sales Returns ₱12,000"], "answer": 2, "explanation": "Contra-revenue accounts have Debit balances. To close them to zero, you must Credit the account and Debit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-581": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Net Loss", "type": "Multiple Choice", "competency": "Apply", "question": "Income Summary shows a Net Loss of ₱15,000. Apply the entry to close this to the owner's capital account.", "options": ["Debit Income Summary ₱15,000; Credit Capital ₱15,000", "Debit Capital ₱15,000; Credit Income Summary ₱15,000", "Debit Sales ₱15,000; Credit Income Summary ₱15,000", "Debit Drawings ₱15,000; Credit Income Summary ₱15,000"], "answer": 1, "explanation": "A Net Loss means Income Summary has a Debit balance. To close it, Credit Income Summary and Debit Capital (reducing equity)."}},
{"FABM1-MerchBusAcgMCQ-582": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Periodic Inventory (Beg)", "type": "Multiple Choice", "competency": "Apply", "question": "Under the Periodic System, apply the entry to close Beginning Inventory of ₱25,000.", "options": ["Debit Merchandise Inventory ₱25,000; Credit Income Summary ₱25,000", "Debit Income Summary ₱25,000; Credit Merchandise Inventory ₱25,000", "Debit Capital ₱25,000; Credit Merchandise Inventory ₱25,000", "No entry required"], "answer": 1, "explanation": "Beginning Inventory is a cost that must be removed. Credit the Asset account to zero it out, and Debit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-583": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Periodic Inventory (End)", "type": "Multiple Choice", "competency": "Apply", "question": "Under the Periodic System, apply the entry to record Ending Inventory of ₱35,000.", "options": ["Debit Merchandise Inventory ₱35,000; Credit Income Summary ₱35,000", "Debit Income Summary ₱35,000; Credit Merchandise Inventory ₱35,000", "Debit Cost of Goods Sold ₱35,000; Credit Merchandise Inventory ₱35,000", "Debit Merchandise Inventory ₱35,000; Credit Capital ₱35,000"], "answer": 0, "explanation": "Ending Inventory is the new asset value. Debit Merchandise Inventory to set it up, and Credit Income Summary (reducing the cost of goods available)."}},
{"FABM1-MerchBusAcgMCQ-584": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Accruals", "type": "Multiple Choice", "competency": "Apply", "question": "You adjusted for Accrued Utilities of ₱2,500 (Dr Utilities Exp, Cr Utilities Payable). Apply the reversing entry.", "options": ["Debit Utilities Payable ₱2,500; Credit Cash ₱2,500", "Debit Utilities Payable ₱2,500; Credit Utilities Expense ₱2,500", "Debit Utilities Expense ₱2,500; Credit Utilities Payable ₱2,500", "No reversing entry"], "answer": 1, "explanation": "The reversing entry is the exact opposite of the adjusting entry for accruals. Dr Payable, Cr Expense."}},
{"FABM1-MerchBusAcgMCQ-585": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Expenses", "type": "Multiple Choice", "competency": "Apply", "question": "Apply the entry to close Freight-Out (₱1,000) and Rent Expense (₱5,000).", "options": ["Debit Freight-Out ₱1,000, Debit Rent Exp ₱5,000; Credit Income Summary ₱6,000", "Debit Income Summary ₱6,000; Credit Freight-Out ₱1,000, Credit Rent Exp ₱5,000", "Debit Capital ₱6,000; Credit Expenses ₱6,000", "Debit Sales ₱6,000; Credit Expenses ₱6,000"], "answer": 1, "explanation": "Expenses have Debit balances. To close them, Credit the specific expense accounts and Debit Income Summary for the total."}},
{"FABM1-MerchBusAcgMCQ-586": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Revenue has a balance of ₱250,000. Apply the closing entry.", "options": ["Debit Sales ₱250,000; Credit Income Summary ₱250,000", "Debit Income Summary ₱250,000; Credit Sales ₱250,000", "Debit Sales ₱250,000; Credit Capital ₱250,000", "Debit Cash ₱250,000; Credit Sales ₱250,000"], "answer": 0, "explanation": "Revenue has a Credit balance. To close it, Debit Sales and Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-587": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Perpetual Shrinkage Adjustment", "type": "Multiple Choice", "competency": "Apply", "question": "Perpetual records show Inventory ₱80,000. Count shows ₱79,200. Apply the adjustment.", "options": ["Debit Inventory ₱800; Credit COGS ₱800", "Debit Sales ₱800; Credit Inventory ₱800", "Debit Cost of Goods Sold ₱800; Credit Inventory ₱800", "Debit Loss ₱800; Credit Purchases ₱800"], "answer": 2, "explanation": "Inventory is overstated by ₱800. Credit Inventory to reduce it. Debit COGS (standard practice for immaterial amounts)."}},
{"FABM1-MerchBusAcgMCQ-588": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Drawings", "type": "Multiple Choice", "competency": "Apply", "question": "Owner's Drawings account has a debit balance of ₱8,000. Apply the closing entry.", "options": ["Debit Income Summary ₱8,000; Credit Drawings ₱8,000", "Debit Capital ₱8,000; Credit Drawings ₱8,000", "Debit Drawings ₱8,000; Credit Capital ₱8,000", "Debit Drawings ₱8,000; Credit Cash ₱8,000"], "answer": 1, "explanation": "Drawings are closed directly to Capital. Debit Capital (to reduce equity) and Credit Drawings."}},
{"FABM1-MerchBusAcgMCQ-589": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 9: Post-Closing Trial Balance", "type": "Multiple Choice", "competency": "Apply", "question": "Which account should NOT appear on the Post-Closing Trial Balance?", "options": ["Accounts Payable", "Owner's Capital", "Merchandise Inventory", "Sales Returns and Allowances"], "answer": 3, "explanation": "Sales Returns is a nominal (temporary) account and must be closed. Only real (permanent) accounts appear on the Post-Closing TB."}},
{"FABM1-MerchBusAcgMCQ-590": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchase Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase Discounts has a credit balance of ₱2,000. Apply the closing entry.", "options": ["Debit Purchase Discounts ₱2,000; Credit Income Summary ₱2,000", "Debit Income Summary ₱2,000; Credit Purchase Discounts ₱2,000", "Debit Purchase Discounts ₱2,000; Credit Purchases ₱2,000", "Debit Capital ₱2,000; Credit Purchase Discounts ₱2,000"], "answer": 0, "explanation": "It is a contra-cost account (Credit balance). To close, Debit Purchase Discounts and Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-591": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Accrued Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Adjusted for Accrued Service Revenue ₱4,000 (Dr AR, Cr Revenue). Apply the reversal.", "options": ["Debit Revenue ₱4,000; Credit AR ₱4,000", "Debit AR ₱4,000; Credit Revenue ₱4,000", "Debit Cash ₱4,000; Credit AR ₱4,000", "No reversal needed"], "answer": 0, "explanation": "Reversal is the opposite of the adjustment. Dr Revenue (Nominal), Cr Receivable (Real)."}},
{"FABM1-MerchBusAcgMCQ-592": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Income Summary (Profit)", "type": "Multiple Choice", "competency": "Apply", "question": "Income Summary has Credits of ₱100,000 and Debits of ₱80,000. Apply the closing entry for the balance.", "options": ["Debit Capital ₱20,000; Credit Income Summary ₱20,000", "Debit Income Summary ₱20,000; Credit Capital ₱20,000", "Debit Income Summary ₱100,000; Credit Capital ₱100,000", "Debit Sales ₱20,000; Credit Capital ₱20,000"], "answer": 1, "explanation": "Net Income is ₱20,000 (Credit balance). To close, Debit Income Summary and Credit Capital."}},
{"FABM1-MerchBusAcgMCQ-593": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Supplies (Asset Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Supplies account (Asset) has ₱5,000. Count shows ₱1,000 remaining. Apply the adjustment.", "options": ["Debit Supplies Expense ₱4,000; Credit Supplies ₱4,000", "Debit Supplies Expense ₱1,000; Credit Supplies ₱1,000", "Debit Supplies ₱4,000; Credit Supplies Expense ₱4,000", "Debit Supplies ₱1,000; Credit Cash ₱1,000"], "answer": 0, "explanation": "Used = ₱5,000 - ₱1,000 = ₱4,000. Debit Expense, Credit Asset."}},
{"FABM1-MerchBusAcgMCQ-594": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchases", "type": "Multiple Choice", "competency": "Apply", "question": "Purchases account has a Debit balance of ₱150,000. Apply the closing entry.", "options": ["Debit Purchases ₱150,000; Credit Income Summary ₱150,000", "Debit Income Summary ₱150,000; Credit Purchases ₱150,000", "Debit Capital ₱150,000; Credit Purchases ₱150,000", "Debit Cost of Goods Sold ₱150,000; Credit Purchases ₱150,000"], "answer": 1, "explanation": "Purchases is a temporary cost account with a Debit balance. To close, Credit Purchases and Debit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-595": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Calculation of Capital Impact", "type": "Multiple Choice", "competency": "Apply", "question": "Sales ₱80k, Expenses ₱60k, Drawings ₱5k. Apply the closing steps to determine the net increase in Capital.", "options": ["₱20,000", "₱15,000", "₱25,000", "₱80,000"], "answer": 1, "explanation": "Net Income (80-60) = ₱20k increase. Drawings ₱5k decrease. Net Increase = 20k - 5k = ₱15,000."}},
{"FABM1-MerchBusAcgMCQ-596": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Prepaid (Expense Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Recorded Prepaid Rent using Expense Method. Adjusted at year-end to set up the asset (Dr Prepaid, Cr Expense). Apply reversal.", "options": ["Debit Rent Expense; Credit Prepaid Rent", "Debit Prepaid Rent; Credit Rent Expense", "Debit Rent Expense; Credit Cash", "No reversal"], "answer": 0, "explanation": "Reversal restores the Expense method state for the new year. Dr Expense, Cr Asset (Prepaid)."}},
{"FABM1-MerchBusAcgMCQ-597": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchase Returns", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase Returns has a Credit balance of ₱3,000. Apply the closing entry.", "options": ["Debit Income Summary ₱3,000; Credit Purchase Returns ₱3,000", "Debit Purchase Returns ₱3,000; Credit Income Summary ₱3,000", "Debit Purchase Returns ₱3,000; Credit Purchases ₱3,000", "Debit Capital ₱3,000; Credit Purchase Returns ₱3,000"], "answer": 1, "explanation": "To close a Credit balance account, you Debit the account and Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-598": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Unearned (Liability Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Unearned Rent ₱12,000. At year end, ₱3,000 has been earned. Apply the adjustment.", "options": ["Debit Unearned Rent ₱3,000; Credit Rent Income ₱3,000", "Debit Rent Income ₱3,000; Credit Unearned Rent ₱3,000", "Debit Unearned Rent ₱9,000; Credit Rent Income ₱9,000", "Debit Cash ₱3,000; Credit Rent Income ₱3,000"], "answer": 0, "explanation": "Liability method records total as liability. Adjustment recognizes the earned portion. Debit Liability, Credit Revenue."}},
{"FABM1-MerchBusAcgMCQ-599": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Freight-In", "type": "Multiple Choice", "competency": "Apply", "question": "Freight-In has a Debit balance of ₱4,000. Apply the closing entry.", "options": ["Debit Freight-In ₱4,000; Credit Income Summary ₱4,000", "Debit Income Summary ₱4,000; Credit Freight-In ₱4,000", "Debit Capital ₱4,000; Credit Freight-In ₱4,000", "Debit Purchases ₱4,000; Credit Freight-In ₱4,000"], "answer": 1, "explanation": "Freight-In is a temporary cost account (Debit). Close by Crediting it and Debiting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-600": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Accrued Interest Expense", "type": "Multiple Choice", "competency": "Apply", "question": "Note Payable ₱100,000, 12%, 3 months accrued. Apply the adjustment.", "options": ["Debit Interest Exp ₱3,000; Credit Interest Payable ₱3,000", "Debit Interest Exp ₱12,000; Credit Interest Payable ₱12,000", "Debit Interest Payable ₱3,000; Credit Cash ₱3,000", "Debit Interest Exp ₱1,000; Credit Interest Payable ₱1,000"], "answer": 0, "explanation": "₱100,000 * 12% * 3/12 = ₱3,000. Debit Expense, Credit Payable."}},
{"FABM1-MerchBusAcgMCQ-601": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Sales Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Discounts (Debit balance ₱1,500). Apply the closing entry.", "options": ["Debit Income Summary ₱1,500; Credit Sales Discounts ₱1,500", "Debit Sales Discounts ₱1,500; Credit Income Summary ₱1,500", "Debit Sales ₱1,500; Credit Sales Discounts ₱1,500", "Debit Capital ₱1,500; Credit Sales Discounts ₱1,500"], "answer": 0, "explanation": "Close Debit balance accounts by Crediting them and Debiting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-602": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 9: Post-Closing Verification", "type": "Multiple Choice", "competency": "Apply", "question": "Total Debits in Post-Closing TB are ₱500,000. Total Credits are ₱490,000. Apply accounting logic to identify the likely error.", "options": ["A ₱10,000 Liability was omitted", "A ₱10,000 Expense was omitted", "A ₱5,000 Asset was recorded twice", "Net Income was too high"], "answer": 0, "explanation": "Credits are too low. Liabilities are Credits. Omitting a liability causes Credits to be understated."}},
{"FABM1-MerchBusAcgMCQ-603": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Capital Balance Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Capital ₱100k. Withdrawals ₱10k. Rev ₱50k. Exp ₱60k. Apply closing to find End Capital.", "options": ["₱80,000", "₱90,000", "₱140,000", "₱100,000"], "answer": 0, "explanation": "Net Loss = 50 - 60 = (10k). End Cap = 100k - 10k(Draw) - 10k(Loss) = ₱80,000."}},
{"FABM1-MerchBusAcgMCQ-604": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Unearned (Income Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Used Income Method for Unearned Revenue. Adjusted at year-end (Dr Revenue, Cr Unearned). Apply reversal.", "options": ["Debit Unearned Revenue; Credit Revenue", "Debit Revenue; Credit Unearned Revenue", "Debit Unearned Revenue; Credit Cash", "No reversal"], "answer": 0, "explanation": "Reversal restores the Income method state. Dr Liability (Unearned), Cr Revenue."}},
{"FABM1-MerchBusAcgMCQ-605": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Control Environment - Hiring", "type": "Multiple Choice", "competency": "Evaluate", "question": "A retail chain hires cashiers without performing background checks to fill positions quickly. Evaluate the risk to internal control.", "options": ["Low risk; cash registers prevent theft", "High risk; employees with a history of theft may be given access to assets", "Moderate risk; acceptable if they are paid minimum wage", "No risk; most people are honest"], "answer": 1, "explanation": "Human Resource controls are vital. Hiring trustworthy employees is the first line of defense against fraud. Skipping background checks exposes the company to high risk."}},
{"FABM1-MerchBusAcgMCQ-606": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Physical Controls - Warehouse", "type": "Multiple Choice", "competency": "Evaluate", "question": "The warehouse loading dock is left unlocked and unattended during lunch breaks. Evaluate the control weakness.", "options": ["Acceptable; drivers need access", "Major weakness; unauthorized removal of inventory can occur without detection", "Minor weakness; inventory is heavy", "Acceptable if there are cameras"], "answer": 1, "explanation": "Physical controls require that assets be secured when not in use or supervised. An open dock allows anyone to steal goods easily."}},
{"FABM1-MerchBusAcgMCQ-607": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Segregation of Duties - IT", "type": "Multiple Choice", "competency": "Evaluate", "question": "The computer programmer who wrote the accounting software also has access to the live production data and can modify records. Evaluate the segregation of duties.", "options": ["Efficient; they can fix bugs instantly", "Critical failure; the programmer can alter data to hide fraud and cover their tracks in the code", "Acceptable; programmers are not accountants", "Efficient; saves IT support costs"], "answer": 1, "explanation": "In IT controls, those who develop/test software should never have access to live production data. They could manipulate the system to process fraudulent transactions."}},
{"FABM1-MerchBusAcgMCQ-608": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Cash Disbursements", "type": "Multiple Choice", "competency": "Evaluate", "question": "Vendors are paid immediately upon receipt of an invoice, before the goods are inspected. Evaluate the financial risk.", "options": ["Low; builds vendor goodwill", "High; paying for damaged or unreceived goods becomes likely and difficult to recover", "Moderate; acceptable for small amounts", "Zero; banks reverse payments easily"], "answer": 1, "explanation": "Payment should only occur after the 'Three-Way Match' (PO, Receiving Report, Invoice) confirms that the goods ordered were actually received in good condition."}},
{"FABM1-MerchBusAcgMCQ-609": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Bank Deposits", "type": "Multiple Choice", "competency": "Evaluate", "question": "Cash receipts are accumulated for a week before being deposited in the bank to save trips. Evaluate this cash management policy.", "options": ["Efficient; saves transportation costs", "Dangerous; high risk of theft and robbery while cash sits on premises", "Acceptable if kept in a safe", "Efficient; reduces bank fees"], "answer": 1, "explanation": "Cash should be deposited daily. Holding large amounts of cash on-site increases the risk of robbery and makes internal theft tempting."}},
{"FABM1-MerchBusAcgMCQ-610": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Documentation Procedures", "type": "Multiple Choice", "competency": "Evaluate", "question": "Employees use 'Post-it' notes to record inventory withdrawals instead of formal requisition forms. Evaluate the audit trail.", "options": ["Sufficient; notes are written evidence", "Insufficient; notes can be lost or discarded easily, destroying the audit trail", "Sufficient; it is faster", "Insufficient; notes are too small"], "answer": 1, "explanation": "Formal, pre-numbered documents are required to ensure all transactions are recorded and none are missing. Loose notes are unreliable."}},
{"FABM1-MerchBusAcgMCQ-611": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Independent Verification", "type": "Multiple Choice", "competency": "Evaluate", "question": "The inventory count is performed by the same warehouse staff who are responsible for the custody of the goods. Evaluate the reliability of the count.", "options": ["High; they know where everything is", "Low; they can hide shortages or theft by falsifying the count", "Moderate; acceptable if supervised", "High; they are experts"], "answer": 1, "explanation": "Physical counts should be performed by independent personnel (e.g., auditors or accounting staff) to prevent custodians from covering up missing items."}},
{"FABM1-MerchBusAcgMCQ-612": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Payroll - Timekeeping", "type": "Multiple Choice", "competency": "Evaluate", "question": "A manual time clock is used, and supervisors do not approve time cards before payroll processing. Evaluate the risk of payroll fraud.", "options": ["Low; time clocks are accurate", "High; employees can punch in for absent friends ('buddy punching')", "Moderate; acceptable for small companies", "Zero; the accountant checks the math"], "answer": 1, "explanation": "Without supervisor approval, 'buddy punching' is rampant. Employees can get paid for hours they didn't work."}},
{"FABM1-MerchBusAcgMCQ-613": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Purchasing - Bidding", "type": "Multiple Choice", "competency": "Evaluate", "question": "Large capital purchases are made without a competitive bidding process; the manager simply picks a vendor. Evaluate the financial implication.", "options": ["Efficient; saves time", "Inefficient; the company likely overpays and risks favoritism/fraud", "Acceptable; the manager knows best", "Efficient; builds loyalty"], "answer": 1, "explanation": "Competitive bidding ensures the company gets the best price and quality. Skipping it leads to overpaying and opens the door for kickbacks."}},
{"FABM1-MerchBusAcgMCQ-614": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Sales - Credit Approval", "type": "Multiple Choice", "competency": "Evaluate", "question": "Salespeople are authorized to approve credit limits for new customers to close deals faster. Evaluate the conflict of interest.", "options": ["Beneficial; speeds up sales", "Harmful; salespeople prioritize volume over collectibility, leading to bad debts", "Neutral; sales and credit are the same", "Beneficial; empowers employees"], "answer": 1, "explanation": "Segregation of duties: The person selling (motivated by commission) should not approve credit (motivated by risk minimization)."}},
{"FABM1-MerchBusAcgMCQ-615": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Petty Cash Vouchers", "type": "Multiple Choice", "competency": "Evaluate", "question": "Petty cash vouchers are written in pencil and do not require a receipt attached. Evaluate the control.", "options": ["Adequate; trust is key", "Inadequate; amounts can be altered and expenses faked", "Adequate; receipts are often lost", "Inadequate; ink is required by law"], "answer": 1, "explanation": "Vouchers must be in ink (permanent) and supported by third-party evidence (receipts) to prevent the custodian from stealing cash and creating fake expenses."}},
{"FABM1-MerchBusAcgMCQ-616": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Financial Reporting", "type": "Multiple Choice", "competency": "Evaluate", "question": "The same accountant prepares the journal entries, posts to the ledger, and prepares the financial statements without review. Evaluate the risk of error.", "options": ["Low; the accountant is consistent", "High; material errors or fraud can go undetected without independent review", "Moderate; software prevents errors", "Low; as long as the trial balance balances"], "answer": 1, "explanation": "Independent review is essential. If one person does everything from entry to reporting, errors (intentional or accidental) will not be caught."}},
{"FABM1-MerchBusAcgMCQ-617": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Expense Reimbursement", "type": "Multiple Choice", "competency": "Evaluate", "question": "Employees submit travel expense reports without original receipts, using credit card statements instead. Evaluate the sufficiency of evidence.", "options": ["Sufficient; the statement shows the amount", "Insufficient; statements don't show *what* was bought (e.g., alcohol vs food)", "Sufficient; credit cards are secure", "Insufficient; statements are not legal documents"], "answer": 1, "explanation": "Credit card statements show the total but not the details. Employees could buy personal items or restricted items (alcohol) and claim them as business meals."}},
{"FABM1-MerchBusAcgMCQ-618": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Check Voiding", "type": "Multiple Choice", "competency": "Evaluate", "question": "Voided checks are thrown in the trash. Evaluate the risk.", "options": ["No risk; they are void", "High risk; they can be retrieved and used to forge signatures or alter the 'void' mark", "Low risk; banks won't cash them", "No risk; trash is secure"], "answer": 1, "explanation": "Voided checks should be defaced and filed (retained) to account for the sequence number. Throwing them away allows potential misuse."}},
{"FABM1-MerchBusAcgMCQ-619": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Accounts Receivable Write-off", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Accounts Receivable clerk can write off bad debts without authorization. Evaluate the fraud possibility.", "options": ["Low; writing off debts hurts the company", "High; the clerk can steal a customer payment and cover it up by writing off the account", "Moderate; customers would complain", "Low; the system prevents this"], "answer": 1, "explanation": "This is a classic skimming scheme. The clerk takes the cash payment, then writes off the customer's balance so no one notices the money is missing."}},
{"FABM1-MerchBusAcgMCQ-620": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Inventory - Obsolete Stock", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company has no policy for reviewing inventory for obsolescence. Evaluate the impact on the financial statements.", "options": ["Assets and Income are likely overstated", "Assets and Income are understated", "Liabilities are overstated", "No impact"], "answer": 0, "explanation": "If dead stock is not written down to its Net Realizable Value, the Asset (Inventory) and Net Income are artificially high."}},
{"FABM1-MerchBusAcgMCQ-621": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Whistleblower Policy", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company has no anonymous hotline for reporting fraud. Evaluate the detection capability.", "options": ["Adequate; employees can tell their boss", "Inadequate; employees fear retaliation and won't report issues", "Adequate; auditors find all fraud", "Inadequate; hotlines are required by law"], "answer": 1, "explanation": "Most fraud is detected by tips. Without a safe, anonymous way to report, employees will stay silent to protect their jobs."}},
{"FABM1-MerchBusAcgMCQ-622": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Fixed Assets", "type": "Multiple Choice", "competency": "Evaluate", "question": "Company laptops are not tagged with ID numbers and no log is kept of who has them. Evaluate asset safeguarding.", "options": ["Good; employees are responsible", "Poor; theft or loss cannot be tracked to individuals", "Good; laptops are cheap", "Poor; depreciation cannot be calculated"], "answer": 1, "explanation": "Fixed asset registers and tagging are essential to track custody. Without them, assets 'walk away' and the company doesn't know who is responsible."}},
{"FABM1-MerchBusAcgMCQ-623": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Journal Entries", "type": "Multiple Choice", "competency": "Evaluate", "question": "Journal entries can be posted to prior periods (backdating) without a system flag or approval. Evaluate the data integrity.", "options": ["Flexible; helps correct errors", "Dangerous; allows manipulation of past financial results", "Neutral; accounting is an art", "Flexible; standard feature"], "answer": 1, "explanation": "The system should lock closed periods. Allowing backdating lets management rewrite history to meet earnings targets."}},
{"FABM1-MerchBusAcgMCQ-624": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Access Controls", "type": "Multiple Choice", "competency": "Evaluate", "question": "The server room is unlocked and accessible to all janitorial staff. Evaluate the information security.", "options": ["Secure; janitors are trusted", "Insecure; risk of physical damage or data theft", "Secure; servers have passwords", "Insecure; servers get dirty"], "answer": 1, "explanation": "Physical security of the IT infrastructure is critical. Unauthorized access can lead to hardware theft, sabotage, or data breaches."}},
{"FABM1-MerchBusAcgMCQ-625": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Bank Statements", "type": "Multiple Choice", "competency": "Evaluate", "question": "Bank statements are mailed directly to the bookkeeper who reconciles the account. Evaluate the mail flow.", "options": ["Efficient; goes to the right person", "Weakness; statements should go to a manager/owner first to check for odd transactions", "Efficient; saves the manager time", "Weakness; mail is slow"], "answer": 1, "explanation": "The bookkeeper could intercept the statement and alter it (or hide it) if they are committing fraud. Independent review of the unopened statement is a strong control."}},
{"FABM1-MerchBusAcgMCQ-626": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Change Fund", "type": "Multiple Choice", "competency": "Evaluate", "question": "The ₱500 change fund in the cash register is never counted or verified at the start of a shift. Evaluate the accountability.", "options": ["Acceptable; it's a small amount", "Unacceptable; if cash is short at the end, it's unclear if it was the change fund or sales receipts", "Acceptable; saves time", "Unacceptable; the fund grows over time"], "answer": 1, "explanation": "You must verify the starting balance to know if the ending balance is correct. Without this, you can't hold the cashier accountable for shortages."}},
{"FABM1-MerchBusAcgMCQ-627": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Related Party Transactions", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company rents a warehouse from the CEO's spouse at double the market rate. This was not disclosed to the board. Evaluate the governance.", "options": ["Smart; keeps money in the family", "Failure of control; related party transactions must be disclosed and at arm's length", "Smart; secure tenure", "Failure; the spouse is not an employee"], "answer": 1, "explanation": "Related party transactions are high risk for conflict of interest. They must be disclosed and ideally approved by independent directors to ensure fairness."}},
{"FABM1-MerchBusAcgMCQ-628": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Disaster Recovery", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company backs up its accounting data daily but keeps the backup drive in the drawer next to the server. Evaluate the backup plan.", "options": ["Good; easy to restore", "Poor; a fire or flood would destroy both the original and the backup", "Good; data is safe", "Poor; backups should be weekly"], "answer": 1, "explanation": "Backups must be stored off-site (or in the cloud). If the office burns down, the on-site backup is useless."}},
{"FABM1-MerchBusAcgMCQ-629": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Training", "type": "Multiple Choice", "competency": "Evaluate", "question": "New employees are given passwords but no training on cybersecurity or fraud policies. Evaluate the preventive control.", "options": ["Sufficient; common sense prevails", "Insufficient; employees are the weakest link and need training to recognize threats", "Sufficient; software blocks everything", "Insufficient; passwords are hard to remember"], "answer": 1, "explanation": "Controls fail if people don't understand them. Training is a preventive control that reduces accidental errors and increases fraud awareness."}},
{"FABM1-MerchBusAcgMCQ-630": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Ownership", "type": "Multiple Choice", "competency": "Understand", "question": "Goods are in transit at year-end. If the terms are FOB Destination, which party includes the goods in their inventory count?", "options": ["The Buyer", "The Seller", "The Carrier", "Neither party"], "answer": 1, "explanation": "FOB Destination means title (ownership) transfers only upon delivery. Therefore, while in transit, the goods still belong to the Seller."}},
{"FABM1-MerchBusAcgMCQ-631": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is deducted from Gross Sales to arrive at Net Sales?", "options": ["Cost of Goods Sold", "Freight-Out", "Sales Returns and Allowances", "Operating Expenses"], "answer": 2, "explanation": "Net Sales is calculated as Gross Sales minus Contra-Revenue accounts: Sales Returns, Sales Allowances, and Sales Discounts."}},
{"FABM1-MerchBusAcgMCQ-632": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "In a Multi-Step Income Statement, what is the correct order of the first three subtotals?", "options": ["Gross Profit -> Net Sales -> Operating Income", "Net Sales -> Gross Profit -> Operating Income", "Net Sales -> Operating Income -> Gross Profit", "Gross Profit -> Operating Income -> Net Income"], "answer": 1, "explanation": "The sequence is: (1) Gross Sales - Contra = Net Sales; (2) Net Sales - COGS = Gross Profit; (3) Gross Profit - Operating Exp = Operating Income."}},
{"FABM1-MerchBusAcgMCQ-633": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Freight Accounting", "type": "Multiple Choice", "competency": "Understand", "question": "Why is Freight-In not classified as an Operating Expense?", "options": ["It is a cost directly attributable to acquiring the inventory, making it part of the asset's cost", "It is paid to a third party", "It is always a small amount", "It is a selling cost"], "answer": 0, "explanation": "Freight-In is a product cost (capitalized into Inventory), whereas Freight-Out is a period cost (expensed as Selling Expense)."}},
{"FABM1-MerchBusAcgMCQ-634": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Understand", "question": "Under the Periodic Inventory System, which account is used to record the acquisition of merchandise?", "options": ["Merchandise Inventory", "Cost of Goods Sold", "Purchases", "Assets"], "answer": 2, "explanation": "The Periodic system uses the temporary account 'Purchases' to record acquisitions. The Perpetual system debits 'Merchandise Inventory' directly."}},
{"FABM1-MerchBusAcgMCQ-635": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "What does 'EOM' stand for in credit terms like 'n/10 EOM'?", "options": ["End of Month", "Early Order Mode", "End of March", "Estimate of Month"], "answer": 0, "explanation": "EOM stands for End of Month, indicating that the credit period starts after the current month ends."}},
{"FABM1-MerchBusAcgMCQ-636": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Contra Accounts", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is a Contra-Revenue account?", "options": ["Purchase Discounts", "Sales Discounts", "Freight-Out", "Cost of Goods Sold"], "answer": 1, "explanation": "Sales Discounts reduce the revenue earned from sales. Purchase Discounts are Contra-Cost accounts."}},
{"FABM1-MerchBusAcgMCQ-637": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Profitability Analysis", "type": "Multiple Choice", "competency": "Understand", "question": "If Net Sales increases while Cost of Goods Sold remains constant, what happens to Gross Profit?", "options": ["It decreases", "It remains the same", "It increases", "It becomes zero"], "answer": 2, "explanation": "Gross Profit = Net Sales - COGS. If the first term increases and the subtraction remains the same, the result increases."}},
{"FABM1-MerchBusAcgMCQ-638": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Errors", "type": "Multiple Choice", "competency": "Understand", "question": "If Ending Inventory is understated, what is the effect on Cost of Goods Sold?", "options": ["COGS is understated", "COGS is overstated", "COGS is unaffected", "Net Income is overstated"], "answer": 1, "explanation": "COGS = Goods Available - Ending Inventory. If you subtract a smaller number (understated inventory), the result (COGS) becomes larger (overstated)."}},
{"FABM1-MerchBusAcgMCQ-639": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Business Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "What is the correct sequence of the merchandising operating cycle?", "options": ["Cash -> Inventory -> Accounts Receivable -> Cash", "Inventory -> Cash -> Accounts Receivable -> Inventory", "Cash -> Accounts Receivable -> Inventory -> Cash", "Accounts Receivable -> Inventory -> Cash -> Accounts Payable"], "answer": 0, "explanation": "Buy goods (Cash -> Inv), Sell goods on credit (Inv -> AR), Collect money (AR -> Cash)."}},
{"FABM1-MerchBusAcgMCQ-640": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "A debit memorandum issued by a buyer to a seller usually results in:", "options": ["A decrease in the buyer's Accounts Payable", "An increase in the buyer's Inventory", "A decrease in the seller's Accounts Payable", "An increase in the buyer's Cash"], "answer": 0, "explanation": "The buyer debits their AP (reducing liability) because they have returned goods or requested an allowance."}},
{"FABM1-MerchBusAcgMCQ-641": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Account Classification", "type": "Multiple Choice", "competency": "Understand", "question": "In which section of the Income Statement is 'Sales Salaries Expense' reported?", "options": ["Cost of Goods Sold", "General and Administrative Expenses", "Selling Expenses", "Other Expenses"], "answer": 2, "explanation": "Expenses directly related to the selling function (salespeople, advertising, delivery) are classified as Selling Expenses."}},
{"FABM1-MerchBusAcgMCQ-642": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "VAT", "type": "Multiple Choice", "competency": "Understand", "question": "The 'Input Tax' account represents:", "options": ["VAT charged to customers on sales", "VAT paid to suppliers on purchases", "Income tax paid to the BIR", "Property tax on the warehouse"], "answer": 1, "explanation": "Input Tax is the VAT passed on to the business by its suppliers. It is a credit (asset) that can be deducted from Output Tax payable."}},
{"FABM1-MerchBusAcgMCQ-643": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Perpetual System", "type": "Multiple Choice", "competency": "Understand", "question": "Which journal entry is unique to the Perpetual Inventory System at the time of sale?", "options": ["Debit Cash, Credit Sales", "Debit Accounts Receivable, Credit Sales", "Debit Cost of Goods Sold, Credit Merchandise Inventory", "Debit Sales Returns, Credit Accounts Receivable"], "answer": 2, "explanation": "Only the Perpetual system records the cost (COGS) and reduction of inventory immediately at the moment of sale. Periodic does not."}},
{"FABM1-MerchBusAcgMCQ-644": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Trade discounts are NOT recorded in the books because:", "options": ["They are illegal", "They represent the difference between catalog price and the actual agreed-upon price", "They are too small to matter", "They are only for cash sales"], "answer": 1, "explanation": "Trade discounts are a pricing mechanism to determine the 'real' price. The accounting system only records the actual transaction price."}},
{"FABM1-MerchBusAcgMCQ-645": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Cost Formula", "type": "Multiple Choice", "competency": "Understand", "question": "Which formula correctly represents 'Net Cost of Purchases'?", "options": ["Purchases + Freight In", "Purchases - Returns - Discounts", "Purchases - Returns - Discounts + Freight In", "Purchases + Returns + Discounts"], "answer": 2, "explanation": "First, find Net Purchases (Purchases - Returns - Discounts), then add the acquisition cost (Freight-In) to get the total Net Cost of Purchases."}},
{"FABM1-MerchBusAcgMCQ-646": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Definition", "type": "Multiple Choice", "competency": "Understand", "question": "Merchandise Inventory includes:", "options": ["Goods held on consignment from another company", "Office supplies used by the accountants", "Goods owned by the company and held for resale", "Equipment used in the warehouse"], "answer": 2, "explanation": "Inventory strictly refers to goods owned by the entity that are intended for resale. Consigned goods belong to the consignor, not the holder."}},
{"FABM1-MerchBusAcgMCQ-647": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Source Documents", "type": "Multiple Choice", "competency": "Understand", "question": "Which document evidences the transfer of goods from the seller to the common carrier?", "options": ["Bill of Lading", "Purchase Requisition", "Sales Invoice", "Check Voucher"], "answer": 0, "explanation": "The Bill of Lading is the contract between the shipper (seller) and the carrier, acknowledging receipt of the goods for transport."}},
{"FABM1-MerchBusAcgMCQ-648": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Normal Balances", "type": "Multiple Choice", "competency": "Understand", "question": "What is the normal balance of the 'Purchase Returns and Allowances' account?", "options": ["Debit", "Credit", "Zero", "It depends"], "answer": 1, "explanation": "It reduces the cost of Purchases (which is a Debit). Therefore, Purchase Returns must have a Credit balance."}},
{"FABM1-MerchBusAcgMCQ-649": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Net Income Impact", "type": "Multiple Choice", "competency": "Understand", "question": "An increase in Sales Returns and Allowances, holding all else constant, will cause:", "options": ["Net Income to increase", "Gross Profit to decrease", "Cost of Goods Sold to increase", "Operating Expenses to decrease"], "answer": 1, "explanation": "Sales Returns reduce Net Sales. Since Gross Profit = Net Sales - COGS, a reduction in Net Sales lowers Gross Profit."}},
{"FABM1-MerchBusAcgMCQ-650": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Periodic Closing", "type": "Multiple Choice", "competency": "Understand", "question": "In the closing process for a Periodic System, the Beginning Inventory is closed by:", "options": ["Debiting Merchandise Inventory", "Crediting Merchandise Inventory", "Debiting Sales", "Crediting Purchases"], "answer": 1, "explanation": "Beginning Inventory is a debit balance (Asset/Cost). To remove it from the books for the new period, you must Credit it."}},
{"FABM1-MerchBusAcgMCQ-651": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Operating Expenses", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is an Administrative Expense?", "options": ["Advertising Expense", "Delivery Van Depreciation", "Store Clerk Wages", "Office Utilities Expense"], "answer": 3, "explanation": "Administrative expenses relate to the general management of the business (office), whereas the others are Selling expenses."}},
{"FABM1-MerchBusAcgMCQ-652": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Gross Profit", "type": "Multiple Choice", "competency": "Understand", "question": "Gross Profit is best described as:", "options": ["The profit after tax", "The difference between what you sold a product for and what you paid to buy it", "The cash remaining in the bank", "The total revenue of the company"], "answer": 1, "explanation": "Conceptually, Gross Profit is the markup: Sales Price minus Acquisition Cost (COGS)."}},
{"FABM1-MerchBusAcgMCQ-653": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Business Types", "type": "Multiple Choice", "competency": "Understand", "question": "A business that buys raw materials and converts them into finished products is a:", "options": ["Merchandising Business", "Service Business", "Manufacturing Business", "Hybrid Business"], "answer": 2, "explanation": "Manufacturing involves conversion/processing. Merchandising involves buying and selling without changing the form of the goods."}},
{"FABM1-MerchBusAcgMCQ-654": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Sales Terms", "type": "Multiple Choice", "competency": "Understand", "question": "The term '2/10, n/30' is an example of:", "options": ["Trade Discounts", "Cash Discounts", "Quantity Discounts", "Seasonal Discounts"], "answer": 1, "explanation": "This is a Cash Discount (or Sales/Purchase Discount) offered to induce prompt payment of the account."}},
{"FABM1-MerchBusActgMCQ-655": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Chain Trade Discounts", "type": "Multiple Choice", "competency": "Analyze", "question": "A wholesaler sells goods with a list price of ₱100,000 and offers chain trade discounts of 20% and 10%. Analyze the amount to be credited to Sales.", "options": ["₱72,000", "₱70,000", "₱100,000", "₱80,000"], "answer": 0, "explanation": "Chain discounts are applied sequentially. ₱100,000 - 20% = ₱80,000. ₱80,000 - 10% = ₱72,000. Sales is recorded at the net invoice price of ₱72,000."}},
{"FABM1-MerchBusActgMCQ-656": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Destination, Collect", "type": "Multiple Choice", "competency": "Analyze", "question": "Goods are shipped FOB Destination, Freight Collect. The buyer pays the carrier ₱1,000. Analyze the Buyer's journal entry.", "options": ["Debit Accounts Payable ₱1,000; Credit Cash ₱1,000", "Debit Freight-In ₱1,000; Credit Cash ₱1,000", "Debit Delivery Expense ₱1,000; Credit Cash ₱1,000", "No entry required"], "answer": 0, "explanation": "Since terms are FOB Destination, the Seller is responsible for freight. If the Buyer pays the carrier (Collect), they are paying on behalf of the Seller, reducing the amount owed to the Seller (Debit AP)."}},
{"FABM1-MerchBusActgMCQ-657": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Destination, Collect", "type": "Multiple Choice", "competency": "Analyze", "question": "Referring to the previous scenario (FOB Destination, Freight Collect, Buyer pays ₱1,000), analyze the Seller's journal entry.", "options": ["Debit Freight-Out ₱1,000; Credit Accounts Receivable ₱1,000", "Debit Freight-Out ₱1,000; Credit Cash ₱1,000", "Debit Accounts Receivable ₱1,000; Credit Sales ₱1,000", "No entry required"], "answer": 0, "explanation": "The Seller bears the cost (Debit Freight-Out). Since the Buyer paid it, the Seller's collection right from the Buyer is reduced (Credit Accounts Receivable)."}},
{"FABM1-MerchBusActgMCQ-658": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Owner Withdrawal", "type": "Multiple Choice", "competency": "Analyze", "question": "The owner withdraws merchandise for personal use. The goods cost ₱5,000 and sell for ₱8,000. Using the Perpetual System, analyze the entry.", "options": ["Debit Drawings ₱5,000; Credit Inventory ₱5,000", "Debit Drawings ₱8,000; Credit Sales ₱8,000", "Debit Drawings ₱8,000; Credit Inventory ₱8,000", "Debit Drawings ₱5,000; Credit Purchases ₱5,000"], "answer": 0, "explanation": "Withdrawals are recorded at Cost, not Sales Price. In a perpetual system, credit the Inventory account directly."}},
{"FABM1-MerchBusActgMCQ-659": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "VAT on Returns", "type": "Multiple Choice", "competency": "Analyze", "question": "A customer returns goods sold for ₱1,120 inclusive of 12% VAT. Analyze the debit entries for the Seller (Revenue side only).", "options": ["Debit Sales Returns ₱1,000; Debit Output Tax ₱120", "Debit Sales Returns ₱1,120", "Debit Sales Returns ₱1,000; Debit Input Tax ₱120", "Debit Sales Returns ₱1,120; Credit Cash ₱1,120"], "answer": 0, "explanation": "The return reduces the Net Sales and the tax liability. Base Sales Return = ₱1,120 / 1.12 = ₱1,000. VAT portion = ₱120. Both the revenue contra and Output Tax are debited."}},
{"FABM1-MerchBusActgMCQ-660": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Partial Collection", "type": "Multiple Choice", "competency": "Analyze", "question": "A customer owes ₱20,000 with terms 2/10, n/30. They pay ₱10,000 cash within the discount period. Analyze the amount credited to Accounts Receivable.", "options": ["₱10,204.08", "₱10,000.00", "₱9,800.00", "₱10,200.00"], "answer": 0, "explanation": "The cash received (₱10,000) represents 98% of the debt settled. Credit AR = ₱10,000 / 0.98 = ₱10,204.08."}},
{"FABM1-MerchBusActgMCQ-661": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Settlement of Note", "type": "Multiple Choice", "competency": "Analyze", "question": "Paid a ₱50,000 promissory note plus ₱1,000 interest. Analyze the credit side of the entry.", "options": ["Credit Cash ₱51,000", "Credit Notes Payable ₱50,000; Credit Interest Expense ₱1,000", "Credit Cash ₱50,000", "Credit Accounts Payable ₱51,000"], "answer": 0, "explanation": "The total cash outflow is the principal plus interest. Credit Cash for the full payment of ₱51,000."}},
{"FABM1-MerchBusActgMCQ-662": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Periodic Inventory Return", "type": "Multiple Choice", "competency": "Analyze", "question": "Under the Periodic System, a company returns defective goods to a supplier (originally bought on credit) for ₱2,500. Analyze the entry.", "options": ["Debit Accounts Payable ₱2,500; Credit Purchase Returns and Allowances ₱2,500", "Debit Accounts Payable ₱2,500; Credit Merchandise Inventory ₱2,500", "Debit Cash ₱2,500; Credit Purchase Returns and Allowances ₱2,500", "Debit Purchase Returns and Allowances ₱2,500; Credit Accounts Payable ₱2,500"], "answer": 0, "explanation": "In Periodic, returns are credited to the contra-cost account 'Purchase Returns and Allowances', not Inventory. The debit reduces the Liability."}},
{"FABM1-MerchBusActgMCQ-663": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Recovery of Bad Debts", "type": "Multiple Choice", "competency": "Analyze", "question": "A customer whose account of ₱5,000 was previously written off sends a check for the full amount. Analyze the first entry required.", "options": ["Debit Accounts Receivable ₱5,000; Credit Allowance for Bad Debts ₱5,000", "Debit Cash ₱5,000; Credit Accounts Receivable ₱5,000", "Debit Cash ₱5,000; Credit Bad Debts Expense ₱5,000", "Debit Cash ₱5,000; Credit Miscellaneous Income ₱5,000"], "answer": 0, "explanation": "First, you must reinstate the account receivable that was written off. Debit AR and Credit Allowance. The second entry records the cash collection."}},
{"FABM1-MerchBusActgMCQ-664": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight Capitalization", "type": "Multiple Choice", "competency": "Analyze", "question": "Bought machinery ₱100,000 and inventory ₱50,000. Paid freight ₱5,000 (allocated based on weight: ₱4,000 to machinery, ₱1,000 to inventory). Analyze the debit to Inventory.", "options": ["₱51,000", "₱50,000", "₱55,000", "₱54,000"], "answer": 0, "explanation": "Freight is capitalized to the specific asset it relates to. Inventory cost = Purchase ₱50k + Allocated Freight ₱1k = ₱51,000."}},
{"FABM1-MerchBusActgMCQ-665": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Compound Entry Sales", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold goods for ₱20,000. Terms: 50% cash, 50% on 30-day note. Analyze the debit side.", "options": ["Debit Cash ₱10,000; Debit Notes Receivable ₱10,000", "Debit Cash ₱20,000", "Debit Accounts Receivable ₱20,000", "Debit Cash ₱10,000; Debit Accounts Receivable ₱10,000"], "answer": 0, "explanation": "The company received cash and a promissory note. Both are assets. Debit Cash and Debit Notes Receivable."}},
{"FABM1-MerchBusActgMCQ-666": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Petty Cash Shortage", "type": "Multiple Choice", "competency": "Analyze", "question": "Replenishing Petty Cash: Receipts total ₱8,800. Cash remaining ₱1,100. Fund size ₱10,000. Analyze the entry for the difference.", "options": ["Debit Cash Short and Over ₱100", "Credit Cash Short and Over ₱100", "Debit Miscellaneous Expense ₱1,200", "Credit Cash ₱100"], "answer": 0, "explanation": "Total accounted for: ₱8,800 + ₱1,100 = ₱9,900. Fund is ₱10,000. Shortage is ₱100. Shortages are debited to 'Cash Short and Over' (Expense)."}},
{"FABM1-MerchBusActgMCQ-667": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Purchase with VAT", "type": "Multiple Choice", "competency": "Analyze", "question": "Purchased supplies for ₱1,120 cash (VAT inclusive). Analyze the credit to Cash.", "options": ["₱1,120", "₱1,000", "₱1,254.40", "₱120"], "answer": 0, "explanation": "The cash paid is the full invoice amount including tax. Credit Cash ₱1,120."}},
{"FABM1-MerchBusActgMCQ-668": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Bank Service Charge", "type": "Multiple Choice", "competency": "Analyze", "question": "The bank statement shows a service charge of ₱150 not yet recorded. Analyze the adjusting entry.", "options": ["Debit Bank Service Charge ₱150; Credit Cash ₱150", "Debit Cash ₱150; Credit Bank Service Charge ₱150", "Debit Accounts Payable ₱150; Credit Cash ₱150", "Debit Miscellaneous Expense ₱150; Credit Accounts Payable ₱150"], "answer": 0, "explanation": "The charge reduces the cash balance and is an expense. Debit Expense, Credit Cash."}},
{"FABM1-MerchBusActgMCQ-669": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Prepaid Insurance", "type": "Multiple Choice", "competency": "Analyze", "question": "Paid ₱12,000 for a 1-year insurance policy. Analyze the transaction using the Asset Method.", "options": ["Debit Prepaid Insurance ₱12,000; Credit Cash ₱12,000", "Debit Insurance Expense ₱12,000; Credit Cash ₱12,000", "Debit Prepaid Insurance ₱1,000; Credit Cash ₱1,000", "Debit Cash ₱12,000; Credit Prepaid Insurance ₱12,000"], "answer": 0, "explanation": "The asset method capitalizes the prepayment. Debit the asset 'Prepaid Insurance' and Credit Cash."}},
{"FABM1-MerchBusActgMCQ-670": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sale of Asset", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold old office furniture for ₱2,000 cash. Book value was ₱1,500. Analyze the credit to Gain on Sale.", "options": ["Credit Gain on Sale ₱500", "Debit Gain on Sale ₱500", "Credit Sales ₱2,000", "Credit Gain on Sale ₱2,000"], "answer": 0, "explanation": "Cash (2,000) - Book Value (1,500) = Gain of ₱500. Gains are credited."}},
{"FABM1-MerchBusActgMCQ-671": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Allowance", "type": "Multiple Choice", "competency": "Analyze", "question": "Granted a ₱300 allowance to a customer for minor defects. The customer has not yet paid. Analyze the entry.", "options": ["Debit Sales Returns and Allowances ₱300; Credit Accounts Receivable ₱300", "Debit Sales Returns and Allowances ₱300; Credit Cash ₱300", "Debit Sales ₱300; Credit Accounts Receivable ₱300", "Debit Accounts Receivable ₱300; Credit Sales Returns and Allowances ₱300"], "answer": 0, "explanation": "An allowance reduces the amount the customer owes. Debit the Contra-Revenue account and Credit Accounts Receivable."}},
{"FABM1-MerchBusActgMCQ-672": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Refund Check", "type": "Multiple Choice", "competency": "Analyze", "question": "Issued a check for ₱1,000 to a customer who returned goods originally paid for with cash. Analyze the entry.", "options": ["Debit Sales Returns and Allowances ₱1,000; Credit Cash ₱1,000", "Debit Accounts Receivable ₱1,000; Credit Cash ₱1,000", "Debit Sales ₱1,000; Credit Cash ₱1,000", "Debit Cash ₱1,000; Credit Sales Returns and Allowances ₱1,000"], "answer": 0, "explanation": "Since the original sale was cash, the return requires a cash refund. Debit Sales Returns (Contra-Revenue) and Credit Cash."}},
{"FABM1-MerchBusActgMCQ-673": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Purchase Order", "type": "Multiple Choice", "competency": "Analyze", "question": "Sent a Purchase Order to a supplier for goods worth ₱50,000. Analyze the journal entry.", "options": ["No journal entry is required", "Debit Purchases ₱50,000; Credit Accounts Payable ₱50,000", "Debit Merchandise Inventory ₱50,000; Credit Accounts Payable ₱50,000", "Debit Accounts Payable ₱50,000; Credit Cash ₱50,000"], "answer": 0, "explanation": "A Purchase Order is a request/authorization. No exchange of value has occurred yet, so no accounting entry is made."}},
{"FABM1-MerchBusActgMCQ-674": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Accrued Salaries", "type": "Multiple Choice", "competency": "Analyze", "question": "At year-end, unpaid salaries amount to ₱15,000. Analyze the effect if this entry is omitted.", "options": ["Expenses are understated, Liabilities are understated", "Expenses are overstated, Liabilities are overstated", "Expenses are understated, Net Income is understated", "Liabilities are overstated, Equity is understated"], "answer": 0, "explanation": "The entry should be Dr Salary Expense, Cr Salary Payable. Omitting it means Expenses are too low (understated) and Liabilities are too low (understated)."}},
{"FABM1-MerchBusActgMCQ-675": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Dishonored Note", "type": "Multiple Choice", "competency": "Analyze", "question": "A customer dishonors a ₱10,000 note. Interest accrued is ₱200. Analyze the debit to Accounts Receivable.", "options": ["₱10,200", "₱10,000", "₱9,800", "₱200"], "answer": 0, "explanation": "When a note is dishonored, the full claim (Principal + Interest) is charged back to the customer's open account. Debit AR ₱10,200."}},
{"FABM1-MerchBusActgMCQ-676": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Cost of Asset (Discount Lost)", "type": "Multiple Choice", "competency": "Analyze", "question": "Bought equipment ₱20,000 terms 2/10, n/30. Paid after 30 days. If the company records assets at net, analyze the entry for the extra payment.", "options": ["Debit Purchase Discounts Lost (Expense)", "Debit Equipment (Asset)", "Debit Interest Income", "Debit Accounts Payable"], "answer": 0, "explanation": "Under the net method, the asset is recorded at ₱19,600. The extra ₱400 paid for missing the discount is a financing penalty (Expense), not an increase in the asset's value."}},
{"FABM1-MerchBusActgMCQ-677": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Consignment", "type": "Multiple Choice", "competency": "Analyze", "question": "Received goods on consignment from Vendor V worth ₱100,000. Analyze the entry.", "options": ["No journal entry; recorded in memo only", "Debit Merchandise Inventory ₱100,000; Credit Accounts Payable ₱100,000", "Debit Purchases ₱100,000; Credit Accounts Payable ₱100,000", "Debit Consigned Goods ₱100,000; Credit Cash ₱100,000"], "answer": 0, "explanation": "Consigned goods remain the property of the consignor. The consignee (holder) makes no entry until the goods are sold."}},
{"FABM1-MerchBusActgMCQ-678": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Net Sales Calculation (Entry)", "type": "Multiple Choice", "competency": "Analyze", "question": "Closing entry analysis: Sales ₱500k, Returns ₱20k, Discounts ₱10k. Analyze the credit to Income Summary for Net Sales.", "options": ["₱470,000", "₱500,000", "₱530,000", "₱480,000"], "answer": 0, "explanation": "While traditionally Sales and Contra-Sales are closed separately, the net effect transferred to Income Summary is Net Sales. ₱500k - ₱20k - ₱10k = ₱470,000."}},
{"FABM1-MerchBusActgMCQ-679": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Credit Card Deposit", "type": "Multiple Choice", "competency": "Analyze", "question": "Deposited credit card slips totaling ₱20,000. The bank charges 5%. Analyze the debit to Cash.", "options": ["₱19,000", "₱20,000", "₱1,000", "₱21,000"], "answer": 0, "explanation": "The bank deducts the fee immediately. Cash received = ₱20,000 * 95% = ₱19,000. (Dr Cash 19k, Dr CC Expense 1k, Cr Sales 20k)."}},
{"FABM1-MerchBusAcgMCQ-680": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Net Loss", "type": "Multiple Choice", "competency": "Apply", "question": "After closing revenues and expenses, the Income Summary account has a Debit balance of ₱45,000. Apply the entry to close this balance.", "options": ["Debit Income Summary ₱45,000; Credit Capital ₱45,000", "Debit Capital ₱45,000; Credit Income Summary ₱45,000", "Debit Drawings ₱45,000; Credit Income Summary ₱45,000", "Debit Sales ₱45,000; Credit Income Summary ₱45,000"], "answer": 1, "explanation": "A Debit balance in Income Summary represents a Net Loss. To close it, you must Credit Income Summary and Debit the Owner's Capital account (reducing equity)."}},
{"FABM1-MerchBusAcgMCQ-681": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchase Returns", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase Returns and Allowances has a Credit balance of ₱6,500. Apply the closing entry.", "options": ["Debit Purchase Returns ₱6,500; Credit Income Summary ₱6,500", "Debit Income Summary ₱6,500; Credit Purchase Returns ₱6,500", "Debit Purchase Returns ₱6,500; Credit Purchases ₱6,500", "Debit Capital ₱6,500; Credit Purchase Returns ₱6,500"], "answer": 0, "explanation": "It is a contra-cost account with a Credit balance. To close it to zero, you must Debit the account and Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-682": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Sales Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Discounts has a Debit balance of ₱2,200. Apply the closing entry.", "options": ["Debit Sales Discounts ₱2,200; Credit Income Summary ₱2,200", "Debit Income Summary ₱2,200; Credit Sales Discounts ₱2,200", "Debit Sales ₱2,200; Credit Sales Discounts ₱2,200", "Debit Capital ₱2,200; Credit Sales Discounts ₱2,200"], "answer": 1, "explanation": "Sales Discounts is a contra-revenue account (Debit). To close it, Credit the account and Debit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-683": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Accrued Interest", "type": "Multiple Choice", "competency": "Apply", "question": "On Dec 31, the company adjusted for Accrued Interest Expense of ₱1,500 (Dr Interest Exp, Cr Interest Payable). Apply the reversing entry on Jan 1.", "options": ["Debit Interest Payable ₱1,500; Credit Interest Expense ₱1,500", "Debit Interest Expense ₱1,500; Credit Interest Payable ₱1,500", "Debit Interest Payable ₱1,500; Credit Cash ₱1,500", "No reversing entry is needed"], "answer": 0, "explanation": "The reversing entry is the exact opposite of the accrual adjustment. Debit the Liability (Payable) and Credit the Expense."}},
{"FABM1-MerchBusAcgMCQ-684": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Freight-In", "type": "Multiple Choice", "competency": "Apply", "question": "Freight-In account has a Debit balance of ₱8,000. Apply the closing entry.", "options": ["Debit Freight-In ₱8,000; Credit Income Summary ₱8,000", "Debit Cost of Goods Sold ₱8,000; Credit Freight-In ₱8,000", "Debit Income Summary ₱8,000; Credit Freight-In ₱8,000", "Debit Capital ₱8,000; Credit Freight-In ₱8,000"], "answer": 2, "explanation": "Freight-In is a temporary cost account with a Debit balance. Close it by Crediting Freight-In and Debiting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-685": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Calculating Net Income", "type": "Multiple Choice", "competency": "Apply", "question": "Sales ₱200k, Sales Returns ₱10k, Purchases ₱120k, Expenses ₱40k. Apply the closing process to determine the credit to Capital.", "options": ["₱30,000", "₱40,000", "₱70,000", "₱20,000"], "answer": 0, "explanation": "Net Sales = 190k. Total Costs/Exp = 120k + 40k = 160k. Net Income = 190k - 160k = ₱30,000."}},
{"FABM1-MerchBusAcgMCQ-686": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Drawings", "type": "Multiple Choice", "competency": "Apply", "question": "The owner withdrew ₱20,000 cash. Apply the entry to close the Owner's Drawing account.", "options": ["Debit Income Summary ₱20,000; Credit Drawings ₱20,000", "Debit Drawings ₱20,000; Credit Cash ₱20,000", "Debit Capital ₱20,000; Credit Drawings ₱20,000", "Debit Drawings ₱20,000; Credit Capital ₱20,000"], "answer": 2, "explanation": "Drawings are closed directly to Capital to reflect the reduction in equity. Debit Capital, Credit Drawings."}},
{"FABM1-MerchBusAcgMCQ-687": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Supplies (Expense Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Supplies Expense was debited ₱10,000 during the year. Count shows ₱2,000 on hand. Apply the adjustment.", "options": ["Debit Supplies (Asset) ₱2,000; Credit Supplies Expense ₱2,000", "Debit Supplies Expense ₱2,000; Credit Supplies (Asset) ₱2,000", "Debit Supplies (Asset) ₱8,000; Credit Supplies Expense ₱8,000", "Debit Supplies Expense ₱8,000; Credit Supplies (Asset) ₱8,000"], "answer": 0, "explanation": "Expense Method records everything as expense initially. You must set up the asset for what is left (₱2,000). Debit Asset, Credit Expense."}},
{"FABM1-MerchBusAcgMCQ-688": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Supplies (Expense Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Referring to the previous question (Adj: Dr Supplies ₱2k, Cr Supplies Exp ₱2k), apply the reversing entry.", "options": ["Debit Supplies Expense ₱2,000; Credit Supplies ₱2,000", "Debit Supplies ₱2,000; Credit Supplies Expense ₱2,000", "Debit Cash ₱2,000; Credit Supplies ₱2,000", "No reversing entry"], "answer": 0, "explanation": "Since the Expense Method was used (creating an asset adjustment), it is reversed. Debit Expense, Credit Asset."}},
{"FABM1-MerchBusAcgMCQ-689": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Periodic Inventory (Beg)", "type": "Multiple Choice", "competency": "Apply", "question": "In the Periodic System, apply the entry to close Beginning Inventory of ₱55,000.", "options": ["Debit Merchandise Inventory ₱55,000; Credit Income Summary ₱55,000", "Debit Income Summary ₱55,000; Credit Merchandise Inventory ₱55,000", "Debit Cost of Goods Sold ₱55,000; Credit Merchandise Inventory ₱55,000", "Debit Capital ₱55,000; Credit Merchandise Inventory ₱55,000"], "answer": 1, "explanation": "Beginning Inventory is removed (Credited) and transferred to Income Summary (Debited) as part of the cost of goods available calculation."}},
{"FABM1-MerchBusAcgMCQ-690": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Periodic Inventory (End)", "type": "Multiple Choice", "competency": "Apply", "question": "In the Periodic System, apply the entry to record Ending Inventory of ₱60,000.", "options": ["Debit Income Summary ₱60,000; Credit Merchandise Inventory ₱60,000", "Debit Merchandise Inventory ₱60,000; Credit Income Summary ₱60,000", "Debit Merchandise Inventory ₱60,000; Credit Capital ₱60,000", "Debit Sales ₱60,000; Credit Merchandise Inventory ₱60,000"], "answer": 1, "explanation": "Ending Inventory is established as an asset. Debit Merchandise Inventory and Credit Income Summary (effectively deducting it from costs)."}},
{"FABM1-MerchBusAcgMCQ-691": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Compound Expense Closing", "type": "Multiple Choice", "competency": "Apply", "question": "Close the following: Rent Exp ₱12,000; Salaries Exp ₱20,000; Utilities Exp ₱3,000.", "options": ["Debit Rent ₱12k, Salaries ₱20k, Utilities ₱3k; Credit Income Summary ₱35k", "Debit Income Summary ₱35,000; Credit Rent ₱12k, Salaries ₱20k, Utilities ₱3k", "Debit Capital ₱35,000; Credit Expenses ₱35,000", "Debit Income Summary ₱35,000; Credit Accounts Payable ₱35,000"], "answer": 1, "explanation": "Debit Income Summary for the total expenses. Credit each individual expense account to zero them out."}},
{"FABM1-MerchBusAcgMCQ-692": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 9: Post-Closing TB Content", "type": "Multiple Choice", "competency": "Apply", "question": "Which account should appear on the Post-Closing Trial Balance?", "options": ["Depreciation Expense", "Sales Revenue", "Accumulated Depreciation", "Income Summary"], "answer": 2, "explanation": "Accumulated Depreciation is a contra-asset (Real Account) and carries over. All others are nominal accounts which are closed."}},
{"FABM1-MerchBusAcgMCQ-693": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Unearned (Income Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Received ₱24,000 for service, credited to 'Service Revenue'. At year-end, ₱4,000 is unearned. Adjustment: Dr Revenue, Cr Unearned Revenue. Apply the reversal.", "options": ["Debit Unearned Revenue ₱4,000; Credit Service Revenue ₱4,000", "Debit Service Revenue ₱4,000; Credit Unearned Revenue ₱4,000", "Debit Unearned Revenue ₱20,000; Credit Service Revenue ₱20,000", "No reversal needed"], "answer": 0, "explanation": "Since the Income Method was used (creating a liability adjustment), it is reversed. Debit Liability, Credit Revenue."}},
{"FABM1-MerchBusAcgMCQ-694": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Income Summary (Profit)", "type": "Multiple Choice", "competency": "Apply", "question": "Income Summary has a Credit balance of ₱75,000. Apply the closing entry.", "options": ["Debit Capital ₱75,000; Credit Income Summary ₱75,000", "Debit Sales ₱75,000; Credit Capital ₱75,000", "Debit Income Summary ₱75,000; Credit Capital ₱75,000", "Debit Income Summary ₱75,000; Credit Cash ₱75,000"], "answer": 2, "explanation": "A Credit balance is a Profit. Close it by Debiting Income Summary and Crediting Capital."}},
{"FABM1-MerchBusAcgMCQ-695": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Depreciation", "type": "Multiple Choice", "competency": "Apply", "question": "Truck cost ₱500,000, life 5 years, no salvage. Apply the annual depreciation adjustment.", "options": ["Debit Depreciation Exp ₱100,000; Credit Accumulated Depreciation ₱100,000", "Debit Depreciation Exp ₱100,000; Credit Truck ₱100,000", "Debit Accumulated Depreciation ₱100,000; Credit Depreciation Exp ₱100,000", "Debit Truck Expense ₱100,000; Credit Cash ₱100,000"], "answer": 0, "explanation": "Straight line: ₱500k / 5 = ₱100k. Debit Expense, Credit Contra-Asset (Accumulated Depreciation)."}},
{"FABM1-MerchBusAcgMCQ-696": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Revenue balance is ₱350,000. Apply the closing entry.", "options": ["Debit Income Summary ₱350,000; Credit Sales ₱350,000", "Debit Sales ₱350,000; Credit Income Summary ₱350,000", "Debit Sales ₱350,000; Credit Capital ₱350,000", "Debit Cash ₱350,000; Credit Sales ₱350,000"], "answer": 1, "explanation": "Revenue has a Credit balance. Debit Sales to close it, Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-697": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Bad Debts", "type": "Multiple Choice", "competency": "Apply", "question": "Accounts Receivable ₱200,000. Estimate 2% uncollectible. Allowance has ₱500 credit balance. Apply adjustment.", "options": ["Debit Bad Debts Exp ₱4,000; Credit Allowance ₱4,000", "Debit Bad Debts Exp ₱3,500; Credit Allowance ₱3,500", "Debit Bad Debts Exp ₱4,500; Credit Allowance ₱4,500", "Debit Allowance ₱3,500; Credit Bad Debts Exp ₱3,500"], "answer": 1, "explanation": "Target = 200k * 2% = ₱4,000. Existing Credit = ₱500. Required Adjustment = ₱4,000 - ₱500 = ₱3,500."}},
{"FABM1-MerchBusAcgMCQ-698": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Accrued Salaries", "type": "Multiple Choice", "competency": "Apply", "question": "Year-end adjustment: Dr Salaries Expense ₱5,000, Cr Salaries Payable ₱5,000. Apply the reversal.", "options": ["Debit Salaries Expense ₱5,000; Credit Salaries Payable ₱5,000", "Debit Salaries Payable ₱5,000; Credit Cash ₱5,000", "Debit Salaries Payable ₱5,000; Credit Salaries Expense ₱5,000", "No reversal needed"], "answer": 2, "explanation": "Accruals are reversed. Debit the Liability (Payable) and Credit the Expense."}},
{"FABM1-MerchBusAcgMCQ-699": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchase Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase Discounts has a Credit balance of ₱1,800. Apply the closing entry.", "options": ["Debit Purchase Discounts ₱1,800; Credit Income Summary ₱1,800", "Debit Income Summary ₱1,800; Credit Purchase Discounts ₱1,800", "Debit Purchase Discounts ₱1,800; Credit Purchases ₱1,800", "Debit Capital ₱1,800; Credit Purchase Discounts ₱1,800"], "answer": 0, "explanation": "Contra-cost (Credit balance). Close by Debiting Purchase Discounts and Crediting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-700": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Capital Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Capital ₱100k. Net Income ₱30k. Drawings ₱10k. Apply the closing effects to find Ending Capital.", "options": ["₱120,000", "₱130,000", "₱140,000", "₱110,000"], "answer": 0, "explanation": "End Cap = Beg + NI - Drawings. 100k + 30k - 10k = ₱120,000."}},
{"FABM1-MerchBusAcgMCQ-701": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Perpetual Inventory Shrinkage", "type": "Multiple Choice", "competency": "Apply", "question": "System shows ₱50,000 inventory. Count shows ₱49,000. Apply the adjustment.", "options": ["Debit Inventory ₱1,000; Credit COGS ₱1,000", "Debit COGS ₱1,000; Credit Inventory ₱1,000", "Debit Loss ₱1,000; Credit Purchases ₱1,000", "Debit Sales ₱1,000; Credit Inventory ₱1,000"], "answer": 1, "explanation": "Inventory is overstated. Credit Inventory. Debit Cost of Goods Sold (or Loss on Shrinkage)."}},
{"FABM1-MerchBusAcgMCQ-702": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Contra-Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Returns has a Debit balance of ₱4,000. Apply the closing entry.", "options": ["Debit Sales ₱4,000; Credit Sales Returns ₱4,000", "Debit Income Summary ₱4,000; Credit Sales Returns ₱4,000", "Debit Sales Returns ₱4,000; Credit Income Summary ₱4,000", "Debit Capital ₱4,000; Credit Sales Returns ₱4,000"], "answer": 1, "explanation": "Contra-revenue (Debit). Close by Crediting the account and Debiting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-703": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Income Summary Analysis", "type": "Multiple Choice", "competency": "Apply", "question": "If Income Summary is debited for Expenses ₱90k and Credited for Revenues ₱80k, apply the closing entry for the balance.", "options": ["Debit Capital ₱10,000; Credit Income Summary ₱10,000", "Debit Income Summary ₱10,000; Credit Capital ₱10,000", "Debit Sales ₱10,000; Credit Capital ₱10,000", "Debit Drawings ₱10,000; Credit Income Summary ₱10,000"], "answer": 0, "explanation": "Debits (Exp) > Credits (Rev) = Net Loss of ₱10k. Income Summary has a Debit balance. Close by Crediting Income Summary and Debiting Capital."}},
{"FABM1-MerchBusAcgMCQ-704": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Identifying Reversals", "type": "Multiple Choice", "competency": "Apply", "question": "Which of the following adjustments is typically reversed?", "options": ["Depreciation Expense", "Bad Debts Expense", "Accrued Salaries Expense", "Amortization Expense"], "answer": 2, "explanation": "Accruals (Payables/Receivables created by adjustment) are typically reversed to simplify record keeping in the next period. Depreciation and Bad Debts are never reversed."}},
{"FABM1-MerchBusAcgMCQ-705": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Accounts Receivable - Lapping", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Accounts Receivable clerk collects cash payments from customers and also posts the payments to the customer subsidiary ledgers. Evaluate the fraud risk.", "options": ["Low; the clerk is efficient", "High; the clerk can engage in 'lapping' by stealing a payment and covering it with a subsequent payment", "Moderate; acceptable if the clerk is bonded", "Zero; the general ledger is separate"], "answer": 1, "explanation": "Segregation of Duties failure. Custody of the asset (cash) and recording (posting to AR) must be separated to prevent lapping schemes."}},
{"FABM1-MerchBusAcgMCQ-706": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Purchasing - Blind Count", "type": "Multiple Choice", "competency": "Evaluate", "question": "The copy of the Purchase Order sent to the Receiving Department omits the quantities ordered. Evaluate the purpose of this control.", "options": ["Inefficient; the receiving clerk has to call purchasing to know what to check", "Strong control; it forces the receiving clerk to physically count the goods rather than just checking off a number", "Weak control; it slows down the receiving process", "Error; the quantity should always be visible"], "answer": 1, "explanation": "A 'blind' purchase order forces an actual count. If the clerk sees '100 units' on the paper, they might just sign without counting. If it's blank, they must count to fill it in."}},
{"FABM1-MerchBusAcgMCQ-707": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Signature Stamps", "type": "Multiple Choice", "competency": "Evaluate", "question": "The check signature stamp is kept in an unlocked drawer accessible to all accounting staff. Evaluate the physical control.", "options": ["Adequate; the stamp is hard to find", "Inadequate; any employee can fraudulently authorize checks using the stamp", "Adequate; checks still need a payee", "Inadequate; stamps should be illegal"], "answer": 1, "explanation": "A signature stamp is equivalent to the signer's actual signature. It must be stored in a safe or locked box with strictly limited access."}},
{"FABM1-MerchBusAcgMCQ-708": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Voucher Cancellation", "type": "Multiple Choice", "competency": "Evaluate", "question": "Paid invoices are not stamped 'PAID' or perforated after payment processing. Evaluate the risk of duplicate payments.", "options": ["Low; the accounting system prevents duplicates", "High; the invoice could be resubmitted for payment a second time (inadvertently or fraudulently)", "Moderate; acceptable if the vendor is honest", "Zero; checks are numbered"], "answer": 1, "explanation": "Canceling documents (stamping 'PAID') prevents them from being reused to support a duplicate or fraudulent disbursement."}},
{"FABM1-MerchBusAcgMCQ-709": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Inventory - Write-downs", "type": "Multiple Choice", "competency": "Evaluate", "question": "The warehouse manager is allowed to write off damaged inventory without authorization from the accounting or finance department. Evaluate the risk.", "options": ["Efficient; clears space quickly", "High risk; the manager can steal good inventory and write it off as 'damaged' to hide the theft", "Low risk; damaged goods have no value", "Moderate; acceptable for small items"], "answer": 1, "explanation": "Asset write-offs reduce the accountability for the asset. Authorization must come from a level above custody to prevent concealment of theft."}},
{"FABM1-MerchBusAcgMCQ-710": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Reporting Structure", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Credit Manager, who approves credit limits for customers, reports to the VP of Sales. Evaluate the reporting line.", "options": ["Optimal; Sales and Credit must work together", "Flawed; the Credit Manager should report to the Treasurer/CFO to ensure independence from sales targets", "Optimal; speeds up the approval process", "Flawed; Credit should report to the Warehouse"], "answer": 1, "explanation": "Sales departments want high volume. Credit departments want low risk. If Credit reports to Sales, pressure to approve risky customers will override internal controls."}},
{"FABM1-MerchBusAcgMCQ-711": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Cash Over/Short Analysis", "type": "Multiple Choice", "competency": "Evaluate", "question": "A cashier's drawer is perfectly balanced (zero over/short) every single day for a year. Evaluate this performance.", "options": ["Excellent; the cashier is perfect", "Suspicious; human error typically causes small variances, suggesting the cashier might be manipulating the count", "Good; indicates strong training", "Suspicious; the register is broken"], "answer": 1, "explanation": "Perfect balancing over a long period is a 'red flag'. It often means the cashier is holding a 'slush fund' or covering shortages with personal money to avoid detection."}},
{"FABM1-MerchBusAcgMCQ-712": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Expense Reports", "type": "Multiple Choice", "competency": "Evaluate", "question": "The CEO approves their own expense reimbursement reports. Evaluate the authorization control.", "options": ["Appropriate; the CEO is the highest authority", "Inappropriate; nobody should approve their own expenses; the Board or CFO should approve the CEO's", "Appropriate; the CEO owns the company", "Inappropriate; the secretary should do it"], "answer": 1, "explanation": "Management override risk. To ensure valid business expenses, even the CEO's spending should be reviewed by an independent party (e.g., the Board's Audit Committee)."}},
{"FABM1-MerchBusAcgMCQ-713": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Master Vendor File", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Accounts Payable clerk has edit access to change vendor bank account details in the master file. Evaluate the risk.", "options": ["Efficient; corrections are made fast", "Critical risk; the clerk can divert payments to their own personal bank account", "Low risk; banks verify names", "Moderate risk; acceptable during holidays"], "answer": 1, "explanation": "This is a common fraud. If the person paying the bills can also change *where* the money goes, they can redirect legitimate payments to themselves."}},
{"FABM1-MerchBusAcgMCQ-714": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "IT - Audit Logs", "type": "Multiple Choice", "competency": "Evaluate", "question": "The accounting system is configured to allow users to delete audit logs/trails. Evaluate the system integrity.", "options": ["Flexible; saves disk space", "Unacceptable; audit trails must be permanent to trace who did what and when", "Acceptable; logs are only for errors", "Flexible; protects user privacy"], "answer": 1, "explanation": "Audit trails are the 'black box' of accounting. If they can be deleted, a fraudster can erase the evidence of their unauthorized transactions."}},
{"FABM1-MerchBusAcgMCQ-715": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Purchase Requisitions", "type": "Multiple Choice", "competency": "Evaluate", "question": "Department heads can order goods directly from suppliers without submitting a purchase requisition to the Purchasing Department. Evaluate the efficiency vs. control.", "options": ["Efficient; cuts red tape", "Weak control; risks unauthorized purchases, loss of bulk discounts, and budget overruns", "Strong control; decentralization is best", "Efficient; suppliers prefer it"], "answer": 1, "explanation": "Centralized purchasing ensures authorized spending and negotiates better prices. Direct ordering leads to 'maverick spend' and lack of control."}},
{"FABM1-MerchBusAcgMCQ-716": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Sequence Checks", "type": "Multiple Choice", "competency": "Evaluate", "question": "The accounting software does not flag missing invoice numbers in the sequence. Evaluate the completeness assertion.", "options": ["Acceptable; numbers are arbitrary", "Weakness; missing numbers could indicate unrecorded sales or theft of stock", "Acceptable; digital records don't skip", "Weakness; customers need order"], "answer": 1, "explanation": "Sequence checks verify completeness. A gap in the sequence suggests a transaction occurred but was deleted or not recorded."}},
{"FABM1-MerchBusAcgMCQ-717": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Risk Assessment - New Market", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company expands into international sales without assessing the risks of foreign currency exchange or shipping regulations. Evaluate the risk assessment component.", "options": ["Bold; fortune favors the brave", "Deficient; failure to identify and analyze risks leads to potential financial losses", "Adequate; learn as you go", "Bold; accounting will catch up"], "answer": 1, "explanation": "Risk Assessment is a core component of COSO. Management must identify risks *before* they impact the business."}},
{"FABM1-MerchBusAcgMCQ-718": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Monitoring - Internal Audit", "type": "Multiple Choice", "competency": "Evaluate", "question": "Internal Audit never visits the remote branch offices, auditing only the headquarters. Evaluate the monitoring effectiveness.", "options": ["Cost-effective; HQ is where the money is", "Ineffective; remote branches often have the weakest controls and highest risk of fraud due to lack of oversight", "Effective; branches are small", "Cost-effective; travel is expensive"], "answer": 1, "explanation": "Fraudsters know where the auditors don't go. Remote locations with little supervision are high-risk areas that require monitoring."}},
{"FABM1-MerchBusAcgMCQ-719": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Code of Conduct", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company has a Code of Conduct, but it is not discussed during orientation and is buried in a file. Evaluate the communication of values.", "options": ["Sufficient; it is in writing", "Insufficient; values must be communicated and reinforced to establish a strong control environment", "Sufficient; employees know right from wrong", "Insufficient; it should be video based"], "answer": 1, "explanation": "Information and Communication. A policy that no one knows about is useless. It must be actively communicated to set the tone."}},
{"FABM1-MerchBusAcgMCQ-720": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Shipping Documents", "type": "Multiple Choice", "competency": "Evaluate", "question": "The shipping department releases goods based on a phone call from the sales manager, without a printed Sales Order or Pick List. Evaluate the control.", "options": ["Flexible; good for rush orders", "Weak; high risk of shipping errors or giving away inventory without a recorded sale", "Strong; verbal contracts are valid", "Flexible; saves paper"], "answer": 1, "explanation": "Shipping must be authorized by valid documentation (Sales Order) to ensure the goods are billed. 'Verbal' shipments often result in unbilled revenue."}},
{"FABM1-MerchBusAcgMCQ-721": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Conflict of Interest", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Purchasing Manager owns a substantial stock interest in one of the company's main suppliers. This is not disclosed. Evaluate the situation.", "options": ["Smart investing", "Conflict of interest/Fraud risk; the manager may approve unfavorable terms to benefit their investment", "Neutral; personal life is private", "Smart; aligning interests"], "answer": 1, "explanation": "Undisclosed conflicts of interest undermine objective decision-making. The manager might overpay the supplier to boost their own stock value."}},
{"FABM1-MerchBusAcgMCQ-722": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Data Backups", "type": "Multiple Choice", "competency": "Evaluate", "question": "Backups are performed daily, but restoration from the backup is never tested. Evaluate the Disaster Recovery Plan.", "options": ["Reliable; backups happen daily", "Unreliable; without testing, there is no guarantee the data can be recovered when needed", "Reliable; technology is stable", "Unreliable; backups should be hourly"], "answer": 1, "explanation": "A backup is only good if it can be restored. Corrupted backup files are common; testing is required to ensure business continuity."}},
{"FABM1-MerchBusAcgMCQ-723": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Safe Combinations", "type": "Multiple Choice", "competency": "Evaluate", "question": "The combination to the main safe has not been changed since the previous store manager was fired two years ago. Evaluate the physical security.", "options": ["Adequate; the old manager is gone", "Inadequate; access must be revoked (combo changed) immediately upon employee turnover", "Adequate; safes are heavy", "Inadequate; safes should use keys"], "answer": 1, "explanation": "Access controls must be updated when personnel change. The fired manager (or anyone they told) still has access to the cash."}},
{"FABM1-MerchBusAcgMCQ-724": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Invoice Approval", "type": "Multiple Choice", "competency": "Evaluate", "question": "Invoices for services (e.g., consulting, cleaning) are approved by the department head who requested them before payment. Evaluate this procedure.", "options": ["Necessary; only the requestor can verify the service was actually performed", "Unnecessary; creates a bottleneck", "Risky; the department head might be biased", "Necessary; accounting knows the rates"], "answer": 0, "explanation": "For services (where there is no receiving report), approval by the person who supervised the work is the primary evidence that the liability is valid."}},
{"FABM1-MerchBusAcgMCQ-725": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Refund Fraud", "type": "Multiple Choice", "competency": "Evaluate", "question": "Customer refunds are issued to the customer's credit card. The system blocks refunds to a card different from the original purchase card. Evaluate this setting.", "options": ["Restrictive; hurts customer service", "Strong control; prevents employees from processing fake refunds to their own credit cards", "Restrictive; customers change cards often", "Weak control; cash is safer"], "answer": 1, "explanation": "This prevents 'Credit Card Refund Fraud' where an employee refunds a legitimate customer's purchase onto the employee's own card."}},
{"FABM1-MerchBusAcgMCQ-726": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Inventory Tagging", "type": "Multiple Choice", "competency": "Evaluate", "question": "During the physical inventory count, count tags are pre-numbered and all tags (used and unused) are accounted for at the end. Evaluate this procedure.", "options": ["Bureaucratic; slows down the count", "Essential; ensures all items are counted once and no area is double-counted or missed", "Bureaucratic; digital scanners don't need tags", "Essential; allows for random sampling"], "answer": 1, "explanation": "Accounting for the sequence of tags ensures completeness (everything was counted) and existence (no fake items added), preventing manipulation of the count."}},
{"FABM1-MerchBusAcgMCQ-727": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Surprise Audits", "type": "Multiple Choice", "competency": "Evaluate", "question": "The internal auditor conducts surprise cash counts at branch locations. Evaluate the deterrent effect.", "options": ["High; increases the perceived risk of detection", "Low; auditors are friendly", "High; employees work harder", "Low; cash is irrelevant"], "answer": 0, "explanation": "The 'perception of detection' is a powerful fraud deterrent. If employees know an auditor could show up anytime, they are less likely to steal."}},
{"FABM1-MerchBusAcgMCQ-728": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Scrap Sales", "type": "Multiple Choice", "competency": "Evaluate", "question": "Revenue from the sale of scrap metal/waste is collected in cash by the warehouse manager and used for 'team parties' without recording. Evaluate the accounting.", "options": ["Acceptable; it boosts morale", "Unacceptable; all revenue sources must be recorded and deposited; off-book funds are high risk", "Acceptable; scrap has no cost", "Unacceptable; parties are tax deductible"], "answer": 1, "explanation": "All company inflows are company assets. Allowing off-book 'slush funds' creates an environment where theft is normalized and tax laws are violated."}},
{"FABM1-MerchBusAcgMCQ-729": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Manual Cheques", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company occasionally uses manual hand-written cheques that are not entered into the system immediately. Evaluate the control.", "options": ["Flexible; useful for emergencies", "Weakness; manual cheques often bypass standard approval workflows and distort real-time cash balances", "Strong; handwriting is hard to forge", "Weakness; they look unprofessional"], "answer": 1, "explanation": "Manual cheques break the automated control chain. They are often forgotten (understating cash outflows) or used to bypass approval limits."}},
{"FABM1-MerchBusAcgMCQ-730": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Operating Cycle", "type": "Multiple Choice", "competency": "Understand", "question": "Why is the cash-to-cash operating cycle of a merchandiser usually longer than that of a service firm?", "options": ["Merchandisers have less efficient employees", "Merchandisers must first buy inventory and sell it before collecting cash, adding an extra stage", "Service firms usually demand upfront payment", "Merchandisers are regulated more strictly"], "answer": 1, "explanation": "The inventory holding period creates a lag between cash outflow (buying goods) and cash inflow (selling goods), extending the cycle."}},
{"FABM1-MerchBusAcgMCQ-731": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Ownership", "type": "Multiple Choice", "competency": "Understand", "question": "Under 'FOB Shipping Point', who pays the freight costs and who owns the goods while they are in transit?", "options": ["Seller pays, Buyer owns", "Buyer pays, Buyer owns", "Seller pays, Seller owns", "Buyer pays, Seller owns"], "answer": 1, "explanation": "FOB Shipping Point means title transfers at the shipping dock. Therefore, the Buyer owns the goods in transit and is responsible for the shipping costs."}},
{"FABM1-MerchBusAcgMCQ-732": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Contra Accounts", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is considered a 'Contra-Revenue' account?", "options": ["Cost of Goods Sold", "Purchase Discounts", "Sales Returns and Allowances", "Freight-Out"], "answer": 2, "explanation": "Sales Returns and Allowances is deducted from Gross Sales to arrive at Net Sales, making it a contra-revenue account."}},
{"FABM1-MerchBusAcgMCQ-733": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Profitability", "type": "Multiple Choice", "competency": "Understand", "question": "Gross Profit minus Operating Expenses results in:", "options": ["Net Sales", "Cost of Goods Sold", "Operating Income (Income from Operations)", "Comprehensive Income"], "answer": 2, "explanation": "The multi-step income statement structure is: Gross Profit - Operating Expenses = Operating Income."}},
{"FABM1-MerchBusAcgMCQ-734": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Systems", "type": "Multiple Choice", "competency": "Understand", "question": "A key characteristic of the Perpetual Inventory System is that:", "options": ["It requires a physical count to determine the Cost of Goods Sold", "It updates the Inventory account after every purchase and sale transaction", "It is only used for low-value items", "It records cost of goods sold only at the end of the year"], "answer": 1, "explanation": "Perpetual systems maintain a continuous ('perpetual') record of inventory balances, updating immediately with every transaction."}},
{"FABM1-MerchBusAcgMCQ-735": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Credit Terms", "type": "Multiple Choice", "competency": "Understand", "question": "In the credit term 'n/30', what does 'n' stand for?", "options": ["Net", "New", "Next", "No discount"], "answer": 0, "explanation": "'n' stands for Net. 'n/30' means the Net (full) amount of the invoice is due within 30 days."}},
{"FABM1-MerchBusAcgMCQ-736": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "A 'Quantity Discount' is typically offered to:", "options": ["Encourage early payment of the invoice", "Encourage customers to buy in larger volumes", "Clear out damaged goods", "Reward loyal employees"], "answer": 1, "explanation": "Quantity discounts (a form of trade discount) reduce the unit price when a customer purchases large amounts to stimulate volume sales."}},
{"FABM1-MerchBusAcgMCQ-737": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Cost Components", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following expenditures is NOT included in the cost of Merchandise Inventory?", "options": ["Import duties", "Freight-in", "Advertising costs to promote the product", "Insurance on goods while in transit (FOB Shipping Point)"], "answer": 2, "explanation": "Advertising is a selling expense. Only costs incurred to bring the inventory to its location and condition for sale (freight, duties, insurance in transit) are capitalized."}},
{"FABM1-MerchBusAcgMCQ-738": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Business Types", "type": "Multiple Choice", "competency": "Understand", "question": "Which type of business generates revenue primarily by performing tasks for customers rather than selling goods?", "options": ["Merchandising", "Manufacturing", "Service", "Retail"], "answer": 2, "explanation": "Service businesses sell time, expertise, or labor (e.g., accounting firms, salons), distinguishing them from merchandisers who sell tangible goods."}},
{"FABM1-MerchBusAcgMCQ-739": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Periodic System", "type": "Multiple Choice", "competency": "Understand", "question": "In a Periodic Inventory System, the account 'Purchases' is used to record:", "options": ["The cost of goods sold", "The acquisition of merchandise for resale", "The return of damaged goods", "The purchase of office equipment"], "answer": 1, "explanation": "The 'Purchases' account is a temporary account specific to the Periodic system used to track the cost of inventory bought during the period."}},
{"FABM1-MerchBusAcgMCQ-740": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "VAT", "type": "Multiple Choice", "competency": "Understand", "question": "What is 'Output Tax'?", "options": ["The tax a business pays when buying goods", "The tax a business charges to its customers when selling goods", "The income tax paid at the end of the year", "The property tax on the store building"], "answer": 1, "explanation": "Output Tax is the VAT collected by the seller from the customer. It is an 'output' of the business operations."}},
{"FABM1-MerchBusAcgMCQ-741": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Purchase Returns", "type": "Multiple Choice", "competency": "Understand", "question": "From the buyer's perspective, a 'Purchase Allowance' means:", "options": ["Returning the goods physically to the seller", "Keeping the goods but receiving a reduction in the price due to defects", "Receiving a discount for paying early", "Buying goods on credit"], "answer": 1, "explanation": "An allowance occurs when the buyer keeps defective or wrong goods in exchange for a price reduction, whereas a return involves sending them back."}},
{"FABM1-MerchBusAcgMCQ-742": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Freight Terms", "type": "Multiple Choice", "competency": "Understand", "question": "If terms are 'FOB Destination, Freight Prepaid', who bears the freight cost?", "options": ["The Buyer", "The Seller", "The Carrier", "Split 50/50"], "answer": 1, "explanation": "FOB Destination means the Seller is responsible for the goods until delivery. Freight Prepaid means the Seller paid the carrier. Thus, the Seller bears the cost."}},
{"FABM1-MerchBusAcgMCQ-743": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Income Statement", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following is reported under 'Other Comprehensive Income' rather than Net Income?", "options": ["Sales Revenue", "Unrealized gains on available-for-sale securities", "Cost of Goods Sold", "Rent Expense"], "answer": 1, "explanation": "Unrealized gains/losses on certain investments are part of OCI, not the regular operating Net Income."}},
{"FABM1-MerchBusAcgMCQ-744": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Flow", "type": "Multiple Choice", "competency": "Understand", "question": "Goods Available for Sale minus Ending Inventory equals:", "options": ["Gross Profit", "Net Purchases", "Cost of Goods Sold", "Sales Revenue"], "answer": 2, "explanation": "This is the fundamental logic of the periodic system: (Total Goods Available) - (What is left) = (What must have been sold)."}},
{"FABM1-MerchBusAcgMCQ-745": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Financial Position", "type": "Multiple Choice", "competency": "Understand", "question": "Accounts Payable is classified as:", "options": ["Current Asset", "Owner's Equity", "Current Liability", "Non-Current Liability"], "answer": 2, "explanation": "Accounts Payable represents amounts owed to suppliers for inventory/services, typically due within a short period (30-60 days), making it a Current Liability."}},
{"FABM1-MerchBusAcgMCQ-746": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Source Documents", "type": "Multiple Choice", "competency": "Understand", "question": "A 'Check Voucher' is typically used to:", "options": ["Request goods from the warehouse", "Authorize and document a cash disbursement", "Bill a customer", "Record the receipt of goods"], "answer": 1, "explanation": "A Check Voucher (or Disbursement Voucher) is an internal document used to authorize the issuance of a check for payment."}},
{"FABM1-MerchBusAcgMCQ-747": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Revenue Recognition", "type": "Multiple Choice", "competency": "Understand", "question": "Sales revenue should be recognized when:", "options": ["The cash is collected", "The order is received", "The performance obligation is satisfied (goods are transferred)", "The invoice is printed"], "answer": 2, "explanation": "Under accrual accounting and new standards (PFRS 15), revenue is recognized when control of the goods transfers to the customer."}},
{"FABM1-MerchBusAcgMCQ-748": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Subsidiary Ledgers", "type": "Multiple Choice", "competency": "Understand", "question": "The sum of the balances in the Accounts Receivable Subsidiary Ledger must equal:", "options": ["Total Sales for the year", "The Cash account balance", "The Accounts Receivable control account balance in the General Ledger", "Total Assets"], "answer": 2, "explanation": "The Control Account (GL) summarizes the total. The Subsidiary Ledger provides the detail. They must always reconcile/match."}},
{"FABM1-MerchBusAcgMCQ-749": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Purchase Discounts", "type": "Multiple Choice", "competency": "Understand", "question": "Why do managers often view 'Purchase Discounts Lost' as a sign of inefficiency?", "options": ["It means the company bought too much inventory", "It represents an unnecessary financing cost paid for missing a payment deadline", "It means the supplier increased prices", "It implies the goods were defective"], "answer": 1, "explanation": "Forgoing a cash discount (e.g., 2/10) is very expensive in annualized interest terms. Missing it is usually poor cash management."}},
{"FABM1-MerchBusAcgMCQ-750": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Normal Balance", "type": "Multiple Choice", "competency": "Understand", "question": "What is the normal balance of the 'Freight-Out' account?", "options": ["Debit", "Credit", "Zero", "It varies"], "answer": 0, "explanation": "Freight-Out is an expense (Selling Expense). All expenses normally have Debit balances."}},
{"FABM1-MerchBusAcgMCQ-751": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Operating Expenses", "type": "Multiple Choice", "competency": "Understand", "question": "Depreciation on the office building is classified as:", "options": ["Cost of Goods Sold", "Selling Expense", "Administrative (General) Expense", "Other Expense"], "answer": 2, "explanation": "Costs related to the general administration/office facilities are Administrative Expenses. (Depreciation on a delivery truck would be Selling)."}},
{"FABM1-MerchBusAcgMCQ-752": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Inventory Valuation", "type": "Multiple Choice", "competency": "Understand", "question": "Which principle requires inventory to be reported at the lower of its cost or its net realizable value?", "options": ["Matching Principle", "Revenue Recognition Principle", "Lower of Cost and Net Realizable Value (LCNRV)", "Historical Cost Principle"], "answer": 2, "explanation": "LCNRV ensures assets are not overstated. If the market value drops below cost, the inventory must be written down."}},
{"FABM1-MerchBusAcgMCQ-753": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Account Titles", "type": "Multiple Choice", "competency": "Understand", "question": "Which of the following accounts is a temporary (nominal) account?", "options": ["Merchandise Inventory", "Sales Revenue", "Accounts Payable", "Owner's Capital"], "answer": 1, "explanation": "Revenue accounts are temporary; they track performance for a period and are closed to zero at year-end. The others are permanent (real) accounts."}},
{"FABM1-MerchBusAcgMCQ-754": {"subject": "FABM1", "topic": "Nature and Operations of Merchandising Business", "subtopic": "Closing Process", "type": "Multiple Choice", "competency": "Understand", "question": "The primary purpose of closing entries at the end of the year is to:", "options": ["Correct errors made during the year", "Update the Inventory account", "Zero out temporary accounts and transfer net income to capital", "Prepare the tax return"], "answer": 2, "explanation": "Closing entries reset the revenue, expense, and drawing accounts for the next year and move the period's profit/loss into equity."}},  
{"FABM1-MerchBusActgMCQ-755": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Perpetual System - Sale", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold merchandise for ₱15,000 on account (Cost ₱9,000). Using the Perpetual Inventory System, analyze the entry to record the cost.", "options": ["Debit Cost of Goods Sold ₱9,000; Credit Merchandise Inventory ₱9,000", "Debit Merchandise Inventory ₱9,000; Credit Cost of Goods Sold ₱9,000", "Debit Cost of Goods Sold ₱15,000; Credit Merchandise Inventory ₱15,000", "No entry required for cost at the time of sale"], "answer": 0, "explanation": "In a Perpetual system, the cost must be recognized immediately upon sale. Debit the expense (COGS) and Credit the asset (Inventory) to reduce the balance."}},
{"FABM1-MerchBusActgMCQ-756": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Shipping Point, Prepaid", "type": "Multiple Choice", "competency": "Analyze", "question": "Goods purchased FOB Shipping Point. The seller prepaid the ₱2,000 freight charge and added it to the invoice. Analyze the Buyer's entry upon payment within the discount period (assuming discount applies only to goods).", "options": ["Debit Accounts Payable (Total); Credit Cash (Total - Discount on Goods)", "Debit Accounts Payable (Total); Credit Cash (Total - Discount on Total)", "Debit Freight-In ₱2,000; Credit Cash ₱2,000", "Debit Accounts Payable (Goods); Credit Cash (Goods)"], "answer": 0, "explanation": "The buyer owes the total invoice (Goods + Freight). The discount applies only to the goods. Cash paid = (Goods - Discount) + Freight. The full liability is debited."}},
{"FABM1-MerchBusActgMCQ-757": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Purchase Returns (Gross Method)", "type": "Multiple Choice", "competency": "Analyze", "question": "Returned goods purchased on credit for ₱4,000. Analyze the effect on the accounting equation.", "options": ["Assets Decrease, Liabilities Decrease", "Assets Increase, Equity Increases", "Liabilities Decrease, Equity Increases", "Assets Decrease, Expenses Increase"], "answer": 2, "explanation": "Debit Accounts Payable (Liability Decrease). Credit Purchase Returns (Contra-Cost), which increases Net Income and therefore Equity."}},
{"FABM1-MerchBusActgMCQ-758": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales with VAT", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold goods for ₱22,400 inclusive of 12% VAT. Analyze the credit to Sales Revenue.", "options": ["₱20,000", "₱22,400", "₱25,088", "₱2,400"], "answer": 0, "explanation": "Sales Revenue is the amount before tax. ₱22,400 / 1.12 = ₱20,000. The ₱2,400 difference is Output Tax."}},
{"FABM1-MerchBusActgMCQ-759": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Settlement with Return", "type": "Multiple Choice", "competency": "Analyze", "question": "Customer billed ₱50,000 (2/10, n/30). Returned ₱5,000 goods before payment. Paid within discount period. Analyze cash received.", "options": ["₱44,100", "₱45,000", "₱49,000", "₱44,000"], "answer": 0, "explanation": "Adjusted AR = ₱50k - ₱5k = ₱45k. Discount = 2% of ₱45k = ₱900. Cash = ₱45k - ₱900 = ₱44,100."}},
{"FABM1-MerchBusActgMCQ-760": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Trade Discount Calculation", "type": "Multiple Choice", "competency": "Analyze", "question": "Catalog price ₱20,000. Trade discount 15%. Credit terms 2/10, n/30. Analyze the debit to Accounts Receivable.", "options": ["₱17,000", "₱20,000", "₱16,660", "₱19,600"], "answer": 0, "explanation": "AR is recorded at invoice price (net of trade discount). ₱20,000 - 15% = ₱17,000. Cash discounts are not deducted at the time of recording the sale."}},
{"FABM1-MerchBusActgMCQ-761": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Periodic Freight-In", "type": "Multiple Choice", "competency": "Analyze", "question": "Under the Periodic System, paid ₱1,500 freight on inventory purchase (FOB Shipping Point). Analyze the debit.", "options": ["Debit Freight-In ₱1,500", "Debit Merchandise Inventory ₱1,500", "Debit Purchases ₱1,500", "Debit Delivery Expense ₱1,500"], "answer": 0, "explanation": "In Periodic, freight on purchases is tracked in a separate account called 'Freight-In', not added directly to Inventory."}},
{"FABM1-MerchBusActgMCQ-762": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sale of Supplies", "type": "Multiple Choice", "competency": "Analyze", "question": "Sold excess office supplies at cost ₱500 to a neighboring business on account. Analyze the credit.", "options": ["Credit Office Supplies ₱500", "Credit Sales ₱500", "Credit Merchandise Inventory ₱500", "Credit Gain on Sale ₱500"], "answer": 0, "explanation": "Since supplies are not inventory (not primarily held for resale), crediting 'Sales' is incorrect. Credit the asset account 'Office Supplies' directly to reduce it."}},
{"FABM1-MerchBusActgMCQ-763": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Discount on Partial Payment", "type": "Multiple Choice", "competency": "Analyze", "question": "Terms 2/10, n/30. Customer pays ₱9,800 cash within the discount period as partial payment. Analyze the credit to Accounts Receivable.", "options": ["₱10,000", "₱9,800", "₱9,604", "₱10,200"], "answer": 0, "explanation": "To find the credit to AR, divide the cash paid by the complement of the discount rate. ₱9,800 / 0.98 = ₱10,000."}},
{"FABM1-MerchBusActgMCQ-764": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Cost of Equipment (Discount)", "type": "Multiple Choice", "competency": "Analyze", "question": "Purchased store equipment for ₱100,000, terms 2/10, n/30. Paid within the discount period. Analyze the recorded cost of the equipment.", "options": ["₱98,000", "₱100,000", "₱102,000", "₱90,000"], "answer": 0, "explanation": "Assets should be recorded at their actual cash cost. ₱100,000 - 2% = ₱98,000. Any discount taken reduces the capitalized cost of the asset."}},
{"FABM1-MerchBusActgMCQ-765": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "FOB Destination Cost", "type": "Multiple Choice", "competency": "Analyze", "question": "Seller ships goods FOB Destination. Seller pays ₱500 freight. Analyze the impact on the Buyer's books.", "options": ["No entry required for freight", "Debit Freight-In ₱500", "Debit Inventory ₱500", "Debit Accounts Payable ₱500"], "answer": 0, "explanation": "FOB Destination means the Seller bears the cost. The Buyer records nothing regarding the freight."}},
{"FABM1-MerchBusActgMCQ-766": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Periodic Purchase Returns", "type": "Multiple Choice", "competency": "Analyze", "question": "Periodic System: Returned ₱2,000 of defective goods to supplier. Analyze the credit entry.", "options": ["Credit Purchase Returns and Allowances ₱2,000", "Credit Merchandise Inventory ₱2,000", "Credit Purchases ₱2,000", "Credit Accounts Payable ₱2,000"], "answer": 0, "explanation": "In Periodic, the 'Purchase Returns and Allowances' account is used. In Perpetual, you would credit 'Merchandise Inventory'."}},
{"FABM1-MerchBusActgMCQ-767": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Inventory Shortage Entry", "type": "Multiple Choice", "competency": "Analyze", "question": "Perpetual Inventory balance ₱60,000. Physical count ₱58,500. Analyze the adjusting entry.", "options": ["Debit Cost of Goods Sold ₱1,500; Credit Merchandise Inventory ₱1,500", "Debit Merchandise Inventory ₱1,500; Credit Cost of Goods Sold ₱1,500", "Debit Loss on Theft ₱1,500; Credit Purchases ₱1,500", "Debit Sales ₱1,500; Credit Merchandise Inventory ₱1,500"], "answer": 0, "explanation": "The asset is overstated. Credit Inventory to reduce it to the physical count. Debit COGS for the shrinkage."}},
{"FABM1-MerchBusActgMCQ-768": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Discount Forfeited", "type": "Multiple Choice", "competency": "Analyze", "question": "Customer pays after the discount period. Analyze the entry to record the receipt.", "options": ["Debit Cash; Credit Accounts Receivable", "Debit Cash; Debit Sales Discounts; Credit Accounts Receivable", "Debit Cash; Credit Sales; Credit Interest Income", "Debit Cash; Credit Accounts Receivable; Credit Sales Discounts Forfeited"], "answer": 0, "explanation": "If the discount is missed, the customer pays the full gross amount. Simply Debit Cash and Credit Accounts Receivable for the full value."}},
{"FABM1-MerchBusActgMCQ-769": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "VAT Input Tax", "type": "Multiple Choice", "competency": "Analyze", "question": "Purchased inventory ₱56,000 (VAT inclusive). Analyze the amount debited to the 'Purchases' account.", "options": ["₱50,000", "₱56,000", "₱6,000", "₱62,720"], "answer": 0, "explanation": "Purchases is recorded net of tax. ₱56,000 / 1.12 = ₱50,000. The ₱6,000 is debited to Input Tax."}},
{"FABM1-MerchBusActgMCQ-770": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Freight - FOB Shipping Point, Collect", "type": "Multiple Choice", "competency": "Analyze", "question": "Goods shipped FOB Shipping Point, Freight Collect ₱800. Analyze the Seller's entry.", "options": ["No entry required for freight", "Debit Freight-Out ₱800; Credit Cash ₱800", "Debit Accounts Receivable ₱800; Credit Cash ₱800", "Debit Freight-Out ₱800; Credit Accounts Payable ₱800"], "answer": 0, "explanation": "Seller has no responsibility. Buyer owns goods in transit and Buyer pays carrier. Seller records nothing."}},
{"FABM1-MerchBusActgMCQ-771": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Credit Card Fee", "type": "Multiple Choice", "competency": "Analyze", "question": "Sales ₱10,000 on credit cards. Bank charges 3%. Analyze the debit to 'Credit Card Expense'.", "options": ["₱300", "₱9,700", "₱10,000", "₱30"], "answer": 0, "explanation": "Expense is 3% of ₱10,000 = ₱300."}},
{"FABM1-MerchBusActgMCQ-772": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Net Price Method (Recording)", "type": "Multiple Choice", "competency": "Analyze", "question": "Purchased goods ₱20,000, terms 2/10, n/30. Using the Net Price Method, analyze the initial debit to Purchases.", "options": ["₱19,600", "₱20,000", "₱20,400", "₱18,000"], "answer": 0, "explanation": "Net method records purchases at the discounted price immediately. ₱20,000 * 98% = ₱19,600."}},
{"FABM1-MerchBusActgMCQ-773": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Adjusting Entry - Unearned Revenue", "type": "Multiple Choice", "competency": "Analyze", "question": "Customer paid ₱10,000 in advance for goods. Goods were delivered today. Analyze the entry.", "options": ["Debit Unearned Sales Revenue ₱10,000; Credit Sales ₱10,000", "Debit Cash ₱10,000; Credit Sales ₱10,000", "Debit Sales ₱10,000; Credit Unearned Sales Revenue ₱10,000", "Debit Accounts Receivable ₱10,000; Credit Sales ₱10,000"], "answer": 0, "explanation": "The liability (Unearned Revenue) is satisfied by delivery. Debit the Liability to remove it, Credit Sales to recognize revenue."}},
{"FABM1-MerchBusActgMCQ-774": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Collection of Written-off Account", "type": "Multiple Choice", "competency": "Analyze", "question": "Recovered ₱1,000 from a customer previously written off. Analyze the impact on Accounts Receivable.", "options": ["Increases then Decreases", "Increases only", "Decreases only", "No effect"], "answer": 0, "explanation": "First, you reinstate the account (Increase AR). Then, you record the collection (Decrease AR). Net effect on balance is zero, but the trail is recorded."}},
{"FABM1-MerchBusActgMCQ-775": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Compound Purchase", "type": "Multiple Choice", "competency": "Analyze", "question": "Purchased inventory ₱30,000 and supplies ₱5,000 on account. Analyze the credit entry.", "options": ["Credit Accounts Payable ₱35,000", "Credit Cash ₱35,000", "Credit Accounts Payable ₱30,000; Credit Cash ₱5,000", "Credit Purchases ₱35,000"], "answer": 0, "explanation": "Total liability to the supplier is the sum of items purchased. Credit Accounts Payable ₱35,000."}},
{"FABM1-MerchBusActgMCQ-776": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Sales Return with VAT", "type": "Multiple Choice", "competency": "Analyze", "question": "Accepted a return of goods sold for ₱5,600 (VAT inclusive). Analyze the debit to Output Tax.", "options": ["₱600", "₱5,000", "₱5,600", "₱672"], "answer": 0, "explanation": "The tax originally collected must be reversed. ₱5,600 / 1.12 = ₱5,000 (Sales). Tax = ₱600. Debit Output Tax ₱600."}},
{"FABM1-MerchBusActgMCQ-777": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Correction of Error", "type": "Multiple Choice", "competency": "Analyze", "question": "A ₱500 Freight-In payment was erroneously debited to Advertising Expense. Analyze the correcting entry.", "options": ["Debit Freight-In ₱500; Credit Advertising Expense ₱500", "Debit Advertising Expense ₱500; Credit Freight-In ₱500", "Debit Freight-In ₱500; Credit Cash ₱500", "Debit Cash ₱500; Credit Advertising Expense ₱500"], "answer": 0, "explanation": "Remove the error (Credit Advertising) and place it in the right account (Debit Freight-In)."}},
{"FABM1-MerchBusActgMCQ-778": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Note Dishonor", "type": "Multiple Choice", "competency": "Analyze", "question": "Customer failed to pay ₱20,000 note. Analyze the reclassification.", "options": ["Debit Accounts Receivable ₱20,000; Credit Notes Receivable ₱20,000", "Debit Bad Debts Expense ₱20,000; Credit Notes Receivable ₱20,000", "Debit Notes Receivable ₱20,000; Credit Accounts Receivable ₱20,000", "Debit Loss on Notes ₱20,000; Credit Notes Receivable ₱20,000"], "answer": 0, "explanation": "When a note matures and isn't paid, it's removed from Notes Receivable and charged back to Accounts Receivable to pursue collection."}},
{"FABM1-MerchBusActgMCQ-779": {"subject": "FABM1", "topic": "Analyzing and Recording Merchandising Business Transactions", "subtopic": "Net Sales Calculation", "type": "Multiple Choice", "competency": "Analyze", "question": "Gross Sales ₱100k, Freight-Out ₱5k, Sales Returns ₱8k, Sales Discounts ₱2k. Analyze Net Sales.", "options": ["₱90,000", "₱85,000", "₱92,000", "₱95,000"], "answer": 0, "explanation": "Net Sales = Gross - Returns - Discounts. 100k - 8k - 2k = ₱90,000. Freight-Out is an operating expense, not a contra-revenue."}},
  {"FABM1-MerchBusAcgMCQ-680": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Net Loss", "type": "Multiple Choice", "competency": "Apply", "question": "After closing revenues and expenses, the Income Summary account has a Debit balance of ₱45,000. Apply the entry to close this balance.", "options": ["Debit Capital ₱45,000; Credit Income Summary ₱45,000", "Debit Sales ₱45,000; Credit Income Summary ₱45,000", "Debit Income Summary ₱45,000; Credit Capital ₱45,000", "Debit Drawings ₱45,000; Credit Income Summary ₱45,000"], "answer": 0, "explanation": "A Debit balance in Income Summary represents a Net Loss. To close it, you must Credit Income Summary and Debit the Owner's Capital account (reducing equity)."}},
{"FABM1-MerchBusAcgMCQ-681": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchase Returns", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase Returns and Allowances has a Credit balance of ₱6,500. Apply the closing entry.", "options": ["Debit Capital ₱6,500; Credit Purchase Returns ₱6,500", "Debit Purchase Returns ₱6,500; Credit Income Summary ₱6,500", "Debit Income Summary ₱6,500; Credit Purchase Returns ₱6,500", "Debit Purchase Returns ₱6,500; Credit Purchases ₱6,500"], "answer": 1, "explanation": "It is a contra-cost account with a Credit balance. To close it to zero, you must Debit the account and Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-682": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Sales Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Discounts has a Debit balance of ₱2,200. Apply the closing entry.", "options": ["Debit Sales Discounts ₱2,200; Credit Income Summary ₱2,200", "Debit Sales ₱2,200; Credit Sales Discounts ₱2,200", "Debit Income Summary ₱2,200; Credit Sales Discounts ₱2,200", "Debit Capital ₱2,200; Credit Sales Discounts ₱2,200"], "answer": 2, "explanation": "Sales Discounts is a contra-revenue account (Debit). To close it, Credit the account and Debit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-683": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Accrued Interest", "type": "Multiple Choice", "competency": "Apply", "question": "On Dec 31, the company adjusted for Accrued Interest Expense of ₱1,500 (Dr Interest Exp, Cr Interest Payable). Apply the reversing entry on Jan 1.", "options": ["Debit Interest Payable ₱1,500; Credit Interest Expense ₱1,500", "No reversing entry is needed", "Debit Interest Payable ₱1,500; Credit Cash ₱1,500", "Debit Interest Expense ₱1,500; Credit Interest Payable ₱1,500"], "answer": 0, "explanation": "The reversing entry is the exact opposite of the accrual adjustment. Debit the Liability (Payable) and Credit the Expense."}},
{"FABM1-MerchBusAcgMCQ-684": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Freight-In", "type": "Multiple Choice", "competency": "Apply", "question": "Freight-In account has a Debit balance of ₱8,000. Apply the closing entry.", "options": ["Debit Freight-In ₱8,000; Credit Income Summary ₱8,000", "Debit Cost of Goods Sold ₱8,000; Credit Freight-In ₱8,000", "Debit Capital ₱8,000; Credit Freight-In ₱8,000", "Debit Income Summary ₱8,000; Credit Freight-In ₱8,000"], "answer": 3, "explanation": "Freight-In is a temporary cost account with a Debit balance. Close it by Crediting Freight-In and Debiting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-685": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Calculating Net Income", "type": "Multiple Choice", "competency": "Apply", "question": "Sales ₱200k, Sales Returns ₱10k, Purchases ₱120k, Expenses ₱40k. Apply the closing process to determine the credit to Capital.", "options": ["₱30,000", "₱70,000", "₱40,000", "₱20,000"], "answer": 0, "explanation": "Net Sales = 190k. Total Costs/Exp = 120k + 40k = 160k. Net Income = 190k - 160k = ₱30,000."}},
{"FABM1-MerchBusAcgMCQ-686": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Drawings", "type": "Multiple Choice", "competency": "Apply", "question": "The owner withdrew ₱20,000 cash. Apply the entry to close the Owner's Drawing account.", "options": ["Debit Income Summary ₱20,000; Credit Drawings ₱20,000", "Debit Drawings ₱20,000; Credit Capital ₱20,000", "Debit Capital ₱20,000; Credit Drawings ₱20,000", "Debit Drawings ₱20,000; Credit Cash ₱20,000"], "answer": 2, "explanation": "Drawings are closed directly to Capital to reflect the reduction in equity. Debit Capital, Credit Drawings."}},
{"FABM1-MerchBusAcgMCQ-687": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Supplies (Expense Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Supplies Expense was debited ₱10,000 during the year. Count shows ₱2,000 on hand. Apply the adjustment.", "options": ["Debit Supplies (Asset) ₱2,000; Credit Supplies Expense ₱2,000", "Debit Supplies Expense ₱2,000; Credit Supplies (Asset) ₱2,000", "Debit Supplies (Asset) ₱8,000; Credit Supplies Expense ₱8,000", "Debit Supplies Expense ₱8,000; Credit Supplies (Asset) ₱8,000"], "answer": 0, "explanation": "Expense Method records everything as expense initially. You must set up the asset for what is left (₱2,000). Debit Asset, Credit Expense."}},
{"FABM1-MerchBusAcgMCQ-688": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Supplies (Expense Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Referring to the previous question (Adj: Dr Supplies ₱2k, Cr Supplies Exp ₱2k), apply the reversing entry.", "options": ["Debit Supplies Expense ₱2,000; Credit Supplies ₱2,000", "Debit Cash ₱2,000; Credit Supplies ₱2,000", "Debit Supplies ₱2,000; Credit Supplies Expense ₱2,000", "No reversing entry"], "answer": 0, "explanation": "Since the Expense Method was used (creating an asset adjustment), it is reversed. Debit Expense, Credit Asset."}},
{"FABM1-MerchBusAcgMCQ-689": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Periodic Inventory (Beg)", "type": "Multiple Choice", "competency": "Apply", "question": "In the Periodic System, apply the entry to close Beginning Inventory of ₱55,000.", "options": ["Debit Merchandise Inventory ₱55,000; Credit Income Summary ₱55,000", "Debit Cost of Goods Sold ₱55,000; Credit Merchandise Inventory ₱55,000", "Debit Capital ₱55,000; Credit Merchandise Inventory ₱55,000", "Debit Income Summary ₱55,000; Credit Merchandise Inventory ₱55,000"], "answer": 3, "explanation": "Beginning Inventory is removed (Credited) and transferred to Income Summary (Debited) as part of the cost of goods available calculation."}},
{"FABM1-MerchBusAcgMCQ-690": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Periodic Inventory (End)", "type": "Multiple Choice", "competency": "Apply", "question": "In the Periodic System, apply the entry to record Ending Inventory of ₱60,000.", "options": ["Debit Merchandise Inventory ₱60,000; Credit Income Summary ₱60,000", "Debit Income Summary ₱60,000; Credit Merchandise Inventory ₱60,000", "Debit Sales ₱60,000; Credit Merchandise Inventory ₱60,000", "Debit Merchandise Inventory ₱60,000; Credit Capital ₱60,000"], "answer": 0, "explanation": "Ending Inventory is established as an asset. Debit Merchandise Inventory and Credit Income Summary (effectively deducting it from costs)."}},
{"FABM1-MerchBusAcgMCQ-691": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Compound Expense Closing", "type": "Multiple Choice", "competency": "Apply", "question": "Close the following: Rent Exp ₱12,000; Salaries Exp ₱20,000; Utilities Exp ₱3,000.", "options": ["Debit Capital ₱35,000; Credit Expenses ₱35,000", "Debit Income Summary ₱35,000; Credit Rent ₱12k, Salaries ₱20k, Utilities ₱3k", "Debit Rent ₱12k, Salaries ₱20k, Utilities ₱3k; Credit Income Summary ₱35k", "Debit Income Summary ₱35,000; Credit Accounts Payable ₱35,000"], "answer": 1, "explanation": "Debit Income Summary for the total expenses. Credit each individual expense account to zero them out."}},
{"FABM1-MerchBusAcgMCQ-692": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 9: Post-Closing TB Content", "type": "Multiple Choice", "competency": "Apply", "question": "Which account should appear on the Post-Closing Trial Balance?", "options": ["Sales Revenue", "Accumulated Depreciation", "Depreciation Expense", "Income Summary"], "answer": 1, "explanation": "Accumulated Depreciation is a contra-asset (Real Account) and carries over. All others are nominal accounts which are closed."}},
{"FABM1-MerchBusAcgMCQ-693": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Unearned (Income Method)", "type": "Multiple Choice", "competency": "Apply", "question": "Received ₱24,000 for service, credited to 'Service Revenue'. At year-end, ₱4,000 is unearned. Adjustment: Dr Revenue, Cr Unearned Revenue. Apply the reversal.", "options": ["Debit Unearned Revenue ₱20,000; Credit Service Revenue ₱20,000", "Debit Unearned Revenue ₱4,000; Credit Service Revenue ₱4,000", "No reversal needed", "Debit Service Revenue ₱4,000; Credit Unearned Revenue ₱4,000"], "answer": 1, "explanation": "Since the Income Method was used (creating a liability adjustment), it is reversed. Debit Liability, Credit Revenue."}},
{"FABM1-MerchBusAcgMCQ-694": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Income Summary (Profit)", "type": "Multiple Choice", "competency": "Apply", "question": "Income Summary has a Credit balance of ₱75,000. Apply the closing entry.", "options": ["Debit Income Summary ₱75,000; Credit Capital ₱75,000", "Debit Income Summary ₱75,000; Credit Cash ₱75,000", "Debit Capital ₱75,000; Credit Income Summary ₱75,000", "Debit Sales ₱75,000; Credit Capital ₱75,000"], "answer": 0, "explanation": "A Credit balance is a Profit. Close it by Debiting Income Summary and Crediting Capital."}},
{"FABM1-MerchBusAcgMCQ-695": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Depreciation", "type": "Multiple Choice", "competency": "Apply", "question": "Truck cost ₱500,000, life 5 years, no salvage. Apply the annual depreciation adjustment.", "options": ["Debit Depreciation Exp ₱100,000; Credit Truck ₱100,000", "Debit Depreciation Exp ₱100,000; Credit Accumulated Depreciation ₱100,000", "Debit Accumulated Depreciation ₱100,000; Credit Depreciation Exp ₱100,000", "Debit Truck Expense ₱100,000; Credit Cash ₱100,000"], "answer": 1, "explanation": "Straight line: ₱500k / 5 = ₱100k. Debit Expense, Credit Contra-Asset (Accumulated Depreciation)."}},
{"FABM1-MerchBusAcgMCQ-696": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Revenue balance is ₱350,000. Apply the closing entry.", "options": ["Debit Sales ₱350,000; Credit Income Summary ₱350,000", "Debit Income Summary ₱350,000; Credit Sales ₱350,000", "Debit Sales ₱350,000; Credit Capital ₱350,000", "Debit Cash ₱350,000; Credit Sales ₱350,000"], "answer": 0, "explanation": "Revenue has a Credit balance. Debit Sales to close it, Credit Income Summary."}},
{"FABM1-MerchBusAcgMCQ-697": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Adjusting Bad Debts", "type": "Multiple Choice", "competency": "Apply", "question": "Accounts Receivable ₱200,000. Estimate 2% uncollectible. Allowance has ₱500 credit balance. Apply adjustment.", "options": ["Debit Bad Debts Exp ₱4,000; Credit Allowance ₱4,000", "Debit Allowance ₱3,500; Credit Bad Debts Exp ₱3,500", "Debit Bad Debts Exp ₱3,500; Credit Allowance ₱3,500", "Debit Bad Debts Exp ₱4,500; Credit Allowance ₱4,500"], "answer": 2, "explanation": "Target = 200k * 2% = ₱4,000. Existing Credit = ₱500. Required Adjustment = ₱4,000 - ₱500 = ₱3,500."}},
{"FABM1-MerchBusAcgMCQ-698": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Reversing Accrued Salaries", "type": "Multiple Choice", "competency": "Apply", "question": "Year-end adjustment: Dr Salaries Expense ₱5,000, Cr Salaries Payable ₱5,000. Apply the reversal.", "options": ["Debit Salaries Payable ₱5,000; Credit Salaries Expense ₱5,000", "Debit Salaries Payable ₱5,000; Credit Cash ₱5,000", "Debit Salaries Expense ₱5,000; Credit Salaries Payable ₱5,000", "No reversal needed"], "answer": 0, "explanation": "Accruals are reversed. Debit the Liability (Payable) and Credit the Expense."}},
{"FABM1-MerchBusAcgMCQ-699": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Purchase Discounts", "type": "Multiple Choice", "competency": "Apply", "question": "Purchase Discounts has a Credit balance of ₱1,800. Apply the closing entry.", "options": ["Debit Purchase Discounts ₱1,800; Credit Income Summary ₱1,800", "Debit Purchase Discounts ₱1,800; Credit Purchases ₱1,800", "Debit Income Summary ₱1,800; Credit Purchase Discounts ₱1,800", "Debit Capital ₱1,800; Credit Purchase Discounts ₱1,800"], "answer": 0, "explanation": "Contra-cost (Credit balance). Close by Debiting Purchase Discounts and Crediting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-700": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Capital Calculation", "type": "Multiple Choice", "competency": "Apply", "question": "Beg Capital ₱100k. Net Income ₱30k. Drawings ₱10k. Apply the closing effects to find Ending Capital.", "options": ["₱130,000", "₱140,000", "₱110,000", "₱120,000"], "answer": 3, "explanation": "End Cap = Beg + NI - Drawings. 100k + 30k - 10k = ₱120,000."}},
{"FABM1-MerchBusAcgMCQ-701": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 7: Perpetual Inventory Shrinkage", "type": "Multiple Choice", "competency": "Apply", "question": "System shows ₱50,000 inventory. Count shows ₱49,000. Apply the adjustment.", "options": ["Debit COGS ₱1,000; Credit Inventory ₱1,000", "Debit Inventory ₱1,000; Credit COGS ₱1,000", "Debit Loss ₱1,000; Credit Purchases ₱1,000", "Debit Sales ₱1,000; Credit Inventory ₱1,000"], "answer": 0, "explanation": "Inventory is overstated. Credit Inventory. Debit Cost of Goods Sold (or Loss on Shrinkage)."}},
{"FABM1-MerchBusAcgMCQ-702": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Closing Contra-Revenue", "type": "Multiple Choice", "competency": "Apply", "question": "Sales Returns has a Debit balance of ₱4,000. Apply the closing entry.", "options": ["Debit Income Summary ₱4,000; Credit Sales Returns ₱4,000", "Debit Sales ₱4,000; Credit Sales Returns ₱4,000", "Debit Sales Returns ₱4,000; Credit Income Summary ₱4,000", "Debit Capital ₱4,000; Credit Sales Returns ₱4,000"], "answer": 0, "explanation": "Contra-revenue (Debit). Close by Crediting the account and Debiting Income Summary."}},
{"FABM1-MerchBusAcgMCQ-703": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 8: Income Summary Analysis", "type": "Multiple Choice", "competency": "Apply", "question": "If Income Summary is debited for Expenses ₱90k and Credited for Revenues ₱80k, apply the closing entry for the balance.", "options": ["Debit Capital ₱10,000; Credit Income Summary ₱10,000", "Debit Income Summary ₱10,000; Credit Capital ₱10,000", "Debit Sales ₱10,000; Credit Capital ₱10,000", "Debit Drawings ₱10,000; Credit Income Summary ₱10,000"], "answer": 0, "explanation": "Debits (Exp) > Credits (Rev) = Net Loss of ₱10k. Income Summary has a Debit balance. Close by Crediting Income Summary and Debiting Capital."}},
{"FABM1-MerchBusAcgMCQ-704": {"subject": "FABM1", "topic": "Closing Procedures in Merchandising Business", "subtopic": "Step 10: Identifying Reversals", "type": "Multiple Choice", "competency": "Apply", "question": "Which of the following adjustments is typically reversed?", "options": ["Depreciation Expense", "Accrued Salaries Expense", "Bad Debts Expense", "Amortization Expense"], "answer": 1, "explanation": "Accruals (Payables/Receivables created by adjustment) are typically reversed to simplify record keeping in the next period. Depreciation and Bad Debts are never reversed."}},
{"FABM1-MerchBusAcgMCQ-730": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Bank Reconciliation", "type": "Multiple Choice", "competency": "Evaluate", "question": "The bookkeeper is responsible for recording cash receipts, making bank deposits, and preparing the monthly bank reconciliation. Evaluate this assignment of duties.", "options": ["Efficient; reduces communication errors", "Acceptable; the bank statement is an independent record", "High risk; the bookkeeper can steal cash and manipulate the reconciliation to hide the theft", "Low risk; the bank catches all errors"], "answer": 2, "explanation": "This is a fundamental segregation of duties failure. The person with custody (deposits) and recording (ledger) responsibilities should not perform the reconciliation (independent check)."}},
{"FABM1-MerchBusAcgMCQ-731": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Documentation - Receipts", "type": "Multiple Choice", "competency": "Evaluate", "question": "Sales clerks use a generic, unnumbered receipt book purchased from a bookstore to issue receipts to customers. Evaluate the risk.", "options": ["Low; a receipt is a receipt", "High; without pre-printed sequence numbers, management cannot ensure all sales are recorded (completeness)", "Moderate; acceptable for cash sales", "Zero; customers don't check numbers"], "answer": 1, "explanation": "Pre-numbered documents are essential for completeness controls. Without them, a clerk can rip out a receipt copy and pocket the cash without leaving a missing number trail."}},
{"FABM1-MerchBusAcgMCQ-732": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Physical Access", "type": "Multiple Choice", "competency": "Evaluate", "question": "Sales staff have keys to the warehouse to check stock levels for customers. Evaluate the physical control over inventory.", "options": ["Excellent; improves customer service speed", "Weakness; sales staff can remove inventory without authorization or recording", "Acceptable; sales staff are employees", "Weakness; keys should be digital"], "answer": 1, "explanation": "Custody of assets should be restricted to warehouse personnel. Giving Sales access allows for theft that can be blamed on warehouse shrinkage."}},
{"FABM1-MerchBusAcgMCQ-733": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Cash Disbursements", "type": "Multiple Choice", "competency": "Evaluate", "question": "After signing checks, the Treasurer returns them to the Accounts Payable clerk to mail to vendors. Evaluate the control weakness.", "options": ["No weakness; the clerk knows the addresses", "Critical weakness; the clerk can retrieve a fraudulent check they set up, alter the payee, or intercept it", "Minor weakness; saves the Treasurer time", "No weakness; the check is already signed"], "answer": 1, "explanation": "Once signed, checks are liquid assets. They should be mailed directly by the signer or a mailroom, never returned to the person who prepared the voucher (AP)."}},
{"FABM1-MerchBusAcgMCQ-734": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Payroll Authorization", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Human Resources department authorizes pay rate changes, while the Payroll department processes the checks. Evaluate the segregation of duties.", "options": ["Effective; separates authorization (HR) from recording/processing (Payroll)", "Ineffective; HR should process the checks", "Ineffective; Payroll should set the rates", "Redundant; one department is enough"], "answer": 0, "explanation": "This is a correct segregation. If Payroll could change rates AND print checks, they could give themselves unauthorized raises."}},
{"FABM1-MerchBusAcgMCQ-735": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "IT Security - Unattended", "type": "Multiple Choice", "competency": "Evaluate", "question": "Accounting staff leave their computer terminals logged in and unlocked when going to lunch. Evaluate the IT control environment.", "options": ["Acceptable; the office is locked", "Unacceptable; unauthorized users can process transactions under the absent employee's ID", "Acceptable; saves login time", "Unacceptable; wastes electricity"], "answer": 1, "explanation": "Unattended, unlocked terminals bypass access controls. Anyone walking by can enter fraudulent data, and the audit trail will blame the absent employee."}},
{"FABM1-MerchBusAcgMCQ-736": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Small Business Controls", "type": "Multiple Choice", "competency": "Evaluate", "question": "In a small shop with only two employees, the owner reviews every bank statement and signs every check personally. Evaluate this 'Compensating Control'.", "options": ["Ineffective; the owner is too busy", "Effective; active owner oversight mitigates the lack of segregation of duties", "Ineffective; segregation is required by law", "Effective; only if the owner is an accountant"], "answer": 1, "explanation": "In small businesses where segregation is impossible, the owner's direct involvement/review acts as the primary control against fraud."}},
{"FABM1-MerchBusAcgMCQ-737": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Auditor Independence", "type": "Multiple Choice", "competency": "Evaluate", "question": "The internal auditor designs and implements a new inventory system, then audits it the following month. Evaluate the objectivity.", "options": ["High; they know the system best", "Compromised; this is a self-review threat (auditing one's own work)", "High; saves consulting fees", "Compromised; the audit is too soon"], "answer": 1, "explanation": "Auditors cannot objectively evaluate a system they built. They must remain independent of operations."}},
{"FABM1-MerchBusAcgMCQ-738": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Purchasing - Rotation", "type": "Multiple Choice", "competency": "Evaluate", "question": "Purchasing agents manage the same vendors for 20 years without rotation. Evaluate the fraud risk.", "options": ["Low; long relationships are efficient", "High; risk of cozy relationships leading to kickbacks or supplier favoritism", "Moderate; acceptable if prices are low", "Zero; agents are loyal"], "answer": 1, "explanation": "Job rotation prevents employees from establishing collusive relationships with vendors. Long tenure with a specific vendor increases the risk of kickbacks."}},
{"FABM1-MerchBusAcgMCQ-739": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Inventory Valuation", "type": "Multiple Choice", "competency": "Evaluate", "question": "Returned goods are placed back on the shelf without inspection for damage. Evaluate the financial statement risk.", "options": ["Inventory Assets may be overstated (broken goods valued as new)", "Inventory Assets are understated", "COGS is overstated", "Sales are understated"], "answer": 0, "explanation": "If damaged goods are recorded as new inventory, the asset value is overstated on the balance sheet, and future customers will receive bad products."}},
{"FABM1-MerchBusAcgMCQ-740": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Fixed Asset Disposal", "type": "Multiple Choice", "competency": "Evaluate", "question": "A manager sells a company truck to his brother for ₱10,000 when the market value is ₱50,000. Evaluate the control failure.", "options": ["Conflict of Interest / Lack of independent valuation", "Segregation of Duties", "Data Processing Error", "Lack of Documentation"], "answer": 0, "explanation": "Selling assets below market value to a related party is a form of asset misappropriation. Controls should require independent valuation and arm's-length transaction proof."}},
{"FABM1-MerchBusAcgMCQ-741": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Cybersecurity", "type": "Multiple Choice", "competency": "Evaluate", "question": "Employees receive no training on how to identify phishing emails. Evaluate the IT risk.", "options": ["High risk of ransomware or data breach via credential theft", "Low risk; the firewall stops everything", "Moderate risk; spam filters work", "High risk; computers will slow down"], "answer": 0, "explanation": "Phishing is the most common entry point for hackers. Lack of training is a significant control weakness in the Control Environment."}},
{"FABM1-MerchBusAcgMCQ-742": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Cash Receipts - Mail", "type": "Multiple Choice", "competency": "Evaluate", "question": "The mailroom clerk opens checks and immediately prepares a list of receipts before passing them to accounting. Evaluate this control.", "options": ["Strong; establishes a control total to prevent theft by accounting staff", "Weak; the clerk should not open mail", "Strong; speeds up the deposit", "Weak; the list is unnecessary"], "answer": 0, "explanation": "This initial list serves as the independent record. Accounting must match the deposit slip to this list. If accounting steals a check, the list will reveal the discrepancy."}},
{"FABM1-MerchBusAcgMCQ-743": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Ghost Vendors", "type": "Multiple Choice", "competency": "Evaluate", "question": "The system automatically checks for duplicate addresses between the Employee Master File and the Vendor Master File. Evaluate the purpose.", "options": ["To save postage", "To detect 'Ghost Vendors' (employees setting themselves up as suppliers)", "To merge files", "To detect duplicate payments"], "answer": 1, "explanation": "A match between an employee's home address and a vendor's address is a major red flag for fraud (Self-dealing)."}},
{"FABM1-MerchBusAcgMCQ-744": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Unclaimed Wages", "type": "Multiple Choice", "competency": "Evaluate", "question": "Unclaimed payroll checks are returned to the payroll clerk for safekeeping. Evaluate the risk.", "options": ["High; the clerk can forge the signature and cash the check", "Low; the clerk is responsible", "Moderate; acceptable for 30 days", "High; the checks might expire"], "answer": 0, "explanation": "Unclaimed assets should never go back to the person who authorized/prepared them. They should go to an independent cashier or treasurer."}},
{"FABM1-MerchBusAcgMCQ-745": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Inventory Tagging", "type": "Multiple Choice", "competency": "Evaluate", "question": "During a physical count, several boxes of inventory are found without count tags. The counters ignore them. Evaluate the assertion violated.", "options": ["Existence", "Completeness", "Valuation", "Rights and Obligations"], "answer": 1, "explanation": "Completeness means 'all assets are recorded'. Ignoring untagged items means the final inventory number will be incomplete (understated)."}},
{"FABM1-MerchBusAcgMCQ-746": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Stale Checks", "type": "Multiple Choice", "competency": "Evaluate", "question": "Outstanding checks older than 6 months are written off to 'Miscellaneous Income' without investigation. Evaluate the policy.", "options": ["Appropriate; extra income is good", "Inappropriate; liability may still exist or funds may be escheatable to the government", "Appropriate; banks don't honor old checks", "Inappropriate; should be Sales Revenue"], "answer": 1, "explanation": "Just because a check wasn't cashed doesn't mean the debt is gone. Writing it off to income hides potential liabilities or unclaimed property issues."}},
{"FABM1-MerchBusAcgMCQ-747": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Journal Entry Review", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Controller posts adjusting journal entries at year-end without review or approval by the CFO. Evaluate the risk.", "options": ["Low; the Controller is an expert", "High; risk of earnings management or covering up fraud via adjustments", "Moderate; acceptable if audited later", "Low; software prevents errors"], "answer": 1, "explanation": "Top-side journal entries are a common method for financial statement fraud. All manual adjustments must be reviewed and approved."}},
{"FABM1-MerchBusAcgMCQ-748": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Physical Security - Server", "type": "Multiple Choice", "competency": "Evaluate", "question": "The server room has no fire suppression system and is located in the basement (flood risk). Evaluate the Disaster Recovery readiness.", "options": ["Adequate; servers are waterproof", "Inadequate; physical environmental risks threaten business continuity", "Adequate; data is in the cloud", "Inadequate; basements are cold"], "answer": 1, "explanation": "Physical controls include protection against elements. A flood or fire could destroy the company's ability to operate."}},
{"FABM1-MerchBusAcgMCQ-749": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Petty Cash - Supporting Docs", "type": "Multiple Choice", "competency": "Evaluate", "question": "Petty cash vouchers are submitted in pencil without attached invoices. Evaluate the validity.", "options": ["Valid; pencil allows for corrections", "Invalid; lack of permanence and evidence allows for alteration and theft", "Valid; trust the employee", "Invalid; must be typed"], "answer": 1, "explanation": "Ink prevents alteration. Attached invoices provide evidence. Pencil + no receipt = high fraud risk."}},
{"FABM1-MerchBusAcgMCQ-750": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Customer Credit Limits", "type": "Multiple Choice", "competency": "Evaluate", "question": "The sales system allows salespeople to override customer credit limits to force a sale through. Evaluate the control.", "options": ["Good; empowers sales", "Bad; defeats the purpose of the credit limit control", "Good; increases revenue", "Bad; slows down the system"], "answer": 1, "explanation": "A control that can be easily overridden by the person it is meant to constrain (salesperson) is a useless control."}},
{"FABM1-MerchBusAcgMCQ-751": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Check Sequence", "type": "Multiple Choice", "competency": "Evaluate", "question": "The accounting manager notices a gap in the check numbers (e.g., 101, 102, 104...) but ignores it. Evaluate the oversight.", "options": ["Minor issue; check 103 was probably voided", "Major issue; check 103 could have been stolen and used fraudulently", "Minor issue; checks are cheap", "Major issue; the bank will be confused"], "answer": 1, "explanation": "Missing documents are a red flag. The missing check must be investigated to ensure it wasn't stolen."}},
{"FABM1-MerchBusAcgMCQ-752": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Whistleblower Hotline", "type": "Multiple Choice", "competency": "Evaluate", "question": "The fraud hotline is answered by the company's legal department (an external third party). Evaluate the design.", "options": ["Effective; ensures anonymity and independence", "Ineffective; it should be answered by the CEO", "Effective; lawyers are scary", "Ineffective; costs too much"], "answer": 0, "explanation": "Third-party management of hotlines increases trust. Employees are more likely to report if they know their boss won't answer the phone."}},
{"FABM1-MerchBusAcgMCQ-753": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Receivables Write-off", "type": "Multiple Choice", "competency": "Evaluate", "question": "The Collections Officer, who handles cash payments, is authorized to write off bad debts. Evaluate the Segregation of Duties.", "options": ["Efficient; they know who won't pay", "Conflict of Interest; they can steal a payment and write off the balance to hide it", "Efficient; saves management time", "Conflict of Interest; they might write off too little"], "answer": 1, "explanation": "Custody (Collections) must be separate from Authorization (Write-off). Combined, they allow for skimming with concealment."}},
{"FABM1-MerchBusAcgMCQ-754": {"subject": "FABM1", "topic": "Evaluating Internal Control Policies and Procedures in Merchandising Business", "subtopic": "Invoice Processing", "type": "Multiple Choice", "competency": "Evaluate", "question": "The company processes payments based on 'Statements of Account' rather than individual invoices. Evaluate the risk.", "options": ["Low; statements summarize invoices", "High; risk of duplicate payments if invoices are also paid later", "Moderate; acceptable for utilities", "Low; statements are legal demands"], "answer": 1, "explanation": "You should pay from specific Invoices, not Statements. Statements often include amounts already paid or pending. Paying from statements often leads to double payment."}}  
];
